[6103] = {"Die Ausbildung des Tiers", nil, {"Sprecht mit Jocaste in der Enklave des Cenarius in Darnassus."}},
[6122] = {"Die eigentliche Ursache", nil, {"Benutzt das leere Gefäß bei den Klippenquellfällen und nehmt eine Wasserprobe vom Höhleneingang bei den Wasserfällen.\n\nBringt die gefüllte Probe der Klippenquellfälle zu Alanndarian Nachtweise nach Auberdine in Dunkelküste."}},
[6123] = {"Das Heilmittel sammeln", nil, {"Bringt 5 Erdwurzeln und 12 Lunarfungi zu Alanndarian Nachtweise in Auberdine, damit sie eine Heilsalbe machen kann."}},
[6124] = {"Heilung der Kranken", nil, {"Benutzt die heilende Tiersalbe bei 10 kränklichen Rehen in ganz Dunkelküste. Das müsste sie heilen. Es wurde berichtet, dass kränkliche Rehe südlich des Klippenquells, im Norden von Auberdine und bis hinunter in die südlichen Regionen von Dunkelküste, wo das Eschental beginnt, aufgetaucht ist."}},
[6125] = {"Macht über Gift", nil, {"Kehrt zu Mathrengyl Bärenfährte in der Enklave des Cenarius in Darnassus zurück."}},
[6126] = {"Neue Lektionen", nil, {"Reist zur Mondlichtung und sprecht mit Dendrite Sternenschauer im Dorf Nachthafen."}},
[6127] = {"Die eigentliche Ursache", nil, {"Benutzt die leere Glutnebelgipfelprobe beim Glutnebelgipfel und nehmt eine Wasserprobe vom Teich auf der Spitze des Gipfels.\n\nBringt das gefüllte Behältnis vom Glutnebelgipfel zu Tonga Runentotem beim Wegekreuz im Brachland."}},
[6128] = {"Das Heilmittel sammeln", nil, {"Bringt 5 Erdwurzeln und 5 Kodohörner zu Tonga Runentotem beim Wegekreuz, damit er eine Heilsalbe machen kann."}},
[6129] = {"Heilung der Kranken", nil, {"Benutzt die heilende Tiersalbe bei 10 kränklichen Gazellen im nördlichen Teil des Brachlandes. Das müsste sie heilen. Kränkliche Gazellen wurden überall nördlich der Ost-West-Straße beim Wegekreuz gesehen."}},
[6130] = {"Macht über Gift", nil, {"Kehrt zu Turak Runentotem auf der Anhöhe der Ältesten in Donnerfels zurück."}},
[6132] = {"Holt mich hier raus!", nil, {"Eskortiert Melizza Brummsumms und sprecht anschließend mit Hornizz Brummsumms beim Kodofriedhdof Desolace."}},
[6133] = {"Das Ersuchen des Waldläuferlords", nil, {"Begebt Euch zur nördlichsten Grenze der Östlichen Pestländer und bringt die Registratur von Quel'Thalas zurück. Der Gegenstand befindet sich irgendwo in der Jagdhütte von Quel'Lithien.\n\nAußerdem müsst Ihr 8 Waldläufer, 8 Pfadschreiter und 8 Waldarbeiter töten."}},
[6134] = {"Geisterplasmasuche", nil, {"Bringt 8 Einheiten Geisterplasma und die Kiste mit den Geistermagneten zu Hornizz Brummsumms in Desolace."}},
[6135] = {"Dämmerschwinge, oh, wie ich Euch hasse...", nil, {"Sucht Dämmerschwinge und tötet ihn. Nehmt ein Büschel vom Fell Dämmerschwinges und bringt es zu Nathanos Pestrufer.\n\nMan sagt, dass Dämmerschwinge nördlich von hier durch das Land streift."}},
[6136] = {"Der Korpulente", nil, {"Sucht in den nördlichen Wüsten der Östlichen Pestländer nach Blutschlinger und vernichtet ihn. Kehrt zu Nathanos Pestrufer zurück, sobald Ihr diese Mission erledigt habt."}},
[6141] = {"Bruder Anton", nil, {"Sprecht mit Bruder Anton in Desolace."}},
[6142] = {"Muschelköder", nil, {"Sucht 10 Teile Fleisch der weichschaligen Muschel und bringt sie zu Mai'Lahii im Dorf Schattenflucht."}},
[6143] = {"Andere Fische zu grillen", nil, {"Tötet 7 Myrmidonen der Zackenkämme, 7 Naga der Zackenkämme und 5 Zauberinnen der Zackenkämme, dann sprecht in Desolace mit Drulzegar in Schattenflucht."}},
[6145] = {"Der Purpurrote Kurier", nil, {"Kehrt in die Östlichen Pestländer zurück und spürt den Purpurroten Kurier auf. Tötet den Kurier und schnappt Euch den Befehl des Obersten Kreuzfahrers.\n\nBringt den Befehl zu Nathanos Pestrufer und wartet auf weitere Anweisungen."}},
[6146] = {"Nathanos' List", nil, {"Reist nach Tyrs Hand, südöstlich der Siedlung Marris. Dort angekommen beißt in den faulen Apfel. In der Verkleidung des Scharlachroten Kreuzzugs überbringt Ihr Lord Valdelmar den Befehl des Obersten Kreuzfahrers."}},
[6148] = {"Das scharlachrote Orakel Demetria", nil, {"Der Scharlachrote Kreuzzug ist unterwegs. Irgendwo auf der Straße von Tyrs Hand nach Stratholme findet Ihr das Orakel Demetria und ihr Gefolge. Jagt sie und tötet sie. Kehrt zu Nathanos Pestrufer zurück, wenn Ihr erfolgreich wart."}},
[6161] = {"Nehmt Rackmores Schatz!", nil, {"Sucht Rackmores Silberschlüssel. Sucht Rackmores goldenen Schlüssel. Sucht und öffnet Rackmores Truhe."}},
[6162] = {"Eines Ehemanns letzter Kampf", nil, {"Tötet Oberanführer Ror und bringt seine Klaue zu Dreka'Sur beim Blutgiftposten im Teufelswald."}},
[6163] = {"Ramstein", nil, {"Reist nach Stratholme und tötet Ramstein den Verschlinger. Bringt seinen Kopf als Souvenir zu Nathanos."}},
[6181] = {"Eine eilige Nachricht", nil, {"Bringt Lewis' Notiz zu Thor dem Greifenmeister."}},
[6182] = {"Der Erste und Letzte", nil, {"Sprecht mit Meister Mathias Shaw in der Altstadt von Sturmwind. Er wohnt in der Kaserne."}},
[6184] = {"Flint Schattenmoor", nil, {"Reist zum Zugwindlager in den westlichen Pestländern und trefft Euren Kontaktmann Flint Schattenmoor."}},
[6185] = {"Die Östlichen Pestländer", nil, {"Sucht in den östlichen Pestländern nach Hinweisen auf den 'Pestrufer' und die vermissten SI:7-Agenten. Wenn Ihr SI:7-Insignien findet, bringt sie zu Flint Schattenmoor am Zugwindlager."}},
[6186] = {"Der Pestrufer kommt", nil, {"Kehrt nach Sturmwind zurück und informiert König Varian Wrynn über das Schicksal von Nathanos Marris."}},
[6187] = {"Ordnung muss wiederhergestellt werden", nil, {"Stellt eine Armee auf und reist zu den Östlichen Pestländern. Beginnt einen gnadenlosen Angriff auf Nathanos Pestrufer und jeden Abschaum der Horde, der ihn beschützen mag.\n\nBewahrt einen klaren Kopf, Bomdiyada. Die Horde wird den Waldläuferlord mit dem Leben verteidigen."}},
[6261] = {"Dungar Trinkfest", nil, {"Bringt Osrics Kiste zu Dungar Trinkfest dem Greifenmeister."}},
[6281] = {"Weiter nach Sturmwind", nil, {"Bucht einen Greifenritt bei Greifenmeister Thor, dann bringt Lewis' Notiz zu Osric Strang, im Laden Begrenzte Immunität, in der Altstadt von Sturmwind."}},
[6282] = {"Harpyien drohen", nil, {"Maggran Erdbinder in Sonnenfels möchte, dass Ihr 7 Harpyien der Blutfurien, 7 Wegelagerinnen der Blutfurien, 7 Töterinnen der Blutfurien und 7 Wildfedern der Blutfurien erschlagt."}},
[6283] = {"Blutfuriengeschlecht", nil, {"Maggran in Sonnenfels möchte, dass Ihr die Zerfetzerin der Blutfurien beseitigt und ihre sterblichen Überreste als Beweis für Eure Tat mitbringt."}},
[6284] = {"Arachnophobie", nil, {"Tötet Besseleth und bringt Besseleths Fangzahn zu Maggran in Sonnenfels."}},
[6285] = {"Zurück zu Lewis", nil, {"Bucht einen Greifenritt zur Späherkuppe bei Greifenmeister Dungar Trinkfest, dann bringt Osrics Kiste zu Lewis an der Späherkuppe."}},
[6301] = {"Zyklus der Wiedergeburt", nil, {"Tammra Windfeld in Sonnenfels möchte, dass Ihr 10 Gaeasamenkörner sammelt."}},
[6321] = {"Nachschub für Brill", nil, {"Bringt Morris' Anforderungsliste zu Fledermausführerin Anette."}},
[6322] = {"Michael Garrett", nil, {"Bringt Gordons Kiste zu Michael Garrett."}},
[6323] = {"Flug nach Unterstadt", nil, {"Bucht einen Fledermausritt zur Unterstadt bei Fledermausführerin Anette und bringt dann Morris' Anforderungsliste zu Gordon Wendheim in Unterstadt."}},
[6324] = {"Rückkehr zu Morris", nil, {"Bucht einen Fledermausritt nach Brill bei Fledermausmeister Michael Garrett und bringt dann Gordons Kiste zu Todeswache Morris in Brill."}},
[6341] = {"Auf nach Darnassus", nil, {"Sprecht mit Fidelio, um einen Flug nach Darnassus zu erstehen, und bringt dann die Rezeptsammlung von Dolanaar zu Schwester Aquinne in Darnassus."}},
[6342] = {"Ein unerwartetes Geschenk", nil, {"Bringt Schwester Aquinnes Notiz zu Leora, der Hippogryphenmeisterin in Darnassus."}},
[6343] = {"Rückkehr zu Nyoma", nil, {"Sprecht mit Leora, um einen Hippogryphen zu mieten, der Euch nach Dolanaar zurückbringt. Sobald Ihr in Dolanaar angekommen seid, bringt Schwester Aquinnes Nachricht zu Nyoma."}},
[6344] = {"Andenken an Zuhause", nil, {"Bringt die Rezeptsammlung von Dolanaar zu Fidelio in Dolanaar."}},
[6361] = {"Ein Bündel Bälge", nil, {"Bringt das Bündel Bälge zu Windreitermeister Tak im Dorf der Bluthufe."}},
[6362] = {"Flug nach Donnerfels", nil, {"Bucht einen Windreiter nach Donnerfels bei Windreitermeister Tak und bringt dann das Bündel Bälge zu Ahanu in Donnerfels."}},
[6363] = {"Tal der Windreitermeister", nil, {"Bringt Ahanus Lederwaren zu Tal in Donnerfels."}},
[6364] = {"Rückkehr zu Varg", nil, {"Bucht einen Windreiter zum Dorf der Bluthufe bei Windreitermeister Tak und bringt dann Ahanus Lederwaren zu Varg Windraunen."}},
[6365] = {"Fleisch für Orgrimmar", nil, {"Bringt Grimtaks Fleisch zu Burok in Klingenhügel."}},
[6381] = {"Neues Leben", nil, {"Pflanzt 10 Gaeasamenkörner in Gaeaerdhügel und bringt Tammra in Sonnenfels dann die restlichen verzauberten Gaeasamenkörner zurück."}},
[6382] = {"Die Jagd im Eschental", nil, {"Sprecht mit Senani Donnerherz im Splitterholzposten im Eschental."}},
[6384] = {"Flug nach Orgrimmar", nil, {"Bucht einen Windreiter nach Orgrimmar bei Windreitermeister Burok und bringt dann Grimtaks Fleisch zu Gryshka in Orgrimmar."}},
[6385] = {"Doras der Windreitermeister", nil, {"Bringt Gryshkas Brief zu Doras in Orgrimmar."}},
[6386] = {"Kehrt zum Klingenhügel zurück", nil, {"Bucht einen Windreiter zum Klingenhügel bei Windreitermeister Doras und bringt dann Gryshkas Brief zu Grimtak in Klingenhügel."}},
[6387] = {"Ehrenlehrlinge", nil, {"Bringt Gremlocks Liste zu Brolan Windbart in Kharanos."}},
[6388] = {"Gryth Thurden", nil, {"Bringt die Ehrenbeile zu Gryth Thurden in Eisenschmiede."}},
[6391] = {"Flug nach Eisenschmiede", nil, {"Bucht einen Greifen nach Eisenschmiede von Greifenmeister Brolan Windbart, dann bringt Gremlocks Liste zu Golnir Geröllzeh in Eisenschmiede."}},
[6392] = {"Rückkehr zu Gremlock", nil, {"Bucht einen Greifen nach Kharanos bei Greifenmeister Gryth Thurden, dann bringt die Ehrenhacken zu Gremlock Pilsnor in Kharanos."}},
[6393] = {"Krieg der Elementare", nil, {"Bringt 10 Pyrophore zu Tsunaman in Sonnenfels."}},
[6394] = {"Thazz'rils Hacke", nil, {"Bringt Thazz'rils Hacke zu Großknecht Thazz'ril."}},
[6395] = {"Marlas letzter Wunsch", nil, {"Bringt Samuel Fipps' sterbliche Überreste zu Marlas Grab und kehrt dann zu Novize Elreth zurück."}},
[6401] = {"Kaya lebt", nil, {"Teilt Tammra Windfeld, die in Sonnenfels lebt, die gute Nachricht mit."}},
[6421] = {"Steinschlagklamm", nil, {"Erkundet die Tiefen der Höhle in der Steinschlagklamm und bringt Mor'rogal in Sonnenfels 10 Resonitkristalle, damit er sie untersuchen kann."}},
[6441] = {"Satyrhörner", nil, {"Sammelt für Pixel im Splitterholzposten 16 Satyrhörner."}},
[6442] = {"Die Naga am Zoramstrand", nil, {"Bringt Marukai am Zoramstrand 20 Köpfe von Rächerflossen."}},
[6461] = {"Blutfresser", nil, {"Xen'zilla am Malaka'jin möchte, dass Ihr 10 Kluftmooskrabbler und 7 Kluftmoosgiftspucker tötet."}},
[6462] = {"Trollglücksbringer", nil, {"Bringt Mitsuwa bei Höllschreis Wacht 8 Trollglücksbringer."}},
[6481] = {"Der Irdene erhebt sich", nil, {"Öffnet den Resonitbehälter mit dem verzauberten Resonitkristall und tötet Goggeroc. Kehrt mit der Vollzugsmeldung und dem verzauberten Resonitkristall zu Mor'rogal zurück."}},
[6482] = {"Freiheit für Ruul", nil, {"Begleitet Ruul und sprecht dann mit Yama Schneehuf im Splitterholzposten."}},
[6501] = {"Das Großdrachenauge", nil, {"Ihr müsst die Welt nach jemandem absuchen, der fähig ist, die Macht des Fragments des Großdrachenauges wieder herzustellen. Die einzige Information, die Ihr über ein solches Wesen besitzt ist, dass es existiert."}},
[6502] = {"Drachenfeueramulett", nil, {"Ihr müsst das Blut des schwarzen Drachenhelden von General Drakkisath bekommen. Ihr findet Drakkisath in seinem Thronsaal hinter den Hallen des Aufstiegs auf der Schwarzfelsspitze."}},
[6503] = {"Die Kundschafter des Eschentals", nil, {"Tötet 9 Kundschafter des Eschentals."}},
[6504] = {"Die verlorenen Seiten", nil, {"Findet die 12 fehlenden Seiten der Schredderanleitung und fügt sie zu den Kapiteln 1, 2 und 3 zusammen. Bringt die Seiten zu Gurda Zornnarbe in der Nähe des Splitterholzpostens zurück."}},
[6521] = {"Eine unheilige Allianz", nil, {"Bringt den Kopf von Botschafter Malcin zu Bragor Blutfaust nach Unterstadt."}},
[6522] = {"Eine unheilige Allianz", nil, {"Bringt die kleine Rolle zu Bragor Blutfaust im Königsviertel von Unterstadt."}},
[6523] = {"Schützt Kaya", nil, {"Begleitet Kaya Flachhuf zum Camp Aparaje und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6541] = {"Meldung bei Kadrak", nil, {"Meldet Euch im Wachturm im Nördlichen Brachlands bei Kadrak."}},
[6542] = {"Meldung bei Kadrak", nil, {"Meldet Euch im Wachturm im Norden des Brachlands bei Kadrak."}},
[6543] = {"Die Berichte des Kriegshymnenklans", nil, {"Öffnet die gebündelten Berichte. Bringt die Berichte des Kriegshymnenklans dem Späher des Kriegshymnenklans, dem Läufer des Kriegshymnenklans und der Vorhut des Kriegshymnenklans. Meldet die Neuigkeiten, die Ihr von ihnen erfahrt, Kadrak im nördlichen Wachturm des Brachlands."}},
[6544] = {"Toreks Angriff", nil, {"Eskortiert Torek bei seinem Angriff auf den Außenposten der Silberschwingen und Duriel Mondfeuer. Erstattet dann Ertog Zornhauer im Splitterholzposten Bericht."}},
[6548] = {"Rächt mein Dorf", nil, {"Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6561] = {"Schurkerei in der Tiefschwarzen Grotte", nil, {"Bringt den Kopf von Zwielichtfürst Kelris zu Bashana Runentotem in Donnerfels."}},
[6562] = {"Ärger in der Tiefe", nil, {"Sprecht im Eschental mit Je'neu Sancrea."}},
[6563] = {"Die Essenz von Aku'mai", nil, {"Bringt 20 Saphire von Aku'mai zu Je'neu Sancrea im Eschental."}},
[6565] = {"Treue zu den Alten Göttern", nil, {"Tötet Lorgus Jett."}},
[6566] = {"Was der Wind erzählt", nil, {"Hört Thrall zu."}},
[6567] = {"Der Champion der Horde", nil, {"Sucht in Schattenflucht in Desolace nach Rokaro."}},
[6568] = {"Meisterin der Illusionen", nil, {"Überbringt Myranda der Vettel in den Westlichen Pestländern Rokaros Brief."}},
[6569] = {"Oculus-Illusionen", nil, {"Reist zur Schwarzfelsspitze und sammelt 20 schwarze Drachenbrutaugen. Kehrt zu Myranda der Vettel zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[6571] = {"Nachschub des Kriegshymnenklans", nil, {"Tragt den Nachschub des Kriegshymnenklans zusammen: Sägeblätter des Kriegshymnenklans, ein Holzfällerseil, Öl des Kriegshymnenklans und die Axtlieferung des Kriegshymnenklans für Locke Okarr im Splitterholzposten."}},
[6582] = {"Die Prüfung der Schädel, Seher", nil, {"Ihr müsst Seher, den Champion der blauen Großdrachen, finden und ihn töten. Schlagt seinem Kadaver den Schädel ab und bringt ihn zu Aschenschwinge.\n\nIhr wisst, dass Seher in Winterquell zu finden ist."}},
[6583] = {"Die Prüfung der Schädel, Somnus", nil, {"Vernichtet den Drachenchampion des grünen Drachenschwarms, Somnus. Nehmt seinen Schädel und bringt ihn zu Aschenschwinge zurück."}},
[6584] = {"Die Prüfung der Schädel, Chronalis", nil, {"Chronalis, ein Kind Nozdormus, bewacht die Höhlen der Zeit in der Tanariswüste. Vernichtet ihn und bringt seinen Schädel zu Aschenschwinge."}},
[6585] = {"Die Prüfung der Schädel, Axtroz", nil, {"Begebt Euch nach Grim Batol und spürt Axtroz auf, den Drachenchampion des roten Drachenschwarms. Vernichtet ihn und nehmt seinen Schädel. Bringt den Schädel zu Aschenschwinge zurück."}},
[6602] = {"Blut des schwarzen Großdrachenhelden", nil, {"Begebt Euch zur Schwarzfelsspitze und tötet General Drakkisath. Sammelt sein Blut und bringt es zu Rokaro."}},
[6603] = {"Probleme in Winterquell!", nil, {"Findet Donova Schneeweh bei den Thermalquellen in Winterquell."}},
[6604] = {"Wütende Wildekin", nil, {"Sprecht mit Jaron Steinschleifer in Sternfall."}},
[6605] = {"Eine seltsame Person", nil, {"Sprecht im Teufelswald mit Winna Hazzard."}},
[6607] = {"Nat Pagle, Extremangler", nil, {"Nat Pagle möchte, dass Ihr folgende Fische fangt:\n\nNebelschilf-Mahi Mahi vom Nebelschilfstrand in den Sümpfen des Elends.\n\nEinen Sar'therisbarsch vom Sar'therisstrand in Desolace.\n\nFeralas-Ahi aus dem Verdantis in Feralas.\n\nBlauwimpel von der Ungezähmten Küste im Schlingendorntal.\n\nKehrt zu Nat Pagle zurück, wenn Ihr alle an Land gezogen habt!"}},
[6608] = {"Ihr zu gut", nil, {"Ihr nehmt an, dass Lumak Euch zu erklären versucht, dass er nicht mehr tun kann, um Eure Angelfertigkeit zu verbessern. Ihr solltet seinen Freund aufsuchen, diesen Nat Pagle, einen Menschen, der auf einer der Inseln südlich von Theramore (jedenfalls glaubt Ihr, dass Lumak das gemeint hat) auf Fischfang geht."}},
[6609] = {"Ich hab nix mehr!", nil, {"Reist nach Kalimdor zur Küstenstadt Theramore. Dort müsst Ihr nach einer Insel suchen, die südlich der Hauptstadt liegt. Nat Pagle erwartet Euch!"}},
[6610] = {"Muschelette Surprise", nil, {"Dirge Schnetzelhack möchte, dass Ihr folgende Dinge besorgt:\n\n10 Rieseneier. Ihr findet sie bei den Rocs, die Tanaris durchstreifen, oder anderen Großvogelarten.\n\n10 Stück scharfes Muschelfleisch. Das findet Ihr natürlich in Muscheln!\n\n20 Stück feiner alter Cheddar. Begebt Euch zu einer Käserei!\n\nSammelt all diese Gegenstände und kehrt dann zu Dirge zurück."}},
[6611] = {"Ab mit Euch nach Gadgetzan!", nil, {"Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere."}},
[6612] = {"Ich kenne einen Typen...", nil, {"Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere."}},
[6621] = {"König der Fäulnisklauen", nil, {"Platziert Senanis Banner auf dem Totemhügel der Fäulnisklauen. Die Furbolgs dürfen das Banner nicht zerstören. Besiegt Häuptling Murgut und bringt Murguts Totem zu Senani in der Silberwindzuflucht."}},
[6622] = {"Triage", nil, {"Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.\n\nDenkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt."}},
[6623] = {"Traumachirurg der Horde", nil, {"Reist nach Hammerfall im Arathihochland und sprecht mit Doktor Gregory Victor darüber, dass Ihr auch ein Traumachirurg der Horde werden wollt."}},
[6624] = {"Triage", nil, {"Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.\n\nDenkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt."}},
[6625] = {"Traumachirurg der Allianz", nil, {"Reist nach Theramore in die Düstermarschen und sprecht mit Doktor Gustav van Howzen darüber, dass Ihr auch ein Traumachirurg der Allianz werden wollt."}},
[6626] = {"Ein Hort des Bösen", nil, {"Tötet 8 Schlachtwachen der Klingenhauer, 8 Dornenwirker der Klingenhauer und 8 Kultistinnen der Totenköpfe und kehrt dann zu Myriam Mondsang nahe dem Eingang zu den Hügeln der Klingenhauer zurück."}},
[6628] = {"Test der Lehre", nil, {"Beantwortet Parqual Fintallas' Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch in Unterstadt sein, wenn Ihr bereit seid."}},
[6629] = {"Tötet Grundig Finsterwolke", nil, {"Tötet Grundig Finsterwolke und 6 Schläger der Grimmtotem und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6641] = {"Vorsha die Peitscherin", nil, {"Bezwingt Vorsha die Peitscherin."}},
