[6844] = {"Umber, Archivar", nil, {"Bringt das verkrustete Silithidenobjekt zu Umber, dem Archivar des Zirkels des Cenarius, ins Dorf Nachthafen auf der Mondlichtung."}},
[6846] = {"Der Angriff soll beginnen!", nil, {"Anforderungen:"}},
[6862] = {"Zinnzischelix's tragbare Schreddereinheit", nil, {"Meisteringenieur Zinnzischelix möchte, dass Ihr ihm Folgendes bringt:\n\n*30 Thoriumbarren.\n\n*50 Mithrilbarren.\n\n*75 Eisenbarren.\n\n*1 Dampfsäge."}},
[6881] = {"Ivus der Waldfürst", nil, {"Anforderungen:"}},
[6921] = {"Inmitten der Ruinen", nil, {"Bringt den Tiefenkern zu Je'neu Sancrea beim Außenposten von Zoram'gar im Eschental."}},
[6922] = {"Baron Aquanis", nil, {"Bringt die seltsame Wasserkugel zu Je'neu Sancrea in der Tiefschwarzen Grotte."}},
