[6943] = {"Ruf der Lüfte - Ichmans Luftflotte", nil, {"Anforderungen:"}},
[6961] = {"Altvater Winter ist hier!", nil, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar."}},
[6962] = {"Leckereien für Altvater Winter", nil, {"Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Orgrimmar."}},
[6963] = {"Gestohlene Winterhauchleckereien", nil, {"Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird."}},
[6964] = {"Der Anlass für die Feiertage", nil, {"Sprecht mit Sagorne Gratläufer im Tal der Weisheit von Orgrimmar über das Winterhauchfest."}},
[6981] = {"Der leuchtende Splitter", nil, {"Begebt Euch nach Ratschet, um jemanden zu finden, der Euch mehr über den leuchtenden Splitter sagen kann.\n\nLiefert dann den Splitter ab, wie Ihr angewiesen wurdet."}},
[6982] = {"Vorräte der Eisbeißermine", nil, {"Bringt 10 Vorräte der Eisbeißermine zum Rüstmeister der Allianz in Dun Baldar."}},
[6983] = {"So was Gemeines...", nil, {"Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes."}},
[6984] = {"Ein Dankeschön von Kokelwälder!", nil, {"Sprecht mit Altvater Winter in Orgrimmar."}},
[6985] = {"Vorräte der Eisenschachtmine", nil, {"Bringt 10 Vorräte der Eisenschachtmine zum Rüstmeister der Horde in die Burg Frostwolf."}},
[7001] = {"Verwaiste Ställe", nil, {"Findet einen Frostwolf im Alteractal. Wenn Ihr Euch in der Nähe eines Frostwolfs befindet, benutzt den Frostwolfmaulkorb um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Frostwolf bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden."}},
[7002] = {"Widderledernes Zaumzeug", nil, {"Anforderungen:"}},
[7003] = {"Geschrumpfte Riesen", nil, {"Benutzt Zorbins Ultraschrumpfer um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt Zorbin von Schiller 15 Schrumpfüberreste von diesen Riesen. Ihr findet ihn bei den Docks, an der Vergessenen Küste in Feralas."}},
[7021] = {"Altvater Winter ist hier!", nil, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar."}},
[7022] = {"Altvater Winter ist hier!", nil, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede."}},
[7023] = {"Altvater Winter ist hier!", nil, {"Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede."}},
