[7027] = {"Verwaiste Ställe", nil, {"Findet einen Alteracwidder im Alteractal. Wenn Ihr Euch in der Nähe eines Alteracwidders befindet, benutzt das Zähmungshalsband der Sturmlanzen um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Alteracwidder bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden."}},
[7028] = {"Dunkles Böses", nil, {"Sammelt 15 theradrische Kristallschnitzereien für Trista in Desolace."}},
[7029] = {"Schlangenzunges Verderbnis", nil, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.\n\nBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.\n\nHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Vark Schlachtnarbe in Schattenflucht zurück."}},
[7041] = {"Schlangenzunges Verderbnis", nil, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.\n\nBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.\n\nHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Talendria an der Nijelspitze zurück."}},
[7042] = {"Gestohlene Winterhauchleckereien", nil, {"Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird."}},
[7043] = {"So was Gemeines...", nil, {"Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes."}},
[7044] = {"Legenden von Maraudon", nil, {"Beschafft die beiden Teile des Szepters von Celebras: den Celebriangriff und den Celebriandiamanten.\n\nFindet einen Weg, um mit Celebras zu sprechen."}},
[7061] = {"Das Winterhauchfest", nil, {"Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, bringt es zu Baine Bluthuf in Donnerfels."}},
[7062] = {"Der Anlass für die Feiertage", nil, {"Sprecht mit dem Historiker Karnik in der Forscherliga von Eisenschmiede über das Winterhauchfest."}},
[7063] = {"Das Winterhauchfest", nil, {"Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, dann bringt es zu Muradin Bronzebart nach Eisenschmiede."}},
[7064] = {"Verderbnis von Erde und Samenkorn", nil, {"Tötet Prinzessin Theradras und kehrt zu Selendra in der Nähe von Schattenflucht in Desolace zurück."}},
[7065] = {"Verderbnis von Erde und Samenkorn", nil, {"Erschlagt Prinzessin Theradras und kehrt zum Bewahrer Marandis an der Nijelspitze in Desolace zurück."}},
[7066] = {"Samenkorn des Lebens", nil, {"Sucht Remulos bei der Mondlichtung auf und gebt ihm das Samenkorn des Lebens."}},
[7067] = {"Die Anweisungen des Pariahs", nil, {"Lest die Anweisungen des Pariahs. Beschafft Euch danach das Amulett der Vereinigung von Maraudon und bringt es dem Zentaurenpariah im südlichen Desolace."}},
[7068] = {"Schattensplitter", nil, {"Sammelt 10 Schattensplitter aus Maraudon und bringt sie zu Uthel'nay nach Orgrimmar."}},
