[7082] = {"Die Friedhöfe von Alterac", nil, {"Erobert einen Friedhof und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück."}},
[7101] = {"Türme und Bunker", nil, {"Erobert einen gegnerischen Turm und kehrt anschließend zu Korporal Teeka Murrblut im Alteracgebirge zurück."}},
[7102] = {"Türme und Bunker", nil, {"Zerstört das Banner im Inneren eines gegnerischen Bunkers oder Turms. Kehrt anschließend zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück."}},
[7121] = {"Der Rüstmeister", nil, {"Sprecht mit dem Rüstmeister der Sturmlanzen."}},
[7122] = {"Eine Mine erobern", nil, {"Erobert eine Mine, die die Sturmlanzen noch nicht kontrollieren, und kehrt dann zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück."}},
[7123] = {"Sprecht mit unserem Rüstmeister", nil, {"Sprecht mit dem Rüstmeister der Frostwölfe."}},
[7124] = {"Eine Mine erobern", nil, {"Erobert eine Mine und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück."}},
[7141] = {"Die Schlacht um Alterac", nil, {"Betretet das Alteractal, bezwingt den Hordegeneral Drek'thar und kehrt dann zu Ausgrabungsleiter Steinhauer im Alteracgebirge zurück."}},
[7142] = {"Die Schlacht ums Alteractal", nil, {"Betretet das Alteractal und bezwingt den Zwergengeneral, Vanndar Sturmlanze. Kehrt anschließend zu Voggah Todesgriff im Alteracgebirge zurück."}},
[7161] = {"Flaggenjagd", nil, {"Begebt Euch in die Höhle der Wildpfoten südöstlich vom Hauptstützpunkt im Alteractal und findet das Banner der Frostwölfe. Bringt anschließend Kriegsmeister Laggrond das Banner."}},
[7162] = {"Flaggenjagd", nil, {"Begebt Euch in die Eisschwingenhöhle südwestlich von Dun Baldar im Alteractal und findet das Banner der Sturmlanzen. Bringt anschließend Leutnant Haggerdin im Alteracgebirge das Banner."}},
