[7181] = {"Die Legende von Korrak", nil, {"Den Legenden nach erscheint der mächtige Anführer der Winteraxtrolle nach Belieben, um Chaos unter den Bewohnern des Alteractals zu stiften.\n\nSolltet Ihr ihn zu Gesicht bekommen, dann vernichtet ihn und kehrt zu Kriegsmeister Laggrond im Alteracgebirge zurück."}},
[7201] = {"Das letzte Element", nil, {"Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat."}},
[7202] = {"Korrak der Blutrünstige", nil, {"Den Legenden nach erscheint der mächtige Anführer der Winteraxtrolle nach Belieben, um Chaos unter den Bewohnern des Alteractals zu stiften.\n\nSolltet Ihr ihn zu Gesicht bekommen, dann vernichtet ihn und kehrt zu Leutnant Haggerdin im Alteracgebirge zurück."}},
[7221] = {"Sprecht mit Ausgrabungsleiter Steinhauer", nil, {"Sprecht mit Ausgrabungsleiter Steinhauer in der Garnison von Dun Baldar."}},
[7222] = {"Sprecht mit Voggah Todesgriff", nil, {"Sprecht mit Voggah in Burg Frostwolf."}},
[7223] = {"Rüstungsfetzen", nil, {"Bringt Murgot Tiefenschmied in Dun Baldar 20 Rüstungsfetzen."}},
[7224] = {"Beutezug im Feindesland", nil, {"Bringt 20 Rüstungsfetzen zu Schmied Regzar im Dorf der Frostwölfe."}},
[7241] = {"Die Verteidigung der Frostwölfe", nil, {"Reist ins Alteractal, das sich im gleichnamigen Gebirge befindet. Sucht dort außerhalb des Tunneleingangs Kriegsmeister Laggrond auf und beginnt Eure Karriere als Soldat der Frostwölfe. Das Alteractal findet Ihr nördlich von Tarrens Mühle am Fuß des Alteracgebirges."}},
[7261] = {"Die hoheitliche Anordnung", nil, {"Reist ins Alteractal im Vorgebirge des Hügellands. Wenn Ihr dort angekommen seid, meldet Euch umgehend bei Leutnant Haggerdin.\n\nFür den Ruhm von Bronzebart!"}},
[7281] = {"Bruderliebe", nil, {"Begebt Euch ins Gebiet der Sturmlanzen und tötet Kommandant Karl Philips. Kehrt zu Kommandant Louis Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt."}},
[7282] = {"Bruderliebe", nil, {"Begebt euch ins Frostwolfgebiet und tötet Kommandant Louis Philips. Kehrt zu Kommandant Karl Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt."}},
[7301] = {"Gefallene Himmelsstürmer", nil, {"Begebt Euch ins Frostwolfterritorium und sucht nach Schwadronskommandant Slidore, Schwadronskommandant Vipore und Schwadronskommandant Ichman. Meldet Euch bei Kommandant Duffy, sobald Ihr Eure Mission erfüllt habt."}},
[7302] = {"Gefallene Himmelsstürmer", nil, {"Findet heraus was aus Schwadronskommandant Guse, Schwadronskommandant Jeztor, und Schwadronskommandant Mulverick geworden ist. Wenn Eure Mission erfüllt ist, kehrt zu Kommandant Mulfort zurück."}},
[7321] = {"Milde Schildkrötensuppe", nil, {"Bringt 10 Stücke Schildkrötenfleisch zu Christoph Jeffkott in Tarrens Mühle."}},
[7367] = {"Die Gefahr bannen", nil, {"Findet den Sprengmeister der Frostwölfe und tötet ihn. Kehrt nach Abschluss des Auftrags zum Sprengmeister der Sturmlanzen zurück."}},
[7383] = {"Teldrassil: Die Bürde der Kaldorei", nil, {"Füllt die Amethystphiole."}},
[7385] = {"Eine Gallone Blut", nil, {"Anforderungen:"}},
[7386] = {"Haufenweise Kristalle", nil, {"Anforderungen:"}},
[7441] = {"Pusillin und der Älteste Azj'Tordin", nil, {"Reist nach Düsterbruch und macht den Dämonen Pusillin ausfindig. Überzeugt ihn mit allen Mitteln davon, Euch Azj'Tordin's Buch der Zauberformeln zu geben.\n\nKehrt mit dem Buch zu Az'Tordin, beim Larisspavillon in Feralas, zurück."}},
[7461] = {"Der innere Wahnsinn", nil, {"Zerstört alle Wächter, die um die 5 Pylonen herumstehen, welche Immol'thars Gefängnis mit Energie versorgen. Sobald die Pylone deaktiviert wurden, wird sich das Kraftfeld, das Immol'thar umgibt, auflösen.\n\nBetretet Immol'thars Gefängnis und vernichtet den verdorbenen Dämonen. Anschließend müsst Ihr Prinz Tortheldrin im Athenaeum entgegentreten.\n\nKehrt nach Abschluss der Aufgabe zur uralten Shen'dralar im Hof zurück."}},
[7463] = {"Arkane Erfrischung", nil, {"Reist nach Düsterbruch in das Wucherborkenviertel und besiegt den Wasserelementar Hydrobrut. Kehrt anschließend mit der Hydrobrutessenz zum Wissenshüter Lydros im Athenaeum zurück."}},
[7481] = {"Elfische Legenden", nil, {"Sucht in Düsterbruch nach Telmius Traumsucher. Meldet Euch anschließend bei dem Weisen Korolusk in Camp Mojache."}},
[7482] = {"Elfische Legenden", nil, {"Sucht in Düsterbruch nach Telmius Traumsucher. Meldet Euch anschließend bei der Gelehrten Runendorn in der Mondfederfeste."}},
[7483] = {"Buchband der Schnelligkeit", nil, {"Bringt ein Buchband der Schnelligkeit, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 2-mal das Blut von Helden, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum der Schnelligkeit zu erhalten."}},
[7484] = {"Buchband des Fokus", nil, {"Bringt ein Buchband des Fokus, 1 makellosen schwarzen Diamanten, 4 große glänzende Splitter und 2-mal Schattenhaut, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Fokus zu erhalten."}},
[7485] = {"Buchband des Schutzes", nil, {"Bringt ein Buchband des Schutzes, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 1 ausgefranste Monstrositätenstickerei zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Schutzes zu erhalten."}},
[7486] = {"Die Belohnung eines Helden", nil, {"Nehmt Eure Belohnung aus Hydraxis' Kasten."}},
[7488] = {"Lethtendris' Netz", nil, {"Bringt Lethtendris' Netz zu Latronicus Mondspeer in der Mondfederfeste in Feralas."}},
[7489] = {"Lethtendris' Netz", nil, {"Bringt Lethtendris' Netz zu Talo Dornhuf im Camp Mojache in Feralas."}},
[7490] = {"Sieg für die Horde", nil, {"Bringt Onyxias Kopf zu Garrosh in Orgrimmar."}},
[7491] = {"Für alle sichtbar", nil, {"Sucht Oberanführer Runthak im Tal der Stärke auf."}},
[7492] = {"Camp Mojache", nil, {"Sprecht mit Talo Dornhuf in Camp Mojache, in Feralas."}},
