[7782] = {"Der Herrscher des Schwarzfels", nil, {"Reist in nach Sturmwind in das Tal der Helden und sprecht mit Feldmarschall Steinsteg."}},
[7783] = {"Der Herrscher des Schwarzfels", nil, {"Bringt Garrosh in Orgrimmar den Kopf von Nefarian."}},
[7784] = {"Der Herrscher des Schwarzfels", nil, {"Sprecht mit Oberanführer Runthak in Orgrimmar. Er wartet im Tal der Stärke auf Euch."}},
[7785] = {"Untersuchung des Gefäßes", nil, {"Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian."}},
[7786] = {"Donneraan der Windsucher", nil, {"Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 verzauberte Elementiumbarren und die Essenz des Feuerfürsten bringen."}},
[7787] = {"Donnerzorn erwache!", nil, {"Präsentiert Hochlord Demitrian die vom Wind berührte, schlummernde Klinge."}},
