[8304] = {"Liebste Natalia", nil, {"Befragt Frankal und Rutgar in Bronzebarts Lager über den Aufenthaltsort von Natalia Mar'alith."}},
[8305] = {"Lang Vergessenes", nil, {"Sucht die Kristallträne in Silithus und blickt in ihr Innerstes."}},
[8306] = {"Der Schlund des Wahnsinns", nil, {"Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.\n\nSprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.\n\nUnd Bomdiyada, denkt an die Worte des Kommandanten: \"Tut, was Ihr tun müsst...\""}},
[8307] = {"Wüstenrezept", nil, {"Haltet beim Lager des Schattenhammers westlich von Burg Cenarius nach Informationen zum Zubereiten von Sandwurmfleisch Ausschau."}},
[8308] = {"Der verlorene Brief von Brann Bronzebart", nil, {"Liefert Brann Bronzebarts verlorenen Brief in Bronzebarts Lager im südlichen Silithus ab."}},
[8309] = {"Glyphenjagd", nil, {"Benutzt das Abschriftenset für Geologen, um ein Abpausbild des glyphenverzierten Kristalls des Regalschwarms anzufertigen."}},
[8310] = {"Knackt den Kode", nil, {"Erhaltet ein intaktes Silithidengehirn des Regalschwarms von einem beliebigen Silithiden im Bau des Regalschwarms."}},
[8311] = {"Schlotternachtssüßigkeiten für Jesper!", nil, {"Sprecht mit den Gastwirten in Sturmwind, Eisenschmiede und Darnassus sowie mit Talvash del Kissel in Eisenschmiede. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.\n\nKehrt zu Jesper außerhalb von Sturmwind mit einem Marzipan aus Darnassus, einer Kaugummikugel aus Gnomeregan, Nugat aus Sturmwind und einem Pfefferminzbonbon aus Eisenschmiede zurück."}},
[8312] = {"Schlotternachtssüßigkeiten für Spuks!", nil, {"Sprecht mit den Gastwirten in Orgrimmar, Unterstadt und Donnerfels sowie mit Kali Remik im Tal der Geister in Orgrimmar. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.\n\nKehrt zu Spuks in den Ruinen von Lordaeron mit einem Marzipan aus Donnerfels, einer Kaugummikugel der Dunkelspeere, Nugat aus Orgrimmar und einem Pfefferminzbonbon aus Unterstadt zurück."}},
[8313] = {"Geteiltes Wissen", nil, {"Bringt die ausgerissene Rezeptseite zu Calandrath ins Gasthaus der Burg Cenarius."}},
[8314] = {"Des Rätsels Lösung", nil, {"Bringt das glyphenverzierte Kristallprisma zu Geologin Lerchenbann auf Burg Cenarius in Silithus."}},
[8315] = {"Die Anrufung", nil, {"Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr den Kristallentriegelungsmechanismus vom Abgesandten der Qiraji beschafft.\n\nNehmt die Glyphen der Anrufung, begebt Euch zu den Knochen von Grakkarond südlich von Burg Cenarius und zeichnet sie dort in den Sand. Sollte der Abgesandte der Qiraji erscheinen, tötet ihn und nehmt den Kristallentriegelungsmechanismus an Euch. Kehrt anschließend zu Geologin Lerchenbann zurück.\n\nFür diese Aufgabe werdet Ihr eine kleine Armee benötigen, Scanner!"}},
[8317] = {"Küchengehilfe", nil, {"Calandrath von der Burg Cenarius braucht 10 geräucherte Wüstenknödel."}},
[8318] = {"Geheime Nachrichten", nil, {"Bringt Bor Wildmähne auf Burg Cenarius 10 verschlüsselte Schattenhammertexte."}},
[8319] = {"Verschlüsselte Schattenhammertexte", nil, {"Anforderungen:"}},
[8320] = {"Geolords des Schattenhammers", nil, {"Tötet 10 Zwielichtgeolords und kehrt anschließend zu Huum Wildmähne auf Burg Cenarius zurück."}},
[8321] = {"Vyral der Üble", nil, {"Tötet Vyral den Üblen und erhaltet Vyrals Siegelring."}},
[8322] = {"Faule Eier", nil, {"Werft die faulen Eier in die großen Braufässchen im Gasthaus von Süderstade."}},
[8323] = {"Wahrer Glauben", nil, {"Ortell der Einsiedler möchte, dass Ihr ihm 10 verschlüsselte Schattenhammertexte beschafft."}},
[8324] = {"Fester Glauben", nil, {"Anforderungen:"}},
[8325] = {"Rückeroberung der Insel der Sonnenwanderer", nil, {"Tötet 6 Manawyrm; sie halten sich rund um die brennenden Kristalle auf, die man nahe des Turms und des Vorbaus findet."}},
[8326] = {"Bedauerliche Maßnahmen", nil, {"Sammelt 8 Luchshalsbänder von erlegten Bachtatzenluchsen und Bachtatzenjungen ein. Kehrt zu Magistrix Erona auf der Insel der Sonnenwanderer zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
[8327] = {"Berichterstattung an Lanthan Perilon", nil, {"Sprecht mit Lanthan Perilon auf der Insel der Sonnenwanderer. Er steht in der Nähe des Pfads, der vom Sonnenturm Richtung Westen verläuft."}},
[8328] = {"Ausbildung zum Magier", nil, {"Sprecht mit Julia Sonnensturm - der Magierlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8329] = {"Ausbildung zum Krieger", nil, {"Sprecht mit Delios - dem Kriegerlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8330] = {"Solanians Habe", nil, {"Sammelt Brunnenbehüter Solanians Seherkugel, seine Rolle der Geißelmagie und sein Tagebuch ein. Ihr findet die Gegenstände auf der Insel der Sonnenwanderer bei dem Tümpel, dem Brunnen und bei einem der brennenden Kristalle. Bringt sie zu dem Brunnenbehüter oben im Sonnenturm auf der Insel der Sonnenwanderer, sobald Ihr alle gefunden habt."}},
[8332] = {"Fürsten des Rates", nil, {"Bringt Aurel Goldblatt auf Burg Cenarius 1 großen glänzenden Splitter und 3 Abyssische Wappen."}},
[8334] = {"Aggressivität", nil, {"Tötet 7 Borklinge und 7 wilde Borklinge. Kehrt dann zu Lanthan Perilon auf der Insel der Sonnenwanderer zurück."}},
[8335] = {"Felendren der Verbannte", nil, {"Tötet 8 Arkangespenster, 2 besudelte Arkangespenster und Felendren den Verbannten. Ihr findet sie in der Akademie von Falthrien. Bringt Felendrens Kopf zu Lanthan Perilon auf der Insel der Sonnenwanderer."}},
[8336] = {"Für eine Handvoll Fragmente", nil, {"Sammelt 6 Arkanspäne und bringt sie zu Arkanist Ithanas auf der Insel der Sonnenwanderer. Derartige Fragmente findet man bei allen Mana benutzenden Kreaturen auf der Insel."}},
[8337] = {"<UNUSED>", nil, {"Anforderungen:"}},
[8338] = {"Besudelter Arkanspan", nil, {"Bringt den besudelten Arkanspan zu Arkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8341] = {"Lords des Rates", nil, {"Bringt Aurel Goldblatt auf Burg Cenarius 5 große glänzende Splitter und 3 abyssische Siegel."}},
[8345] = {"Der Schrein von Dath'Remar", nil, {"Lest die Tafel am Schrein von Dath'Remar und kehrt anschließend zu Brunnenbehüter Solanian beim Sonnenturm auf der Insel der Sonnenwanderer zurück."}},
[8346] = {"Unstillbarer Durst", nil, {"Verwendet die Fähigkeit 'Arkaner Strom' auf einen Manawyrm und kehrt anschließend zu Arkanist Helion zurück."}},
[8347] = {"Unterstützung für die Kundschafter", nil, {"Sprecht mit Kundschafterin Alarion nahe der Brücke, die in den Süden der Insel der Sonnenwanderer führt."}},
[8348] = {"Siegel der Fürsten", nil, {"Bringt Bor Wildmähne auf Burg Cenarius das abyssische Siegel."}},
[8349] = {"Bor Wildmähne", nil, {"Sprecht mit Bor Wildmähne auf Burg Cenarius."}},
[8350] = {"Erfolgreiche Lieferung", nil, {"Bringt Eronas Päckchen zu Gastwirtin Delaniel im Gasthaus beim Falkenplatz im Immersangwald."}},
[8352] = {"Szepter des Rates", nil, {"Bringt Bor Wildmähne auf Burg Cenarius ein abyssisches Szepter."}},
[8354] = {"Hühnchengegacker für ein Pfefferminzbonbon", nil, {"Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!"}},
[8356] = {"Muskelspiel für Nugat", nil, {"Ihr müsst vor Gastwirtin Allison angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Sturmwind gibt!"}},
[8358] = {"Kaugummikugelexpress", nil, {"Ahmt für Kali Remik einen Zug nach und sie wird Euch dafür eine Kaugummikugel der Dunkelspeere geben!"}},
[8359] = {"Muskelspiel für Nugat", nil, {"Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!"}},
[8360] = {"Tanzen für Marzipan", nil, {"Tanzt für Gastwirtin Pala und Ihr werdet im Gegenzug etwas von dem Marzipan aus Donnerfels bekommen!"}},
[8361] = {"Abyssische Kontakte", nil, {"Bor Wildmähne auf Burg Cenarius in Silithus möchte, dass Ihr einen abyssischen Templer vernichtet und ihm dessen abyssisches Wappen als Beweis für Eure Tat bringt."}},
[8365] = {"Piratenhüte Ahoy!", nil, {"Sammelt 20 Südmeerpiratenhüte und bringt sie zurück zur eingebildeten Modeschöpferin beim Dampfdruckpier in Tanaris."}},
[8366] = {"Gaunerei und Erpressung im Südmeer", nil, {"Sicherheitschef Bilgenritzel beim Dampfdruckpier in Tanaris beauftragt Euch, 10 Piraten der Südmeerfreibeuter, 10 Seeräuber der Südmeerfreibeuter, 10 Dockarbeiter der Südmeerfreibeuter und 10 Schwadroneure der Südseefreibeuter zu töten."}},
[8367] = {"Große Ehre", nil, {"Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath."}},
[8368] = {"Schlacht der Kriegshymnenschlucht", nil, {"Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8369] = {"Die Invasoren des Alteractals", nil, {"Bringt 3 Ehrenabzeichen des Alteractals zu einem der Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8370] = {"Eroberung des Arathibeckens", nil, {"Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8371] = {"Gemeinsames Bestreben", nil, {"Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Brigadegeneral der Allianz in einer der Hauptstädte, Dalaran oder Shattrath."}},
