[8410] = {"Elementarbeherrschung", nil, {"Bath'rah der Windbehüter möchte, dass Ihr jeweils eine Probe Luft, Feuer, Erde und Wasser für ihn sammelt."}},
[8412] = {"Geistertotem", nil, {"Bringt Bath'rah dem Windbehüter die Komponenten für das Geistertotem."}},
[8413] = {"Die Macht des Voodoos", nil, {"Bringt Bath'rah dem Windbehüter die Voodoofedern."}},
[8414] = {"Austreibung des Bösen", nil, {"Sammelt 20 Geißelsteine des Dieners und bringt sie zu Hohepriester Thel'danis in den Westlichen Pestländern."}},
[8415] = {"Zugwindlager", nil, {"Sprecht mit Kommandant Ashlam Ehrenschlag im Zugwindlager."}},
[8417] = {"Ein geplagter Geist", nil, {"Sprecht mit dem gefallenen Helden der Horde am Eingang zu den Verwüsteten Landen."}},
[8418] = {"Erschaffung des Steins der Macht", nil, {"Beschafft Euch gelbe, blaue und grüne Voodoofedern von den Trollen im Versunkenen Tempel."}},
[8419] = {"Die Bitte eines Wichtels", nil, {"Bringt ein Stück Teufelsstoff zu Impsy im Teufelswald."}},
[8420] = {"Heiß und brennend", nil, {"Bringt ein Stück Teufelsstoff zu Impsy im Teufelswald."}},
[8421] = {"Das richtige Zeug", nil, {"Bringt 10 Stück faules Holz und 4 Blutgiftessenzen zu Impsy im Teufelswald."}},
[8422] = {"Federn von Trollen", nil, {"Bringt 6 Voodoofedern von den Trollen aus dem Versunkenen Tempel."}},
[8423] = {"Kriegerverwandtschaft", nil, {"Tötet 7 Hölleneber in den Verwüsteten Landen und kehrt zu dem gefallenen Helden der Horde zurück."}},
[8424] = {"Krieg den Schattenanbetern", nil, {"Merzt die Schattenanbeter in den Verwüsteten Landen aus und kehrt zum gefallenen Helden der Horde zurück."}},
[8425] = {"Voodoofedern", nil, {"Bringt die Voodoofedern der Trolle im Versunkenen Tempel zu dem gefallenen Helden der Horde."}},
