[8460] = {"Verbündeter der Holzschlundfeste", nil, {"Grazle möchte, dass Ihr Euch beweist, indem Ihr 6 Krieger der Totenwaldfelle, 6 Pfadfinder der Totenwaldfelle und 6 Gärtner der Totenwaldfelle besiegt. Kehrt zu ihm in den Süden des Teufelswalds nahe des Smaragdrefugiums zurück, sobald Ihr dies getan habt."}},
[8461] = {"Totenwaldfelle des Nordens", nil, {"Nafien möchte, dass Ihr 6 Höhlenbehüter der Totenwaldfelle, 6 Rächer der Totenwaldfelle und 6 Schamanen der Totenwaldfelle besiegt. Kehrt danach zu ihm in den Norden des Teufelswalds nahe des Eingangs der Holzschlundfeste zurück."}},
[8462] = {"Sprecht mit Nafien", nil, {"Reist nach Norden entlang der Hauptstraße des Teufelswalds und sprecht mit einem Furbolg namens Nafien. Er hält außerhalb des Eingangs zur Holzschlundfeste Wache."}},
[8463] = {"Instabile Manakristalle", nil, {"Bringt Aeldon Sonnenmal auf dem Falkenplatz 6 instabile Manakristalle."}},
[8464] = {"Aktivität der Winterfelle", nil, {"Salfa möchte, dass Ihr 8 Schamanen der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Ursa der Winterfelle erledigt. Salfa befindet sich genau außerhalb des Eingangs zur Holzschlundfeste in Winterquell."}},
[8465] = {"Sprecht mit Salfa", nil, {"Durchquert die Holzschlundfeste und verlasst sie in Richtung Winterquell. Sprecht mit Salfa, die außerhalb des Eingangs zur Holzschlundfeste Wache steht."}},
[8468] = {"Gesucht: Thaelis der Manadurstige", nil, {"Bringt Thaelis Kopf zu Unteroffizier Kan'ren beim Falkenplatz im Immersangwald."}},
[8470] = {"Ritualtotem der Totenwaldfelle", nil, {"Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist."}},
[8471] = {"Ritualtotem der Winterfelle", nil, {"Bringt das Ritualtotem in die Holzschlundfeste und findet heraus, ob einer der dortigen Furbolgs eine Verwendung für diesen Gegenstand hat. Die Furbolgs der Holzschlundfeste werden solange nicht mit Euch sprechen, bis Euer Ruf bei ihnen neutral oder höher ist."}},
[8472] = {"Schwere Fehlfunktion", nil, {"Sammelt 6 Arkankerne von den Arkanpatrouillen, die in den Ruinen von Silbermond umherstreifen, und bringt sie Magister Jaronis beim Falkenplatz im Immersangwald."}},
[8473] = {"Eine traurige Aufgabe", nil, {"Tötet 10 siechende Waldbehüter beim Versengten Hain und meldet Euch anschließend bei Larianna Flusswind, die sich im Turm nordwestlich des Versengten Hains im Immersangwald aufhält."}},
[8474] = {"Weißborkes Anhänger", nil, {"Macht die Person ausfindig, die Weißborkes Anhänger angefertigt haben könnte. Ihr könntet Hinweise über den Gegenstand erfahren."}},
[8475] = {"Die Todesschneise", nil, {"Tötet 8 Seuchenknochenplünderer und kehrt anschließend zu Waldläuferin Jaela in der Todesschneise im Immersangwald zurück."}},
[8476] = {"Amaniübergriff", nil, {"Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer im Immersangwald möchte, dass Ihr 5 Berserker der Amani und 5 Axtwerfer der Amani tötet."}},
[8477] = {"Der Hammer des Speermachers", nil, {"Sucht in Tor'Watha, südöstlich der Zuflucht der Weltenwanderer, nach Speermacher Otembe und bringt Otembes Hammer zu Arathel Sonnenschmied bei der Zuflucht der Weltenwanderer im Immersangwald."}},
[8479] = {"Zul'Marosh", nil, {"Ven'jashi, der gefangene Troll in Tor'Watha möchte, dass Ihr ihm Häuptling Zul'Maroshs Kopf bringt. Häuptling Zul'Marosh befindet sich in Zeb'Watha, jenseits des Elrendarsees."}},
[8480] = {"Zurückgelassene Waffen", nil, {"Bringt 8 Waffen der Sin'dorei zu Velendris Weißflor im Immersangwald."}},
[8481] = {"Die Wurzel alles Bösen", nil, {"Stellt die Fackel zur Dämonenbeschwörung im Eingang von Grolnars Höhle, im Lager der Winterfellfurbolgs auf. Besiegt den Dämon und erlangt die Essenz von Xandivious für Gorn Einauge in der Holzschlundfeste."}},
[8482] = {"Belastende Dokumente", nil, {"Bringt die belastenden Dokumente zu Aeldon Sonnenmal auf dem Falkenplatz im Immersangwald."}},
[8483] = {"Der Zwergenspion", nil, {"Bringt den Kopf von Ausgrabungsleiter Ambossel zu Aeldon Sonnenmal beim Falkenplatz im Immersangwald."}},
[8484] = {"Das Bemühen um Frieden", nil, {"Bringt das Friedensangebot der Holzschlundfeste zu König Magni Bronzebart in Eisenschmiede."}},
[8486] = {"Arkane Instabilität", nil, {"Leybewahrerin Velania möchte, dass Ihr 5 Managespenster und 5 Manapirscher tötet."}},
[8487] = {"Besudelte Erde", nil, {"Lehrling Mirveda beim Sanktum des Ostens im Immersangwald möchte, dass Ihr 8 besudelte Erdproben einsammelt."}},
[8488] = {"Ungewöhnliche Ergebnisse", nil, {"Beschützt Lehrling Mirveda vor dem Angriff der Geißel."}},
[8490] = {"Wiederaufladen der Verteidigung", nil, {"Platziert den energieerfüllten Kristall beim Runenstein des Ostens und verteidigt ihn 1 Minute lang vor der Geißel. Bringt den energieerfüllten Kristall anschließend zu Runenwächter Deryan im Immersangwald, um Eure Belohnung zu erhalten."}},
[8491] = {"Pelzjagd", nil, {"Velan Lichteiche in Morgenluft im Immersangwald möchte, dass Ihr ihm 6 Bachtatzenfelle bringt."}},
[8492] = {"Die Allianz braucht Kupferbarren!", nil, {"Bringt 20 Kupferbarren zu Unteroffizier Steinbraue im Militärviertel in Eisenschmiede."}},
[8494] = {"Die Allianz braucht Eisenbarren!", nil, {"Bringt 20 Eisenbarren zu Korporal Schwarz im Militärviertel in Eisenschmiede."}},
[8498] = {"Zwielichtschlachtbefehle", nil, {"Beschafft Euch die Schlachtbefehle des Schattenhammers und bringt sie zu Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch den taktischen Einsatz X, um diese Aufgabe abschließen zu können."}},
[8499] = {"Die Allianz braucht Thoriumbarren!", nil, {"Bringt 20 Thoriumbarren zu Dame Doppelzopf im Militärviertel in Eisenschmiede."}},
[8501] = {"Ziel: Stecher des Ashischwarms", nil, {"Tötet 30 Stecher des Ashischwarms und erstattet anschließend Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz XII, um diese Aufgabe abschließen zu können."}},
[8502] = {"Ziel: Arbeiter des Ashischwarms", nil, {"Tötet 30 Arbeiter des Ashischwarms und erstattet Bericht bei Kommandant Mar'alith auf Burg Cenarius in Silithus. Ihr benötigt auch Kampfeinsatz III, um diese Aufgabe abschließen zu können."}},
[8503] = {"Die Allianz braucht Würgetang!", nil, {"Bringt 20 Stück Würgetang zu dem Gefreiten Drechselknacks im Militärviertel in Eisenschmiede."}},
[8505] = {"Die Allianz braucht lila Lotus!", nil, {"Bringt 20 lila Lotus zu Meister Nachtweise im Militärviertel in Eisenschmiede."}},
[8507] = {"Felddienst", nil, {"Erstattet am Posten der Eisenschmiedebrigade beim Zoraschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie von Hauptmann Rußschmied unterzeichnen und kehrt zu Windrufer Kaldon auf Burg Cenarius in Silithus zurück.\n\nHinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Eisenschmiedebrigade wird Euch für den PvP-Kampf markieren."}},
[8509] = {"Die Allianz braucht Arthas' Tränen!", nil, {"Bringt 20 Arthas' Tränen zu Stabsfeldwebel Germaine im Militärviertel in Eisenschmiede."}},
[8511] = {"Die Allianz braucht leichtes Leder!", nil, {"Bringt 10 Einheiten leichtes Leder zu Bonnie Steinschinder im Militärviertel in Eisenschmiede."}},
