[9134] = {"Himmelsmeisterin Abendlicht", nil, {"Bringt Sathiels Waren zu Himmelsmeisterin Abendlicht am Hirtentor direkt außerhalb von Silbermond."}},
[9135] = {"Zurück zu Sathiel", nil, {"Erwerbt einen Flug zurück nach Morgenluft im Immersangwald und liefert Sathiels Waren bei Sathiel ab."}},
