[9313] = {"Reise zur Azurwacht", nil, {"Sprecht mit Techniker Dyvuun bei der Azurwacht auf der Azurmythosinsel."}},
[9314] = {"Nachricht von der Azurwacht", nil, {"Sprecht mit Versorgerin Chellan bei der Azurwacht auf der Azurmythosinsel."}},
[9315] = {"Anok'suten", nil, {"Tötet Anok'suten und kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück."}},
[9316] = {"Wilde Goblins", nil, {"Brianna Schneider auf der Designerinsel möchte, dass Ihr ihr 10 Goblinzähne bringt."}},
[9319] = {"Licht im Dunkeln", nil, {"Findet und berührt die Freudenfeuer innerhalb der Schwarzfelsspitze, dem Düsterbruch, Scholomance und Stratholme. Kehrt dann zum Flammenbewahrer zurück."}},
[9322] = {"Glühende Feuer in Kalimdor", nil, {"Sucht die Lager des Sonnenwendfests in Azshara, Silithus, dem Krater von Un'Goro und Winterquell auf. Kehrt dann zum Flammenbewahrer zurück."}},
[9323] = {"Glühende Feuer in den Östlichen Königreichen", nil, {"Reist zu den Lagern des Sonnenwendfests in den Verwüsteten Landen, den Östlichen Pestländern, dem Hinterland und der Sengenden Schlucht. Kehrt dann zum Flammenbewahrer zurück."}},
[9324] = {"Raubt die Flamme von Orgrimmar", nil, {"Bringt die Flamme von Orgrimmar zum Meister der Lehren."}},
[9325] = {"Raubt die Flamme von Donnerfels", nil, {"Bringt die Flamme von Donnerfels zum Meister der Lehren."}},
[9326] = {"Raubt die Flamme von Unterstadt", nil, {"Bringt die Flamme von Unterstadt zum Meister der Lehren."}},
[9327] = {"Die Verlassenen", nil, {"Sprecht mit Hochexekutor Mavren in Tristessa in den Geisterlanden."}},
[9328] = {"Held der Sin'dorei", nil, {"Bringt Dar'Khans Kopf zu Lor'themar Theron in Silbermond."}},
[9329] = {"Die Verlassenen", nil, {"Sprecht mit Hochexekutor Mavren in Tristessa in den Geisterlanden."}},
[9330] = {"Raubt die Flamme von Sturmwind", nil, {"Bringt die Flamme von Sturmwind zum Geschichtenerzähler."}},
[9332] = {"Raubt die Flamme von Darnassus", nil, {"Bringt die Flamme von Darnassus zum Geschichtenerzähler."}},
[9340] = {"Die Große Kluft", nil, {"Waldläuferhauptmann Venn'ren in der Falkenwacht möchte, dass Ihr 8 Steinsichelwelpen und 3 Steinsichelalphas bei der Großen Kluft östlich der Falkenwacht erlegt."}},
[9345] = {"Herstellung der Salbe", nil, {"Grelag im Rückenbrecherposten möchte, dass Ihr ihm 12 Höllenwirbelkräuter für seine Salbe bringt."}},
[9349] = {"Felshetzereier sammeln", nil, {"Bringt 12 Felshetzereier zu Legassi bei der Zeppelinabsturzstelle auf der Höllenfeuerhalbinsel."}},
[9351] = {"Wilde Leerwandler", nil, {"Bringt 10 kondensierte Leerwandleressenzen zu \"Schreihals\" Quack Lufthans bei der Zeppelinabsturzstelle."}},
[9352] = {"Darnassische Eindringlinge", nil, {"Reist zum Sanktum des Westens südwestlich vom Falkenplatz und vernichtet jegliche Eindringlinge, die Ihr dort findet. Berichtet anschließend Leybewahrerin Velania."}},
[9353] = {"iCoke-Geschenkgutschein einlösen", nil, {"Anforderungen:"}},
[9355] = {"Eine Aufgabe für einen intelligenten Helden", nil, {"Tötet 15 plündernde Krustenbohrer und kehrt dann zu Großknecht Biggums in der Ehrenfeste zurück."}},
[9356] = {"So weich wie Butter", nil, {"Bringt 12 fleischige Bussardflügel zu Legassi bei der Zeppelinabsturzstelle."}},
[9358] = {"Waldläuferin Sareyn", nil, {"Meldet Euch bei Waldläuferin Sareyn außerhalb von Morgenluft im Immersangwald."}},
[9359] = {"Zuflucht der Weltenwanderer", nil, {"Meldet Euch bei Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer im Immersangwald."}},
[9360] = {"Angriff der Amani", nil, {"Bringt die Invasionspläne der Amani zu Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer."}},
[9361] = {"Hölleneber, die etwas andere Delikatesse", nil, {"Legassi bei der Zeppelinabsturzstelle möchte, dass Ihr 8 Stück geläutertes Hölleneberfleisch sammelt. Um das Fleisch zu reinigen, schüttet die Läuterungsmischung über das besudelte Hölleneberfleisch, das Ihr von den Höllenebern gesammelt habt."}},
[9362] = {"Kriegsherr Krellian", nil, {"Beschafft die prismatische Muschel für Erzmagier Xylem. Der Erzmagier befindet sich in einem Turm auf den Klippen von Azshara."}},
[9363] = {"Warnt Morgenluft", nil, {"Reist nach Morgenluft im Immersangwald und sprecht mit Waldläufer Degolien."}},
[9364] = {"Gespaltene Magie", nil, {"Verwandelt die Grollflossen von Azshara und tötet die Kopien, die einige Sekunden später auftauchen. Wenn Ihr 50 verwandelte Kopien vernichtet habt, kehrt zu Erzmagier Xylem in Azshara zurück."}},
[9366] = {"Die Suche nach Teufelsblut", nil, {"Ryathen der Düstere an der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 6 Teufelsblutproben von den Terrorbestien bringt, die bei den Teichen von Aggonar umherstreifen."}},
[9367] = {"Das Fest des Feuers", nil, {"Wärmt Euch an den Freudenfeuern in Sturmwind, Eisenschmiede und Darnassus. Sprecht anschließend mit einem Meister der Lehren innerhalb der Hauptstädte."}},
[9368] = {"Das Fest des Feuers", nil, {"Wärmt Euch an den Freudenfeuern in Orgrimmar, Donnerfels und Unterstadt. Sprecht anschließend mit einem Geschichtenerzähler innerhalb der Hauptstädte."}},
[9369] = {"Wiederaufladen der Heilkristalle", nil, {"Beschafft Euch 6 Phiolen mit Mottenblut von den in der Nähe hausenden Talmotten und bringt sie zu Proenitus bei der Absturzstelle im Am'mental."}},
[9370] = {"Stoppt das Säuberungsritual", nil, {"Platziert den Signalstein bei den Teichen von Aggonar am Altar von Aggonar und besiegt alle Anachoreten der Draenei, die auf Eure Signal reagieren. Kehrt anschließend mit dem Signalstein zu Ryathen dem Düsteren bei der Falkenwacht zurück."}},
[9371] = {"Botanikerin Taerix", nil, {"Sprecht mit Botanikerin Taerix bei der Absturzstelle im Am'mental."}},
[9372] = {"Dämonische Verderbnis", nil, {"Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel möchte, dass Ihr 6 Blutproben eines Höllenebers von bulligen Höllenebern sammelt."}},
[9373] = {"Fehlender Schriftverkehr", nil, {"Bringt den zerfressenen Lederkoffer zu Thiah Rotmähne in der Nähe der Ruinen von Sha'naar im westlichen Teil der Höllenfeuerhalbinsel."}},
[9374] = {"Arelions Tagebuch", nil, {"Magistrix Carinda bei der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr zu den Teichen von Aggonar reist und Arelions Tagebuch sucht."}},
[9375] = {"Der Weg zur Falkenwacht", nil, {"Beschützt die verwundete Blutelfenpilgerin auf Ihrem Weg zur Falkenwacht und sprecht dann mit Taleris Morgenblick."}},
[9376] = {"Die Pflicht eines Pilgers", nil, {"Die verletzte Blutelfpilgerin wurde von giftigen Stachelklauenfelshetzern angegriffen, als sie die Staubstachelschlucht erkunden wollte. Findet ihr Lager und haltet nach dem vermissten Beutel Ausschau."}},
[9377] = {"Hunger nach Macht", nil, {"Beschafft eine potente Leerwandleressenz von den unkontrollierten Leerwandlern und kehrt anschließend zu Taleris Morgenblick in der Falkenwacht zurück."}},
[9381] = {"Treffsichere Pfeile", nil, {"Sammelt 8 Schwanzfedern eines Knochenhäuters von den Knochenhäutergeiern und bringt sie Falknerin Drenna Flusswind in der Falkenwacht."}},
[9383] = {"Ein gewagter Plan", nil, {"Wendet den geweihten Kristall auf einen verwundeten unkontrollierten Leerwandler an. Kehrt anschließend zu Elsaana beim Tempel von Telhamat zurück."}},
[9385] = {"Tobende Felshetzer", nil, {"Tötet 10 Stachelklauenfelshetzer und kehrt anschließend zu Gremni Langbart auf der Höllenfeuerhalbinsel zurück."}},
[9386] = {"Licht im Dunkeln", nil, {"Findet und berührt die Freudenfeuer innerhalb der Schwarzfelsspitze, dem Düsterbruch, Scholomance und Stratholme. Sprecht dann mit dem Flammenbewahrer."}},
[9387] = {"Die Quelle der Verderbtheit", nil, {"Reist zu den Ruinen von Sha'naar auf der Höllenfeuerhalbinsel und besorgt 5 dämonische Essenzen von den Zuchtmeistern der Illidari. Kehrt zu Apotheker Azethen in der Falkenwacht zurück, wenn Ihr die Aufgabe erfüllt habt."}},
[9388] = {"Lodernde Flammen in Kalimdor", nil, {"Sucht die Freudenfeuer im Eschental, dem Brachland, der Dunkelküste und dem Steinkrallengebirge auf. Sprecht anschließend mit dem Flammenbewahrer."}},
[9389] = {"Lodernde Flammen in den Östlichen Königreichen", nil, {"Sucht die Freudenfeuer im Vorgebirge des Hügellands, im Silberwald, in Westfall und dem Sumpfland auf. Sprecht anschließend mit dem Flammenbewahrer."}},
[9390] = {"Die Suche nach Sedai", nil, {"Anachoret Obadei möchte, dass Ihr östlich von hier auf der Höllenfeuerhalbinsel nach seinem Bruder Sedai Ausschau haltet."}},
[9391] = {"Den Weg kennzeichnen", nil, {"Waldläuferhauptmann Venn'ren an der Falkenwacht möchte, dass Ihr auf der Höllenfeuerhalbinsel die Große Kluft aufsucht und dort das südliche Leuchtsignal, westliche Leuchtsignal und das zentrale Leuchtsignal entzündet. Kehrt anschließend mit der brennenden Fackel zu ihm zurück."}},
[9392] = {"Ausbildung zum Schurken", nil, {"Sprecht mit Pfadpirscher Avokor - dem Schurkenlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[9393] = {"Ausbildung zum Jäger", nil, {"Sprecht mit Waldläuferin Sallina - der Jägerlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[9394] = {"Wo ist Wyllithen?", nil, {"Findet Grundbewahrer Wyllithen beim Nachtschimmergrund im Immersangwald."}},
[9395] = {"Saltherils Hafen", nil, {"Sprecht mit Lord Saltheril in Saltherils Hafen im Immersangwald."}},
[9396] = {"Die Magie der Arakkoa", nil, {"Bringt 4 Rollen der Haal'eshi zu Arkanist Calesthris Morgenstern bei der Falkenwacht auf der Höllenfeuerhalbinsel."}},
[9397] = {"Kommt ein Vogel geflogen", nil, {"Reist zum Haal'eshbau auf der Höllenfeuerhalbinsel und durchsucht die Kalirinester nach einem jungen Kaliriweibchen. Benutzt den leeren Vogelkäfig, um es zu fangen, und kehrt anschließend zu Falknerin Drenna Flusswind in der Falkenwacht zurück."}},
[9398] = {"Tödliche Raubtiere", nil, {"Späher Vanura im Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr 8 Steinsichelwelpen und 4 Steinsichelalpha tötet."}},
[9399] = {"Die Grausamkeit der Zuchtmeister", nil, {"Reist zu den Ruinen von Sha'naar und erschlagt 4 Zuchtmeister der Illidari. Kehrt anschließend zu Ikan zum Tempel von Telhamat auf der Höllenfeuerhalbinsel zurück."}},
[9400] = {"Der Assassine", nil, {"Nazgrel möchte, dass Ihr zu der Straße nördlich der Höllenfeuerzitadelle reist und nach einem Assassinen namens Krun Rückenbrecher Ausschau haltet."}},
[9401] = {"Eine seltsame Waffe", nil, {"Bringt die schwere Steinaxt zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel."}},
[9402] = {"Fangt!", nil, {"Taucht auf den Grund des Stillwispertümpels und holt die Azurphiole für Instrukteur Antheol."}},
[9403] = {"Das reinste Wasser", nil, {"Instrukteur Antheol möchte, dass Ihr die Azurphiole bei den Elrendarfällen auffüllt."}},
[9404] = {"Noch lebendig", nil, {"Tötet die Immersangwaldbehüter bis Ihr einen lebendigen Zweig für Instrukteur Antheol findet."}},
[9405] = {"Das Anliegen des Kriegshäuptlings", nil, {"Bringt die schwere Steinaxt zu Scharfseherin Regulkut in Thrallmar auf der Höllenfeuerhalbinsel."}},
[9406] = {"Die Mag'har", nil, {"Bringt den Brief von den Mag'har zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel."}},
[9407] = {"Durch das Dunkle Portal", nil, {"Reist durch das Dunkle Portal und meldet Euch bei Generalleutnant Orion. Er erwartet Euch auf der anderen Seite - in der Scherbenwelt."}},
[9408] = {"Vergessene Helden", nil, {"Sprecht mit Truppenkommandant Danath Trollbann in der Ehrenfeste."}},
[9409] = {"Eilige Lieferung!", nil, {"Bringt die Phiolensammlung zu Zalduun bei der Absturzstelle im Am'mental."}},
[9410] = {"Spirituelle Führung", nil, {"Verwendet das Wolfstotem an dem Ort, wo Ihr Krun Rückenbrechers Leichnam gefunden habt und folgt dem Ahnengeistwolf."}},
[9415] = {"Meldung bei Marschall Himmelswand", nil, {"Sprecht mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus."}},
[9416] = {"Meldung bei General Kirika", nil, {"Sprecht mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus."}},
[9417] = {"Die Arakkoabedrohung", nil, {"Gremni Langbart auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Windspäher der Haal'eshi und 6 Krallenwachen der Haal'eshi tötet."}},
[9418] = {"Avruus Kugel", nil, {"Bringt Avruus Kugel zum Altar der Haal'eshi südöstlich des Haal'eshbaus auf der Höllenfeuerhalbinsel und gebt sie Aeranas, sobald er frei ist."}},
[9419] = {"Durchkämmen der Wüste", nil, {"Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus."}},
[9420] = {"Feinste Daunen", nil, {"Mirren Langbart auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 8 Kalirifedern von den Kaliris im Haal'eshbau bringt."}},
[9421] = {"Ausbildung zum Schamanen", nil, nil},
[9422] = {"Durchkämmen der Wüste", nil, {"Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus."}},
[9423] = {"Rückkehr zu Obadei", nil, {"Kehrt zu Anachoret Obadei beim Tempel von Telhamat auf der Höllenfeuerhalbinsel zurück und berichtet ihm von Sedais Tod."}},
[9424] = {"Makurus Rache", nil, {"Makuru im Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 10 Ahnenperlen der Mag'har bringt."}},
[9425] = {"Bericht an Tarrens Mühle", nil, {"Reist nach Tarrens Mühle im Vorgebirge des Hügellands und sprecht mit Beraterin Morgenabend."}},
[9426] = {"Die Teiche von Aggonar", nil, {"Amaan der Weise möchte, dass Ihr zu den Teichen von Aggonar geht und 6 Teufelsblutschrecken und 6 mörderische Faulschleime tötet."}},
[9427] = {"Reinigung des Wassers", nil, {"Amaan der Weise möchte, dass Ihr zu den Teichen von Aggonar reist und die Reinigungsphiole bei Aggonars Leiche benutzt. Kehrt zu ihm zurück, wenn Ihr das Wasser von Aggonars Essenz befreit habt."}},
[9428] = {"Bericht an den Splitterholzposten", nil, {"Reist zum Splitterholzposten im Eschental und sprecht mit Berater Sonnenschwur."}},
[9429] = {"Reist nach Dunkelhain", nil, {"Reist nach Dunkelhain im Dämmerwald und sprecht mit Anachoret Delan."}},
[9430] = {"Relikte von Sha'naar", nil, {"Amaan der Weise beim Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr zu den Ruinen von Sha'naar reist und 10 Relikte von Sha'naar einsammelt."}},
[9431] = {"Eine andere Lösung", nil, {"Sammelt 2 Handvoll reinigende Erde von den donnernden Felskloppern und bringt sie zu Wizlo Lagerputzer beim Weißgipfelposten."}},
[9432] = {"Reist nach Astranaar", nil, {"Reist nach Astranaar im Eschental und sprecht mit Verteidigerin Palanaar."}},
[9433] = {"Ein Bad im Mondbrunnen", nil, {"Benutzt die Bedienungseinheit für Robotron in der Nähe der versteckten Steuerungskonsole in einer kleinen Gruppe von Büschen am Rand der Nadel, die sich am dichtesten an Thalanaar befindet, und übernehmt die Steuerung des Robotron 3000. Steuert den Roboter zum westlichen Ende von Tausend Nadeln zum Allianzaußenposten von Thalanaar. Benutzt den Roboter, um eine Probe Mondbrunnenwasser zu besorgen und bringt sie zu Wizlo Lagerputzer beim Weißgipfelposten."}},
[9434] = {"Das Tonikum testen", nil, {"Bringt die Kiste mit Reagenzien zu Magistrix Elosai im Freiwindposten."}},
[9435] = {"Vermisste Kristalle", nil, {"Besorgt die Lieferung seltener Kristalle von den Verlassenen und bringt sie zu Huraan in Süderstade zurück."}},
[9436] = {"Erkenntnisse über die Blutskalpe", nil, {"Nemeth Falkenauge im Basislager von Grom'gol möchte, dass Ihr ihm ein Totem der Blutskalpe von einem Schamanen der Blutskalpe bringt."}},
[9437] = {"Der Untergang der Morgenbö", nil, {"Balandar Sternenlicht in Brackenwall möchte, dass Ihr die Fracht der Morgenbö wiederholt und den Ersten Maat Ithania von den Streitkräften von Theramore in der Nordwacht befreit."}},
[9438] = {"Bote für Thrall", nil, {"Reist nach Orgrimmar und übergebt Thrall Nazgrels Brief."}},
[9439] = {"Unabgeholtes Gepäck", nil, {"Holt den Beutel des Beraters und das Rapier des Beraters aus der Festung Angor und kehrt dann zu Berater Sarophas in Kargath zurück."}},
[9440] = {"Kleine Häppchen", nil, {"Verfüttert die teuflischen Häppchen an die gefangenen Tiere der Verirrten. Bringt die Überbleibsel zu Cersei Abendsänger in Steinard in den Sümpfen des Elends."}},
[9441] = {"Entsandt zu den Mag'har", nil, {"Kehrt zu Gorkan Blutfaust beim Posten der Mag'har auf der Höllenfeuerhalbinsel zurück und bietet ihm im Namen Thralls Eure Dienste an."}},
[9442] = {"Eine erschöpfende Krankheit", nil, {"Erdruferin Ryga beim Posten der Mag'har auf der Höllenfeuerhalbinsel möchte, dass Ihr 1 Trockenkappe aus einer Höhle in der großen Kluft besorgt."}},
[9443] = {"Das sogenannte Mal des Lichtbringers", nil, {"Nehmt das Mal des Lichtbringers an Euch und kehrt anschließend zu Mehlar Dämmerklinge beim Bollwerk in den Westlichen Pestländern zurück."}},
[9444] = {"Entweiht Uthers Grabmal", nil, {"Entweiht Uthers Grabmal und kehrt anschließend zu Mehlar Dämmerklinge beim Bollwerk in den Westlichen Pestländern zurück."}},
[9447] = {"Salbe verabreichen", nil, {"Erdruferin Ryga möchte, dass Ihr die Heilsalbe 10 entkräfteten Grunzern beim Posten der Mag'har auf der Höllenfeuerhalbinsel verabreicht."}},
[9449] = {"Ruf der Erde", nil, {"Sprecht mit dem Geist des Tals beim Heiligen Hain im Am'mental."}},
[9452] = {"Schnapper - sehr, sehr lecker!", nil, {"Verwendet das Fischernetz der Draenei, um 10 Schnapper zu fangen. Bringt die Fische und das Netz anschließend zu Diktynna auf der Azurmythosinsel zurück."}},
[9453] = {"Findet Acteon!", nil, {"Bringt die Ladung Schnapper zu Acteon bei der Azurwacht."}},
[9454] = {"Die große Mondweidenjagd", nil, {"Acteon bei der Azurwacht möchte, dass Ihr ihm 6 Mondweidenhirschlenden bringt."}},
[9455] = {"Seltsame Funde", nil, {"Bringt den schwach leuchtenden Kristall zu Exarch Menelaous bei der Azurwacht."}},
[9456] = {"Nachtpirscherjagd", nil, {"Exarch Menelaous bei der Azurwacht möchte, dass Ihr 8 infizierte Nachtpirscherjungen tötet."}},
[9457] = {"Ein ungewöhnlicher Patron", nil, {"Benutzt Naias' Gabe in der Nähe des Altars von Naias, um den Wasserelementar Naias zu rufen. Besiegt ihn und kehrt dann zu Nemeth Falkenauge im Basislager von Grom'gol mit dem Herz von Naias zurück."}},
[9461] = {"Ruf des Feuers", nil, {"Geht zu Prophet Velen in der Halle des Lichts in der Exodar."}},
[9463] = {"Medizinische Notwendigkeit", nil, {"Anachoret Fateema bei der Azurwacht möchte, dass Ihr für sie 8 Lauerwurzranken sammelt."}},
[9464] = {"Ruf des Feuers", nil, {"Sprecht mit Glutos beim Glutgrund auf der Azurmythosinsel."}},
[9465] = {"Ruf des Feuers", nil, {"Erlangt die Ritualfackel wieder und bringt sie zu Glutos beim Glutgrund auf der Azurmythosinsel."}},
[9466] = {"Gesucht: Schwarzkralle die Wilde", nil, {"Bringt Schwarzkralles Klauen zu Waldläuferhauptmann Venn'ren bei der Falkenwacht auf der Höllenfeuerhalbinsel."}},
[9469] = {"Federbarts Unterstützung", nil, {"Geht zu Federbarts Hütte und stattet dem Zwerg dort einen Besuch ab. Solltet Ihr ihn nicht dort antreffen, so sucht nach Hinweisen auf seinen Verbleib."}},
[9470] = {"Eine Geste des guten Willens", nil, {"Botschafter Rualeth auf dem Nistgipfel möchte, dass Ihr Hexendoktor Mai'jin und seine Spinne Tcha'kaz tötet."}},
[9471] = {"Die Raubtiere zur Beute machen", nil, {"Botschafter Rualeth auf dem Nistgipfel möchte, dass Ihr 10 räudige Silbermähnen und 5 Silbermähnenwölfe tötet."}},
[9472] = {"Arelions Geliebte", nil, {"Magistrix Carinda bei der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr Viera Sonnenwisper aus der Siedlung herauslockt und Carindas Rolle der Vergeltung auf sie anwendet."}},
[9473] = {"Eine alternative Alternative", nil, {"Daedal bei der Azurwacht möchte, dass Ihr für ihn 5 Azurlöwenmäulchen sammelt."}},
[9474] = {"Das Mal des Lichtbringers", nil, {"Besorgt das Mal des Lichtbringers und kehrt dann zu Anachoret Truuen im Zugwindlager in den Westlichen Pestländern zurück."}},
[9475] = {"Wiedererlangen der Eier", nil, {"Erlangt 5 Greifeneier und bringt sie zu Botschafter Rualeth beim Nistgipfel."}},
[9476] = {"Suche nach Federbart", nil, {"Reist nach Shadra'Alor und sucht nach Spuren von Federbart."}},
[9483] = {"Die feinen Freuden im Leben", nil, {"Anforderungen:"}},
[9484] = {"Die Zähmung der Bestie", nil, {"Verwendet die Zähmerrute, um einen wahnsinnigen Drachenfalken zu zähmen. Kehrt anschließend zu Leutnant Dämmerflucht zurück."}},
[9485] = {"Die Zähmung der Bestie", nil, {"Verwendet die Zähmerrute, um eine Nebelfledermaus zu zähmen. Kehrt anschließend zu Leutnant Dämmerflucht zurück."}},
[9487] = {"Arkanhäscher", nil, {"Arkanist Vandril möchte, dass Ihr 10 Arkantrümmer für ihn sammelt."}},
[9488] = {"Eine einfache Robe", nil, {"Arkanist Vandril möchte, dass Ihr ihm 15 Leinenstoff und eine Seidendrüse der Spindelweber bringt."}},
[9489] = {"Ordnung in der Schneise", nil, {"Wirkt das Machtwort: Seelenstärke auf 6 Waldläufer von Immersang in der Todesschneise."}},
[9490] = {"Die Felsklauenmatriarchin", nil, {"Besorgt Schwarzkralles Klauen von Schwarzkralle der Wilden in der Großen Kluft und bringt sie zu Ikan beim Tempel von Telhamat auf der Höllenfeuerhalbinsel."}},
[9491] = {"Gier", nil, {"Eralan in Tristessa möchte, dass Ihr ihr einen gekörnten Goldring aus den Amanikatakomben bringt."}},
