[9672] = {"Das Vermächtnis des Blutfluchs", nil, {"Admiral Odesyus hat Euch gebeten, nach Kapitän Edward Hanes zu suchen und mit ihm zu sprechen."}},
[9674] = {"Die Naga des Blutfluchs", nil, {"Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr 10 Naga des Blutfluchs tötet."}},
[9676] = {"Ausbildung zum Paladin", nil, {"Sprecht mit Jesthenis Sonnensturm - dem Paladinlehrer - im Sonnenturm auf der Insel der Sonnenwanderer."}},
[9677] = {"Aufforderung des Ritterfürsten Heldenblut", nil, {"Sprecht mit Ritterfürst Heldenblut beim Platz der Weltenwanderer in Silbermond."}},
[9678] = {"Die erste Prüfung", nil, {"Reist zu der Insel, die Euch Ritterfürst Heldenblut beschrieben hat, sucht die Höhle und entzündet die Kohlepfanne. Kehrt, nachdem Ihr die erste Prüfung bestanden habt, zu Ritterfürst Heldenblut in Silbermond zurück."}},
[9680] = {"Die Vergangenheit aufwühlen", nil, {"Erzmagier Alturus möchte, dass Ihr zu den Bergen südlich von Karazhan im Gebirgspass der Totenwinde geht und ein verkohltes Knochenfragment besorgt."}},
