[9696] = {"Übersetzungen...", nil, {"Bringt das Schreiben der Sonnenfalken zur Vernehmungsbeauftragten Elysia bei der Blutwacht."}},
[9697] = {"Behüterin Leesa'oh", nil, {"Sprecht mit Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen."}},
