[9715] = {"Bringt mir ein Gebüsch!", nil, {"Sammelt 5 Stück Bluthibiskus und bringt sie zu Gzhun'tt in Sporeggar in den Zangarmarschen."}},
[9716] = {"Anomalie am Umbrafennsee", nil, {"Untersucht den Grund für die sinkenden Wasserspiegel am Umbrafennsee. Kehrt dann zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
[9717] = {"Los geht's!", nil, {"Besorgt einen Tiefensporenfarn und bringt ihn zu T'shu in Sporeggar in den Zangarmarschen."}},
[9718] = {"Der Flug der Krähe", nil, {"Ysiel Windsänger möchte, dass Ihr das Sturmkrähenamulett benutzt, um die größeren Seen in den Zangarmarschen zu erkunden."}},
[9719] = {"Tötet die Mutter", nil, {"Bringt das Gehirn der Schattenmutter zu Khn'nix in Sporeggar in den Zangarmarschen."}},
[9720] = {"Das Gleichgewicht muss erhalten bleiben", nil, {"Ysiel Windsänger möchte, dass Ihr die Eisenrankensamenkörner benutzt, um die Dampfpumpensteuerungen am Schlangensee, Umbrafennsee, Sumpflichtsee und der Lagune zu zerstören. Kehrt danach mit den übrigen Samen zu ihr in die Zuflucht des Cenarius in den Zangarmarschen zurück."}},
[9721] = {"Aufforderung von Lord Solanar", nil, {"Sprecht mit Lord Solanar Blutzorn in Silbermond."}},
[9723] = {"Eine Geste der Hingabe", nil, {"Bringt 40 Einheiten Runenstoff, 6 Arkanitbarren, 10 Einheiten Sonnengras, 5 Dunkelrunen und 150 Goldstücke zu Lord Solanar Blutzorn in Silbermond."}},
