[9729] = {"Fhwoor zerschmettern!", nil, {"Begleitet Fwoohr in das Gebiet bei der Dampfpumpe am Sumpflichtsee, um die Lade von Ssslith zu holen. Bringt Fhwoor und die Lade sicher zurück und meldet Euch bei Gzhun'tt in Sporeggar in den Zangarmarschen."}},
[9730] = {"Anführer der Dunkelkämme", nil, {"Tötet Rajah Haghazed bei der Lagune und kehrt danach zu Aufseher Hamuht bei der Expedition des Cenarius in den Zangarmarschen zurück, um eine Belohnung zu erhalten."}},
[9731] = {"Kanalisationspläne", nil, {"Durchsucht den Grund des Schlangensees nach einem Ablauf. Kehrt mit Neuigkeiten über Eure Entdeckung zu Ysiel Windsänger in der Zuflucht des Cenarius zurück."}},
[9732] = {"Rückkehr in die Marschen", nil, {"Kehrt zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück."}},
[9735] = {"Wahre Meister des Lichts", nil, {"Bringt Lord Solanar Blutzorn in Silbermond eine Phiole Weihwasser von Tyrs Hand."}},
