[9757] = {"Sucht Jägerin Kella Nachtbogen auf", nil, {"Sprecht mit Jägerin Kella Nachtbogen nahe Valaars Steg."}},
[9758] = {"Rückkehr zu Arkanist Vandril", nil, {"Kehrt zu Arkanist Vandril in Tristessa in den Geisterlanden zurück."}},
[9759] = {"Ihre Welt enden lassen", nil, {"Folgt und beschützt Demolierer Legoso, während er versucht, die Vektorspule zu zerstören und tötet Sironas. Kehrt zu Exarch Admetius bei der Blutwacht zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
[9760] = {"Verteidigers Ruh'", nil, {"Sprecht mit Verteidigerin Corin bei Verteidigers Ruh' auf der Blutmythosinsel."}},
[9761] = {"Den Weg frei machen", nil, {"Verteidigerin Corin bei Verteidigers Ruh' möchte, dass Ihr 8 Saboteure der Sonnenfalken und 8 Agenten der Sonnenfalken tötet."}},
[9762] = {"Die ungeschriebene Prophezeiung", nil, nil},
[9763] = {"Das Versteck des Kriegsherren", nil, {"Behüterin Jhang möchte, dass Ihr Kriegsherr Kalithresh im Echsenkessel findet und tötet."}},
[9764] = {"Befehle von Lady Vashj", nil, {"Bringt die Befehle von Lady Vashj zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen."}},
