[9814] = {"Berstkappenpilze, Mann!", nil, {"Bringt Hexendoktor Tor'gash 6 Berstkappen."}},
[9815] = {"Schlammtauchen", nil, {"Elementarist Lo'ap am Thron der Elemente in Nagrand möchte, dass Ihr 5 schlammbesudelte Kerne besorgt."}},
[9816] = {"Habt Ihr so was schon mal gesehen?", nil, {"Bringt den Murlockäfig nach Dolchfenn und stellt ihn auf den flachen Stein in der Nähe des Dorfs."}},
[9817] = {"Anführerin der Blutschuppen", nil, {"Tötet Rajis Fyashe am Schlangensee und kehrt dann zu Aufseher Hamuht in der Zuflucht des Cenarius in den Zangarmarschen zurück, um eine Belohnung zu erhalten."}},
[9818] = {"Im Untergrund", nil, {"Kommuniziert mit Gordawg."}},
[9819] = {"Die gequälte Erde", nil, {"Gordawg am Thron der Elemente in Nagrand möchte, dass Ihr 10 gequälte Erdgeister tötet."}},
[9820] = {"GESUCHT: Boss Grog'ak", nil, {"Bringt Boss Grog'aks Kopf zu Schattenjäger Denjai in Zabra'jin."}},
[9821] = {"Verdammnis schmecken", nil, {"Gordawg am Thron der Elemente in Nagrand möchte, dass Ihr ihm 10 Kerne eines wütenden Zermalmers bringt."}},
