[9827] = {"Siechendes Basidium", nil, {"Bringt das siechende Basidium zu Ruam in Telredor in den Zangarmarschen."}},
[9828] = {"Siechendes Basidium", nil, {"Bringt das siechende Basidium zu Reavij im Sumpfrattenposten in den Zangarmarschen."}},
[9829] = {"Khadgar", nil, {"Bringt Alturus' Bericht zu Khadgar in Shattrath in den Wäldern von Terokkar."}},
