[10004] = {"Geduld und Verständnis", nil, {"Altruis der Leidende im westlichen Nagrand möchte, dass Ihr die Blaupausen des Meisterplaners zu Sal'salabim in Shattrath bringt und ihn überredet, euch zu helfen."}},
[10005] = {"Erdbinder Tavgren Bescheid sagen", nil, {"Sprecht mit Erdbinder Tavgren außerhalb des Cenariusdickichts in den Wäldern von Terokkar."}},
[10006] = {"Erdbinder Tavgren Bescheid sagen", nil, {"Sprecht mit Erdbinder Tavgren außerhalb des Cenariusdickichts in den Wäldern von Terokkar."}},
[10007] = {"Die Reihen lichten", nil, {"Tötet 10 Verwüster der Knochenmalmer und 6 Kreuzbrecher der Knochenmalmer und kehrt dann zu Theloria Dämmermantel zurück."}},
[10008] = {"Was in Terokkar geschieht, bleibt in Terokkar", nil, {"Schattenpirscher Kaide möchte, dass Ihr 10 Verwüster der Knochenmalmer und 6 Kreuzbrecher der Knochenmalmer tötet."}},
[10009] = {"Ein paar Schädel spalten", nil, {"Sal'salabim in Shattrath möchte, dass Ihr Raliqs Schulden, Kusch'kuschs Schulden und Floons Schulden eintreibt."}},
