[10011] = {"Konstruktionslager: zerstört", nil, {"Altruis der Leidende im westlichen Nagrand möchte, dass Ihr das Konstruktionslager: Hass und das Konstruktionslager: Furcht zerstört."}},
[10012] = {"Höllenorcpläne", nil, {"Bringt die Höllenorcpläne zu Bertelm in Allerias Feste in den Wäldern von Terokkar."}},
[10013] = {"Eine ungesehene Hand", nil, {"Bringt die Höllenorcpläne zu Rokag in der Steinbrecherfeste in den Wäldern von Terokkar."}},
[10016] = {"Waldworgschwänze", nil, {"Bertelm in Allerias Feste möchte, dass Ihr ihm 12 Waldworgschwänze bringt. Die Schwänze können von Waldworgs oder Waldworgalphas stammen."}},
[10017] = {"Schwindende Vorräte", nil, {"Bringt 8 Schreckensgiftbeutel zu Sha'nir in Shattrath.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
