[10337] = {"Almabtrieb", nil, {"Begleitet Bessy sicher zu ihrem Besitzer Thadell zurück."}},
[10338] = {"Manaschmiede Duro abschalten", nil, {"Kehrt zur Manaschmiede Duro zurück und besorgt Euch den Zugangskristall von Vorarbeiter Athanel. Benutzt ihn, um die Steuerkonsole von Duro abzuschalten, und meldet Euch danach bei Spionagemeister Thalodien.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10339] = {"Das Astraleum", nil, {"Kommandant Ameer möchte, dass Ihr 5 Auftragsmörder des Astraleums, 5 Stoßtruppsoldaten des Astraleums, 2 Forscher des Astraleums und Hauptmann Zovax tötet und dann den Transponder des Astraleums Zeta aktiviert."}},
[10340] = {"Trümmerposten", nil, {"Vorpostenkommandant Königstein möchte, dass Ihr mit Schwadronskommandant Dabir'ee sprecht, auf einem Greifen zum Trümmerposten fliegt und Euch bei Runetog Wildhammer meldet."}},
[10341] = {"Tretet sie, solange sie am Boden liegen", nil, {"Tötet 8 Beschwörer des Sonnenzorns, 6 Jadgschützen des Sonnenzorns und 4 Zenturios des Sonnenzorns. Kehrt zu Magistrix Larynna in Area 52 zurück, wenn Ihr die Aufgabe erledigt habt.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10342] = {"Schieferhautschiefer", nil, {"Sammelt 5 Stück Schieferhautschiefer und bringt sie zu Stiefels in Area 52 im Nethersturm."}},
[10343] = {"Die unendliche Invasion", nil, {"Sucht die von Manakreaturen heimgesuchte Gegend in Kirin'Var nach einem Manabombenfragment ab und bringt sie zu Zaubererleutnant Morran."}},
[10344] = {"Schwadronskommandant Greifgar", nil, {"Sprecht mit Schwadronskommandant Greifgar im Trümmerposten."}},
[10345] = {"Fleisch geworden", nil, {"Benutzt den Anzünder des Protektorats um 12 siechende Leichen zu zerstören. Bringt den Anzünder des Protektorats zu Agent Araxes außerhalb des Zugangsschachts Zeon zurück, wenn Ihr erfolgreich wart."}},
[10346] = {"Rückkehr zur Abyssischen Untiefe", nil, {"Greifnerin Heulwind im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
[10347] = {"Rückkehr zur Abyssischen Untiefe", nil, {"Schwadronskommandant Brack im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
[10348] = {"Neue Möglichkeiten", nil, {"Sammelt 15 Elfenbeinglocken und kehrt dann zu Mehrdad im Mittelreichposten zurück."}},
[10349] = {"Die Erdbinderin", nil, {"Bringt den Purpurkristallsplitter zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
[10350] = {"Behomat", nil, {"Sprecht mit Behomat bei der Händlertreppe in der Exodar."}},
[10351] = {"Natürliche Heilmittel", nil, {"Benutzt den Samen der Wiederbelebung beim Zirkel des Erdbinders, um das Land um den Kristall herum zu heilen. Kehrt dann zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel mit Euren Beobachtungen zurück."}},
