[10388] = {"Rückkehr nach Thrallmar", nil, {"Kehrt nach Thrallmar zurück und sprecht mit Nazgrel."}},
[10389] = {"Das Leid und die Dunkelheit", nil, {"Magister Blutfalke möchte, dass Ihr 10 Terrorbestien bei den Teichen von Aggonar tötet."}},
[10390] = {"Konstruktionslager: Mageddon", nil, {"Nazgrel in Thrallmar möchte, dass Ihr das Konstruktionslager: Mageddon angreift, 10 Diener der Gan'arg tötet und mit Schmiedebestie Sägeklinges Kopf zurückkehrt."}},
[10391] = {"Kanonen des Zorns", nil, {"Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Nazgrel in Thrallmar zurück."}},
[10392] = {"Die Schwelle zum Abgrund", nil, {"Tötet Arix'Amal und nehmt Euch den Torschlüssel der Brennenden Legion. Benutzt den Schlüssel bei der Rune der Bosheit und kehrt danach zu Nazgrel in Thrallmar zurück."}},
[10393] = {"Boshafte Pläne", nil, {"Bringt den Schriftwechsel der Legion zu Magister Blutfalke in Thrallmar."}},
[10394] = {"Unterbrechung beim Konstruktionslager: Mageddon", nil, {"Tötet 10 Diener der Gan'arg und Schmiedebestie Sägeklinge im Konstruktionslager: Mageddon und kehrt danach zu Feldmarschall Brock beim Ehrenposten zurück."}},
[10395] = {"Der dunkle Schriftwechsel", nil, {"Bringt den Schriftwechsel zu Warpseher Kryv in der Haupthalle der Ehrenfeste."}},
[10396] = {"Der Feind meines Feindes...", nil, {"Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Feldmarschall Brock beim Ehrenposten zurück."}},
[10397] = {"Invasionspunkt: Vernichter", nil, {"Tötet Arix'Amal. Nehmt den Torschlüssel der Brennenden Legion und benutzt ihn bei der Rune der Bosheit."}},
[10399] = {"Das Herz der Dunkelheit", nil, {"Tötet 10 Terrorbestien bei den Teichen von Aggonar und kehrt dann zu Warpseher Kryv in der Haupthalle der Ehrenfeste zurück."}},
[10400] = {"Oberanführer", nil, {"Tötet Arazzius den Grausamen bei den Teichen von Aggonar und kehrt danach zu Truppenkommandant Danath Trollbann in der Ehrenfeste zurück."}},
[10403] = {"Naladu", nil, {"Sucht Naladu in den Ruinen von Sha'naar."}},
[10405] = {"S-A-B-O-T-A-G-E", nil, {"Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr eine vorbereitete Bandage des Astraleums besorgt."}},
[10406] = {"Die Nachricht überbringen", nil, {"Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Demolierer des Protektorats zur Energieleitung der Manaschmiede Ultris begleitet und ihn beschützt, während er die Leitung sabotiert."}},
[10407] = {"Socrethars Schatten", nil, {"Anachoret Karja in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10408] = {"Nexuskönig Salhadaar", nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Störer des Protektorats benutzt, um Salhadaar Kraftfelder zu senken und Nexuskönig Salhadaar zu vernichten."}},
[10409] = {"Todesstoß für die Legion", nil, {"Hohepriesterin Ishanah möchte, dass Ihr zum Transporter der Legion am nordwestlichsten Ende des Nethersturms geht und Euch zu Socrethars Sitz teleportiert. Wenn Ihr dort seid, besiegt Socrethar.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10410] = {"Ishanahs Hilfe", nil, {"Bringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des endlosen Lichts auf der Aldorhöhe in Shattrath.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10411] = {"Elektroschock!", nil, {"Forscher Navuud im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 30 Leerenabschaumkugeln und 30 Sickerschlickkugeln zerstört."}},
[10412] = {"Siegel der Feuerschwingen", nil, {"Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel der Feuerschwingen bringt.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10413] = {"Die Schrecken der Verschmutzung", nil, {"Bringt die Phiole mit Leerenschreckensschlamm zu Forscher Navuud im Wachposten des Protektorakts im Nethersturm."}},
[10414] = {"Einzelne Siegel der Feuerschwingen", nil, {"Anforderungen:"}},
[10415] = {"Mehr Siegel der Feuerschwingen", nil, {"Anforderungen:"}},
[10416] = {"Bündelung der Macht", nil, {"Voren'thal der Seher in Shattrath möchte, dass Ihr einen arkanen Folianten besorgt.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10417] = {"Führt eine Diagnose durch!", nil, {"Beschafft die Diagnoseergebnisse und bringt sie zu Mehrdad im Mittelreichposten."}},
[10418] = {"Die Saboteure ausschalten", nil, {"Mehrdad beim Mittelreichposten möchte, dass Ihr 8 Stachelschuppenkrokilisken tötet."}},
[10419] = {"Arkane Folianten", nil, {"Anforderungen:"}},
[10420] = {"Ein reinigendes Licht", nil, {"Bringt eine Ladung teuflische Waffen zu Ishanah in Shattrath.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
