[10422] = {"Hauptmann Tyralius", nil, {"Fleischtreiber Viridius im Wachposten des Protektorats im Nethersturm möchte, dass Ihr Hauptmann Tyralius findet und befreit."}},
[10423] = {"Zur Sturmsäule", nil, {"Sprecht mit Ghabar in der Sturmsäule im Nethersturm."}},
[10424] = {"Diagnose: kritisch", nil, {"Benutzt das Diagnosegerät in der Nähe des Generators der Biokuppel Sutheron und bringt die Diagnoseergebnisse zu Ghabar in der Sturmsäule zurück."}},
[10425] = {"Ausbruch aus dem Stützpunkt", nil, {"Begleitet den gefangenen Vorposten des Protektorats zurück zum Wachposten des Protektorats im Nethersturm."}},
[10426] = {"Flora der Biokuppel", nil, {"Benutzt den Energiefeldmodulator bei den Farahlonpeitschern. Beobachtet die Ergebnisse und meldet Euch bei Aurine Mondflamm in der Sturmsäule zurück, wenn Ihr 10 Tests durchgeführt habt."}},
[10427] = {"Fauna der Biokuppel", nil, {"Markiert 12 Talbuks für Aurine Mondflamm in der Sturmsäule. Ihr könnt mit dem Talbukmarkierer sowohl Talbukböcke als auch Talbukkühe markieren."}},
[10428] = {"Der vermisste Fischer", nil, {"Sucht Cowlen bei den südöstlichen Docks der Silbermythosinsel vor der Küste der Azurmythosinsel."}},
[10429] = {"Wenn die Natur zu weit geht", nil, {"Aurine Mondflamm in der Sturmsäule möchte, dass Ihr ihr ein titanisches Hydraherz bringt."}},
[10430] = {"Den Prototyp testen", nil, {"Bringt das Testreparaturgerät zu Tashar in der Biokuppel Fernfeld im Nethersturm."}},
[10431] = {"Hilfe von außen", nil, {"Geht zu Tulumans Landeplatz, nördlich von Area 52, und sprecht mit Kaylaan.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10432] = {"Eindeutige Beweise", nil, {"Besorgt Euch 8 Befehle von Kael'thas von den Dämonen bei der Manaschmiede Ara und bringt sie zu Spionagemeister Thalodien in Area 52.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10433] = {"Den Schein wahren", nil, {"Bringt 10 Reißzahnpelze zu Shauly Pore im Mittelreichposten."}},
[10434] = {"Das dynamische Duo", nil, {"Sprecht mit Audi der Nadel auf der Sturmsäule im Nethersturm."}},
[10435] = {"Die Waren zurückholen", nil, {"Audi die Nadel bei der Sturmsäule möchte, dass Ihr 10 Kuppelgeneratorsegmente besorgt."}},
[10436] = {"Freie Bahn!", nil, {"Tashar bei der Biokuppel Fernfeld möchte, dass Ihr 12 Sichelzahnraptoren tötet."}},
[10437] = {"Anleitung zur Zerstörung", nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 8 Fragmente von Dimensius besorgt."}},
[10438] = {"Auf netherischen Schwingen", nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr den Phasenstörer bei der Leerenleitung auf der Manaschmiede Ultris benutzt. Sendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten. Die Entladungen des Phasenstörers werden automatisch das Ziel treffen, solange Ihr nahe genug seid, um ihn zu benutzen.\n\nKehrt zu Professor Dabiri zurück, wenn Ihr erfolgreich wart!"}},
[10439] = {"Dimensius der alles Verschlingende", nil, {"Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr mit Hauptmann Saeed sprecht, seiner Armee zur Manaschmiede Ultris folgt und Dimensius den alles Verschlingenden tötet!"}},
[10440] = {"Erfolg!", nil, {"Kehrt zur Sturmsäule zurück und sprecht mit Ghabar im Nethersturm."}},
[10442] = {"Dem Posten des Cenarius helfen", nil, {"Falknerin Drenna Flusswind möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet."}},
