[10487] = {"Drachenstaub", nil, {"Beschafft Euch 4 Einheiten Siechdrachenstaub."}},
[10488] = {"Die Eigenen schützen", nil, {"Wendet Gor'dreks Salbe an 5 Terrorwölfen der Donnerfürsten an."}},
[10489] = {"Einen uralten Baum fällen", nil, {"Besorgt Starkast Tiefenwurz' Stumpf."}},
[10490] = {"Ruf des Wassers", nil, {"Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel."}},
[10491] = {"Ruf der Luft", nil, {"Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel."}},
[10493] = {"Ein aufrichtiges Angebot", nil, {"Sammelt 15 Giftproben aus Silithus und bringt sie zusammen mit 20 Gold und einem Paar Armschienen des Lichts zu Mokvar in Orgrimmar zurück."}},
[10500] = {"Die Horde braucht Euch!", nil, {"Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar."}},
[10501] = {"Die Allianz braucht Euch!", nil, {"Sprecht mit Feldmarschall Schneefall im Militärviertel von Eisenschmiede."}},
[10502] = {"Die Oger der Blutschläger", nil, {"Tötet 30 Oger der Blutschläger im Außenposten der Blutschläger."}},
[10503] = {"Die Speerspießerbedrohung", nil, {"Tötet 30 Oger der Speerspießer und 10 Raptoren der Speerspießer in der Wehr der Speerspießer und im Grund der Speerspießer."}},
[10504] = {"Die Oger der Speerspießer", nil, {"Tötet 30 Oger der Speerspießer in der Wehr der Speerspießer und dem Grund der Speerspießer."}},
[10505] = {"Die Oger der Blutschläger", nil, {"Tötet 30 Oger der Blutschläger im Außenposten der Blutschläger."}},
[10506] = {"Eine düstere Lage", nil, {"Streut Rinas Schwächungspulver auf 5 Terrorwölfe der Blutschläger im Außenposten der Blutschläger in der Blutschlägerklamm."}},
[10507] = {"Der Wendepunkt", nil, {"Benutzt Socrethars Teleportationsstein beim Invasionspunkt: Oberanführer im Norden der Konstruktionsbasis: Vergessenheit, um Eure Gruppe zu Socrethars Sitz zu bringen. Wenn Ihr dort seid, benutzt Voren'thals Präsenz, um Socrethar zu besiegen.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10508] = {"Ein Geschenk für Voren'thal", nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10509] = {"Für Ruhm bestimmt", nil, {"Bringt Socrethars Teleportationsstein zu Voren'thal dem Seher auf der Sehertreppe in Shattrath.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10510] = {"In die Draenethystmine", nil, {"Sammelt 5 Kristalle der Draenethystmine."}},
[10511] = {"Merkwürdiges Gebräu", nil, {"Sammelt 11 Einheiten von Blutschlägers Hauweggebräu."}},
[10512] = {"Die Speerspießer abfüllen", nil, {"Versucht, mit Blutschlägers Hauweggebräu 5 Oger der Speerspießer betrunken zu machen."}},
[10513] = {"Oronok Herzeleid", nil, {"Sucht Oronok Herzeleid auf dem zerschlagenen Vorsprung nördlich der Zisterne der Echsennarbe."}},
[10514] = {"Ich war schon vieles...", nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Schattenmondknollen aus den Zerschlagenen Weiten besorgt.\n\nBringt Oronoks Eberpfeife zu ihm zurück, wenn Ihr fertig seid."}},
[10515] = {"Eine Lektion gelernt", nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Eier eines gefräßigen Schinders in den zerschlagenen Weiten zerstört."}},
[10516] = {"Die Gefangenschaft der Verteidigerin", nil, {"Holt Verteidigerin Vuuleens Klinge und Verteidigerin Vuuleens Schild zurück."}},
[10517] = {"Eure Zeit ist gekommen, Gorr'Dim...", nil, {"Tötet Gorr'Dim, den Anführer der Oger in der Wehr der Speerspießer."}},
[10518] = {"Das Banner aufstellen", nil, {"Benutzt das Banner der Speerspießer auf dem Nordschlägerturm, um Gurn Grubnosh anzulocken, und beschafft Euch das Banner der Speerspießer sowie Gurn Grubnoshs Helm."}},
[10519] = {"Die Litanei der Verdammnis - Wahrheit und Geschichte", nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr Euch seine Geschichte anhört. Sprecht mit Oronok, um ihm zusagen, dass er mit der Geschichte beginnen kann."}},
[10521] = {"Grom'tor, Sohn von Oronok", nil, {"Sucht Grom'tor, Sohn von Oronok in der Echsennarbe im Schattenmondtal."}},
[10522] = {"Die Litanei der Verdammnis - Grom'tors Angriff", nil, {"Grom'tor, Sohn von Oronok, in der Echsennarbe im Schattenmondtal möchte, dass Ihr den ersten Teil der Litanei der Verdammnis besorgt."}},
[10523] = {"Die Litanei der Verdammnis - Erster Teil", nil, {"Bringt Grom'tors Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10524] = {"Artefakte der Donnerfürsten", nil, {"Beschafft Euch die Trommel der Donnerfürsten, den Pfeil der Donnerfürsten und die Schrifttafel der Donnerfürsten."}},
[10525] = {"Wegweisende Visionen", nil, {"Benutzt das feurige Seelenfragment, um Euren Geist in eine wegweisende Vision zu versetzen, wodurch Ihr den Aufenthaltsort des letzten Artefakts der Donnerfürsten erfahren solltet."}},
[10526] = {"Die Donnerlanze", nil, {"Beschafft Euch die Donnerlanze."}},
[10527] = {"Ar'tor, Sohn von Oronok", nil, {"Findet Ar'tor, Sohn von Oronok, bei der Stätte der Illidari im Schattenmondtal."}},
[10528] = {"Dämonische Kristallgefängnisse", nil, {"Sucht und tötet Schmerzensmeisterin Gabrissa in der Stätte der Illidari und kehrt mit dem kristallenen Schlüssel zum Leichnam von Ar'tor, Sohn von Oronok, zurück."}},
[10531] = {"Die Schlacht um das Arathibecken!", nil, {"Erobert die Mine, das Sägewerk, die Schmiede und den Hof und meldet Euch anschließend bei Feldmarschall Lichtmark in der Zuflucht."}},
[10532] = {"Zerschlagt die Versorgungslinien Arathors", nil, {"Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall."}},
[10535] = {"Die Ressourcen des Arathibeckens", nil, {"Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht."}},
[10537] = {"Lohn'goron, Bogen des Herzeleid", nil, {"Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr Lohn'goron, den Bogen des Herzeleid, von den Dämonen in der Gegend beschafft."}},
[10538] = {"Kochendes Blut", nil, {"Benutzt den Verfluchten Kessel, um das Blut des Blutenden Auges in gekochtes Blut zu verwandeln. Bringt danach 12 Einheiten gekochtes Blut zu Apotheker Albreck im Rückenbrecherposten."}},
[10540] = {"Die Litanei der Verdammnis - Ar'tors Angriff", nil, {"Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr den zweiten Teil der Litanei der Verdammnis von Veneratus dem Vielgesichtigen besorgt.\n\nKreaturen, die vom Geistjäger angegriffen werden oder Schaden erleiden, gewähren Euch keine Beute oder Erfahrung."}},
[10541] = {"Die Litanei der Verdammnis - Zweiter Teil", nil, {"Bringt Ar'tors Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10542] = {"Die ham meine Pfeife und mein Gesöff geklaut!", nil, {"Beschafft Euch T'chalis Wasserpfeife und 10 Einheiten von Blutschlägers Hauweggebräu zurück."}},
[10543] = {"Grimnok und Korgaah, ich komm' Euch holen!", nil, {"Tötet Grimnok Schlachttaufe und Korgaah."}},
[10544] = {"Verflucht seien Eure beiden Klans!", nil, {"Benutzt den heimtückischen mächtigen Fetisch, um 5 Gebäude in der Wehr der Speerspießer und 2 Gebäude im Außenposten der Blutschläger zu verfluchen."}},
[10545] = {"Saufgelage der Speerspießer", nil, {"Macht mit Blutschlägers Hauweggebräu 5 Oger der Speerspießer betrunken."}},
[10546] = {"Borak, Sohn von Oronok", nil, {"Sucht Borak, Sohn von Oronok, in der Nähe der Stätte der Mondfinsternis im Schattenmondtal."}},
[10547] = {"Von Distelköpfen und Eiern...", nil, {"Borak, Sohn von Oronok, bei der Brücke nördlich der Stätte der Mondfinsternis möchte, dass Ihr ein verfaultes Arakkoaei besorgt und es zu Tobias dem Faulfraß in Shattrath, nordwestlich der Wälder von Terokkar, bringt."}},
[10548] = {"Die traurige Wahrheit", nil, {"Eralan in Tristessa möchte, dass Ihr den Schrumpfkopf des Archäologen aus einer der Truhen in Zeb'Tela oder Zeb'Nowa besorgt."}},
[10550] = {"Ein Bündel von Blutdisteln", nil, {"Bringt das Blutdistelbündel zurück zu Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal."}},
[10551] = {"Loyalität gegenüber den Aldor", nil, nil},
[10552] = {"Loyalität gegenüber den Sehern", nil, nil},
[10553] = {"Voren'thal der Seher", nil, {"Sprecht mit Voren'thal dem Seher in der Bibliothek der Seher bei der Sehertreppe in Shattrath.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10554] = {"Ishanah", nil, {"Sprecht mit Ishanah im Schrein des unendlichen Lichts auf der Aldorhöhe in Shattrath.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10555] = {"Fauler Zauber", nil, {"Sammelt 18 ausgezupfte Federn der Lashh'an."}},
[10556] = {"Gekritzel", nil, {"Benutzt die Hand voll Federn im Zauberzirkel der Lashh'an."}},
[10557] = {"Testflug: Das Zephyriumkapazitorium", nil, {"Sprecht mit Rally Zapschnapper und benutzt das Zephyriumkapazitorium."}},
[10558] = {"Respektvoller Ruf in der Ehrenfeste", nil, {"Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Euch einen respektvollen Ruf in der Ehrenfeste verschafft, indem Ihr die Höllenorcs in den Zerschmetterten Hallen der Höllenfeuerzitadelle tötet."}},
[10560] = {"Respektvoller Ruf bei den Sha'tar", nil, {"A'dal in Shattrath möchte, dass Ihr Euch einen respektvollen Ruf bei den Sha'tar verschafft, indem Ihr Kael'thas Streitkräfte in der Festung der Stürme tötet."}},
[10561] = {"Respektvoller Ruf bei den Hütern der Zeit", nil, {"Andormu in den Höhlen der Zeit in Tanaris möchte, dass Ihr Euch einen respektvollen Ruf bei den Hütern der Zeit verdient, indem Ihr weiterhin die Ereignisse im Alten Hügelland und dem Schwarzen Morast in den Höhlen der Zeit verteidigt."}},
[10562] = {"Belagert!", nil, {"Schwadronskommandant Nuainn außerhalb der Wildhammerfeste möchte, dass Ihr 10 angreifende Höllenbestien tötet."}},
[10563] = {"Zur Feste der Legion", nil, {"Begebt Euch zur Feste der Legion und findet das Kommunikationsgerät der Legion. Benutzt die Verkleidung, während Ihr in der Nähe des Kommunikationsgeräts steht, um mehr über die Pläne der Legion herauszufinden. Kehrt danach zu Schwadronskommandant Nuainn in der Wildhammerfeste zurück."}},
[10564] = {"Jagt die Höllenbestien in die Luft!", nil, {"Zündet die Teufelsbombe, während Ihr auf der grünen Glyphe in der Feste der Legion steht. Meldet Euch bei Schwadronskommandant Nuainn in der Wildhammerfeste zurück, wenn Ihr Euren Auftrag erledigt habt."}},
[10565] = {"Die Steine der Vekh'nir", nil, {"Sammelt einen Kristall der Vekh'nir und benutzt ihn beim Zauberzirkel der Vekh'nir, um ihn aufzuladen."}},
[10566] = {"Probieren geht über Studieren", nil, {"Probiert jeden von Dertroks Zauberstäben an einem Ogersklaven der Arakkoa aus. Ihr benötigt einen energieerfüllten Kristall der Vekh'nir für jeden Stab, den Ihr benutzen wollt."}},
[10567] = {"Die Herstellung des Anhängers", nil, {"Sammelt 6 Klauen der Ruuan'ok und benutzt sie bei dem Orakelkreis der Ruuan'ok, um einen Herold des Raben herbeizurufen. Tötet ihn, um den Anhänger des Herolds an Euch zu bringen."}},
[10568] = {"Schrifttafeln von Baa'ri", nil, {"Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr 12 Schrifttafeln von Baa'ri vom Boden und von den Arbeitern der Aschenzungen in den Ruinen von Baa'ri sammelt.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10569] = {"Das Wrack der Sketh'lon", nil, {"Besorgt das Tagebuch des Kommandanten der Sketh'lon - Seite 1, das Tagebuch des Kommandanten der Sketh'lon - Seite 2 und das Tagebuch des Kommandanten der Sketh'lon - Seite 3 von den Arakkoa des Dunklen Rats beim Wrack der Sketh'lon und bringt die Seiten zu Greifenreiter Kieran."}},
[10570] = {"Einen Distelsüchtigen fangen", nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Sturmgrimms Schriftwechsel beschafft."}},
[10571] = {"Oronu der Älteste", nil, {"Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr die Befehle von Akama von Oronu dem Ältesten in den Ruinen von Baa'ri besorgt.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10572] = {"Die Bombe bauen", nil, {"Besorgt Euch einen Teufelshäscherkraftkern und eine Teufelshäscherrüstungsplatte und bringt sie zu Schwadronskommandant Nuainn in der Wildhammerfeste."}},
[10573] = {"Die Todesschmiede", nil, {"Meldet Euch bei Sturmrufer Ewan Wildschwinge im Turm der Todesschmiede."}},
[10574] = {"Die Verderber der Aschenzungen", nil, {"Beschafft die vier Medaillonfragmente von Haalum, Eykenen, Lakaan und Uylaru und kehrt danach zu Anachoret Ceyla beim Altar der Sha'tar im Schattenmondtal zurück.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10575] = {"Der Kerker des Wächters", nil, {"Anachoret Ceyla möchte, dass Ihr den Kerker des Wächters südlich der Ruinen von Baa'ri betretet und Sanoru befragt, um Akamas Aufenthaltsort herauszufinden.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Seher."}},
[10576] = {"Unterwürfigkeit in Schattenmond", nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr 6 Teile der Rüstung der Mondfinsternis besorgt."}},
[10577] = {"Was Illidan will, soll Illidan bekommen...", nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Großkommandant Ruusk in der Stätte der Mondfinsternis Illidans Nachricht überbringt."}},
[10578] = {"Die Litanei der Verdammnis - Boraks Angriff", nil, {"Borak, Sohn von Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr den dritten Teil der Litanei der Verdammnis von Ruul dem Verfinsterer besorgt.\n\nFür diesen Auftrag werdet Ihr eine Gruppe benötigen."}},
[10579] = {"Die Litanei der Verdammnis - Dritter Teil", nil, {"Bringt Boraks Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal."}},
[10580] = {"Wo sind diese verdammten Gnome hin?", nil, {"Sucht im südlichen Teil des Zackengrats im Schergrat nach Hinweisen auf den Verbleib von Toshley und seinen Gnomenfreunden."}},
[10581] = {"Folget den Brotkrumen", nil, {"Folgt der Spur aus Ersatzteilen den Weg hinauf zum Messergrat und sprecht mit Toshley in Toshleys Station im Schergrat."}},
[10582] = {"Diener des Schattenrats", nil, {"Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr 10 Wächter der Todesschmiede und 5 Beschwörer der Todesschmiede tötet."}},
[10583] = {"Flanis' Schicksal", nil, {"Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr die Todesschmiede nach Flanis Flinkschwinge absucht und Flanis' Beutel zurückholt."}},
