[11117] = {"Fangt die wilden Wolpertinger!", nil, {"Goldark Spatzenjäger möchte, dass Ihr 5 betäubte Wolpertinger mit dem Wolpertingernetz einfangt. Er hat auch erwähnt, dass Ihr entweder ordentlich betrunken sein oder eine Syntheholbrille tragen müsst, um die Biester sehen zu können."}},
[11118] = {"Parade der rosa Elekk", nil, {"Reist zu den Biergärten außerhalb von Sturmwind, Darnassus und der Exodar, zappt an jedem Ort drei Elekk und kehrt anschließend zu Goldark Spatzenjäger zurück. Um die rosa Elekk sehen zu können, müsst Ihr betrunken sein oder eine Syntheholbrille tragen."}},
[11119] = {"Angriff auf den Landeplatz von Bash'ir!", nil, {"Sprecht mit dem Äthertechnikerlehrling im Außenposten der Himmelswache im Schergrat."}},
[11120] = {"Parade der rosa Elekk", nil, {"Reist zu den Biergärten außerhalb von Silbermond, Donnerfels und Unterstadt, zappt an jedem Ort drei Elekk und kehrt anschließend zu Glodrak Jägerspatz zurück. Um die rosa Elekk sehen zu können, müsst Ihr betrunken sein oder eine Syntheholbrille tragen."}},
[11121] = {"DAILY TEST QUEST (RAID)", nil, {"Sprecht mit <QUESTGIVER> in <AREA>."}},
[11122] = {"Hin und zurück", nil, {"Holt ein Fässchen von Flynn Feuerbräu in Kharanos und bringt es zu Pol Bernruh zurück. Macht das Ganze dreimal, bevor Euer Widder verschwindet."}},
[11123] = {"Untersuchung der Ruinen", nil, {"Sprecht mit Inspektor Tarem beim Gasthaus \"Zur Süßen Ruh\"."}},
[11124] = {"Untersuchung der Ruinen", nil, {"Untersucht das Gasthaus \"Zur Süßen Ruh\" an der Grenze zwischen dem Brachland und den Düstermarschen."}},
[11126] = {"Verräter unter uns", nil, {"Konfrontiert 5 Aufwiegler der Deserteure und kehrt zu Calia Hastings nahe der Docks in Theramore zurück."}},
[11127] = {"<NYI>Thunderbrew Secrets", nil, {"Stehlt Schnapsbarts geheimes Rezept aus Schnapsbarts Lager in Westfall und kehrt zu Gerstenbräu beim Braufestplatz zurück."}},
[11128] = {"Propagandakrieg", nil, {"Bringt das Propagandamaterial der Deserteure zu Calia Hastings nahe der Docks in Theramore."}},
[11129] = {"Kyle wird vermisst!", nil, {"Ahab Weizhuf beim Dorf der Bluthufe in Mulgore möchte, dass Ihr seinen geliebten Hund Kyle den Wilden füttert.\n\nFüttert Kyle mit zartem Schreiterfleisch und kehrt anschließend zu Ahab Weizhuf zurück."}},
[11130] = {"Oh, es glitzert!", nil, {"Gaunah möchte, dass Ihr Gaunahs Notiz zu Budd in den Geisterlanden, direkt außerhalb von Zul'Aman, bringt."}},
[11131] = {"Löscht das Feuer!", nil, {"Die kostümierte Matrone bittet um Eure Hilfe. Sie möchte, dass Ihr alle Feuer im Dorf löscht. Sprecht noch einmal mit der kostümierten Matrone, sobald alle Feuer erloschen sind."}},
[11132] = {"Versprechen, Versprechen...", nil, {"Budd Winterhäldler in den Axthügeln möchte, dass Ihr bei Hohepriester Nalorakks Terrasse in Zul'Aman nach seiner Karte sucht."}},
[11133] = {"Diskreditierung der Deserteure", nil, {"Calia Hastings möchte, dass Ihr verfälschte Handzettel an 6 Wachen von Theramore verteilt. Erstattet ihr anschließend Bericht nahe der Docks in Theramore."}},
[11134] = {"Das Ende der Deserteure", nil, {"Nehmt Gavis Grauschild gefangen und erstattet anschließend Bericht bei Calia Hastings nahe der Docks in Theramore."}},
[11137] = {"Defias in den Düstermarschen?", nil, {"Besorgt Euch die Befehle der Defias von Garn Mathers und bringt sie zu Hauptmann Wymor bei der Späherwacht."}},
[11138] = {"Renn McGill", nil, {"Bringt die Befehle der Defias zu Renn McGill in den Gewässern nahe der Schreckensmoorküste."}},
[11142] = {"Beobachtet die Insel Alcaz", nil, {"Sprecht mit Cassa Purpurschwinge und nutzt einen ihrer Greifen, um die Insel Alcaz zu beobachten. Sprecht mit Lady Jaina Prachtmeer in Theramore, wenn Ihr die Beobachtung abgeschlossen habt."}},
[11143] = {"Der entscheidende Grimmweis", nil, {"Erstattet Hauptmann Darill bei der Nordwacht Bericht."}},
[11146] = {"Raptorenfänger", nil, {"Unteroffizier Lukas bei der Nordwacht möchte, dass Ihr 6 Raptoren einfangt. Ihr könnt entweder Blutsumpfraptoren oder Blutsumpfkreischer einfangen."}},
[11152] = {"[DEPRECATED] Endlich Frieden", nil, {"Hauptmann Garran Mumm bei der Wehrzitadelle möchte, dass Ihr den Kranz bei dem Monument der Familie Hyal niederlegt."}},
[11153] = {"Durchbrecht die Blockade", nil, {"Nehmt Petrovs Splitterbomben und werft sie auf die Piraten. Beseitigt 25 Piraten der Blockade und 10 Kanonen der Blockade. Kehrt zu Bombenschütze Petrov in Westwacht zurück, sobald Ihr den Auftrag erfüllt habt."}},
[11154] = {"Jagt ihnen eine Guanoangst ein!", nil, {"Sammelt 10 Stück Dunkelklauenguano ein und bringt sie zu Ingenieur Feknut am Stählernen Tor."}},
[11155] = {"Schon wieder Schaufelhauersuppe?", nil, {"Küchenchefin Schwarzkessel hat Euch gebeten, ihr 6 Schaufelhauerfleisch zu beschaffen. Sie befindet sich in Westwacht."}},
[11156] = {"[DEPRECATED] Räuber des Grollhornpostens", nil, {"Tötet 12 Grimmtotem des Grollhornpostens und kehrt zu Lehrling Morlann auf Tabethas Hof zurück."}},
[11157] = {"Das Gelege des Bösen", nil, {"Zerstört 15 Eier der Protodrachen und 15 Protowelpen. Kehrt danach zu Hauptmann Adams in Westwacht zurück."}},
[11158] = {"Blutsumpffedern", nil, {"Bringt 5 Blutsumpffedern zu Brogg in Morastwinkel."}},
[11159] = {"Geister der Festung der Steinbrecher", nil, {"Brogg in Morastwinkel möchte, dass Ihr 10 Geister der Steinbrecher tötet, um sie so ihrer letzten Ruhe zu überantworten."}},
[11160] = {"Das Banner der Steinbrecher", nil, {"Beschafft das Banner der Steinbrecher und bringt es Brogg in Morastwinkel."}},
[11161] = {"Die Essenz der Feindschaft", nil, {"Bringt 10 Essenzen eines schwarzen Drachkin zu Brogg in Morastwinkel."}},
[11162] = {"Herausforderung für den schwarzen Drachenschwarm", nil, {"Stellt das Banner der Steinbrecher nahe des Eingangs zu Onyxias Hort auf, wartet auf ihre Reaktion und kehrt anschließend zu Brogg in Morastwinkel zurück."}},
[11164] = {"Jäger der verlorenen Hauer", nil, {"Prigmon möchte, dass Ihr 10 Hauer eines Waldtrolls von Trollen in Zul'Aman einsammelt. Bringt sie zu ihm bei Budds Lager in den Geisterlanden."}},
[11165] = {"Ein Troll unter Trollen", nil, {"Prigmon möchte, dass Ihr seinen Vetter Zungam in Zul'Aman ausfindig macht und ihm zur Seite steht."}},
[11166] = {"Das X markiert Euren Untergang!", nil, {"Betretet Zul'Aman und erkundet Halazzis Kammer, Jan'alais Plattform und Akil'zons Plattform. Berichtet Budd bei seinem Lager in den Geisterlanden von den Details dieser Bereiche."}},
[11167] = {"Die neue Seuche", nil, {"Apotheker Lysander im Hafen der Vergeltung will, dass Ihr 10 unversehrte Behälter aus dem Schiffswrack im Südosten bergt."}},
[11168] = {"Die Mischung verstärken", nil, {"Bringt Apotheker Lysander im Hafen der Vergeltung im Heulenden Fjord 3 riesige Giftdrüsen von riesigen Gezeitenkrabblern."}},
[11169] = {"Die Waffe der Grimmtotem", nil, {"Lehrling Garion bei Tabethas Hof möchte, dass Ihr das erbeutete Totem an 10 scheckigen Staubsuhlerkrokilisken oder Staubsuhlerdolchrachen ausprobiert."}},
[11170] = {"Test auf See", nil, {"Sprecht mit Fledermausführerin Kamilla, nehmt eine Reitfledermaus und fangt die Verstärkungstruppen der Allianz ab. Werft die Seuchenphiolen auf 16 Reservisten der Nordflotte, sobald Ihr Euch in Reichweite befindet."}},
[11171] = {"Hexlord? Pah!", nil, {"Budd Winterhäldler in den Axthügeln möchte, dass Ihr Hexlord Malacrass in Zul'Aman tötet."}},
[11172] = {"Der Zeppelinabsturz", nil, {"Sprecht mit Moxie Stahlgrill bei Beezils Wrack."}},
[11173] = {"[DEPRECATED] Der Reagenziendieb", nil, {"Bringt 6 Phiolen Marschengift zu Lehrling Garion bei Tabethas Hof."}},
[11174] = {"Korrosionsschutz", nil, {"Moxie Stahlgrill bei Beezils Wrack möchte, dass Ihr 10 Schlamme auflöst."}},
[11175] = {"Meine Tochter", nil, {"Der alte Zasterstein hat Euch gebeten, seine Tochter, die Vorarbeiterin Irena Zasterstein, zu finden. Sie sollte sich am Stählernen Tor befinden."}},
[11176] = {"An die Arbeit", nil, {"Forscherin Irena Zasterstein hat Euch gebeten, mit einem der Chefarchäologen des Stählernen Tores zu sprechen. Diese befinden sich am Stählernen Tor."}},
[11177] = {"Der Einsiedler beim Irrlichtanwesen", nil, {"Sprecht mit \"Sumpfauge\" Jarl beim Irrlichtanwesen."}},
[11178] = {"Blut des Kriegsherren", nil, {"Bringt Zul'jins Blut zu Budd bei seinem Lager in den Geisterlanden, direkt außerhalb von Zul'Aman."}},
[11179] = {"Finlay ist darmlos", nil, {"Sammelt 7 Stück makellosen Schattensäblerkatzendarm und kehrt zu Finlay Pfeilmacher zurück. Er befindet sich in Westwacht."}},
[11180] = {"Was sucht den Hexenhügel heim?", nil, {"Mordant Grimsby beim Irrlichtanwesen möchte, dass Ihr Informationen von 10 auferstandenen Gebeinen oder auferstandenen Geistern sammelt."}},
[11182] = {"Ursachen", nil, {"Tötet 5 Tierführer der Drachenschinder und Skeld Drachensohn. Geht in die Glutstätte und sucht ein Urtum der Glutstätte auf, falls Ihr das schaffen solltet."}},
[11183] = {"Die Reinigung des Hexenhügels", nil, {"Mordant Grimsby beim Irrlichtanwesen möchte, dass Ihr die Fackel am Ende des Docks aufstellt und alles bezwingt, was daraufhin erscheint."}},
[11184] = {"GESUCHT: Schlächterklaue der Gefräßige", nil, {"Tötet Schlächterklaue den Gefräßigen und meldet Euch anschließend bei Drazzit Tropfhahn in Morastwinkel."}},
[11187] = {"Magierleutnant Malister", nil, {"Hauptmann Adams möchte, dass Ihr mit seinem Berater Magierleutnant Malister sprecht. Er befindet sich in Westwacht."}},
[11188] = {"Zwei Untaten...", nil, {"Magierleutnant Malister in Westwacht hat Euch damit beauftragt, 3 Protodrachen zu vernichten."}},
[11190] = {"Nicht allen passt dieselbe Größe", nil, {"Kanonier Ely hat Euch damit beauftragt, 10 Kanonenkugeln der Westwacht einzusammeln und sie zu ihm zurückzubringen. Er befindet sich in Westwacht."}},
[11191] = {"Der alte Leuchtturm", nil, {"Sprecht mit Babs Zischeldrill bei dem Leuchtturm auf der Insel östlich von Theramore."}},
[11192] = {"[DEPRECATED] Drescheröl", nil, {"Bringt 4 Behälter mit Drescheröl zu Babs Zischeldrill beim Leuchtturm in der Nähe von Theramore."}},
[11194] = {"Ist das wahr?", nil, {"Sprecht mit Nat Pagle bei Nats Angelplatz in den Düstermarschen."}},
[11195] = {"Das Spiel mit der Puppe", nil, {"Bringt die zerlumpte Voodoopuppe zu Gaunah in Shattrath."}},
[11198] = {"[DEPRECATED] Bezwingt Tethyr!", nil, {"Major Mühlens bei den Docks von Theramore möchte, dass Ihr Tethyr bezwingt."}},
[11199] = {"Meldung bei Kundschafter Wisslich", nil, {"Hauptmann Adams möchte, dass Ihr Euch bei Kundschafter Wisslich am Glutspeerturm meldet."}},
[11202] = {"Mission 'Ewige Flamme'", nil, {"Kundschafter Wisslich im Glutspeerturm hat vorgeschlagen, dass Ihr mithilfe der Immerbrandfackeln die 4 Pestkessel um Halgrind herum zerstört."}},
[11205] = {"Brennt den Grollhornposten nieder!", nil, {"Brennt das nördliche, östliche und nordöstliche Zelt beim Grollhornposten nieder und kehrt anschließend zu Tabetha auf ihrem Hof zurück."}},
[11206] = {"Das Walten der Gerechtigkeit", nil, {"Kehrt zu Krog in Brackenwall zurück."}},
[11207] = {"Bergt die Fracht!", nil, {"Moxie Stahlgrill bei Beezils Wrack möchte, dass Ihr 8 Teile der Zeppelinfracht bergt."}},
[11208] = {"Lieferung für Drazzit", nil, {"Bringt die Zeppelinfracht zu Drazzit Tropfhahn in Morastwinkel."}},
[11209] = {"Nats Abmachung", nil, {"Nat Pagle bei Nats Angelplatz möchte, dass Ihr seinen neuen Köder ausprobiert."}},
[11210] = {"Oh, es ist wahr!", nil, {"Sprecht mit Major Mühlens bei den Docks von Theramore."}},
[11211] = {"Hilfe für Morastwinkel", nil, {"Sprecht mit Drazzit Tropfhahn in Morastwinkel."}},
[11212] = {"Tabethas Hof", nil, {"Sprecht mit Tabetha auf ihrem Hof im Morast."}},
[11213] = {"Seht nach Tabetha", nil, {"Sprecht mit Tabetha auf ihrem Hof im Morast."}},
[11214] = {"Mission nach Morastwinkel", nil, {"Sprecht mit Drazzit Tropfhahn in Morastwinkel."}},
[11215] = {"Helft Morastwinkel", nil, {"Sprecht mit Drazzit Tropfhahn in Morastwinkel."}},
[11216] = {"Erzmagier Alturus", nil, {"Sprecht mit Erzmagier Alturus außerhalb von Karazhan im Gebirgspass der Totenwinde."}},
[11217] = {"Entwindet den Drachen", nil, {"Gizzix Rußgurgel in Morastwinkel möchte, dass Ihr ihm 8 Drachenwinden bringt."}},
[11218] = {"Warnung! Sprengstoff!", nil, {"Verwendet Stahlrings idiotensicheres Dynamit, um 6 Erzfragmente aus der Flüsterschlucht und 18 Edelsteine aus der Flüsterschlucht zu beschaffen. Bringt die Gegenstände anschließend zu Pionier Stahlring in Westwacht zurück."}},
[11219] = {"Löscht das Feuer!", nil, {"Die maskierte Matrone bittet um Eure Hilfe. Sie möchte, dass Ihr alle Feuer im Dorf löscht. Sprecht noch einmal mit der kostümierten Matrone, sobald alle Feuer erloschen sind."}},
[11220] = {"Der kopflose Reiter", nil, {"Sucht nach dem Kürbisschrein des kopflosen Reiters."}},
[11221] = {"Berichte vom Schlachtfeld", nil, {"Sprecht mit Dunkelläuferin Lyana und Todespirscher Razael. Sie befinden sich auf dem Schlachtfeld in der Blutklamm südlich vom Hafen der Vergeltung. Kehrt zu Hochexekutor Anselm zurück, sobald Ihr die Aufgabe abgeschlossen habt."}},
[11223] = {"Rückkehr zu Jaina", nil, {"Sprecht mit Lady Jaina Prachtmeer in Theramore."}},
[11224] = {"Schickt die Packmulis packen", nil, {"Forscherin Abigail möchte, dass Ihr Euch in die Nähe von 10 verlassenen Packmulis begebt, um jedes Mal Eure Hand zu /heben. Kehrt zu ihr zurück sobald Ihr damit fertig seid. Sie befindet sich in der Westwacht."}},
[11225] = {"Der Einsiedler des Hexenhügels", nil, {"Sprecht mit \"Sumpfauge\" Jarl beim Irrlichtanwesen."}},
[11227] = {"Lasst sie Krähen fressen", nil, {"Pontius, der Hüter des Zwingers im Hafen der Vergeltung, möchte, dass Ihr mithilfe des Seuchenhundkäfigs einen hungrigen Seuchenhund herbeiruft und diesen dann mit dem Krähenfleisch der Fjordkrähen füttert. Letztere befinden sich westlich des Hafens der Vergeltung."}},
[11228] = {"Eine Hölle aus Eis...", nil, {"Sprecht mit Vizeadmiral Keller im Hafen von Valgarde."}},
[11229] = {"Die Windläuferflotte", nil, {"Sprecht mit Fledermausführerin Kamilla im Hafen der Vergeltung und fragt nach einer Möglichkeit, zur Windläufer hindurchzukommen. Meldet Euch dann bei Kapitän Harker an Bord des Schiffs."}},
[11230] = {"Hinterhalt!", nil, {"Kapitän Harker an Bord der Windläufer möchte, dass Ihr 15 Marinesoldaten der Nordflotte tötet."}},
[11231] = {"Von Schlüsseln und Käfigen", nil, {"Vater Levariol möchte, dass Ihr genug Schlüssel für einen Gjalerbronkäfig sammelt, um 10 Gefangene von Gjalerbron zu befreien. Kehrt zu ihm zurück, sobald Ihr damit fertig seid. Er befindet sich in Westwacht."}},
[11232] = {"Lenkt unseren Blick", nil, {"Markiert mit den Rauchsignalen die Stellen entlang der Nordwand am Verlassenen Strand, an denen sich die Kanonen der Allianz befinden. Meldet Euch bei Dunkelläufer Lyana in der Blutklamm, sobald Ihr die Aufgabe abgeschlossen habt."}},
[11233] = {"Den Todesstoß landen", nil, {"Dunkelläuferin Lyana südlich des Hafens der Vergeltung möchte, dass Ihr Hauptmann Olster, Leutnant Celeyne und Unteroffizier Lorric tötet. Ihr findet sie am Verlassenen Strand."}},
[11234] = {"Meldung an Anselm", nil, {"Erstattet Hochexekutor Anselm Bericht. Er befindet sich im Hafen der Vergeltung im Heulenden Fjord."}},
[11235] = {"Mit Gjalerbron fertig werden", nil, {"Hauptmann Adams möchte, dass Ihr 15 Krieger von Gjalerbron, 8 Runenmagier von Gjalerbron und 8 Schlafwächter von Gjalerbron tötet. Kehrt danach zu ihm zurück. Ihr findet ihn in Westwacht."}},
[11236] = {"Nekrogroßfürst Mezhen", nil, {"Tötet Nekrogroßfürst Mezhen. Kehrt dann zu Hauptmann Adams zurück. Ihr findet ihn in Westwacht."}},
[11237] = {"Angriffspläne von Gjalerbron", nil, {"Übergebt die Angriffspläne von Gjalerbron an Magierleutnant Malister. Er befindet sich in Westwacht."}},
[11238] = {"Der Frostwyrm und sein Meister", nil, {"Erschlagt Wyrmrufer Elend und benutzt das Wyrmruferhorn, um Glacion herbeizurufen und ihn niederzustrecken. Kehrt anschließend gemeinsam mit dem Horn zu Magierleutnant Malister zurück. Er befindet sich in Westwacht."}},
[11239] = {"Im Dienste des Lichts", nil, {"Erschlagt 10 ewige Beobachter, 4 furchterregende Schrecken und 2 eitrige Kreaturen. Informiert danach Vater Levariol von Eurem Erfolg. Er befindet sich in Westwacht."}},
[11240] = {"Anführer der Verwirrten", nil, {"Erschlagt Squeeg Götzenjäger und meldet Euch dann bei Pionier Stahlring in Westwacht."}},
[11241] = {"Flammenspur", nil, {"Eskortiert Apotheker Hanes vom Verlassenen Strand. Meldet Euch bei Apotheker Lysander, sobald Ihr diese Aufgabe abgeschlossen habt. Er befindet sich im Hafen der Vergeltung im Heulenden Fjord."}},
[11242] = {"Endlich frei!", nil, {"Bringt den Folianten von Thomas Thomson zu einer maskierten Waisenmatrone in Goldhain, Kharanos oder bei der Azurwacht."}},
[11243] = {"Falls Valgarde fällt...", nil, {"Vizeadmiral Keller möchte, dass Ihr 12 Eindringlinge der Drachenschinder oder Worge der Drachenschinder tötet. Er befindet sich im Hafen von Valgarde im Heulenden Fjord."}},
[11244] = {"Die Retter retten", nil, {"Vizeadmiral Keller möchte, dass Ihr 8 Späher von Valgarde rettet, die mit Harpunen beschossen und zum Sterben liegen gelassen wurden. Er befindet sich im Hafen von Valgarde im Heulenden Fjord."}},
[11245] = {"Türme des sicheren Untergangs", nil, {"Der Unteroffizier der Westwacht meint, dass es sinnvoll wäre, wenn man mit dem Leuchtsignal des Unteroffiziers die Türme in Skorn markiert, damit sie anschließend unter Kanonenbeschuss genommen werden können."}},
[11246] = {"Grausam, aber notwendig", nil, {"Der Unteroffizier der Westwacht möchte, dass Ihr nach Skorn geht und mit der Machete des Unteroffiziers 20 Vrykul des Winterskornstammes zerstückelt."}},
[11247] = {"Brenne, Skorn - brenne!", nil, {"Der Unteroffizier der Westwacht hat Befehle erhalten, nach denen Ihr mit der Fackel des Unteroffiziers die beiden Langhäuser und die Kaserne in Skorn in Brand stecken sollt."}},
[11248] = {"Operation: Skorns Zorn", nil, {"Hauptmann Adams möchte, dass Ihr die Befehlsinsignie der Westwacht in Skorn benutzt und anschließend mit dem Unteroffizier der Westwacht sprecht."}},
[11249] = {"Verhindert seinen Aufstieg!", nil, {"Präsentiert die Vrykulrolle des Aufstiegs in der Nähe des Scheiterhaufens vor der Hütte des Thans, um ihn herauszurufen und ihn niederzustrecken. Ihr seid Euch sicher, dass Vater Levariol an dem Ritual und damit auch an der Schriftrolle Interesse finden wird. Er befindet sich in Westwacht."}},
[11250] = {"Hoch lebe der Eroberer von Skorn!", nil, {"Bringt die Befehlsinsigne der Westwacht zu Hauptmann Adams zurück. Ihr findet ihn in Westwacht."}},
[11251] = {"Ein neuer Abschnitt", nil, {"Kundschafterin Valory möchte, dass Ihr den Geräuschen nachgeht, die vom nordöstlichen Teil von Wyrmskol herstammen."}},
[11252] = {"Nach Utgarde!", nil, {"Verteidiger Mordun hat Euch mit der Exekution von Ingvar dem Brandschatzer, der tief in Utgarde wohnt, beauftragt.\n\nAnschließend sollt Ihr dessen Kopf zu Vizeadmiral Keller bringen."}},
[11253] = {"Den Feind ausschnüffeln", nil, {"Benutzt die Seuchenhundleine in der Nähe der Seuchenhundkäfige im Hafen der Vergeltung und sucht dort nach Hinweisen, wo der Seuchenhundfährtenleser Euch hinführt."}},
[11254] = {"Die Karte aus Drachenhaut", nil, {"Bringt die Karte aus Drachenhaut zu Hochexekutor Anselm im Hafen der Vergeltung."}},
[11255] = {"Gefangene in Wyrmskol", nil, {"Vizeadmiral Keller im Hafen von Valgarde im Heulenden Fjord möchte, dass Ihr 3 Gefangene aus Valgarde befreit."}},
[11256] = {"Skorn muss fallen!", nil, {"Häuptling Aschtotem möchte, dass Ihr das Emblem der Winterhufe in Skorn benutzt und dann mit dem Helden der Winterhufe sprecht."}},
[11257] = {"Grausam, aber notwendig", nil, {"Der Held der Winterhufe möchte, dass Ihr mit der Machete des Kriegerhelden 20 Vrykul des Winterskornstammes in Skorn zerstückelt."}},
[11258] = {"Brenne, Skorn - brenne!", nil, {"Der Held der Winterhufe schlägt vor, dass Ihr mit der Fackel des Helden die zwei Langhäuser im Nordosten und -westen und die Baracken in Skorn in Brand setzt."}},
[11259] = {"Türme des sicheren Untergangs", nil, {"Der Held der Winterhufe meint, dass es sinnvoll wäre, wenn man mit dem Leuchtsignal des Kriegerhelden die Türme in Skorn für Luftangriffe markiert."}},
[11260] = {"Verhindert seinen Aufstieg!", nil, {"Präsentiert die Vrykulrolle des Aufstiegs in der Nähe des Scheiterhaufens vor der Hütte des Thans, um ihn herauszurufen und ihn niederzustrecken. Ihr seid Euch sicher, dass Großmutter Ankha an dem Ritual und damit auch an der Schriftrolle Interesse finden wird. Sie befindet sich im Lager der Winterhufe."}},
[11261] = {"Der Bezwinger von Skorn!", nil, {"Bringt das Zeichen der Winterhufe zu Häuptling Aschtotem im Lager der Winterhufe zurück."}},
[11262] = {"Ingvar muss sterben!", nil, {"Dunkelläuferin Marrah möchte, dass Ihr Ingvar den Brandschatzer in Burg Utgarde tötet."}},
[11263] = {"Mit Gjalerbron fertig werden", nil, {"Häuptling Aschtotem hat Euch gebeten, 15 Krieger von Gjalerbron, 8 Runenmagier von Gjalerbron und 8 Schlafwächter von Gjalerbron zu töten. Kehrt im Anschluss zu ihm im Lager der Winterhufe zurück."}},
[11264] = {"Nekrogroßfürst Mezhen", nil, {"Tötet Nekrogroßfürst Mezhen. Kehrt danach zu Häuptling Aschtotem im Lager der Winterhuf zurück."}},
[11265] = {"Von Schlüsseln und Käfigen", nil, {"Großmutter Ankha möchte, dass Ihr genug Schlüssel für einen Gjalerbronkäfig sammelt, um 10 Gefangene von Gjalerbron zu befreien. Kehrt zu ihr zurück, sobald Ihr damit fertig seid. Sie befindet sich im Lager der Winterhufe."}},
[11266] = {"Angriffspläne von Gjalerbron", nil, {"Übergebt die Angriffspläne von Gjalerbron an Celea Frostmähne. Sie befindet sich im Lager der Winterhufe."}},
[11267] = {"Der Frostwyrm und sein Meister", nil, {"Erschlagt Wyrmrufer Elend und benutzt das Wyrmruferhorn, um Glacion herbeizurufen und ihn niederzustrecken. Kehrt anschließend gemeinsam mit dem Horn zu Celea Frostmähne zurück. Sie befindet sich im Lager der Winterhufe."}},
[11268] = {"Die wandelnden Toten", nil, {"Tötet 10 ewige Beobachter, 4 furchterregende Schrecken und 2 eitrige Kreaturen. Bringt anschließend Nachricht Eures Erfolgs zu Großmutter Ankha im Lager der Winterhufe."}},
[11269] = {"Daunen in letzter Minute", nil, {"Gil Grisert möchte, dass Ihr ihm 10 Büschel Daunen eines gefleckten Hippogryphen bringt."}},
[11270] = {"Krieg ist die Hölle", nil, {"Hochexekutor Anselm im Hafen der Vergeltung möchte, dass Ihr mithilfe der brennenden Fackel 10 der Leichen, die westlich des Orts herumliegen, verbrennt."}},
[11271] = {"Hastige Vorbereitungen", nil, {"Ahota Weißfrost im Lager der Winterhufe möchte, dass Ihr ihm 10 Büschel Daunen eines gefleckten Hippogryphen bringt."}},
[11272] = {"Eine Rechnung begleichen", nil, {"Hochexekutor Anselm möchte, dass Ihr Prinz Keleseth in Burg Utgarde tötet."}},
[11273] = {"Die Menschenliga", nil, {"Sucht Wyrmskol nach Pulroy ab."}},
[11274] = {"Zedd ist wahrscheinlich tot", nil, {"Sucht Wyrmskol nach Zedd ab."}},
[11275] = {"Das Horn schnitzen", nil, {"Nokoma Schneeseher im Lager der Winterhufe möchte, dass Ihr ihr 6 unbeschädigte Widderhörner bringt."}},
[11276] = {"Und da waren es Zwei...", nil, {"Sucht in den Katakomben von Utgarde nach Glorenfeld."}},
[11277] = {"Die Tiefen des Anrüchigen", nil, {"Glorenfeld in den Katakomben von Utgarde möchte, dass Ihr 10 Wyrmskoltafeln einsammelt."}},
[11278] = {"Rückkehr nach Valgarde", nil, {"Bringt Glorenfelds Päckchen zu Beltrand McSorf im Hafen von Valgarde."}},
[11279] = {"Grüne Eier und Welpen", nil, {"Seuchenbringer Tillinghast möchte, dass Ihr 10 Exemplare verseuchter Protowelpen einsammelt und sie anschließend bei ihm abliefert. Er befindet sich in Neu-Agamand."}},
[11280] = {"Draconis Gastritis", nil, {"Seuchenbringer Tillinghast in Neu-Agamand möchte, dass Ihr mit Tillinghasts verseuchtem Fleisch einen Protodrachen anlockt und verfolgt, wie er auf das gefressene Zeug reagiert."}},
[11281] = {"Den Ruf der Natur nachahmen", nil, {"Testet das geschnitzte Horn und kehrt anschließend mit den Ergebnissen zu Nokoma Schneeseher im Lager der Winterhufe zurück."}},
[11282] = {"Eine Lektion in Sachen Angst", nil, {"Gorth möchte, dass Ihr Ulf den Aderlasser, Oric den Elenden und Gunnar Thorvardsson tötet und das Banner der Verlassenen durch ihre Körper rammt. Erschlagt solange Vrykul auf der anderen Seite der Blockade der Verlassenen, bis die Drei auftauchen."}},
[11283] = {"Zahl der Toten in Quälheim", nil, {"Unteroffizier Gorth möchte, dass Ihr nach Quälheim geht und 16 Vrykul des Winterskornstammes tötet."}},
[11284] = {"Der Yeti von nebenan", nil, {"Vorarbeiter Colbey bei Fort Wildervar möchte, dass Ihr Schmetterhorn besiegt."}},
[11285] = {"Quälheim muss brennen!", nil, {"Unteroffizier Gorth will, dass Ihr nach Quälheim geht und Gorths Fackel benutzt, um 1 Behausung der Winterskorn, 1 Wachturm der Winterskorn, 1 Brücke der Winterskorn und 1 Kaserne der Winterskorn in Brand zu stecken."}},
[11286] = {"Die Artefakte des Stählernen Tores", nil, {"Weiser Nebelläufer möchte, dass Ihr 10 Artefakte vom Stählernen Tor aufsammelt. Kehrt zu ihm zurück, sobald Ihr alle habt. Er befindet sich am Stählernen Tor."}},
[11287] = {"Findet den Weisen Nebelläufer", nil, {"Großmutter Ankha hat Euch gebeten nachzusehen, in welcher Verfassung sich der Weise Nebelläufer befindet. Sie glaubt, dass er sich am oder in der Nähe des Stählernen Tores befindet."}},
[11288] = {"Das strahlende Licht", nil, {"Ares der Schwurgebundene in den Katakomben von Utgarde möchte, dass Ihr das geheiligte Artefakt bergt.\n\nDiese Aufgabe ist zeitlich begrenzt."}},
[11289] = {"Von Ehre geleitet", nil, {"Bringt das geheiligte Artefakt zu Lord Irulon Wahrklinge zurück. Er befindet sich in Valgarde."}},
[11290] = {"Schlachtpläne der Drachenschinder", nil, {"Vizeadmiral Keller im Hafen von Valgarde im Heulenden Fjord möchte, dass Ihr ihm die Schlachtpläne der Drachenschinder bringt."}},
[11291] = {"Auf nach Westwacht!", nil, {"Bringt die Schlachtpläne der Drachenschinder zu Hauptmann Adams. Er befindet sich in Westwacht im Heulenden Fjord."}},
[11292] = {"Auf die Schwachen Jagd machen", nil, {"Fallensteller Jethan in Fort Wildervar möchte, dass Ihr ihm aus 8 Fallen gefangene Beute bringt."}},
[11295] = {"Die Offensive beginnt", nil, {"Sprecht mit Unteroffizier Gorth bei der Blockade in der Nähe des Aufzugs westlich vom Hafen der Vergeltung im Heulenden Fjord."}},
[11296] = {"Gefangen im Bruchwald", nil, {"Fernläufer Himmelswolke möchte, dass Ihr 7 seiner Fernläufer der Winterhufe befreit. Meldet Euch wieder bei ihm, sobald Ihr Eure Aufgabe erledigt habt. Er befindet sich am Stählernen Tor."}},
[11297] = {"Nach Eindringlingen Ausschau halten", nil, {"Sprecht mit Apotheker Malthus im Apothekerlager."}},
[11298] = {"Was ist in dem Gebräu?", nil, {"Apotheker Malthus im Apothekerlager möchte, dass Ihr ihm 5 zwergische Fässchen bringt."}},
[11299] = {"Der Ring des Richturteils", nil, {"Findet Daegarn. Sucht im Ring des Richturteils im Inneren der Katakomben von Utgarde."}},
[11300] = {"Überwältigende Niederlage im Ring", nil, {"Besiegt die Gladiatoren im Ring des Richturteils und stellt Euch Oluf dem Gewalttätigen. Besiegt Oluf und nehmt ihm die uralte Glyphe ab.\n\nBringt die uralte Glyphe zu Glorenfeld am Eingang zu den Katakomben von Utgarde."}},
[11301] = {"Gehirne! Wir wollen Gehirne!", nil, {"Apotheker Grick im Apothekerlager möchte, dass Ihr ihm 12 verwirrte Forscherhirne bringt."}},
[11302] = {"Die geheimnisvollen Frostnymphen", nil, {"Sprecht mit Lurielle auf der Gefrorenen Lichtung."}},
[11303] = {"Der Hinterhalt", nil, {"Geht zu Lydells Hinterhalt und sprecht mit Lydell."}},
[11304] = {"Neu-Agamand", nil, {"Bringt Lysanders Erregerstamm zum obersten Seuchenfürsten Harris. Er befindet sich in Neu-Agamand im Westen des Heulenden Fjords"}},
[11305] = {"Eine maßgeschneiderte Formel", nil, {"Geht zu Thorvalds Lager, erschlagt Thorvald, nehmt das Blut des Patriarchen der Drachenschinder an Euch und bringt es zum obersten Seuchenfürsten Harris in Neu-Agamand."}},
[11306] = {"Aufkochen und umrühren", nil, {"Füllt die leere Apothekerflasche im Kessel mit Vrykulblut auf und vermischt ihren Inhalt so lange mit Harris' Seuchenproben, die sich in der Nähe befinden, bis Ihr eine ausgewogene Mixtur erhaltet.\n\nSprecht wieder mit dem obersten Seuchenfürsten Harris, sobald Ihr die Aufgabe erledigt habt."}},
[11307] = {"Feldversuch", nil, {"Geht nach Halgrind und wendet das Seuchenspray auf 10 Verseuchte Vrykul der Drachenschinder an. Kehrt anschließend zum obersten Seuchenfürsten Harris zurück und erstattet ihm über die Ergebnisse Bericht."}},
[11308] = {"Zeit aufzuräumen", nil, {"Sprecht mit \"Eisensäge\" Jenny in Neu-Agamand."}},
[11309] = {"Benötigte Teile", nil, {"\"Eisensäge\" Jenny in Neu-Agamand möchte, dass Ihr 6 Schaufelhauerbänder und 1 Pfund frisches Fleisch besorgt."}},
[11310] = {"Warnung: Ein wenig Montage benötigt", nil, {"Benutzt den Monstrositätenbausatz in Halgrind und tötet mithilfe der hirnlosen Monstrosität 20 verseuchte Vrykul. Kehrt nach Neu-Agamand zu \"Eisensäge\" Jenny zurück, sobald Ihr fertig seid."}},
[11311] = {"Die Elemente bekämpfen", nil, {"Windzähmer Kagan im Lager der Winterhufe möchte, dass Ihr 8 Bergelementare tötet. Zu töten sind entweder heulende Zyklone oder Eissplitterelementare."}},
[11312] = {"Die Gefrorene Lichtung", nil, {"Sprecht mit Lurielle in der Gefrorenen Lichtung."}},
[11313] = {"Geister des Eises", nil, {"Lurielle auf der Gefrorenen Lichtung möchte, dass Ihr ihr 15 eisige Kerne bringt."}},
[11314] = {"Die gefallenen Schwestern", nil, {"Lurielle auf der Gefrorenen Lichtung möchte, dass Ihr ihren Anhänger auf 7 Eisnymphen verwendet."}},
[11315] = {"Wilde Ranken", nil, {"Lurielle auf der Gefrorenen Lichtung möchte, dass Ihr 8 Exemplare des scharlachroten Efeus tötet."}},
[11316] = {"Geflechte der Siechenden Lichtung", nil, {"Lurielle auf der Gefrorenen Lichtung möchte, dass Ihr 10 Dornrankenkriecher tötet."}},
[11317] = {"Die Läuterung", nil, {"Weiser Nebelläufer am Stählernen Tor hat Euch geraten, Euren inneren Aufruhr durch Meditation am Frostklingenschrein zu besänftigen."}},
[11318] = {"Das ist Widderrennen... zumindest fast", nil, {"Ihr habt 4 Minuten, um den Widder zu reiten und dabei 3 verschiedene Geschwindigkeitsstufen für jeweils 8 Sekunden zu halten."}},
[11319] = {"Samen der schwarzbeseelten Bewahrer", nil, {"Lurielle auf der Gefrorenen Lichtung möchte, dass ihr 8 Sporen einfriert."}},
[11320] = {"[NYI] Now this is Ram Racing... Almost.", nil, {"Log Descritpion"}},
[11322] = {"Die Läuterung", nil, {"Behüterin Mondblatt am Stählernen Tor hat Euch geraten, Euren inneren Aufruhr durch Meditation am Frostklingenschrein zu besänftigen."}},
[11323] = {"Im Worgpelz", nil, {"Der Weise Nebelläufer hat Euch gebeten, Ulfang im Bruchwald zu suchen und ihm zu helfen.\n\nKehrt zum Weisen Nebelläufer zurück, um einen Ersatz zu erhalten, solltet Ihr die Worgverkleidung verlieren."}},
[11324] = {"Alphaworg", nil, {"Ulfang hat Euch gebeten, Garwal zu erschlagen. Kehrt danach zum Weisen Nebelläufer am Stählernen Tor zurück und bringt die Worgverkleidung mit."}},
[11325] = {"Im Worgpelz", nil, {"Behüterin Mondblatt hat Euch gebeten, Ulfang im Bruchwald zu suchen und ihm zu helfen.\n\nKehrt zu Behüterin Mondblatt zurück, um einen Ersatz zu erhalten, solltet Ihr die Worgverkleidung verlieren."}},
[11326] = {"Alphaworg", nil, {"Ulfang hat Euch gebeten, Garwal zu erschlagen. Kehrt danach zu Behüterin Mondblatt am Stählernen Tor zurück und bringt die Worgverkleidung mit."}},
[11327] = {"Mission 'Paket beschaffen'", nil, {"Kundschafter Wisslich möchte, dass Ihr nach Halgrind geht und das Paket des Apothekariums beschafft. Kehrt zu ihm zurück, sobald Ihr das getan habt. Er befindet sich am Glutspeerturm."}},
[11328] = {"Mission 'Kunde über die Verlassenen'", nil, {"Kundschafter Wisslich möchte, dass Ihr das Paket des Apothekariums zu Peppy Falschdüse bringt. Sie befindet sich in der Taverne in Westwacht."}},
[11329] = {"Ich werde alles versuchen!", nil, {"Christopher Sloan bei Fort Wildervar möchte, dass Ihr ihm 5 Nordbarben bringt."}},
[11330] = {"Dasch wird auf jeden Fall... funzen!", nil, {"Peppi Falschdüse in Westwacht schlägt vor, dass Ihr dem gefangenen Vrykul der Drachenschinder Peppis Spezialmix verabreicht, um zu sehen, welche Wirkung er hat."}},
[11331] = {"Du sagst's ihm... hicks!", nil, {"Peppi Falschdüse hat Euch dazu gebracht, Hauptmann Adams die schlechten Nachrichten über die Seuche der Verlassenen zu überbringen. Ihr findet ihn in Westwacht."}},
[11332] = {"Mission: Verseucht mal das hier!", nil, {"Hauptmann Adams hat Euch befohlen, 5 Bomben auf die Seuchenkessel bei Neu-Agamand abzuwerfen. Kehrt erst zu ihm zurück, sobald Ihr die Aufgabe erledigt habt. Er befindet sich in Westwacht."}},
[11333] = {"Reise in die Welt der Geister", nil, {"Thoralius der Weise in Valgarde möchte, dass Ihr seinen Reagenzienbeutel findet."}},
[11334] = {"Er schießt, er trifft!", nil, {"[PH] Rinling wants you to shoot a marked target and then return the Target Rifle to him."}},
[11335] = {"Ruf zu den Waffen: Arathibecken", nil, {"Gewinnt eine Schlacht im Arathibecken und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11336] = {"Ruf zu den Waffen: Alteractal", nil, {"Gewinnt eine Schlacht im Alteractal und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11337] = {"Ruf zu den Waffen: Auge des Sturms", nil, {"Gewinnt eine Schlacht im Auge des Sturms und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11338] = {"Ruf zu den Waffen: Kriegshymnenschlucht", nil, {"Gewinnt eine Schlacht in der Kriegshymnenschlucht und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11339] = {"Ruf zu den Waffen: Arathibecken", nil, {"Gewinnt eine Schlacht im Arathibecken und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11340] = {"Ruf zu den Waffen: Alteractal", nil, {"Gewinnt eine Schlacht im Alteractal und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11341] = {"Ruf zu den Waffen: Auge des Sturms", nil, {"Gewinnt eine Schlacht im Auge des Sturms und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11342] = {"Ruf zu den Waffen: Kriegshymnenschlucht", nil, {"Gewinnt eine Schlacht in der Kriegshymnenschlucht und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[11343] = {"Das Echo von Ymiron", nil, {"Thoralius der Weise möchte, dass Ihr die Geisterwelt betretet und Wyrmskol nach Informationen über die Geheimnisse von Wyrmskol durchsucht."}},
[11344] = {"Die Qualen von Niffelvar", nil, {"Thoralius der Weise möchte, dass Ihr die Geisterwelt betretet und Niffelvar nach Informationen über die Geheimnisse von Niffelvar durchsucht."}},
[11346] = {"Das Buch der Runen", nil, {"Ausgrabungsleiter Belvar in Fort Wildervar möchte, dass Ihr ihm das Buch der Runen bringt."}},
[11348] = {"Die Rune des Befehls", nil, {"Ausgrabungsleiter Belvar in Fort Wildervar möchte, dass Ihr die Rune des Befehls austestet und Binder Murdis besiegt."}},
[11349] = {"Die Runen meistern", nil, {"Bringt Ausgrabungsleiter Belvar in Fort Wildervar ein Set mit Eisenrunenwerkzeugen."}},
[11350] = {"Das Buch der Runen", nil, {"Fernläufer Pembe im Lager der Winterhufe möchte, dass Ihr ihm das Buch der Runen bringt."}},
[11351] = {"Die Runen meistern", nil, {"Bringt Fernläufer Pembe im Lager der Winterhufe ein Set mit Eisenrunenwerkzeugen."}},
[11352] = {"Die Rune des Befehls", nil, {"Fernläufer Pembe im Lager der Winterhufe möchte, dass Ihr die Rune des Befehls ausprobiert und Binder Murdis besiegt."}},
[11354] = {"Gesucht: Nazans Reitgerte", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Nazans Reitgerte zu beschaffen. Bringt sie zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11355] = {"Marsch der Riesen", nil, {"Forscher Aderan in Fort Wildervar möchte, dass Ihr mithilfe der runensuchenden Hacke die Leichen von 4 runenbeschriebenen Steinriesen bearbeitet."}},
[11356] = {"Kostümierte Waisenmatrone", nil, {"Sprecht mit einer kostümierten Waisenmatrone in Goldhain, Kharanos oder bei der Azurwacht."}},
[11357] = {"Maskierte Waisenmatrone", nil, {"Sprecht mit einer maskierten Waisenmatrone in Klingenhügel, Brill oder beim Falkenplatz."}},
[11358] = {"Der Leitstein", nil, {"Forscher Aderan in Fort Wildervar möchte, dass Ihr das Probestück einer Rune mit der zerbrochenen Schrifttafel vergleicht."}},
[11359] = {"Megalith zertrümmern", nil, {"Bezwingt Megalith und kehrt anschließend zu Forscher Aderan in Fort Wildervar zurück."}},
[11360] = {"Übung bei der Brandwache", nil, {"Löscht 5 Feuer mit Wassereimern und sprecht anschließend mit der kostümierten Waisenmatrone."}},
[11361] = {"Feuerübung", nil, {"Löscht 5 Feuer mit Wassereimern und sprecht anschließend mit der maskierten Waisenmatrone in Klingenhügel."}},
[11362] = {"Gesucht: Keli'dans gefiederter Stab", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Keli'dans gefiederten Stab zu beschaffen. Bringt ihn zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11363] = {"Gesucht: Messerfausts Siegel", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Messerfausts Siegel zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11364] = {"Gesucht: Zenturionen der Zerschmetterten Hand", nil, {"Netherpirscher Mah'duun hat Euch damit beauftragt, 4 Zenturionen der Zerschmetterten Hand zu töten. Kehrt erst zu ihm in das Untere Viertel in Shattrath zurück, wenn sie alle dem Tod überantwortet wurden."}},
[11365] = {"Marsch der Riesen", nil, {"Weiser Edan im Lager der Winterhufe möchte, dass Ihr mithilfe der Hacke zum Runensuchen die Leichen von 4 runenbeschriebenen Steinriesen bearbeitet."}},
[11366] = {"Der Leitstein", nil, {"Weiser Edan im Lager der Winterhufe möchte, dass Ihr die Runenprobe mit der zerbrochenen Schrifttafel vergleicht."}},
[11367] = {"Megalith zertrümmern", nil, {"Bezwingt Megalith und kehrt anschließend zu Weiser Edan im Lager der Winterhufe zurück."}},
[11368] = {"Gesucht: Das Herz von Quagmirran", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm das Herz von Quagmirran zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11369] = {"Gesucht: Ei der Schattenmutter", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm ein Ei der Schattenmutter zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11370] = {"Gesucht: Die Aufzeichnungen des Kriegsherren", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm die Aufzeichnungen des Kriegsherren zu beschaffen. Bringt sie zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11371] = {"Gesucht: Myrmidonen des Echsenkessels", nil, {"Netherpirscher Mah'duun hat Euch damit beauftragt, 14 Myrmidonen des Echsenkessels zu töten. Kehrt erst zu ihm in das Untere Viertel in Shattrath zurück, wenn sie alle dem Tod überantwortet wurden."}},
[11372] = {"Gesucht: Die Kopfschmuckfedern von Ikiss", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm die Kopfschmuckfedern von Ikiss zu beschaffen. Bringt sie zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11373] = {"Gesucht: Shaffars wundersames Amulett", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Shaffars wundersames Amulett zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11374] = {"Gesucht: Der Seelenedelstein des Exarchen", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm den Seelenedelstein des Exarchen zu beschaffen. Bringt diesen zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11375] = {"Gesucht: Murmurs Flüstern", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Murmurs Flüstern zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11376] = {"Gesucht: Bösartige Ausbilderinnen", nil, {"Netherpirscher Mah'duun hat Euch damit beauftragt, 3 bösartige Ausbilderinnen zu töten. Kehrt erst zu ihm in das Untere Viertel in Shattrath zurück, wenn sie alle dem Tod überantwortet wurden."}},
[11377] = {"Rache ist süß", nil, {"Der Rokk im Unteren Viertel von Shattrath hat Euch darum gebeten, mithilfe seines Kochtopfs einen Kalirieintopf zu kochen. Kehrt anschließend zu ihm zurück."}},
[11378] = {"Gesucht: Der Kopf des Epochenjägers", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm den Kopf des Epochenjägers zu beschaffen. Bringt diesen zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11379] = {"Superheißes Ragout", nil, {"Der Rokk im Unteren Viertel von Shattrath hat Euch darum gebeten, mithilfe seines Kochtopfs ein Dämonenragout Surprise, zwei Rippchen der Mok'Nathal und eine Knusperschlange zu kochen. Kehrt anschließend zu ihm zurück."}},
[11380] = {"Manaziös", nil, {"Der Rokk im Unteren Viertel von Shattrath möchte, dass Ihr 15 Manabeeren in den Biokuppeln im Nethersturm sammelt."}},
[11381] = {"Suppe für die Seele", nil, {"Der Rokk im Unteren Viertel von Shattrath hat Euch darum gebeten, mithilfe seines Kochtopfs eine spirituelle Suppe zu kochen. Kehrt anschließend zu ihm zurück."}},
[11382] = {"Gesucht: Aeonus' Stundenglas", nil, {"Windhändler Zhareem hat Euch darum gebeten, ihm Aeonus' Stundenglas zu beschaffen. Bringt es zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.\n\nDiese Aufgabe kann nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden."}},
[11383] = {"Gesucht: Fürsten der Zeitenrisse", nil, {"Netherpirscher Mah'duun hat Euch damit beauftragt, 4 Fürsten der Zeitenrisse zu töten. Kehrt erst zu ihm in das Untere Viertel in Shattrath zurück, wenn sie alle dem Tod überantwortet wurden."}},
