[11503] = {"Feinde - alte und neue", nil, {"Besiegt 10 feindliche Spieler in Halaa und kehrt zu Karrtog in Garadar zurück."}},
[11504] = {"Die Toten stehen auf!", nil, {"Orfus von Kamagua am Uralten Aufzug im Heulenden Fjord möchte, dass Ihr nach Fengirs Hinweis, Rodins Hinweis, Isuldofs Hinweis und Windans Hinweis sucht."}},
[11505] = {"Geister von Auchindoun", nil, {"Helft der Allianz, einen Turm in der Knochenwüste zu sichern."}},
[11506] = {"Geister von Auchindoun", nil, {"Helft der Horde, einen Turm in der Knochenwüste zu sichern."}},
[11507] = {"Urahn Atuik und Kamagua", nil, {"Orfus von Kamagua im Heulenden Fjord möchte, dass Ihr Orfus' Bündel nach Kamagua zu Urahn Atuik bringt."}},
[11508] = {"Grezzix Achsenbruch", nil, {"Sprecht mit Grezzix Achsenbruch."}},
[11509] = {"Sich einen Ruf machen", nil, {"Sprecht mit \"Silbermond\" Harry im Halunkeneck über die verschwundenen Vrykulartefakte."}},
[11510] = {"\"Bodo von der Hermannsklause\"", nil, {"\"Silbermond\" Harry im Halunkeneck möchte, dass Ihr den Orca \"Bodo von der Hermannsklause\" tötet und den Wahrsagerkristall der Sin'dorei wiederbeschafft."}},
[11511] = {"Der Stab des Sturmfurors", nil, {"Beschafft den Stab des Sturmfurors und bringt ihn zu Orfus von Kamagua im Heulenden Fjord."}},
[11512] = {"Das gefrorene Herz Isuldofs", nil, {"Beschafft das gefrorene Herz Isuldofs und bringt es zu Orfus von Kamagua im Heulenden Fjord."}},
[11513] = {"Manazellen abfangen", nil, {"Beschafft 10 eingeschmuggelte Manazellen und kehrt anschließend zu Exarch Nasuun auf der Terrasse des Lichts in Shattrath zurück."}},
[11514] = {"Das Sonnenbrunnenportal aufrechterhalten", nil, {"Beschafft 10 eingeschmuggelte Manazellen und kehrt anschließend zu Exarch Nasuun auf der Terrasse des Lichts in Shattrath zurück."}},
[11515] = {"Blut für Blut", nil, {"Magistrix Seyla auf Kil'jaedens Thron möchte, dass Ihr mit Hilfe des Teufelstrinkers 4 ausgemergelte Getriebene des Teufelsbluts tötet. Um den Teufelstrinker zu speisen, benötigt Ihr das Dämonenblut von Racheherolden, die sich in der Nähe befinden."}},
[11516] = {"Vernichtet den Durchgang", nil, {"Magistrix Seyla auf Kil'jaedens Thron möchte, dass Ihr mit Hilfe der zischenden Glut ein lebendiges Leuchtfeuer beschwört und damit so lange glühende Höllenfunken in seiner Nähe tötet, bis es zu einem instabilen lebendigen Leuchtfeuer wird. Kehrt anschließend mit letzterem zum Durchgang der Legion zurück und zerstört ihn."}},
[11519] = {"Der verlorene Schild der Aesiriten", nil, {"Sprecht mit Kapitän Ellis an Bord der Gnädigen Schwester."}},
[11520] = {"Die eigenen Wurzeln entdecken", nil, {"Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr zur Messerdornhöhe in der Scherbenwelt reist, 5 Messerdornwurzeln sammelt und wieder zu ihr zurückkehrt."}},
[11521] = {"Eure Wurzeln wiederentdecken", nil, {"Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr zur Messerdornhöhe in der Scherbenwelt reist, 5 Messerdornwurzeln sammelt und wieder zu ihr zurückkehrt."}},
[11522] = {"Anforderungen:", nil, {" - Messerdornwurzel x 5"}},
[11523] = {"Fahrt die Barrieren hoch!", nil, {"Hauptmann Theris Morgenheim beim Sammelpunkt der Zerschmetterten Sonne will, dass Ihr 4 Manaüberreste von getriebenen Unholden und Verschlingern beschafft und sie benutzt, um eine der Kristallbarrieren aufzuladen, welche die Gebäude der Sonnenweiten umgeben."}},
[11524] = {"Unberechenbares Verhalten", nil, {"Verteidigerin Xayann beim Sammelpunkt der Zerschmetterten Sonne möchte, dass Ihr die abgestimmten Kristallkerne ins Innere 5 besiegter herumirrender Wachposten steckt und sie so zu freundlichen Einheiten macht."}},
[11525] = {"Weitere Konvertierungen", nil, {"Verteidigerin Xayann beim Sammelpunkt der Zerschmetterten Sonne möchte, dass Ihr die abgestimmten Kristallkerne ins Innere 5 besiegter herumirrender Wachposten steckt und sie so zu freundlichen Einheiten macht."}},
[11526] = {"Eine Magistrix wird vermisst", nil, {"Benutzt die erbeutete Schriftrolle der Legion in der Nähe des Portals auf dem Platz der Morgenröte. Findet Magistrix Seyla, sobald Ihr auf der anderen Seite des Portals seid."}},
[11527] = {"Meuterei auf der Gnädigen", nil, {"Kapitän Ellis an Bord der Gnädigen Schwester im Heulenden Fjord möchte, dass Ihr 5 Schießpulverfässer aus dem Frachtraum des Schiffs besorgt."}},
[11528] = {"Ein Winterhauchgeschenk", nil, nil},
[11529] = {"Sorlofs Beute", nil, {"Vernichtet Sorlof mithilfe der Dicken Berta, holt Euch Sorlofs Beute und kehrt mit ihr zu Kapitän Ellis auf der Gnädigen Schwester zurück."}},
[11530] = {"Der Schild der Aesiriten", nil, {"Bringt den Schild der Aesiriten zu Orfus von Kamagua. Er befindet sich am Uralten Aufzug im Heulenden Fjord."}},
[11531] = {"Seltsames Motorteil", nil, {"Bringt K. Lee Kleinkram in Telredor das seltsame Motorteil."}},
[11532] = {"Ablenkungsmanöver an der Todesschneise", nil, {"Kampfmagierin Arynna möchte, dass Ihr mit Ayren Wolkenbrecher sprecht, sobald Ihr bereit seid, zur Todesschneise zu fliegen. Tötet dort mithilfe der arkanen Ladungen 2 Oberanführer der Grube, 3 Zauberer der Eredar und 12 Rachevollstrecker."}},
[11533] = {"Die Luftangriffe müssen weitergehen", nil, {"Kampfmagierin Arynna möchte, dass Ihr mit Ayren Wolkenbrecher sprecht, sobald Ihr bereit seid, zur Todesschneise zu fliegen. Tötet dort mithilfe der arkanen Ladungen 2 Oberanführer der Grube, 3 Zauberer der Eredar und 12 Rachevollstrecker."}},
[11534] = {"Meldung an Nasuun", nil, {"Händler Portanuus hat Euch gebeten, mit Exarch Nasuun auf der Terrasse des Lichts in Shattrath zu sprechen."}},
[11535] = {"Vorbereitungen", nil, {"Schmiedin Hauthaa in der Waffenkammer der Sonnenweiten möchte, dass Ihr Myrmidonen der Dunkelflossen, die sich im Osten befinden, tötet und ihre Schlüssel dazu verwendet, 3 Stück Erz aus ihren Truhen zu stehlen."}},
[11536] = {"Hört jetzt nicht auf!", nil, {"Schmiedin Hauthaa in der Waffenkammer der Sonnenweiten möchte, dass Ihr Myrmidonen der Dunkelflossen, die sich im Osten befinden, tötet und ihre Schlüssel dazu verwendet, um 3 Stück Erz aus ihren Truhen zu stehlen."}},
[11537] = {"Die Schlacht muss weitergehen", nil, {"Herold Inuuro möchte, dass Ihr 6 Dämonen der brennenden Legion und den Abgesandten des Hasses auf dem Platz der Morgenröte niederstreckt. Benutzt das Banner der Zerschmetterten Sonne, um den Körper des Abgesandten des Hasses aufzuspießen."}},
[11538] = {"Die Schlacht um die Waffenkammer der Sonnenweiten", nil, {"Herold Inuuro möchte, dass Ihr 6 Dämonen der brennenden Legion und den Abgesandten des Hasses auf dem Platz der Morgenröte oder der Waffenkammer der Sonnenweiten niederstreckt. Benutzt das Banner der Zerschmetterten Sonne, um den Körper des Abgesandten des Hasses aufzuspießen."}},
[11539] = {"Den Hafen einnehmen", nil, {"Magister Ilastar in der Waffenkammer der Sonnenweiten möchte, dass Ihr 6 Beschwörer der Dämmerklingen, 6 Blutritter der Dämmerklingen und 3 Schützen der Dämmerklingen niederstreckt."}},
[11540] = {"Vernichtet die Dämmerklingen", nil, {"Magister Ilastar in der Waffenkammer der Sonnenweiten möchte, dass Ihr 6 Beschwörer der Dämmerklingen, 6 Blutritter der Dämmerklingen und 3 Bogenschützen der Dämmerklingen niederstreckt."}},
[11541] = {"Belästigung an der Küste der Grünkiemen", nil, {"Kapitänin Valindria möchte, dass Ihr 10 Grünkiemensklaven befreit und zu ihr zurückkehrt, sobald Ihr damit fertig seid. Sie befindet sich auf der 'Silbermonds Stolz' im Hafen der Sonnenweiten."}},
[11542] = {"Haltet die Verstärkung auf", nil, {"Verteidiger Kaalan in der Waffenkammer der Sonnenweiten möchte, dass Ihr mit Ayren Wolkenbrecher redet. Fliegt dann über die Unterstützungsflotte der Dämmerklingen hinweg und benutzt das flammende Öl, um die Segel in Brand zu setzen. Erschlagt 6 Reservisten der Dämmerklingen, nachdem Ihr gelandet seid."}},
[11543] = {"Den Feind vom Leibe halten", nil, {"Verteidiger Kaalan in der Waffenkammer der Sonnenweiten möchte, dass Ihr mit Ayren Wolkenbrecher redet und über die Flotte mit Verstärkungstruppen der Dämmerklingen hinwegfliegt. Benutzt solange Ihr fliegt das flammende Öl, um die Segel in Brand zu setzen. Erschlagt 6 Reservisten der Dämmerklingen, nachdem Ihr gelandet seid."}},
[11544] = {"Waffen von Ata'mal", nil, {"Schmiedin Hauthaa möchte, dass Ihr 5 Stück gereinigtes Metall von Ata'mal herstellt. Um dies zu tun, müsst Ihr Waffen von Ata'mal auf ihrem Amboss in der Waffenkammer der Sonnenweiten auf der Insel von Quel'Danas zerlegen."}},
[11546] = {"Geschäft geöffnet", nil, {"Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr 5 Blutbeeren von den Büschen auf Quel'Danas sammelt."}},
[11547] = {"Kenne deine Leylinien", nil, {"Astromant Darnarian im Sanktum der Sonnenweiten möchte, dass Ihr den Kristall des Astromanten am Blutkristall, am Portal auf dem Platz der Morgenröte und in der Nähe des Nagaschreins an der Küste der Grünkiemen benutzt."}},
[11548] = {"Eure weitere Unterstützung", nil, {"Anachoretin Ayuri in den Sonnenweiten auf der Insel des Sonnenbrunnens möchte, dass Ihr 10 Gold spendet."}},
[11549] = {"Ein großmütiger Spender", nil, {"Anachoret Kairthos möchte, dass Ihr 1000 Gold spendet, um Anachoretin Ayuri in ihrem Vorhaben zu unterstützen. Wenn Ihr die Aufgabe abschließen solltet, werdet Ihr den Namen \"Bomdiyada der Zerschmetterten Sonne\" tragen."}},
[11550] = {"Der Betrüger betritt die Bühne...", nil, {"Meldet Euch bei Erzmagier Ne'thul auf der Insel von Quel'Danas, nördlich von Quel'Thalas.\n\nSprecht mit Flugmeistern in Eisenschmiede und Silbermond, sie werden Euch dorthin bringen."}},
[11554] = {"Ein Freund an der Front", nil, nil},
[11555] = {"Eure Verbündeten wollen Euer Wohl", nil, nil},
[11556] = {"Auf dem Schlachtfeld zollt man Euch Respekt", nil, nil},
[11557] = {"Alle Kämpfer begegnen Euch mit Ehrfurcht", nil, nil},
[11558] = {"Gefährliche Liebschaft", nil, {"Besorgt eine modrige Karte des Wächters und bringt sie zu Fenstad Argyle in Unterstadt."}},
[11559] = {"Winterflossenhandel", nil, {"Sammelt 5 Winterflossenmuscheln und liefert sie bei Ahlurglgr ab. Dieser befindet sich in der Zuflucht der Winterflossen."}},
[11560] = {"Oh neeiin, die Kaulquappen!", nil, {"König Mrgl-Mrgl möchte, dass Ihr 20 Kaulquappen der Winterflossen befreit und anschließend zu ihm zurückkehrt. Er befindet sich in der Zuflucht der Winterflossen."}},
[11561] = {"Die da!", nil, {"Brglmurgl in der Zuflucht der Winterflossen möchte, dass Ihr insgesamt 15 Murlocs der Winterflossen tötet, egal ob Krieger, Orakel oder Küstenschläger."}},
[11562] = {"Mein Putzmurloc erpresst mich", nil, {"König Mrgl-Mrgl möchte, dass Ihr mit Mrmrglmr sprecht. Er befindet sich im nordwestlichen Teil der Zuflucht der Winterflossen."}},
[11563] = {"Grmmurggll Mrllggrl Glrggl!!!", nil, {"Mrmrglmr will, dass Ihr mit Glrggls Kopf zu ihm zurückkehrt. Er befindet sich in der Zuflucht der Winterflossen."}},
[11564] = {"Saftiger Orcaeintopf", nil, {"Hackebeil Bmurglbrm hat Euch damit beauftragt, 7 Einheiten saftigen Orcatran zu beschaffen. Bringt sie anschließend zu ihm in die Zuflucht der Winterflossen."}},
[11565] = {"Das Ersatzkostüm", nil, {"Bringt König Mrgl-Mrgls Ersatzkostüm zu ihm zurück. Er befindet sich in der Zuflucht der Winterflossen."}},
[11566] = {"Ergebt Euch... nicht!", nil, {"König Mrgl-Mrgl in der Zuflucht der Winterflossen möchte, dass Ihr ihm die Klaue von Claximus bringt."}},
[11567] = {"Die uralte Rüstung der Kvaldir", nil, {"Bergt die uralte Rüstung der Kvaldir und bringt sie zu Orfus von Kamagua im Heulenden Fjord."}},
[11568] = {"Wieder zur Ruhe kommen lassen", nil, {"Verwendet das Bündel mit Vrykulartefakten bei den geplünderten Grabhügeln des Schildhügels und legt das gefrorene Herz Isuldofs, den Schild der Aesiriten, den Stab des Sturmfurors und die uralte Rüstung der Kvaldir wieder zurück.\n\nKehrt zu Orfus von Kamagua am Uralten Aufzug zurück, falls Ihr erfolgreich sein solltet."}},
[11569] = {"Schlüsselmeister Urmgrgl", nil, {"Glrglrglr in den Höhlen der Winterflossen möchte, dass Ihr Urmgrgls Schlüssel beschafft."}},
[11570] = {"Flucht aus den Höhlen der Winterflossen", nil, {"Eskortiert Lurgglbr, bis er in Sicherheit ist, und überbringt anschließend König Mrgl-Mrgl in der Zuflucht der Winterflossen die guten Neuigkeiten."}},
[11571] = {"Lernen, wie man kommuniziert", nil, {"König Mrgl-Mrgl hat Euch eine Muschel gegeben, die Ihr bei Verbrühers Überresten benutzen sollt. Kehrt mit der gefüllten Muschel des Königs zu ihm zurück. Er befindet sich in der Zuflucht der Winterflossen."}},
[11572] = {"Rückkehr zu Atuik", nil, {"Überbringt Urahn Atuik in Kamagua die schlechten Nachrichten."}},
[11573] = {"Orfus der Kamagua", nil, {"Sucht am Uralten Aufzug im Heulenden Fjord nach Orfus von Kamagua."}},
[11574] = {"Zu knapp für meinen Geschmack", nil, {"Endorah möchte, dass Ihr Euch auf das Bernsteinflöz nördlich der Kriegshymnenfeste begebt und ihren Rettern ihren Dank aussprecht."}},
[11575] = {"Gerade noch rechtzeitig", nil, {"Mücke in der Valianzfeste möchte, dass Ihr Euch auf das Bernsteinflöz im Norden begebt und ihren Rettern ihren Dank aussprecht."}},
[11576] = {"Die Klamm beobachten: Spaltkliffanomalie", nil, {"Garren vom Bernsteinflöz möchte, dass Ihr das Arkanometer bei der Spaltkliffanomalie benutzt."}},
[11578] = {"Die \"Fress\"-Quest (123)aa", nil, {"Tötet Koboldgezücht - und zwar 2 davon."}},
[11579] = {"Die \"Fress\"-Quest (123)aa", nil, {"Tötet Koboldgezücht, und zwar 2 davon."}},
