[11789] = {"Ein Soldat in Not", nil, {"Sanitäter Hagedorn außerhalb der Valianzfeste möchte, dass Ihr Euch ins Innere des Laderaums der Sturmbrecher begebt und Hagedorns Gegengift aus einer Kiste mit Vorräten für Erste Hilfe beschafft."}},
[11790] = {"Kultisten unter uns", nil, {"Sprecht mit Kapitän \"Linksdatsch\" Gaffelsegel an Deck der Sturmbrecher in der Valianzfeste."}},
[11791] = {"Benachrichtigt Arlos", nil, {"Sprecht mit General Arlos im inneren Bereich der Valianzfeste."}},
[11792] = {"Feinde des Lichts", nil, {"Herold Vurenn möchte, dass Ihr die Kultisten der Nekrolyten in der Nähe von Fernhain gen Norden der Valianzfeste bekämpft und ihnen das Kommuniqué der Kultisten abnehmt."}},
[11793] = {"Weitere Ermittlungen", nil, {"Bringt das Kommuniqué der Kultisten zu Verteidigerin Yaala im Gasthaus der Valianzfeste."}},
[11794] = {"Die Jagd ist eröffnet", nil, {"Verteidigerin Yaala möchte, dass Ihr das Okular des Exorzisten an den drei genannten Orten in der Valianzfeste einsetzt, um die Mitglieder des Kults der Verdammten zu enttarnen und zu töten."}},
[11795] = {"Notfallprotokoll: Sektion 8,2. Absatz C", nil, {"Beschafft 6 Insignien eines Piloten von Kurbelzisch von den Leichnamen der Aufklärungspiloten von Kurbelzisch und bringt sie zurück zu Iggy \"Trudelflug\" Zinkenwechsel bei den Siedenden Teichen."}},
[11796] = {"Notfallprotokoll: Sektion 8,2. Paragraph D", nil, {"Iggy \"Trudelflug\" Zinkenwechsel möchte, dass Ihr die Wracks im Osten, Süden und Nordwesten beseitigt."}},
