[12232] = {"Bombardiert die Ballisten", nil, {"Todeswache Molder in Gallgrimm möchte, dass Ihr mit den Bomben des Belagerungsschmiedes 5 Ballisten von Neuherdweiler unter Beschuss nehmt."}},
[12234] = {"Das muss ich wissen", nil, {"Spionagemeisterin Repine möchte, dass Ihr die \"Tagesübersicht des Scharlachroten Ansturms: Abtei\", \"Tagesübersicht des Scharlachroten Ansturms: Kaserne\" und die \"Tagesübersicht des Scharlachroten Ansturms: Strand\" stehlt."}},
[12235] = {"Naxxramas und der Fall von Wintergarde", nil, {"Sprecht mit dem Greifenkommandanten Urik an der Greifenstation in der Feste Wintergarde."}},
[12236] = {"Ursoc, der Bärengott", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr Tur Wutpranke in der Nähe von Ursocs Höhle findet und Ursoc mit seiner Hilfe besiegt. Streut die geläuterte Asche von Vordrassil auf Ursocs Leiche, nachdem Ihr ihn getötet habt."}},
[12237] = {"Flug des Verteidigers von Wintergarde", nil, {"Rettet 5 hilflose Dorfbewohner von Wintergarde und kehrt anschließend zum Greifenkommandanten Urik in Wintergarde zurück."}},
[12238] = {"Die Reinigung Drak'Tharons", nil, {"Drakuru möchte, dass Ihr Drakurus Elixier an seinem Kohlenbecken innerhalb Drak'Tharons benutzt. Um das Elixier dort benutzen zu können, benötigt Ihr 5 Ausdauermojos."}},
[12239] = {"Der Spion in Neuherdweiler", nil, {"Liefert die Tagesübersicht des Scharlachroten Ansturms bei Agentin Skully in Neuherdweiler ab."}},
[12240] = {"Ein Mittel zum Zweck", nil, {"Apothekerin Vicky Levine in Gallgrimm möchte, dass Ihr mithilfe der Termiten der Levinefamilie Großknecht Kaleiki hervorlockt und ihn tötet."}},
[12241] = {"Zerstört den Schössling", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr die grüne Fackel nehmt und sie benutzt, um Vordrassils Schössling zu verbrennen. Bringt Vordrassils Asche zurück zu Windseher Grauhorn."}},
[12242] = {"Vordrassils Samen", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr nach Grauschlund geht und 8 Samen von Vordrassil beschafft."}},
[12243] = {"Feuer auf dem Wasser", nil, {"Apothekerin Vicky Levine in Gallgrimm möchte, dass Ihr mithilfe des Feuerwassers des Apothekers die Segel der Sündenbabel in Brand steckt.\n\nGeht unter Deck, während die Deckmatrosen damit beschäftigt sind, das Feuer zu löschen, und sucht nach Kapitän Shelys Seekarten."}},
[12244] = {"Schredder reparieren", nil, {"Synipus beim Holzfällerposten Blauhimmel möchte, dass Ihr ihm 3 kaputte Schredder bringt."}},
[12245] = {"Keine Gnade für Gefangene", nil, {"Spionagemeisterin Repine hat Euch gebeten, Todeswache Schneider, Chefschreiber Kinnedius, Ingenieur Hindermann und Kanzler Amai zu töten.\n\nFalls Ihr Erfolg habt, berichtet Hochexekutor Zürner in Gallgrimm davon."}},
[12246] = {"Eine mögliche Verbindung", nil, {"Hierophantin Thayreen bei der Ammertannhütte möchte, dass Ihr am Schrein des Grollfangs und am Schrein des Herzbluts 8 Blutproben von wahnsinnigen Furbolgs sammelt."}},
[12247] = {"Kinder von Ursoc", nil, {"Hierophantin Thayreen bei der Ammertannhütte möchte, dass Ihr mit Orsonn im Schrein des Grollfangs und mit Kodian im Schrein des Herzensblutes sprecht."}},
[12248] = {"Vordrassils Schössling", nil, {"Hierophantin Thayreen in der Ammertannhütte möchte, dass Ihr mithilfe der grünen Fackel Vordrassils Schössling anzündet und ihr die daraus entstehende Asche von Vordrassil bringt."}},
[12249] = {"Ursoc, der Bärengott", nil, {"Hierophantin Thayreen bei der Ammertannhütte möchte, dass Ihr Tur Wutpranke in der Nähe von Ursocs Höhle aufsucht und mit seiner Hilfe Ursoc besiegt. Streut anschließend die geläuterte Asche von Vordrassil auf Ursocs Leiche."}},
