[12252] = {"Foltert den Folterer", nil, {"Hochexekutor Zürner in Gallgrimm möchte, dass Ihr Folterer Alphonse fünfmal mit dem Brandeisen bearbeitet und die Informationen aus ihm herausquetscht. Anschließend sollt Ihr ihn töten."}},
[12253] = {"Rettung vom Marktplatz", nil, {"Oberkommandant Halford Wyrmbann in der Feste Wintergarde in der Drachenöde möchte, dass Ihr 6 gefangene Dorfbewohner von Wintergarde befreit."}},
[12254] = {"Ohne Gebet", nil, {"Agentin Skully in Neuherdweiler möchte, dass Ihr das Gebetsbuch von Bischof Straß besorgt."}},
[12255] = {"Der Than von Voldrune", nil, {"Besiegt mithilfe von Flammenbringer den Than Torvald Eriksson und meldet Euch anschließend bei Förster Drach bei der Ammertannhütte."}},
