[12948] = {"Der Champion der Agonie", nil, {"Betretet die Arena im Amphitheater der Agonie und besiegt Vladof den Metzger! Kehrt zu Wodin dem Trolldiener zurück, um Eure Belohnung entgegenzunehmen."}},
[12949] = {"Besorgt den Schlüssel", nil, {"Vaelen der Geschundene in Ufranggs Halle hat Euch gebeten, den Schlüssel zu Vaelens Ketten zu besorgen."}},
[12950] = {"Eimer mit Süßigkeiten", nil, nil},
[12951] = {"Gebt dem Baron Bescheid", nil, {"Sprecht mit Baron Splitter im Schattengewölbe."}},
[12952] = {"Edelsteine perfektionieren", nil, {"Bringt zwei Riesencitrin, zwei Nephrite und zwei Schattenkristalle zu Carter Tiffens im Hafen der Vergeltung."}},
[12953] = {"Valkyrion muss brennen", nil, {"Benutzt die Harpunenkanone von Valkyrion, um 6 trockene Heuhaufen anzuzünden."}},
[12954] = {"Das Amphitheater der Agonie: Yggdras!", nil, {"Betretet die Arena im Amphitheater der Agonie und besiegt Yggdras! Kehrt zu Wodin dem Trolldiener zurück, um Eure Belohnung entgegenzunehmen."}},
[12955] = {"Eliminiert die Konkurrenz", nil, {"Der Springer beim Schattengewölbe möchte, dass Ihr Duelle austragt und Sigrid Eiskind, Efrem den Treuen, Onu'zun und Tinky Dochtpfiff besiegt."}},
[12956] = {"Ein Funken Hoffnung", nil, {"Ihr sollt das veredelte glänzende Erz zu Thorim in den Tempel des Sturms bringen."}},
[12957] = {"Sklaven der Sturmgeschmiedeten", nil, {"Bergfels der Steinformer in Bergfels' Zuflucht möchte, dass Ihr 6 gefangene Mechagnome befreit und 3 sturmgeschmiedete Zuchtmeister tötet."}},
[12958] = {"Lieferung: Blutrotes Nephritamulett", nil, {"Kombiniert ein Vrykulamulett mit einem Nephrit und einem Blutstein, um ein blutrotes Nephritamulet herzustellen und es dann zu Jonas Timotheus in Dalaran zu bringen.\n\nVrykulamulette könnt Ihr von jedem Vrykul in Nordend erhalten."}},
[12959] = {"Lieferung: leuchtende Elfenbeinfigur", nil, {"Kombiniert Elfenbein aus Nordend mit einem Chalzedon und einem Schattenkristall, um eine leuchtende Elfenbeinfigur herzustellen und diese dann zu Jonas Timotheus in Dalaran zu bringen.\n\nElfenbein aus Nordend könnt Ihr von jedem Schaufelhauer oder Mammut in Nordend erhalten."}},
[12960] = {"Lieferung: tückische Sonnenbrosche", nil, {"Kombiniert eine eisenzwergische Brosche mit einem Riesencitrin und einem Sonnenkristall, um eine tückische Sonnenbrosche herzustellen und diese dann zu Jonas Timotheus in Dalaran zu bringen.\n\nEisenzwergische Broschen könnt Ihr von jedem Eisenzwerg in Nordend erhalten."}},
[12961] = {"Lieferung: aufwändige Knochenfigur", nil, {"Kombiniert einen Protodrachenknochen mit einem Sonnenkristall und einem Nephrit, um eine aufwändige Knochenfigur herzustellen und diese dann zu Jonas Timotheus in Dalaran zu bringen.\n\nProtodrachenknochen könnt Ihr von jedem Protodrachen in Nordend erhalten."}},
[12962] = {"Lieferung: helles Rüstungsrelikt", nil, {"Kombiniert den Rest einer Elementarrüstung mit einem Blutstein und einem Riesencitrin, um ein helles Rüstungsrelikt herzustellen und es dann zu Jonas Timotheus in Dalaran zu bringen.\n\nDie Reste einer Elementarrüstung könnt Ihr von jedem Klagegeist in Nordend erhalten."}},
[12963] = {"Lieferung: unbeständige Sonnenkuriosität", nil, {"Kombiniert eine Geißelkuriosität mit einem Sonnenkristall und einem Schattenkristall, um eine unbeständige Sonnenkuriosität herzustellen und diese dann zu Jonas Timotheus in Dalaran zu bringen.\n\nGeißelkuriositäten könnt Ihr von jedem Diener der Geißel in Nordend erhalten."}},
[12964] = {"Das dunkle Erz", nil, {"Bringt 5 Erzproben zu Bergfels dem Steinformer in Bergfels' Zuflucht."}},
[12965] = {"Lokens Gaben", nil, {"Zerstört Lokens Wut, Lokens Kraft und Lokens Gunst und kehrt dann zu Bergfels dem Steinformer nach Bergfels' Zuflucht zurück."}},
[12966] = {"Ihr könnt ihn nicht verpassen", nil, {"König Jokkum in Dun Niffelem möchte, dass Ihr zu Fjorns Amboss geht und Njormeld sucht."}},
[12967] = {"Kampf gegen die Elemente", nil, {"Njormeld wünscht, dass Ihr Snorri zu Fjorns Amboss begleitet und ihm dabei helft, 10 wutentbrannte Klagegeister zu töten."}},
[12968] = {"Yuldas Torheit", nil, {"Iva die Rachsüchtige aus Brunnhildar möchte, das Ihr Yulda die Sturmwirkerin in Valkyrion tötet."}},
[12969] = {"Ist das Euer Goblin?", nil, {"Fordert Agnetta Tyrsdottar heraus, um Zeev Zischelfunken zu retten. Kehrt ins Dorf Brunnhildar zu Lok'lira der Greisin zurück, wenn Ihr erfolgreich wart."}},
[12970] = {"Das Hildarthing", nil, {"Hört Euch den Vorschlag von Lok'lira der Greisin an."}},
[12971] = {"Sich allen Herausforderern stellen", nil, {"Lok'lira die Greisin in Brunnhildar wünscht, dass Ihr 6 siegreiche Herausforderinnen besiegt."}},
[12972] = {"Ihr werdet einen Bären brauchen", nil, {"Sprecht mit Brijana, die sich außerhalb von Brunnhildar aufhält."}},
[12973] = {"Die Gebrüder Bronzebart", nil, {"Begleitet Brann Bronzebart und seine Flugmaschine zur Eisfestung."}},
[12974] = {"Ruf des Champions!", nil, {"Sucht Gurgthock im Amphitheater der Agonie in Zul'Drak auf."}},
[12975] = {"In Gedenken an", nil, {"König Jokkum in Dun Niffelem wünscht, dass Ihr nach Donnerfall geht und 8 Hornfragmente einsammelt."}},
[12976] = {"Ein Monument für die Gefallenen", nil, {"König Jokkum wünscht, dass Ihr Hodirs Hornfragmente zu Njormeld in Dun Niffelem bringt."}},
[12977] = {"Hodirs Ruf", nil, {"Ihr müsst am Donnerfall 5 Vorfahren von Niffelem und 5 ruhelose Frosterben besiegen. Lasst dann über ihren Leichen stehend Hodirs Horn ertönen und geht anschließend zurück zu Hodirs Horn in Dun Niffelem."}},
[12978] = {"Im Angesicht des Sturms", nil, {"Bruor Eisenbann in Bergfels' Zuflucht bittet Euch, 10 Sturmgeschmiedete in Nidavelir zu töten. Tötet Sturmgeschmiedete Champions, Sturmgeschmiedete Magier oder Sturmgeschmiedete Kriegsgolems."}},
[12979] = {"Rüstung der Dunkelheit", nil, {"Bringt 4 Dunkle Rüstungen zu Bruor Eisenbann in Bergfels' Zuflucht."}},
[12980] = {"Die Geheimnisse der Rüstung", nil, {"Bringt die dunkle Plattenrüstung zu Mimirs Werkstatt und bittet Tock den Knecht, sie sich näher anzusehen. Kehrt zu Bruor Eisenbann in Bergfels' Zuflucht zurück, wenn Ihr fertig seid."}},
[12981] = {"Heiß und kalt", nil, {"Ihr sollt zum Frostfeldsee gehen, die zerbrechlichen Klagegeister dort vernichten und die Essenz des Eises auf den qualmenden Schrott bei Fjorns Amboss anwenden. Anschließend sollt Ihr 6 Trümmer gefrorenen Eisenschrotts sammeln und zu Fjorns Amboss in Dun Niffelem zurückkehren."}},
[12982] = {"Gefangene der Schwarzen Klinge", nil, {"Vaelen der Geschundene im Schattengewölbe bittet Euch, 8 Gefangene der Schwarzen Klinge zu befreien. Ihr findet sie in den Käfigen in der weiteren Umgebung von Jotunheim."}},
[12983] = {"Die Letzte ihrer Art", nil, {"Astrid Björnrittar in Brunnhildar wünscht, dass Ihr eine Eisschlundmatriarchin aus der Überwinterungshöhle rettet."}},
[12984] = {"Valduran der Sturmgeborene", nil, {"Benutzt Bergfels' Kriegshorn, sobald Ihr Valduran den Sturmgeborenen gefunden habt. Besiegt ihn und kehrt anschließend zu Bergfels dem Steinformer in Bergfels' Zuflucht zurück."}},
[12985] = {"Einen Kopf schmieden", nil, {"Njormeld in Dun Niffelem möchte, dass Ihr mithilfe der Hacke mit Diamantspitze 8 Sturmgeschmiedete Augen von den toten Eisenriesen auf dem Frostfeldsee beschafft und dann anschließend zu ihm zurückkehrt."}},
[12986] = {"Das Schicksal der Titanen", nil, {"Creteus in den Fußmarschen möchte, dass Ihr zum Tempel des Erfindungsreichtums, Tempel des Winters, Tempel des Lebens und zum Tempel der Ordnung reist und mithilfe der Datenbank jeden Bereich untersucht."}},
[12987] = {"Hodirs Helm platzieren", nil, {"Njormeld möchte, dass Ihr zur Spitze der nördlich gelegenen Eisspitze in Dun Niffelem fliegt und die Worte auf den Tafeln der Verkündung rezitiert."}},
[12988] = {"Sabotiert die Schmieden!", nil, {"Sabotiert die Nordblitzschmiede, die Zentralblitzschmiede und die Südblitzschmiede mit Bergfels' Bomben. Kehrt dann zu Bergfels dem Steinformer in Bergfels' Zuflucht zurück."}},
[12989] = {"Die schleimige Dunkelheit", nil, {"Astrid Björnrittar wünscht, dass Ihr 8 gefräßige Jormungar in der Überwinterungshöhle östlich des Dorfes Brunnhildar tötet."}},
[12991] = {"Sie treffen, wo es wehtut", nil, {"Tötet 10 Sturmgeschmiedete Konstrukteure und kehrt zu Bruor Eisenbann in Bergfels' Zuflucht zurück."}},
[12992] = {"Tötet die Vrykul!", nil, {"Ekel im Schattengewölbe möchte, dass Ihr 15 beliebige Vrykul in der weiteren Umgebung von Jotunheim tötet."}},
[12993] = {"Eine kolossale Bedrohung", nil, {"Gelangt an die Informationen über die Angriffs- und Verteidigungsfähigkeiten des Kolosses und bringt sie zu Bergfels dem Steinformer in Bergfels' Zuflucht."}},
[12994] = {"Jagd auf Spione", nil, {"Ihr müsst 3 Sturmgeschmiedete Spitzel im Uralten Wintertal besiegen.\n\nVerwendet den Fangzahn eines astralen Worgs bei der Leiche des gefallenen Worgs und beschwört damit einen astralen Frostworg, mit dessen Hilfe Ihr versteckte Feinde entlarven könnt. Wenn das Tal von den Spionen befreit wurde, sollt Ihr zur Frostworghöhlenmutter in Dun Niffelem zurückkehren."}},
[12995] = {"Setzt ein Zeichen", nil, {"Baron Splitter im Schattengewölbe wünscht, dass Ihr 15 Banner der Schwarzen Klinge in die Nähe der Vrykulleichen steckt, die Ihr in der weiteren Umgebung von Jotunheim findet."}},
[12996] = {"Das Aufwärmen", nil, {"Astrid Björnrittar aus Brunnhildar möchte, dass Ihr die Zügel der Kriegsbärenmatriarchin nutzt, um Kirgaraak zu besiegen."}},
[12997] = {"In die Grube", nil, {"Astrid Björnrittar aus Brunnhildar möchte, dass Ihr in die Reißzahngrube geht und mit den Zügeln der Kriegsbärenmatriarchin 6 Kriegsbären des Hildarthings besiegt."}},
[12998] = {"Das Herz des Sturms", nil, {"Bergfels der Steinformer in Bergfels' Zuflucht möchte, dass Ihr nach Uldis reist und versucht, Aufseher Narvir daran zu hindern, an das Herz des Sturms zu gelangen."}},
[12999] = {"Die Knochenhexe", nil, {"Sprecht mit der Knochenhexe in der Höhle des Dorfes Njorndar."}},
[13000] = {"Notfallmaßnahmen", nil, {"Blutwache Lorga bittet Euch, zu Gimoraks Bau im Osten zu gehen und von dort 6 Worgfelle mitzubringen."}},
[13001] = {"Hodirs Speer schmieden", nil, {"Wissenshüter Randvir aus Dun Niffelem möchte, dass Ihr 3 Immerfrostsplitter aus den Tiefen der Überwinterungshöhle sowie 3 Bälge ruhiger Mammuts aus dem Uralten Wintertal holt."}},
[13002] = {"Edelsteine perfektionieren", nil, {"Bringt zwei Riesencitrin, zwei Nephrite und zwei Schattenkristalle zu Geba'li in der Kriegshymnenfeste."}},
[13003] = {"Drachentöten leicht gemacht", nil, {"Ihr sollt mit Hodirs Speer einen Wildwyrm im Uralten Wintertal herausfordern. Wenn Ihr ihn getroffen habt, muss es zu einem Kampf auf Leben und Tod kommen."}},
[13004] = {"Edelsteine perfektionieren", nil, {"Bringt zwei Riesencitrin, zwei Nephrite und zwei Schattenkristalle zu Alestros in der Valianzfeste."}},
[13005] = {"Der Eid der Irdenen", nil, {"Thorim, auf der Terrasse der Schöpfer, wünscht, dass Ihr in das Horn der Sturmgipfel blast und die Irdenen ruft, damit sie 20 Eisenzwerge und 7 Eisenschildwachen besiegen."}},
[13006] = {"Eine schmierige Säuberung", nil, {"Ihr müsst Euch in die Überwinterungshöhle westlich von Dun Niffelem begeben und 5 Einheiten viskoses Öl vom Rohöl dort besorgen.\n\nKehrt dann nach Dun Niffelem zurück und tragt das Öl auf Hodirs Helm auf."}},
[13007] = {"Der Eisenkoloss", nil, {"Nutzt einen gezähmten Jormungar, um den Eisenkoloss zu besiegen, und kehrt dann zu Bergfels dem Steinformer in Bergfels' Zuflucht zurück."}},
[13008] = {"Die Taktik der Geißel", nil, {"Kreuzzugskommandant Entari in der Argentumvorhut von Eiskrone bittet Euch, 8 eingesponnene Kreuzfahrer zu befreien."}},
[13009] = {"Ein Neuanfang", nil, {"Njormeld wünscht, dass Ihr die neugeschmiedete Rüstung zu Thorim im Tempel der Stürme bringt."}},
[13010] = {"Krolmir, Hammer der Stürme", nil, {"Thorim möchte, dass Ihr mit König Jokkum in Dun Niffelem sprecht und in Erfahrung bringt, was er über Krolmir weiß."}},
[13011] = {"Jorkuttar töten", nil, {"König Jokkum in Dun Niffelem möchte, dass Ihr in die Überwinterungshöhle geht und Jorkuttar niederstreckt."}},
