[13562] = {"Die letzte Flamme von Bashal'Aran", nil, {"Löscht die letzte Flamme von Bashal'Aran aus."}},
[13563] = {"Ewige Liebe", nil, {"Befreit den Geist von Anaya Dämmerflucht und erhaltet ihren Anhänger."}},
[13564] = {"Ein verlorener Freund", nil, {"Findet Grimmklaue."}},
[13565] = {"Zweiten Grades", nil, {"Benutzt die versteinerte Wurzel, ruft 6 siechende Ents und entledigt Euch Lady Janiras, der Anführerin der Naga."}},
[13566] = {"Materialien für das Ritual", nil, {"Besorgt 3 Schnurrhaare eines Mondpirschers, 3 Büschel gefleckter Rehhaare und 3 Proben Distelbärenfell."}},
[13567] = {"Der Geist des Hirsches", nil, {"Bewahrer Karithus hat Euch angewiesen, den Segen des großen Hirschgeists einzuholen."}},
[13568] = {"Der Geist des Mondpirschers", nil, {"Bewahrer Karithus hat Euch angewiesen, den Segen des großen Mondpirschergeists einzuholen."}},
[13569] = {"Der Bund des Rituales", nil, {"Atmet den Weihrauch vor Bewahrer Karithus ein und trefft eine Entscheidung zwischen dem Segen des großen Hirschgeistes, des großen Distelbärgeistes und des großen Mondpirschergeistes.\n\nNur einer der Geister kann Euch seinen Segen erteilen, also wählt weise."}},
