[14007] = {"Zuverlässiger Schuss", nil, {"Sprecht mit Bäm Megabombe, um 'Zuverlässiger Schuss' zu lernen und schießt dann dreimal auf eine Trainingsattrappe."}},
[14008] = {"Arkane Geschosse", nil, {"Sprecht mit Zisch Zündel, um 'Arkane Geschosse' zu lernen und wendet den Zauber dann zweimal auf eine Trainingsattrappe an.\n\nBedenkt dabei, dass für 'Arkane Geschosse' die Chance besteht, durch die Nutzung anderer offensiver Zauber ausgelöst zu werden. Verwendet zuerst Euren Zauber 'Feuerball' auf eine Trainingsattrappe, um 'Arkane Geschosse' zu ermöglichen."}},
[14009] = {"Blitzheilung", nil, {"Sprecht mit Schwester Goldschimmer, um 'Blitzheilung' zu lernen, und wendet den Zauber dann dreimal auf einen verletzten Angestellten an."}},
[14010] = {"Ausweiden", nil, {"Sprecht mit Slinky Scharfklinge, um 'Ausweiden' zu lernen, und wendet es dann dreimal auf eine Trainingsattrappe an.\n\nDenkt daran, 'Ausweiden' erfordert Combopunkte, also schlagt die Trainingsattrappe zunächst mit 'Finsterer Stoß'."}},
[14011] = {"Urtümlicher Schlag", nil, {"Sprecht mit Lawinus Maximus, um 'Urtümlicher Schlag' zu lernen und wendet ihn dann zweimal auf eine Trainingsattrappe an."}},
[14012] = {"Feuerbrand", nil, {"Sprecht mit Bosa Blick, um 'Feuerbrand' zu lernen, dann wirkt den Zauber 3-mal auf eine Trainingsattrappe."}},
[14013] = {"Sturmangriff", nil, {"Sprecht mit dem Kriegomatic NX-01, um 'Sturmangriff' zu lernen. Führt den Angriff dann auf eine Trainingsattrappe aus."}},
[14014] = {"Holt unsere Sachen zurück!", nil, {"Besorgt 8 Werkzeugkisten."}},
[14016] = {"Der Fluch des Schwarzen Ritters", nil, {"Kreuzfahrerin Rhydalla auf dem Argentumturnierplatz möchte, dass Ihr das Grab des Schwarzen Ritters untersucht."}},
