[14177] = {"Die dankbaren Toten", nil, {"Besorgt Euch ein wenig Totenspeise und gebt sie dem heiteren Trollgeist auf dem Friedhof von Orgrimmar.\n\nBenutzt eine orangefarbene Ringelblume, um den ansonsten unsichtbaren Geist zu sehen."}},
[14178] = {"Ruf zu den Waffen: Arathibecken", nil, {"Gewinnt eine Schlacht im Arathibecken und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14179] = {"Ruf zu den Waffen: Auge des Sturms", nil, {"Gewinnt eine Schlacht im Auge des Sturms und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14180] = {"Ruf zu den Waffen: Kriegshymnenschlucht", nil, {"Gewinnt eine Schlacht in der Kriegshymnenschlucht und meldet Euch anschließend bei einem Brigadegeneral der Allianz in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14181] = {"Ruf zu den Waffen: Arathibecken", nil, {"Gewinnt eine Schlacht im Arathibecken und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14182] = {"Ruf zu den Waffen: Auge des Sturms", nil, {"Gewinnt eine Schlacht im Auge des Sturms und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14183] = {"Ruf zu den Waffen: Kriegshymnenschlucht", nil, {"Gewinnt eine Schlacht in der Kriegshymnenschlucht und meldet Euch anschließend bei einem Kriegshetzer der Horde in einer der Hauptstädte, in Tausendwinter, Dalaran oder Shattrath."}},
[14184] = {"Meine Zeit ist um", nil, {"Furien in Tethris Aran will, dass Ihr auf seinem Drachenfalken zu Furiens Posten reitet und seine Schwester Cerelia über seinen Tod unterrichtet."}},
[14188] = {"Rächt Furien!", nil, {"Cerelia will, dass Ihr mit Sorrem in Furions Posten sprecht."}},
[14189] = {"Übersetzung", nil, {"Sorrem will, dass Ihr die uralten Schrifttafeln findet.\n\nSprecht mit Narimar in Furions Posten, um dorthin zu reiten, wo Furien seine Nachforschungen betrieben hat."}},
[14190] = {"Das perfekte Prisma", nil, {"Gelangt in den Besitz eines Kristallanhängers von einem Saboteur der Talrendisspitze im Tagebau am Gebirgsfuß."}},
[14191] = {"Auf Furiens Spuren", nil, {"Die uralten Schrifttafeln am Schrein von Elune haben Euch den Weg zu dreien der markanten Ruinen in Sargeron gewiesen."}},
[14192] = {"Tausche Linse gegen Prisma", nil, {"Bringt den Kristallanhänger zum Waffenschrank im Hauptquartier des Tagebaus am Gebirgsfuß."}},
[14193] = {"Nichts, was ein paar Melonen nicht richten können", nil, {"Sammelt 8 Melonenfrüchte"}},
[14194] = {"Wiederverfleischlichung", nil, {"Verwendet den Wiederverfleischlicher um acht Bergfußminenarbeiter im Tagebau am Gebirgsfuß wieder zu entsteinern."}},
[14195] = {"Ein Schleier lichtet sich", nil, {"Furiens Tagebuch hat Euch angewiesen, die Ruinen des Palastes von Elune im Norden Sargeron zu suchen."}},
[14196] = {"Feuerteufel", nil, {"Ihr müsst einen tobenden Vortex erzeugen, indem Ihr 7 Anhänger von Elune mit Eurem Feuersturm vernichtet.\n\nSolltet Ihr Euren Vortexedelstein verlieren, erhaltet Ihr einen weiteren aus dem uralten Vortexrunenstein."}},
[14197] = {"Ein zu erfüllendes Pensum", nil, {"Sammelt 20 Barren Gebirgsfußeisen aus dem Tagebau am Gebirgsfuß in Azshara."}},
[14198] = {"Vortex der Vergeltung", nil, {"Setzt den Edelstein des tobenden Vortexes gegen die Ruine der Nijelspitze ein. Während ihr den Feuersturm befehligt, tötet 40 Verteidiger der Nijelspitze und verbrennt 15 Händler der Nijelspitze.\n\nSolltet Ihr Euren Vortexedelstein verlieren, erhaltet Ihr einen weiteren aus dem uralten Vortexrunenstein."}},
[14201] = {"Tausende Geschichten im Sand", nil, {"Sammelt 5 geborgene Artefakte aus den Ruinen um den Mennarsee in Azshara."}},
[14202] = {"Inspiziert das Seeufer", nil, {"Begleitet den Goblinfeldmesser zu den drei Feldmessgeräten, die um den Mennarsee herum aufgestellt sind. Verteidigt ihn, während er die Geräte abliest. Ein weiterer Feldmesser kann stets über das Feldmesssignal herbeigerufen werden."}},
[14204] = {"Aus den Schatten", nil, {"Besiegt 6 Blutfanglauerer."}},
[14209] = {"Motorschaden", nil, {"Untersucht Clubniks Planierraupe und sammelt eine Probe des Ektospritzers ein, der den Motor verklebt."}},
[14212] = {"Aufopferung", nil, {"Reitet mit Lord Darius Crowley und treibt 30 Blutfangpirscher zusammen."}},
[14213] = {"Zehn Pfund Fleisch", nil, {"Ihr müsst 10 Stücke Satyrfleisch von den zahlreichen Satyrn in Sargeron beschaffen und sie in Elunes Kohlenbecken legen."}},
[14214] = {"Nachricht für Graumähne", nil, {"Sprecht mit König Graumähne im Militärviertel."}},
[14215] = {"Die Erinnerung der Toten", nil, {"Bringt Kalythas Ring zum Ufer des Mennarsees. Berührt den Geist von Kalytha, um ihre Erinnerungen noch einmal zu erleben. Sprecht mit den anderen Geistern, bis Ihr das Geheimnis des Mennarsees gelüftet habt."}},
[14216] = {"Das Geheimnis des Sarsensteins", nil, {"Holt den Sarsenstein vom Grund des Mennarsees herauf."}},
[14217] = {"Satyrisches Opfer", nil, {"Ihr müsst 10 Stücke Satyrfleisch von den zahlreichen Satyrn in Sargeron beschaffen und sie in Elunes Kohlenbecken legen."}},
[14218] = {"Mit Blut und Asche", nil, {"Tötet 80 Blutfangpirscher mit den Kanonen der Rebellen."}},
[14219] = {"Zu den Waffen!", nil, {"Ihr sollt Euch 20 Satyrsäbel aus den Waffenregalen und von den Satyrn aus der Gegend besorgen. Dann legt sie auf die Statue von Elunes Dienstmaid."}},
[14220] = {"Dies ist das Ende", nil, nil},
[14221] = {"Niemals aufgeben, nur manchmal zurückziehen", nil, {"Sprecht mit Darius Crowley in der Kathedrale des Erwachenden Lichts."}},
[14222] = {"Das letzte Gefecht", nil, {"Tötet 8 rasende Pirscher."}},
[14223] = {"Seelenfrieden", nil, {"Sorrem in Furions Posten will, dass Ihr Kohor tötet."}},
[14225] = {"Ihr erkennt es, wenn Ihr es seht", nil, {"Sorrem in Furions Posten will, dass Ihr das uralte Beschwörungsritual in Festung Donneraxt findet."}},
[14226] = {"Ärger unter den Füßen", nil, {"Verwendet Euren persönlichen arkanen Assistenten, um 12 Legashidämonen in Insekten zu verwandeln und sie zu zerquetschen."}},
[14227] = {"Die Köpfe zusammenstecken", nil, {"Sammelt 100 gebleichte Schädel aus der Festung Donneraxt, stapelt sie auf dem Beschwörungszirkel der Brennenden Klinge auf und tötet dann Nazargen."}},
[14230] = {"Handbucharbeit", nil, {"Besorgt Euch das Handbuch des Abschwörers aus dem Lager der Legashi."}},
[14231] = {"Useless quest record", nil, {"Anforderungen:"}},
[14232] = {"Ein Satz heiße Ohren", nil, {"Bringt die Ohren von 25 Brennenden Klingen zu Sorrem in Furions Posten."}},
[14233] = {"Orcs können schreiben?", nil, {"Bringt das Feldtagebuch zu Sassy Hartzang an der Schiffbruchküste auf den Verlorenen Inseln zurück."}},
[14234] = {"Der Feind meines Feindes", nil, {"Bringt das Feldtagebuch zu Aggra im Basislager der Horde auf den Verlorenen Inseln."}},
[14235] = {"Das Kleine Horrortal", nil, {"Findet Kilag Blutmaul im Kleinen Horrortal auf den Verlorenen Inseln."}},
[14236] = {"Unkrautvernichter", nil, {"Benutzt den Unkrautvernichter Eures goblinischen Alles-in-1-zigartigen Gürtels, um 100 Pflanzen im Kleinen Horrortal niederzumähen."}},
[14237] = {"Vorrücken", nil, {"Findet Kilag Blutmaul und seine Späher an der Wilden Aussicht auf den Verlorenen Inseln."}},
[14238] = {"Infrarot = Infratot", nil, {"Tötet 10 Assassinen des SI:7.\n\nWenn Eurem orcischen Späher etwas zustößt, kehrt zu Kilag zurück und schaut, ob er einen anderen Partner für Euch hat."}},
[14239] = {"Geht nicht ins Licht!", nil, nil},
[14240] = {"Zu den Klippen", nil, {"Sprecht mit Späher Brax am Pfad entlang der Klippen nördlich der Wilden Schlucht auf den Verlorenen Inseln."}},
[14241] = {"Auf zum Gyroschrauber!", nil, {"Besorgt Euch die Gyroschrauberschlüssel."}},
[14242] = {"Wertvolle Fracht", nil, {"Findet die wertvolle Fracht unter Deck des Allianzschiffs \"Sog der Vergeltung\" am Strand der Verlorenen Inseln."}},
[14243] = {"Die Rache des Kriegshäuptlings", nil, {"Benutzt die Macht des Zyklons der Elemente, um 50 Matrosen der Allianz zu töten.\n\nSobald Ihr Eure Aufgabe erledigt habt, werdet Ihr zur Wilden Aussicht geflogen."}},
[14244] = {"Auf, auf und davon!", nil, {"Überlebt den Schuss mit der Raketenschleuder über die Bedrängnis."}},
[14245] = {"Die Stadt in der Kiste", nil, {"Klettert auf den Sprengstoffberg und benutzt den Auslöser von Instastadt."}},
[14246] = {"Zwangsadoption", nil, {"Legt 3 Sturzflieger-Eier in die Nester von Sturzfliegern und sie beschützt, bis sie eingesammelt wurden."}},
[14247] = {"Raues Land", nil, {"Thunk in Thunks Heimstatt will, dass Ihr die unnatürliche Felszunge findet."}},
[14248] = {"Hilfe gesucht", nil, {"Sprecht mit Vorarbeiter Feuchtlunte außerhalb der Kaja'mithöhle auf den Verlorenen Inseln."}},
[14249] = {"Federgewichtig", nil, {"Besorgt 80 makellose Donnerkopffedern von den Hippogryhpen in der Nähe der Ruinen von Nordressa."}},
[14250] = {"Erneuerbare Ressourcen", nil, {"Verwendet die Arkane Aufladung, um 5 Stück belebtes Basalt von Balboa in der Nähe der Ruinen von Nordressa zu erlangen."}},
[14251] = {"Noch mal von vorne", nil, {"Sammelt 15 Skorpionenstachel."}},
[14252] = {"Gutes Gold für schlechte Schwänze", nil, {"Findet 15 alte Basiliskenschwänze."}},
[14253] = {"Befiederung", nil, {"Sammelt 25 Federn von Schreckenssturzfliegern."}},
[14254] = {"Schwermetall", nil, {"Ihr sollt 7 Stück Element 116 einsammeln und sie zu Bizby in Bizbys Karawane bringen."}},
[14255] = {"Ethel Rethor", nil, {"Cerelia in Furions Posten will, dass Ihr mit Korrah in Ethel Rethor sprecht."}},
[14256] = {"Die wachsende Bedrohung", nil, {"Korrah will, dass Ihr mit ihr sprecht, wenn Ihr bereit seid, die Bedrohung durch die Naga näher zu besehen."}},
[14257] = {"Zackenkämme getötet", nil, {"Sammelt 10 Zackenkammknochen von Naga der Zackenkämme, 30 Zackenkammschuppen von Zauberinnen der Zackenkämme und 4 Zackenkammflossen von Kriegern der Zackenkämme."}},
[14258] = {"Mörsermunition", nil, {"Holt 5 Goblinmörsergranaten aus dem Geheimlaborkomplex in Azshara."}},
[14260] = {"In die Tiefe gehen", nil, {"Benutzt den Zackenkammglücksbringer"}},
[14261] = {"Eiskalt", nil, {"Verwendet den Kryomat 16, um 8 finstere Drachen einzufrieren und zu töten."}},
[14262] = {"Wie man einen Fisch ausnimmt", nil, {"Tötet 6 Sturmwute der Grollflossen und 6 Seerufer der Grollflossen."}},
[14263] = {"Fidele Kräuter", nil, {"Sammelt 8 Bündel Lebendigen Zornthymian in der Nähe der Ruinen von Nordressa."}},
[14264] = {"Nasser als Nass", nil, {"Besiegt 5 Diener von Neptulon. (Ihr müsst in Nagagestalt sein, während Ihr sie bekämpft.)\n\nSolltet Ihr Euren Dreizack der Zackenkämme verlieren, sprecht mit Valishj.\n\nWenn Ihr Euren Zackenkammglücksbringer verliert, sprecht mit Korrah in Ethel Rethor."}},
[14265] = {"Euer Lehrmeister", nil, {"Redet mit Unteroffizier Cleese im Militärviertel."}},
[14266] = {"Sturmangriff", nil, {"Sprecht mit Unteroffizier Cleese und erlernt 'Sturmangriff'. Übt anschließend 'Sturmangriff' gegen einen Blutfangworgen."}},
[14267] = {"Die Untersuchung des Meeresschreins", nil, {"Untersucht den Schrein der Naga für Torg Zweimalmer."}},
[14268] = {"Wasserzeichen", nil, {"Priesterin Valishj will, dass Ihr die uralten Neptulon-Gravuren findet."}},
[14269] = {"Jemand sucht nach Euch", nil, {"Sprecht mit Loren der Hehlerin im Militärviertel."}},
[14270] = {"Die Schlüsselsteinscherbe", nil, {"Findet die Schlüsselsteinscherbe in der Nähe."}},
[14271] = {"Berichtet Zweimalmer", nil, {"Kehrt zu Torg Zweimalmer an der Südlichen Endhaltestelle der Raketenbahn in Azshara zurück und berichtet ihm von Eurer Entdeckung."}},
[14272] = {"Ausweiden", nil, {"Sprecht mit Loren der Hehlerin und lernt 'Ausweiden'. Übt 'Ausweiden' an einem Blutfangworgen."}},
[14273] = {"Zwielichtige Gesellen", nil, {"Sprecht mit Vitus Dunkelwandler im Militärviertel."}},
[14274] = {"Feuerbrand", nil, {"Sprecht mit Vitus Dunkelwandler, um 'Feuerbrand' zu lernen. Übt 'Feuerbrand' an einem Blutfangworgen."}},
[14275] = {"Jemand ist Euch auf der Fährte", nil, {"Sprecht mit Jäger Blake im Militärviertel."}},
[14276] = {"Zuverlässiger Schuss", nil, {"Sprecht mit Jäger Blake, um 'Zuverlässiger Schuss' zu lernen. Übt 'Zuverlässiger Schuss' zweimal gegen einen Blutfangworgen."}},
[14277] = {"Arkane Anfragen", nil, {"Sprecht mit Myriam Zauberwache im Militärviertel."}},
[14278] = {"Sucht die Schwester", nil, {"Sprecht mit Schwester Almyra im Militärviertel."}},
[14279] = {"Blitzheilung", nil, {"Sprecht mit Schwester Almyra, um 'Blitzheilung' zu lernen. Übt 'Blitzheilung' an zwei verwundeten Wachen."}},
[14280] = {"Die Winde kennen Euren Namen… anscheinend", nil, {"Sprecht mit Celestine Erntedank im Militärviertel."}},
[14281] = {"Arkane Geschosse", nil, {"Sprecht mit Miriam Zauberwache und lernt 'Arkane Geschosse'. Übt 'Arkane Geschosse' an einem Blutfangworgen."}},
[14282] = {"Rätsel gelöst", nil, {"Ihr sollt die uralten Neptulon-Gravuren zu Korrah, Forscher des Cenarius bringen."}},
[14283] = {"Verjüngende Berührung", nil, {"Sprecht mit Celestine Erntedank, um 'Verjüngung' zu lernen. Übt 'Verjüngung' an einer verwundeten Wache."}},
[14284] = {"Die Rache eines Klagegeistes", nil, {"Hydronis will, dass Ihr ihn besteigt und die Priesterin Valishj besiegt."}},
[14285] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14286] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14287] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14288] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14289] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14290] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14291] = {"Je mehr, desto sicherer", nil, {"Sprecht mit König Genn Graumähne im Militärviertel."}},
[14292] = {"Der Feind unserer Feinde", nil, {"Korrah, Forscher des Cenarius, will, dass Ihr mit Lord Hydronis sprecht."}},
[14293] = {"Rettet Krennan Aranas", nil, {"Rettet Krennan Aranas."}},
[14294] = {"Neu formieren", nil, {"Sprecht mit König Genn Graumähne im Graumähnenhof in Gilneas."}},
[14295] = {"Schwestern des Meeres", nil, {"Tötet Lady Silisthra und Lady Vesthra, indem Ihr ihre Kraftsteine deaktiviert."}},
[14296] = {"Stolpert nicht", nil, {"Empfangt 6 Energieladungen von den Energieleitungen, während ihr zum Gipfel der Arkanen Spitze gelangt."}},
[14297] = {"Freiheitskämpfer", nil, {"Verwendet die schmiedeeisernen Schlüssel der Kultisten und der Drachenkin, um 4 Arbeiter der Goblins auf dem Säbelrücken zu befreien."}},
[14299] = {"Xylems Asyl", nil, {"Nehmt das Portal zum Turm des Erzmagiers Xylem und sucht im Turm nach Joanna."}},
[14300] = {"Die Prüfung des Feuers", nil, {"Besteht die Prüfung des Feuers, indem Ihr in den Feuerrunen steht und 10 aufeinanderfolgende Explosionen übersteht, ohne getroffen zu werden."}},
[14301] = {"Kehrt zurück und berichtet", nil, {"Kehrt zu Korrah, Forscherin des Cenarius in Ethel Rethor zurück."}},
[14302] = {"Offizielle Einschätzung", nil, {"Korrah, Forscherin des Cenarius, will, dass Ihr Korrahs Bericht zu Karnum in Karnums Hain bringt."}},
[14303] = {"Zurück zu Aggra", nil, {"Informiert Aggra im Basislager der Horde auf den Verlorenen Inseln über die Geschehnisse."}},
[14304] = {"Blutabnahme", nil, {"Sammelt 10 blutgefüllte Egel."}},
[14305] = {"Erntezeit", nil, {"Sammelt 10 Aloedisteln."}},
[14306] = {"Unkraut jäten", nil, {"Tötet 7 entwurzelte Peitscher."}},
[14307] = {"Steife Brise", nil, {"Zerstört 5 Wirbelsturmgänger."}},
[14308] = {"Angriff der Wissenschaft", nil, {"Verwendet den Feuerliminator X-21, um 8 Feuer zu löschen und rettet 6 Laborpraktikanten aus dem Inferno im Geheimlabor.\n\nSprecht mit Assistentin Greely an der Südlichen Endhaltestelle der Raketenbahn, solltet Ihr Euren Feuerliminator verlieren."}},
[14309] = {"Der Kodoflüsterer", nil, {"Benutzt das Kodobesänftigungsmittel in der Nähe von 5 wütenden Kodos."}},
[14310] = {"Schutzverletzung: Kernabkopplung", nil, {"Aktiviert das Reaktorkontrollpult und eskortiert den Azsharitkern zurück zur Südlichen Endhaltestelle der Raketenbahn. Wendet den Feuerliminator X-21 auf den Kern an, damit er sich nicht überhitzt."}},
[14311] = {"Wer Ernten will muss säen", nil, {"Pflanzt 5 Samen des Cenarius in der Nähe der fruchtbaren Erdhügel in der Cenariuswildnis."}},
[14312] = {"Vorstellung beim Khan", nil, {"Garren Finsterwind auf Karnums Lichtung will, dass Ihr mit Khan Leh'prah sprecht."}},
[14313] = {"Wieder unter Menschen", nil, {"Sprecht mit Krennan Aranas in Dämmerhafen."}},
[14314] = {"Ausbruch!", nil, {"Tötet den Wächter der Magram im Dorf der Magram."}},
[14316] = {"Cenarisches Eigentum", nil, {"Sammelt 7 Vorratskisten des Cenarius in den Ländern der Magram."}},
[14318] = {"Feinfühlige Verhandlungen", nil, {"Khan Leh'prah will, dass Ihr den Speer des Khans der Kolkar in den Vaterstein treibt und ihn vor den Magram verteidigt, bis Khan Kammah erscheint.\n\nSprecht mit Khan Leh'prah, sobald Ihr bereit seid, zum Schlachtfeld zu reiten."}},
[14320] = {"Benötigte Zutaten", nil, {"Findet die Kiste mit Alraunenessenz."}},
[14321] = {"Invasion", nil, {"Sprecht mit Gwen Armstead in Dämmerhafen."}},
[14322] = {"Böse Wissenschaft! Pfui!", nil, {"Tötet 8 statisch aufgeladene Hippogryphen."}},
[14323] = {"Supersaugstark", nil, {"Saugt 20 köchelnde Wassertropfen der üblen Platscher im Osten der Ruinen von Arkkoran auf."}},
[14324] = {"Voll heißen Wassers", nil, {"Erlangt eine Kugel voll kochendem Wasser von einem brühenden Wasserlord am Turm von Eldara."}},
[14325] = {"Arbeite für Essen", nil, {"Sammelt 5 Basiliskenflanken."}},
[14326] = {"Aufs Oberdeck", nil, {"Begebt Euch auf das obere Deck der Sog der Vergeltung an der Küste der Verlorenen Inseln und sprecht mit Thrall."}},
[14327] = {"Versprochen ist versprochen", nil, {"Khan Kammah will, dass Ihr mit Khan Leh'prah sprecht."}},
[14328] = {"Drei Prinzen", nil, {"Tötet Rhoho, Sherik, und Jorreth."}},
[14329] = {"Nicht so schnell!", nil, {"Khan Shodo will, dass Ihr 10 Raufer der Maraudine im Tal der Speere tötet.\n\nIhr müsst ihre Leichen nach Hinweisen über den Verbleib von Kherrah durchsuchen."}},
[14330] = {"Hinter verschlossenen Türen", nil, {"Ihr müsst Kherrah finden, die in Maraudon gefangen gehalten wird."}},
[14332] = {"Zeit für eine Veränderung", nil, {"Kherrah will, dass Ihr mit Khan Leh'prah sprecht."}},
[14333] = {"Wo Ihr schon mal hier seid", nil, {"Ihr sollt das Tal der Speere nach 6 entweihten Relikten absuchen."}},
[14334] = {"Blubberwampe", nil, {"Mai'Lahii will, dass Ihr Blubberwampe tötet."}},
[14335] = {"Hummerplage", nil, {"Tötet 5 Kaltschnappkriecher und 5 Kaltschnappzangen."}},
[14336] = {"Töten oder getötet werden", nil, {"Sprecht mit Prinz Liam Graumähne außerhalb von Dämmerhafen."}},
[14337] = {"Schattenflucht", nil, {"Karnum Marschenwirker will, dass Ihr mit Mai'Lahii sprecht."}},
[14338] = {"Der Geistwandlerposten", nil, {"Karnum Marschenwirker will, dass Ihr mit Maurin Knochenspalter sprecht."}},
[14339] = {"Lieferungsgerät", nil, {"Maurin Knochenspalter will, dass Ihr 3 hohle Kodohörner von sterbenden Kodos sammelt."}},
[14340] = {"Schicker Fummel", nil, {"Setzt Euren neuen Zaubererhut auf und dann sprecht mit dem Abbild von Erzmagier Xylem nordöstlich des Nördlichen Umschlagplatzes der Raketenbahn."}},
[14341] = {"Smeeds Harnische", nil, {"Nataka im Geistwandlerposten will, dass Ihr Smeeds Harnische an Smeed ausliefert."}},
[14342] = {"Höllische Übergriffe", nil, {"Felgur Zweischneid will, dass Ihr 10 niedere Höllenbestien vernichtet."}},
[14343] = {"Maurins Gebräu", nil, {"Maurin will, dass Ihr Maurins Gebräu zu Karnum in Karnums Hain bringt."}},
[14344] = {"Jugkars Verderben", nil, {"Ihr seid dazu auserkoren, den höllischen Kern der Macht zu verwenden, um an Jugkars Altar die Herrschaft über Lord Azrethoc zu übernehmen und mit dessen Kräften Jugkar Grimm'rod zu vernichten."}},
[14345] = {"Waschtag", nil, {"Sprecht mit Sorata Feuerspinner am Nördlichen Umschlagplatz der Raketenbahn."}},
[14346] = {"Säubert unseren Riss", nil, {"Karnum Marschenwirker will, dass Ihr Maurins Gebräu verwendet, um 10 Eindringlinge der Zackenkämme, 10 Sirenen der Zackenkämme und 3 Meeresriesen zu töten."}},
[14347] = {"Bleibt standhaft", nil, {"Tötet 10 Eindringlinge der Verlassenen."}},
[14348] = {"Ihr schafft sie nicht alleine", nil, {"Jagt mit den Schwarzpulverfässern 4 entsetzliche Monstrositäten in die Luft."}},
