[1787] = {"Der Foliant der Offenbarung",nil, {"Beschafft eine Schrift der Defias von den abtrünnigen Hexern der Defias in Elwynn und kehrt zu Gazin Tenorm in Sturmwind zurück."}},
[1788] = {"Der Foliant der Offenbarung",nil, {"Sprecht mit Duthorian Rall in Sturmwind."}},
[1789] = {"Das Symbol des Lebens",nil, {"Solltet Ihr es nicht schaffen, Narm zu helfen, nachdem Ihr das Symbol des Lebens benutzt habt, <Name>, dann kommt zu mir zurück und ich werde Euch ein weiteres geben.nnEs ist sowohl für meinen Ehemann als auch für mich wichtig, dass Ihr erfolgreich zurückkehrt. Bitte sputet Euch."}},
[1790] = {"Das Symbol des Lebens",nil, {"Sputet Euch, <Name>, mehr als ein Leben steht auf dem Spiel, wenn Ihr scheitert.nnWenn Ihr Gazin und Henze aus irgendeinem Grund nicht helfen könnt, dann kommt zu mir zurück und ich gebe Euch ein weiteres Symbol des Lebens für Eure Reisen.nnVersteht meine Unterstützung nicht als reine Wohltätigkeit. Ihr müsst Euch wie jeder andere <Klasse> beweisen, und wenn Ihr ständig scheitert, fällt das später auf Euch zurück."}},
[1791] = {"Der Windbehüter",nil, {"Sprecht mit Bath'rah dem Windbehüter."}},
[1792] = {"Wirbelwaffe",nil, {"Ich habe das Wirbelherz und bin bereit, es in ein Gefäß Eurer Wahl zu legen.nnWählt eine Waffe, <Name>, und wisset, dass ihre Macht die Macht des Cyclonian ist."}},
[1793] = {"Der Foliant der Ehre",nil, {"Es steht fest, dass das Licht mit Euch ist, <Name>, doch da die Zeiten zunehmend gefährlicher werden, ist es wichtig zu wissen, welche Kräfte dem Fleische innewohnen. Wenn Dämonen, Großdrachen oder ein anderes Übel einen Fuß in unser Land setzen, wird Eure Tapferkeit auf die Probe gestellt. Werdet Ihr stark genug sein?nnTapferkeit in jedweder Situation gehört ebenso zu einem <Klasse> wie das Licht, seine Rüstung und seine Waffe. Die Schwachen beschützen und den Glauben nicht verlieren, sind zwei weitere Lektionen, die Ihr meistern müsst, wenn Ihr in der Kathedrale des Lichts studiert."}},
[1794] = {"Der Foliant der Ehre",nil, {"Es steht fest, dass das Licht mit Euch ist, <Name>, doch da die Zeiten zunehmend gefährlicher werden, ist es wichtig zu wissen, welche Kräfte dem Fleische innewohnen. Wenn Dämonen, Großdrachen oder ein anderes Übel einen Fuß in unser Land setzen, wird Eure Tapferkeit auf die Probe gestellt. Werdet Ihr stark genug sein?nnTapferkeit in jedweder Situation gehört ebenso zu einem <Klasse> wie das Licht, seine Rüstung und seine Waffe. Die Schwachen beschützen und den Glauben nicht verlieren, sind zwei weitere Lektionen, die Ihr meistern müsst, wenn Ihr in der Kathedrale des Lichts studiert."}},
[1795] = {"Die Bindung",nil, {"Beschwört mit Hilfe des 'Folianten der Kabale' einen Teufelsjäger, unterwerft ihn und bringt den 'Folianten der Kabale' zu Strahad Farsan in Ratschet zurück."}},
[1796] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt die Roben von Arcana zu Menara Leerbringer im Brachland."}},
[1798] = {"Die Suche nach Strahad",nil, {"Sprecht mit Strahad Farsan im Brachland."}},
[1799] = {"Fragmente der Kugel von Orahil",nil, {"Sprecht mit Menaras Akolythen im Inneren des Turms über Ratschet und wählt einen ihrer Wege, um ihm zu folgen.nnBringt danach eine Höllenkugel zu Tabetha in den Düstermarschen."}},
[1800] = {"Der Thronsaal von Lordaeron",nil, {"Besucht mit Eurem Schützling den alten Thronsaal in Lordaeron. Er befindet sich kurz vor dem Abgang nach Unterstadt."}},
[1801] = {"Foliant der Kabale",nil, {"Sprecht mit Jorah Annison in Unterstadt."}},
[1802] = {"Foliant der Kabale",nil, {"Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Krom Starkarm in Eisenschmiede."}},
[1803] = {"Foliant der Kabale",nil, {"Beschafft den schimmeligen Folianten und das zerlumpte Manuskript für Jorah Annison in Unterstadt."}},
[1804] = {"Foliant der Kabale",nil, {"Bringt den rekonstruierten Folianten und 3 Kanalisierungsruten zu Strahad Farsan in Ratschet."}},
[1805] = {"Foliant der Kabale",nil, {"Bringt den rekonstruierten Folianten und 3 Ruten des Kanalisierens zu Strahad Farsan in Ratschet."}},
[1806] = {"Die Prüfung der Rechtschaffenheit",nil, {"Wartet, bis Jordan Stillbrunn eine Waffe für Euch geschmiedet hat."}},
[1818] = {"Gespräch mit Dillinger",nil, {"Sprecht mit Todeswache Dillinger in Brill."}},
[1819] = {"Ulag der Spalter",nil, {"Tötet Ulag den Spalter und sprecht dann mit Todeswache Dillinger."}},
[1820] = {"Gespräch mit Coleman",nil, {"Sprecht mit Coleman Heller."}},
[1821] = {"Agamanderbstücke",nil, {"Bringt Coleman Heller die Axt, das Schwert, den Streitkolben und den Dolch der Familie Agamand."}},
[1822] = {"Erbstückwaffe",nil, {"Ich behalte drei der Waffen, die Ihr mir gebracht habt, als Andenken an meinen Schmerz und Verlust. Aber bitte, <Name>, wählt Euer Lieblingserbstück und behaltet es für Euch.nnMöge es Euch besser dienen als seinen verfluchten vorherigen Besitzern."}},
[1823] = {"Gespräch mit Ruga",nil, {"Sprecht mit Ruga Rachtotem."}},
[1824] = {"Prüfung auf dem Feld der Riesen",nil, {"Bringt 5 zuckende Fühler zu Ruga Rachtotem im Camp Taurajo."}},
[1825] = {"Gespräch mit Thun'grim",nil, {"Sprecht mit Thun'grim Brandblick im Brachland."}},
[1838] = {"Brutale Rüstung",nil, {"Bringt Thun'grim Brandblick 15 rauchige Eisenblöcke, 10 pulverisierte Azurite, 10 Eisenbarren und 1 Phiole mit Phlogiston."}},
[1839] = {"Ula'elek und die brutalen Stulpen",nil, {"Sprecht mit Ula'elek in Durotar."}},
[1840] = {"Orm Steinhuf und der brutale Helm",nil, {"Sprecht mit Orm Steinhuf in Donnerfels."}},
[1841] = {"Velora Nachtens und die brutalen Beinschützer",nil, {"Sprecht in Unterstadt mit Velora Nachtens."}},
[1842] = {"Satyrhufe",nil, {"Bringt 7 ungespaltene Satyrhufe zu Ula'elek in Sen'jin in Durotar."}},
[1843] = {"Brutale Stulpen",nil, {"Danke, <Name>. Diese Stulpen sind meine beste Arbeit. Nehmt sie. Wenn Ihr sie tragt, bringt es mir und meinen Vorfahren Ehre."}},
[1844] = {"Schimärenhorn",nil, {"Bringt ein galvanisiertes Horn zu Orm Steinhuf in Donnerfels."}},
[1845] = {"Brutaler Helm",nil, {"Ich gebe Euch diesen Helm.nnGroße Taten brachten Euch zu mir, <Name>, und wenn es der Erdenmutter gefällt... liegen noch größere vor Euch."}},
[1846] = {"Schienbeinknochen der Drachenmalorcs",nil, {"Bringt 8 stabile Schienbeinknochen eines Drachenmalorcs zu Velora Nachtens in Unterstadt."}},
[1847] = {"Brutale Beinschützer",nil, {"Hier sind Eure Beinschützer, <Name>. Lasst mich wissen, ob sie passen oder geändert werden müssen. Ich möchte zufriedene Kunden! Zumindest früher wollte ich das.nnHeutzutage liegt mir nicht mehr viel an meinem Handwerk. Aber immer noch besser, als eine hirnlose Dienerin der Geißel zu sein!nnDenke ich."}},
[1848] = {"Brutale Halsberge",nil, {"Ich bin sicher, Ihr habt bei dieser Aufgabe viele Unannehmlichkeiten erduldet und viel Blut vergossen, <Name>. Hier ist das Produkt Eurer Mühsal.nnMöge es Euch gute Dienste leisten und möget Ihr eines Tages wiederkommen und erzählen, wie nützlich sie Euch waren."}},
[1858] = {"Die Zerschmetterte Hand",nil, {"Stehlt Tazans Schlüssel, öffnet seinen Ranzen damit und bringt den Inhalt zu Therzok in der Kluft der Schatten in Orgrimmar."}},
[1859] = {"Therzok",nil, {"Sprecht mit Therzok in der Kluft der Schatten in Orgrimmar."}},
[1860] = {"Gespräch mit Jennea",nil, {"Sprecht in Sturmwind mit Jennea Haubitz."}},
[1861] = {"Spiegelsee",nil, {"Bringt eine Probe des Spiegelseewassers zu Jennea Haubitz in Sturmwind."}},
[1878] = {"Wasserbeutelkopfgeld",nil, {"Ihr seid ja schon wieder da, <Name>! Wenn Ihr noch fünf Wasserbeutel eines Wüstenläufers für mich habt, dann habe ich hier ein kleines Paket, das nur auf Euch wartet!"}},
[1879] = {"Gespräch mit Bink",nil, {"Sprecht mit Bink in Eisenschmiede."}},
[1880] = {"Magitastischer Gizmonitor",nil, {"Bringt Bink ihren magitastischen Gizmonitor."}},
[1881] = {"Gespräch mit Anastasia",nil, {"Sprecht mit Anastasia Hirschbrunn."}},
[1882] = {"Balnirs Bauernhof",nil, {"Bringt Balnirlöwenmäulchen zu Anastasia Hirschbrunn im Magierviertel von Unterstadt."}},
[1883] = {"Gespräch mit Un'thuwa",nil, {"Sprecht mit Un'thuwa."}},
[1884] = {"Jujuhaufen",nil, {"Zerstört 4 Jujuhaufen und kehrt dann zu Un'thuwa in Sen'jin zurück."}},
[1885] = {"Mennet Carkad",nil, {"Sprecht mit Mennet Carkad im Schurkenviertel von Unterstadt."}},
[1886] = {"Die Todespirscher",nil, {"Beschafft Astors Vorstellungsschreiben und bringt es zu Mennet Carkad im Schurkenviertel."}},
[1898] = {"Die Todespirscher",nil, {"Sprecht mit Andron Gant in Unterstadt."}},
[1899] = {"Die Todespirscher",nil, {"Bringt Androns Geschäftsbuch zu Mennet Carkad im Schurkenviertel von Unterstadt."}},
[1918] = {"Das beschmutzte Element",nil, {"Bringt die beschmutzte Wasserkugel zu Mastok Wutfauch im Splitterholzposten im Eschental."}},
[1919] = {"Meldung bei Jennea",nil, {"Sprecht in Sturmwind mit Jennea Haubitz."}},
[1920] = {"Untersuchungen im Blauen Eremiten",nil, {"Nehmt Euch die Inkantation der Manifestation und die Truhe mit Verwahrungskästen vom Tisch, hinter Jennea Haubitz. Bringt 3 gefüllte Verwahrungskästen zu Jennea im Magiersanktum."}},
[1921] = {"Materialsammlung",nil, {"Bringt 10 Leinenstoff und 6 aufgeladene Rissedelsteine zu Wynne Larson in Sturmwind."}},
[1938] = {"Urs Abhandlung über Schattenmagie",nil, {"Bringt 'Urs Abhandlung über Schattenmagie' zu Zaubermeister Andromath in Sturmwind."}},
[1939] = {"Zaubermeister Andromath",nil, {"Sprecht mit Zaubermeister Andromath."}},
[1940] = {"Makellose Spinnenseide",nil, {"Bringt 8 Rollen makelloser Spinnenseide zu Wynne Larson in Sturmwind."}},
[1941] = {"Managewirkte Robe",nil, {"Hier ist Eure Robe. Er ist eine meiner besten bisher! Tragt sie voller Stolz, und wenn die Leute Euch fragen... sagt ihnen, Ihr habt sie vom Konfektionsgeschäft Larson."}},
[1942] = {"Astralknotengewand",nil, {"Euer Gewand ist fertig! Die Seide, die Ihr mir gebracht hattet, war so hübsch... es stört Euch doch nicht, dass ich es etwas kürzer gemacht habe, damit noch etwas Seide für mich übrig bleibt, oder?nnDas war nur ein Witz. Das Gewand wird sitzen wie angegossen und es eignet sich gleichermaßen gut, um damit in Abenteuer zu ziehen oder zu einem Empfang zu gehen. Viel Spaß damit!"}},
[1943] = {"Gespräch mit Deino",nil, {"Sprecht mit Deino."}},
[1944] = {"Wasser von Xavian",nil, {"Bringt die Wasserprobe von Xavian zu Deino in Orgrimmar."}},
[1945] = {"Lachende Schwestern",nil, {"Bringt 12 Haarlocken der lachenden Schwestern zu Kil'hala beim Wegekreuz."}},
[1946] = {"Netherspitzengewand",nil, {"Hier habt Ihr Euer Gewand aus Netherspitze, <Name>. Es ist bequem und strapazierfähig und enthält die Magie des Haars der lachenden Schwestern.nnViel Spaß damit, und wenn Euch das Gewand zusagt, dann lasst es doch bitte auch Deino wissen. Sie ist eine Magierin, mit der ich es mir nicht verderben möchte ..."}},
[1947] = {"Reise in die Marschen",nil, {"Sprecht mit Tabetha."}},
[1948] = {"Gegenstände der Macht",nil, {"Bringt 1 Stück Jade und den vom Blitz aufgeladenen Dornbusch zu Tabetha in den Düstermarschen."}},
[1949] = {"Verborgene Geheimnisse",nil, {"Sprecht mit Magus Tirth in der schimmernden Ebene."}},
[1950] = {"Der Knüller schlechthin",nil, {"Bringt den Satz für Tirths Geldkassette in Erfahrung und kehrt zu Tirth zurück."}},
[1951] = {"Rituale der Macht",nil, {"Bringt das Buch 'Rituale der Macht' zu Tabetha in den Düstermarschen."}},
[1952] = {"Der Zauberstab des Magiers",nil, {"Sprecht nach ihrem Ritual mit Tabetha."}},
[1953] = {"Rückkehr in die Marschen",nil, {"Sprecht mit Tabetha."}},
[1954] = {"Die Höllenkugel",nil, {"Bringt eine Höllenkugel zu Tabetha in den Düstermarschen."}},
[1955] = {"Die Austreibung",nil, {"Tötet den Dämon der Kugel und sprecht dann mit Tabetha."}},
[1956] = {"Macht in Uldaman",nil, {"Beschafft Euch eine Obsidiankraftquelle und bringt sie in die Düstermarschen zu Tabetha."}},
[1957] = {"Manawogen",nil, {"Vernichtet 12 Manawogen innerhalb des Zeitlimits."}},
[1958] = {"Himmlische Macht",nil, {"Ihr solltet stolz auf Euch sein, <Name>. Viele <Klasse> fielen den Kreaturen zum Opfer, denen Ihr getrotzt habt.nnDie Himmelskugel ist fertig. Wenn Ihr möchtet, kann ich sie oben an einem Stab anbringen. Es liegt bei Euch. Mir persönlich wäre die Kugel allein ja lieber, aber wer bin ich, mir ein Urteil zu erlauben?"}},
[1959] = {"Meldung bei Anastasia",nil, {"Sprecht mit Anastasia Hirschbrunn."}},
[1960] = {"Untersuchungen im Alchimistenladen",nil, {"Besorgt eine Inkantation der Manifestation und eine Truhe mit Verwahrungskästen von Anastasia Hirschbrunn. Bringt 3 gefüllte Verwahrungskästen, die Truhe mit Verwahrungskästen sowie die Inkantation der Manifestation zu Anastasia in Unterstadt."}},
[1961] = {"Materialsammlung",nil, {"Bringt 10 Leinenstoff und 6 Manaedelsteine aus Dalaran zu Josef Gregorian."}},
[1962] = {"Roben des Zauberfeuers",nil, {"Hier sind Eure Roben, <Name>. Tragt sie mit Bedacht."}},
[1963] = {"Die Zerschmetterte Hand",nil, {"Tötet Tazan und bringt seinen Ranzen zu Therzok in der Kluft der Schatten in Orgrimmar."}},
[1978] = {"Die Todespirscher",nil, {"Bringt Androns Geschäftsbuch zu Varimathras in der Halle der dunklen Fürstin in Unterstadt."}},
[1998] = {"Fenwick Thatros",nil, {"Tötet Fenwick Thatros und bringt seinen Kopf zu Mennet Carkad im Schurkenviertel von Unterstadt."}},
[1999] = {"Werkzeuge des Handwerks",nil, {"Sucht den Statusbericht von Dalaran und bringt ihn zu Mennet Carkad im Schurkenviertel von Unterstadt."}},
[2000] = {"Rokar Klingenschatten",nil, {"Sprecht mit Rokar Klingenschatten in Durotar."}},
[2018] = {"Rokars Test",nil, {"Tötet Vorarbeiter Glibby und bringt seine Pfeife zu Rokar Klingenschatten in Durotar."}},
[2019] = {"Werkzeuge des Handwerks",nil, {"Kehrt zu Therzok in der Kluft der Schatten in Orgrimmar zurück."}},
[2038] = {"Bingles' verschwundene Werkzeuge",nil, {"Sucht Bingles' Werkzeuge und bringt sie ihm zurück. Es sind:nnBingles' Schraubenschlüssel, Bingles' Schraubendreher, Bingles' Hammer und Bingles' Superspreng."}},
[2039] = {"Sucht Bingles",nil, {"Sucht Bingles Blastenheimer am Loch Modan."}},
[2040] = {"Unterirdischer Angriff",nil, {"Holt das Gnoamsprenkelspross aus den Todesminen und bringt es Shoni der Schtillen in Sturmwind."}},
[2041] = {"Sprecht mit Shoni",nil, {"Sprecht mit Shoni der Schtillen in Sturmwind."}},
[2078] = {"Gyromasts Rache",nil, {"Sucht Gelkaks Ersten Maat, den Dreschonator 4100, und führt ihn zu Gelkak zurück."}},
[2098] = {"Gyromasts Rettung",nil, {"Sucht die drei Stücke von Gelkaks Schlüssel und bringt sie Gelkak Gyromast."}},
[2118] = {"Verseuchtes Land",nil, {"Fangt einen lebenden tollwütigen Distelbären und bringt ihn zu Tharnariun.nnSolltet Ihr keinen tollwütigen Distelbären fangen können und Eure Falle verlieren, dann kehrt zu Tharnariun Baumwahrer zurück und bittet um eine neue Falle."}},
[2138] = {"Ausrottung der Infizierten",nil, {"Befreit den Wald von 20 tollwütigen Distelbären und kehrt dann zu Tharnariun Baumwahrer in Dunkelküste zurück."}},
[2139] = {"Tharnariuns Hoffnung",nil, {"Sucht und tötet die Höhlenmutter."}},
[2158] = {"Ruhe und Entspannung",nil, {"Sprecht im Gasthaus Zur Höhle des Löwen mit Gastwirt Farley."}},
[2159] = {"Lieferung für Dolanaar",nil, {"Bringt die Lieferung für Dolanaar zu Gastwirt Keldamyr."}},
[2160] = {"Vorräte für Tannok",nil, {"Bringt die Kiste mit Gasthausvorräten zu Tannok Frosthammer in Kharanos."}},
[2161] = {"Die Bürde eines Peons",nil, {"Bringt Ukors Bürde zu Gastwirt Grosk in Klingenhügel."}},
[2178] = {"Schreitereintopf",nil, {"Bringt 5 Stücke Schreiterfleisch zu Alanndarian Nachtweise in Auberdine."}},
[2198] = {"Die zerrissene Halskette",nil, {"Sucht nach dem Erschaffer der zerrissenen Halskette, um etwas über ihren möglichen Wert zu erfahren."}},
[2199] = {"Lehren haben ihren Preis",nil, {"Bringt fünf Silberbarren zu Talvash del Kissel in Eisenschmiede."}},
[2200] = {"Rückkehr nach Uldaman",nil, {"Sucht in Uldaman nach Hinweisen auf den momentanen Zustand von Talvashs Halskette. Der getötete Paladin, den Talvash erwähnte, hatte die Kette zuletzt."}},
[2201] = {"Suche nach den Edelsteinen",nil, {"Findet den Rubin, den Saphir und den Topas, die in ganz Uldaman verstreut sind. Wenn Ihr sie habt, wendet Euch aus der Ferne an Talvash del Kissel, indem Ihr die Wahrsagephiole nutzt, die er Euch zuvor gegeben hat.nnAus dem Tagebuch, wisst Ihr...n* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.n* Der Topas steckt in einer Urne in einem der Trogggebiete, in der Nähe einiger Zwerge der Allianz.nn* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen."}},
[2202] = {"Reagenzsuche in Uldaman",nil, {"Bringt 12 magenta Funguskappen nach Kargath zu Jarkal Moosblut."}},
[2203] = {"Reagenziensuche im Ödland II",nil, {"Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Jarkal Moosblut in Kargath."}},
[2204] = {"Restaurierung der Halskette",nil, {"Besorgt Euch eine Kraftquelle vom mächtigsten Konstrukt, das Ihr in Uldaman finden könnt, und liefert sie bei Talvash del Kissel in Eisenschmiede ab."}},
[2205] = {"Aufsuchen des SI: 7",nil, {"Liefert Keryn Sylvius' Päckchen an Meister Mathias Shaw in der Kaserne von Sturmwind."}},
[2206] = {"Schneller Eingriff",nil, {"Sucht den Dockmeister der Defias und beschafft den Lieferzeitplan für Meister Mathias Shaw."}},
[2218] = {"Straße der Rettung",nil, {"Sprecht mit Hulfdan Schwarzbart in Eisenschmiede."}},
[2238] = {"Einfache List",nil, {"Sucht Onin MacHammar bei Gnomeregan."}},
[2239] = {"Onins Bericht",nil, {"Bringt Onins Bericht zu Hulfdan in Eisenschmiede."}},
[2240] = {"Die geheime Kammer",nil, {"Lest Baelogs Tagebuch, erforscht die geheime Kammer und erstattet dann Ausgrabungsleiter Sturmlanze Bericht."}},
[2241] = {"Der Apfel fällt",nil, {"Bringt Jannoks Rose zu Syurna in Darnassus."}},
[2242] = {"Das Schicksal ruft",nil, {"Sucht Sethir den Uralten und bringt alle Hinweise, die Ihr finden könnt, zu Syurna."}},
[2258] = {"Reagenziensuche im Ödland",nil, {"Bringt Jarkal Moosblut in Kargath im Ödland 5 Bussardmuskelmägen, 10 Klippenkojotenfangzähne und 5 Felselementarsplitter."}},
[2259] = {"Erion Schattenflüsterer",nil, {"Kontaktiert Erion Schattenflüsterer in Darnassus."}},
[2260] = {"Erions Befehl",nil, {"Reist zur Stadt Sturmwind und meldet Euch bei Renzik \"Das Messer\"."}},
[2278] = {"Die Platinscheiben",nil, {"Sprecht mit dem Steinbehüter und findet heraus, welche uralten Lehren er aufbewahrt. Sobald Ihr alles erfahren habt, was er weiß, aktiviert die Scheiben von Norgannon."}},
[2279] = {"Die Platinscheiben",nil, {"Bringt die Miniaturausgabe der Scheiben von Norgannon zur Forscherliga nach Eisenschmiede."}},
[2280] = {"Die Platinscheiben",nil, {"Bringt die Miniaturausgabe der Scheiben von Norgannon zu einem der Weisen von Donnerfels."}},
[2281] = {"Rendezvous im Rotkammgebirge",nil, {"Geht nach Seenhain im Rotkammgebirge und sprecht mit Lucius."}},
[2282] = {"Althers Mühle",nil, {"Öffnet Lucius' Schließkassette, nehmt die Marke der Diebeskunst heraus und bringt sie zu Lucius nach Seenhain."}},
[2283] = {"Wiederbeschaffung der Halskette",nil, {"Sucht in der Grabungsstätte von Uldaman nach einer wertvollen Halskette und bringt sie nach Orgrimmar zu Dran Droffers. Die Halskette ist vielleicht beschädigt."}},
[2284] = {"Wiederbeschaffung der Halskette, Teil 2",nil, {"Sucht in den Tiefen von Uldaman nach einem Hinweis auf den Verbleib der Edelsteine."}},
[2298] = {"Königliche Erpressung",nil, {"Reist nach Sturmwind und sucht Renzik \"Das Messer\" auf."}},
[2299] = {"Auf Hulfdan!",nil, {"Reist nach Eisenschmiede und sprecht mit Hulfdan Schwarzbart."}},
[2300] = {"Der SI:7",nil, {"Reist nach Sturmwind und sucht Renzik \"Das Messer\" auf."}},
[2318] = {"Übersetzung des Tagebuchs",nil, {"Sucht jemanden, der das Tagebuch des Paladins übersetzen kann. Der nächstgelegene Ort, wo Ihr so jemanden finden könntet, ist Kargath im Ödland."}},
[2338] = {"Übersetzung des Tagebuchs",nil, {"Leiht Jarkal die Halskette. Im Gegenzug wird er das Tagebuch für Euch übersetzen."}},
[2339] = {"Findet die Edelsteine und die Kraftquelle",nil, {"Beschafft in Uldaman alle drei Edelsteine sowie eine Kraftquelle für die Halskette und bringt sie anschließend zu Jarkal Moosblut nach Kargath. Jarkal glaubt, dass sich eine Kraftquelle vielleicht im stärksten Konstrukt in Uldaman findet.nnAus dem Tagebuch, wisst Ihr...n* Der Rubin wurde in einer Bastion der Zwerge der Schattenschmiede versteckt.n* Der Topas steckt in einer Urne in einem der Trogggebiete, in der Nähe einiger Zwerge der Allianz.n* Der Saphir wurde von Grimlok, dem Anführer der Troggs, mitgenommen."}},
[2340] = {"Ablieferung der Edelsteine",nil, {"Liefert die Halskette und die geborgenen Edelsteine bei Dran Droffers in Orgrimmar ab."}},
[2341] = {"Wiederbeschaffung der Halskette, Teil 3",nil, {"Besucht Jarkal Moosblut in Kargath, um festzustellen, ob er Erfolg gehabt hat."}},
[2342] = {"Wiederbeschaffte Schätze",nil, {"Holt Patrick Garretts Familienschatz aus der Truhe der Familie in der südlichen Bankenhalle von Uldaman und bringt diesen zu ihm nach Unterstadt."}},
[2358] = {"Hörner von Nez'ra",nil, {"Bringt die Hörner von Nez'ra zu Arantir nach Seenhain."}},
[2359] = {"Klavens Turm",nil, {"Stehlt den Turmschlüssel der Defias, brecht in den Turm der Defias ein und stellt fest, was sich in der Dämmerwaldtruhe befindet. Bringt Mathias Shaw in Sturmwind dann alle Informationen über das, was Ihr herausgefunden habt. Lest Kearnens Tagebuch, um neue Erkenntnisse über den Turm zu erlangen."}},
[2360] = {"Mathias und die Defias",nil, {"Reist nach Westfall und sucht Agentin Kearnen. Sie wird Euch Bericht erstatten."}},
[2361] = {"Restaurierung der Halskette",nil, {"Ich gebe Euch diese Halskette gern, <Klasse>. Ihr habt mir einen unschätzbaren Dienst erwiesen und dafür danke ich Euch. Tragt die Kette mit Stolz und vergesst nicht, immer wieder in der gebührenden Lautstärke zu verkünden, wer sie für Euch gemacht hat!nnUnd wenn Ihr auch beiläufig erwähnen könntet, dass die Kette nicht verflucht ist, dann wäre das wirklich wunderbar."}},
[2378] = {"Suche nach der Zerschmetterten Hand",nil, {"Reist nach Orgrimmar und sprecht an der Kluft der Schatten mit Shenthul."}},
[2379] = {"Zando'zan",nil, {"Sprecht an der Kluft der Schatten mit Zando'zan."}},
[2380] = {"Nach Orgrimmar!",nil, {"Sprecht an der Kluft der Schatten in Orgrimmar mit Shenthul."}},
[2381] = {"Das Ausplündern der Plünderer",nil, {"Bringt den Schatz der Südmeerfreibeuter zu Wrenix dem Jämmerlichen in Ratschet. Vergesst nicht, einen R.C.V.K. und Diebeswerkzeug von Wrenix' Elektrodingsda-Apparat zu bringen. Ihr werdet diese Dinge brauchen, um Eure Mission zu beenden.nnWenn Ihr von ungewöhnlich feindlichen Papageien angegriffen werdet, benutzt Euren R.C.V.K.!"}},
[2382] = {"Wrenix aus Ratschet",nil, {"Reist nach Ratschet und sprecht mit Wrenix dem Jämmerlichen."}},
[2383] = {"Einfaches Pergament",nil, {"Lest das einfache Pergament und sprecht mit Frang im Tal der Prüfungen."}},
[2398] = {"Die verschollenen Zwerge",nil, {"Sucht in Uldaman nach Baelog."}},
[2399] = {"Die sprießenden Wedel",nil, {"Die Wedel, die Denalan in seinem Garten angepflanzt hat, sind gesprossen und gediehen. Sie zittern leicht, als wollten sie gepflückt werden..."}},
[2418] = {"Kraftsteine",nil, {"Bringt Riggelfuzz im Ödland 8 Kraftsteine aus Dentrium und 8 Kraftsteine aus An'Alleum."}},
[2438] = {"Der smaragdgrüne Traumfänger",nil, {"Bringt den smaragdgrünen Traumfänger zu Tallonkai Flinkwurzel in Dolanaar."}},
[2439] = {"Die Platinscheiben",nil, {"Bringt Euren Belohnungsgutschein zu Dinita Zasterstein im Tresor von Eisenschmiede."}},
[2440] = {"Die Platinscheiben",nil, {"Bringt den Belohnungsgutschein zu Bena Winterhuf nach Donnerfels."}},
[2458] = {"Undercover",nil, {"Reist zum Turm der Venture Company nördlich des Schlickermoores und kontaktiert dort den Agenten der Zerschmetterten Hand, Zuchtmeister Fitzel.nnSobald der Turm in Sichtweite ist, verwendet die Leuchtfeuerwaffe, um Zuchtmeister Fitzel Eure Ankunft anzuzeigen. Feuert ZWEI Leuchtfeuergeschosse ab, nähert Euch dann Zuchtmeister Fitzel und zeigt den Gruß der Zerschmetterten Hand.nnEs ist wichtig, dass Ihr diese Aktionen in der richtigen Reihenfolge durchführt, da Fitzel jede Person, die er nicht erkennt, angreift und eliminiert."}},
[2459] = {"Ferocitas der Traumfresser",nil, {"Tallonkai Flinkwurzel aus Dolanaar möchte, dass Ihr 7 Mystiker der Knarzklauen tötet und das verlorene Juwel findet."}},
[2460] = {"Der Gruß der Zerschmetterten Hand",nil, {"Führt Shenthul gegenüber den Gruß der Zerschmetterten Hand aus."}},
[2478] = {"Mission: möglich, aber nicht sehr wahrscheinlich",nil, {"Stehlt Silixiz' Turmschlüssel von Großknecht Silixiz. Tötet dann 2 mutierte Drohnen der Venture Co., 2 Patrouillen der Venture Co. und 2 Ausgucker der Venture Co.nnEliminiert den obersten Großknecht Gallywix und nehmt seinen Kopf. Knackt dann das Schloss von Gallywix' Schließkassette und stehlt den Behälter mit Zanzils veränderter Mixtur. Bringt den Behälter und Gallywix' Kopf zu Shenthul nach Orgrimmar; er wird Euch weitere Anweisungen geben."}},
[2479] = {"Hinotts Hilfe",nil, {"Reist nach Tarrens Mühle im Vorgebirge des Hügellands und liefert die Probe von Zanzils Mixtur bei Serge Hinott ab.nnUm nach Tarrens Mühle zu kommen, müsst Ihr den Zeppelin nach Unterstadt nehmen und dann auf der Straße nach Süden durch den Silberwald in Richtung Hügellandhof reisen. Folgt den Schildern!"}},
[2480] = {"Hinotts Hilfe",nil, {"Wartet auf Serge Hinott, um das Heilmittel fertig zu stellen."}},
[2498] = {"Rückkehr zu Denalan",nil, {"Rellian Grünwipfel möchte, dass Ihr am Al'Amethsee mit Denalan sprecht."}},
[2499] = {"Grimmeiche",nil, {"Denalan vom Al'Amethsee möchte, dass Ihr den riesengroßen Tumor von Grimmeiche einsammelt."}},
[2500] = {"Reagenziensuche im Ödland",nil, {"Beschafft die Reagenzien, die Ghak Heilsegen aus dem Ödland braucht, und bringt sie ihm nach Thelsamar."}},
[2501] = {"Reagenziensuche im Ödland II",nil, {"Benutzt die leeren thaumaturgischen Gefäße bei versengten Wächtergroßdrachen im Ödland. Wenn Ihr sie gefüllt habt, bringt sie zu Ghak Heilsegen in Thelsamar."}},
[2518] = {"Tränen des Mondes",nil, {"Priesterin A'moora vom Tempel des Mondes in Darnassus möchte, dass Ihr ihr Lady Sathrahs silbrige Spinndrüsen bringt."}},
[2519] = {"Der Tempel des Mondes",nil, {"Schwester Aquinne möchte, dass Ihr im Tempel des Mondes mit Priesterin A'moora sprecht."}},
[2520] = {"Sathrahs Opferung",nil, {"Priesterin A'moora möchte, dass Ihr Lady Sathrahs silbrige Spinndrüsen am Brunnen innerhalb des Tempels ablegt und dann zu ihr zurückkehrt."}},
[2521] = {"Ein Dienst für Kum'isha",nil, {"Falls Ihr auf Euren Reisen durch die verwüsteten Lande je auf eine fehlerlose Draenethystsphäre stoßen solltet, bringt sie zu Kum'isha dem Sammler.nnDoch seid gewarnt. Der Edelstein ist einer der rarsten Kristalle von ganz Azeroth. Jedes Wesen in diesem Land könnte eine fehlerlose Draenethystsphäre bei sich haben.nnIhr erhaltet eine Belohnung für jede fehlerlose Draenethystsphäre, die Ihr beschaffen könnt."}},
[2522] = {"Kum'ishas Bemühungen",nil, {"Findet eine fehlerlose Draenethystsphäre und bringt sie Kum'isha dem Sammler."}},
[2523] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[2541] = {"Der schlafende Druide",nil, {"Bringt einen Voodooglücksbringer des Schamanen zu Oben Zornklaue in den Grabhügel von Ban'ethil."}},
[2561] = {"Druide der Klaue",nil, {"Oben Zornklaue möchte, dass Ihr seinen seelenlosen Körper tötet und dann den Voodooglücksbringer anwendet."}},
[2581] = {"Kicherfangunterkiefer",nil, {"Bringt Blutmagier Drazial 3 Kicherfangunterkiefer, 2 feuerverbrannte Eberlungen und 1 Skorpokzange."}},
[2582] = {"Wut der Zeiten",nil, {"Bringt Blutmagier Drazial 3 Kicherfangunterkiefer, 2 feuerverbrannte Eberlungen und 1 Skorpokzange."}},
