[2608] = {"Die Berührung von Zanzil",nil, {"Ihr solltet Euch /hinlegen, damit Dok Mixilpixil Euch untersuchen kann. Denn wie soll er ein Heilmittel finden, wenn er die Ursache nicht finden kann?"}},
[2609] = {"Die Berührung von Zanzil",nil, {"Bringt Dok Mixilpixil 1 Bündel einfache Wildblumen, 1 verbleite Phiole, 1 Bronzeröhre und 1 Rolle blassgelben Seidenfaden. So eine 'Itis' heilt sich nicht von selbst, <junger Herr/junge Dame>."}},
[2621] = {"Der Entehrte",nil, {"Sprecht in Steinard mit Zentralenkommandant Ruag."}},
[2622] = {"Die fehlenden Befehle",nil, {"Sprecht mit Bengor."}},
[2623] = {"Der Sumpfredner",nil, {"Beschafft die Befehle des Kriegshäuptlings wieder und gebt sie dem gefallenen Helden der Horde zurück."}},
[2641] = {"Sprinkels Geheimzutat",nil, {"Sprinkel in Gadgetzan möchte, dass Ihr einen violetten Tragan sucht und ihr bringt."}},
[2661] = {"Lieferung für Marin",nil, {"Sprinkel möchte, dass Ihr das violette Pulver nach Gadgetzan zu Marin Noggenfogger bringt."}},
[2662] = {"Noggenfoggers Elixier",nil, {"Marin Noggenfogger möchte, dass Ihr ihn nochmals ansprecht, wenn er sein Elixier fertiggestellt hat."}},
[2681] = {"Die Steine, die uns binden",nil, {"Befreit 9 Diener von Razelikh, 3 Diener von Sevine, 3 Diener von Allistarj und 3 Diener von Grol. Kehrt zum gefallenen Helden zurück, sobald Ihr die Aufgabe erfüllt habt. Ihr müsst nahe bei den Steinen bleiben, sonst habt Ihr keinen Erfolg."}},
[2701] = {"Helden der Vergangenheit",nil, {"Öffnet die Truhe und nehmt Euch Eure Belohnung."}},
[2702] = {"Helden der Vergangenheit",nil, {"Sprecht mit Korporal Thund Spalthuf."}},
[2721] = {"Kirith",nil, {"Findet heraus, was aus Leutnant Kirith geworden ist."}},
[2741] = {"Der Super-Ei-O-Mat",nil, {"Der Super-Ei-O-Mat ist die neueste Erfindung von Curgle Cranklehop. Es ist eine ziemlich beeindruckende Maschine.nnDer Super-Ei-O-Mat fängt wild an zu spucken und zu zischen, als Ihr Euch der Steuerkonsole nähert."}},
[2742] = {"Rin'ji steckt in der Falle!",nil, {"Eskortiert Rin'ji aus der Jagdhütte von Quel'Danil heraus und findet dann das, was er bei den Aussichtsklippen im Osten versteckt hat."}},
[2743] = {"Der Mantel der Dunkelheit",nil, {"Kehrt mit Euren neuen Erkenntnissen zum gefallenen Helden der Horde zurück."}},
[2744] = {"Der Dämonenjäger",nil, {"Sprecht mit Loramus Thalipedes in Azshara."}},
[2745] = {"Eindringen ins Schloss",nil, {"Sprecht in Schloss Sturmwind mit Tyrion."}},
[2746] = {"Nicht unwichtige Gegenstände",nil, {"Bringt 3 Stück Seidenstoff und 2 frische Äpfel von Clara nach Sturmwind zu Tyrion."}},
[2747] = {"Ein außergewöhnliches Ei",nil, {"Ich habe bisher nur sehr wenige Eier gesehen, die in außergewöhnlich gutem Zustand aus Feralas geholt wurden. Diese Eier sind extrem selten..."}},
[2748] = {"Ein gutes Ei",nil, {"Ein gutes Ei ist ein Ei, aus dem idealerweise ein Jungtier schlüpft, das frei von den bösen Auswirkungen der Beschwörungen der Oger der Gordunni ist."}},
[2749] = {"Ein gewöhnliches Ei",nil, {"Normalerweise schlüpft etwa aus jedem zweiten Hippogryphen-Ei auch ein Jungtier. Wir hegen und pflegen die Eier nach besten Kräften, und wenn wir Glück haben, dann schlüpft am Ende etwas da raus."}},
[2750] = {"Ein schlechtes Ei",nil, {"Diese Eier können nicht gerettet werden - aus ihnen wird niemals etwas schlüpfen."}},
[2751] = {"Barbarische Waffenschmiedekunst",nil, {"Bringt 2 gemusterte bronzene Armschienen, 2 bronzene Großschwerter und 2 scharfe Klauen zu Orokk Omosh in Orgrimmar."}},
