[2856] = {"Wildlederweste",nil, {"Bringt Jangdor Flinkschreiter im Camp Mojache 2 Schildkrötenschuppenbrustplatten, 2 Schildkrötenschuppenhandschuhe und eine Wildranke."}},
[2857] = {"Wildlederhelm",nil, {"Bringt Jangdor Flinkschreiter im Camp Mojache 2 Tuniken des Nachtschleichers, 2 Stirnbänder des Nachtschleichers und eine Wildranke."}},
