[3379] = {"Schattenweber",nil, {"Tötet die Gruppe der Schattenseidewilderer, die in der sengenden Schlucht umherstreifen. Nilith hat Grund zu der Annahme, dass es fünf von ihnen gibt, die ausgeschaltet werden müssen."}},
[3380] = {"Der Versunkene Tempel",nil, {"Sucht Marvon Nietensucher in Tanaris."}},
[3381] = {"Ein Treffen mit dem Meister",nil, {"Bringt 2 unbeschädigte Hippogryphenfedern zu Sanath Lim-yo in Azshara, wenn Ihr mit seinem Meister sprechen wollt."}},
[3382] = {"Eine Mannschaft in Bedrängnis",nil, {"Verteidigt Kapitän Vanessa Beltis, ihre Mannschaft und die Passagiere der Herrin des Horizonts gegen den Angriff der Naga. Kapitän Vanessa Beltis muss überleben."}},
[3385] = {"Der Untermarkt",nil, {"Erschlagt Handelsmeister Kovic und seinen Diener Clunk. Nehmt den Händlerranzen von seinem Leichnam und bringt ihn zu Nilith."}},
[3402] = {"Der Untermarkt",nil, {"Sprecht mit Vizzklick in Gadgetzan."}},
[3421] = {"Rückreise",nil, {"Ich werde Euch jetzt zu Sanaths Lager zurückbringen.nnMacht Euch nichts aus seinen schlechten Manieren - er ist zu allen unhöflich, außer zum Meister. Vielleicht wird er seine Lektion eines Tages lernen."}},
[3441] = {"Göttliche Vergeltung",nil, {"Lasst Kalaran Windklinge seine Geschichte erzählen."}},
[3442] = {"Die fehlerlose Flamme",nil, {"Sammelt 4 Herzen der Flamme und 4 Kugeln Golemöl und bringt die Gegenstände zu Kalaran Windklinge."}},
[3443] = {"Das Schmieden des Schafts",nil, {"Bringt 8 mit Thorium beschichtete Dolche zu Kalaran Windklinge."}},
[3444] = {"Der Steinreif",nil, {"Holt den Steinreif aus Marvon Nietensuchers Werkstatt in Ratschet."}},
[3445] = {"Der Versunkene Tempel",nil, {"Sucht Marvon Nietensucher in Tanaris."}},
[3446] = {"In die Tiefen",nil, {"Sucht den Altar von Hakkar im Versunkenen Tempel in den Sümpfen des Elends."}},
[3447] = {"Das Geheimnis des Kreises",nil, {"Reist zum Versunkenen Tempel und enthüllt das Geheimnis, das sich in dem Kreis der Statuen verbirgt."}},
[3448] = {"Die Bürde weitergeben",nil, {"Sprecht mit Magier Tymor in Eisenschmiede."}},
[3449] = {"Arkane Runen",nil, {"Verwendet das Abschriftenset, um Abpausbilder von der Rune von Beth'Amara, der Rune von Jin'yael, der Rune von Markri und der Rune von Sael'hai herzustellen, bevor Ihr Euch zu der kleinen Insel vor der südlichen Halbinsel von Azshara aufmacht und dem Piloten Xiggs Luntenzünder signalisiert, dass er sie abholen kann."}},
[3450] = {"Eine einfache Ladung",nil, {"Sprecht mit Xiggs Luntenzünder in Eisenschmiede."}},
[3451] = {"Signal zum Abholen",nil, {"Sprecht mit Xiggs Luntenzünder in Eisenschmiede und erhaltet von ihm eine Standardleuchtpistole."}},
[3452] = {"Die Ummantelung der Flamme",nil, {"Bringt ein Symbol von Ragnaros zu Kalaran Windklinge."}},
[3453] = {"Die Fackel der Vergeltung",nil, {"Wartet bis Kalaran Windklinge die Fackel der Vergeltung vollendet hat."}},
[3454] = {"Die Fackel der Vergeltung",nil, {"Nehmt die Fackel der Vergeltung."}},
[3461] = {"Rückkehr zu Magier Tymor",nil, {"Bringt Magier Tymors Abschriftenset zu ihm zurück nach Eisenschmiede."}},
[3462] = {"Knappe Malzdarr",nil, {"Sprecht mit dem Knappen Malzdarr."}},
[3463] = {"Setzt sie in Brand!",nil, {"Steckt die Wachpostentürme im Norden, Süden, Osten und Westen in Brand, indem Ihr die Fackel der Vergeltung innerhalb der einzelnen Gebäude benutzt."}},
[3481] = {"Plunder...",nil, {"Öffnet die Truhe."}},
[3482] = {"<NYI> <TXT> The Pocked Black Box",nil, {"Bringt diesen Flugschreiber zu Kravel."}},
[3483] = {"Signal zum Abholen",nil, {"Ah, noch eine Leuchtfeuerwaffe, hm? Die kann ich Euch geben.nnUnd vergesst nicht, Südspitze von Azshara. Es ist eine kleine Insel, die in eine Landungsplattform verwandelt wurde. Ihr könnt sie gar nicht verfehlen, wenn Ihr an der richtigen Stelle sucht."}},
[3501] = {"Im Dutzend besser",nil, {"Bringt Kum'isha unvollständige Draenethystfragmente und Ihr werdet für jedes, das Ihr bringt, belohnt."}},
[3502] = {"Des einen Draeneis Plunder...",nil, {"Bringt Kum'isha unvollständige Draenethystfragmente. Jede Art von Monster in den verwüsteten Landen könnte ein Fragment bei sich tragen."}},
[3503] = {"Treffen mit dem Meister",nil, {"Es gab Zeiten, in denen ich alles von denen, die meinen Meister aufsuchen wollten, verlangen konnte. Heute ist es anders, Ihr müsst nur nach ihm fragen und ich schicke Euch zu ihm.nnEs ist ärgerlich für mich, aber ich stelle seine Befehle nicht in Frage. Ich kann mich lediglich an Eurer lächerlichen Aufmachung oder Eurem jämmerlichen Gesichtsausdruck erfreuen. Selbst das langweilt mich...nnSogar dieses Geschwafel langweilt mich! Möchtet Ihr meinen Meister sehen oder nicht?"}},
[3504] = {"Verraten",nil, {"Bringt den versiegelten Brief zu Ag'tor Blutfaust nach Azshara."}},
[3505] = {"Verraten",nil, {"Tötet 10 Blutelfeneintreiber und 10 Blutelfenfeldmesser, bevor Ihr Magus Rimtoris Lager sucht. Sobald Ihr es gefunden habt, sucht nach etwas, das für sie von Bedeutung ist und das wir nehmen können, um sie in einen Kampf zu locken."}},
[3506] = {"Verraten",nil, {"Zerstört den arkanen Fokussierkristall, der an Magus Rimtori gebunden ist, und tötet sie danach. Bringt dann den Kopf von Magus Rimtori zu Ag'tor Blutfaust nach Azshara."}},
[3507] = {"Verraten",nil, {"Sprecht mit Belgrom Felshammer in Orgrimmar."}},
[3508] = {"Den Zauberschutz brechen",nil, {"Wartet, bis Loramus den Erleuchtungszauber vollendet hat."}},
[3509] = {"Der Name der Bestie",nil, {"Sprecht mit Lord Arkkoroc in Azshara. Beginnt Eure Suche am Rand des Wassers, um den Tempel von Arkkoran zu finden."}},
[3510] = {"Der Name der Bestie",nil, {"Erschlagt Hetaera und bringt den blutigen Kopf Hetaeras, den geschlagenen Kopf Hetaeras und den verwundeten Kopf Hetaeras zu Lord Arkkoroc."}},
[3511] = {"Der Name der Bestie",nil, {"Bringt den Härter zurück zu Loramus Thalipedes."}},
[3512] = {"Mit Eranikus' eigenen Worten",nil, {"Reist in den östlichen Teil von Winterquell und sprecht mit Umbranse, dem Geistsprecher."}},
[3513] = {"Die runenverzierte Rolle",nil, {"Bringt die runenverzierte Rolle zum nördlichen Wachturm des Brachlandes."}},
[3514] = {"Hordenpräsenz",nil, {"Tötet die Anführerin des Krallenbaus, Rynthariel die Schlüsselmeisterin."}},
[3517] = {"Wissen stehlen",nil, {"Sucht die Schrifttafel von Beth'Amara, die Schrifttafel von Jin'yael, die Schrifttafel von Markri und die Schrifttafel von Sael'hai, bevor Ihr zu Jediga in Azshara zurückkehrt."}},
[3518] = {"Lieferung an Magatha",nil, {"Bringt die Schrifttafel von Beth'Amara zu Magatha in Donnerfels."}},
[3519] = {"Ein Freund in Not",nil, {"Sprecht mit Dirania Silberschein im Laubschattental."}},
[3520] = {"Kreischergeister",nil, {"Fangt die Geister von 3 Kreischern in Feralas ein und kehrt dann zu Yeh'kinya beim Dampfdruckpier zurück."}},
[3521] = {"Iverrons Gegengift",nil, {"Sammelt 7 Hyazinthpilze, 4 Mondblütenlilien und 1 Waldwebersekret für Dirania Silberschein im Laubschattental."}},
[3522] = {"Iverrons Gegengift",nil, {"Bringt Iverrons Gegengift zu Iverron, bevor die Zeit um ist. Ihr findet Iverron bei der Höhle im Norden."}},
[3523] = {"Geißel der Niederungen",nil, {"Wenn Ihr Belnistrasz helfen wollt, sprecht noch einmal mit ihm und übergebt ihm den Schwurstein, den er Euch gab."}},
[3524] = {"An Land gespült",nil, {"Sammelt Meereskreaturknochen von der gestrandeten Meereskreatur unmittelbar südlich von Auberdine und kehrt dann mit ihnen zu Gwennyth Bly'Leggonde nach Auberdine zurück."}},
[3525] = {"Ausschalten des Götzen",nil, {"Begleitet Belnistrasz zum Götzen der Stacheleber in den Hügeln der Klingenhauer.nnBeschützt Belnistrasz, während er das Ritual durchführt, um den Götzen auszuschalten."}},
[3526] = {"Gobliningenieurskunst",nil, {"Wenn Ihr etwas über die Gobliningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
[3527] = {"Die Prophezeiung von Mosh'aru",nil, {"Bringt die erste und die zweite Schrifttafel von Mosh'aru zu Yeh'kinya nach Tanaris."}},
[3528] = {"Der Gott Hakkar",nil, {"Bringt das gefüllte Ei von Hakkar zu Yeh'kinya nach Tanaris."}},
[3541] = {"Lieferung an Jes'rimon",nil, {"Bringt die Schrifttafel von Jin'yael zu Jes'rimon nach Orgrimmar."}},
[3542] = {"Lieferung an Andron Gant",nil, {"Bringt die Schrifttafel von Markri zu Andron Gant nach Unterstadt."}},
[3561] = {"Lieferung für Erzmagier Xylem",nil, {"Bringt die Schrifttafel von Sael'hai zu Erzmagier Xylem in Azshara."}},
[3562] = {"Magathas Bezahlung für Jediga",nil, {"Bringt Magathas Notiz zu Jediga in Azshara."}},
[3563] = {"Jes'rimons Bezahlung für Jediga",nil, {"Bringt Jes'rimons Notiz zu Jediga in Azshara."}},
[3564] = {"Androns Bezahlung für Jediga",nil, {"Bringt Androns Notiz zu Jediga in Azshara."}},
[3565] = {"Xylems Bezahlung für Jediga",nil, {"Bringt Xylems Notiz zu Jediga in Azshara."}},
[3566] = {"Erhebt Euch, Obsidion!",nil, {"Tötet Lathoric den Schwarzen und Obsidion. Kehrt zurück zu Thorius nach Eisenschmiede mit dem Kopf von Lathoric dem Schwarzen und dem Herzen von Obsidion."}},
[3567] = {"An die Spitze",nil, {"Würdet Ihr den Ausblick von einem der Zwillingskolosse bevorzugen? Ich kann Euch dorthin schicken, <Volk>.nnAber überlegt, wie Ihr wieder herunterkommt. Ich kann Euch nur nach oben schicken."}},
[3568] = {"Verbreitete Verderbnis",nil, {"Füllt alle 4 leeren Phiolen an den Gezeitenteichen entlang der Küste der Ruinen von Eldarath in Azshara und kehrt dann zu Chemiker Miesmut zurück."}},
[3569] = {"Verbreitete Verderbnis",nil, {"Gebt Miesmuts Elixier Thersa Windweise in Unterstadt."}},
[3570] = {"Verbreitete Verderbnis",nil, {"Ich hätte mir nie träumen lassen, dass das Elixier so schnell Wirkung zeigen würde. Danke, <Name>. Ich werde mich sofort an die Untersuchung der restlichen Proben machen. Bestimmt werden sie für Lady Silvanas und die Verlassenen schon bald von Nutzen sein."}},
[3601] = {"Kim'jael, fürwahr!",nil, {"Durchsucht den thalassischen Stützpunkt nach Kim'jaels Kompass, Zielfernrohr, ausgestopftem Huhn und Knobelwizz und bringt ihm dann alles nach Azshara zurück."}},
[3602] = {"Azsharit",nil, {"Sammelt 20 kristallisierte Azsharite im südlichen Azshara. Ihr könnt den Teufelshund benutzen, damit er Euch beim 'Erschnüffeln' der gut versteckten Ablagerungen behilflich ist.nnKehrt zu Loramus zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[3621] = {"Die Herstellung von Teufelsbann",nil, {"Bringt die Lieferung zu Galvan dem Alten im Schlingendorntal."}},
[3625] = {"Verzauberte Azsharitteufelswaffe",nil, {"Wartet, bis Galvan die Waffen fertig geschmiedet hat."}},
[3626] = {"Rückkehr in die verwüsteten Lande",nil, {"Kehrt zum Gefallenen Helden der Horde zurück."}},
[3627] = {"Das zerbrochene Amulett vereinen",nil, {"Tötet Grol den Zerstörer, Lady Sevine und Erzmagier Allistarj. Nehmt das Amulett von Grol, das Amulett von Sevine und das Amulett von Allistarj von ihren Leichen.nnDenkt daran, Eure Verzauberte Azsharitteufelswaffe zu BENUTZEN, um die Triade zu schwächen. Auf eine andere Weise können sie nicht getötet werden - aber verwendet sie weise..."}},
[3628] = {"Ihr seid Rakh'likh, Dämon",nil, {"Vernichtet Razelikh den Entweiher und bringt das abgetrennte Horn des Entweihers zum Gefallenen Helden der Horde. Ihr müsst außerdem den Zauberschutz des Entweihers mit zurückbringen, damit der Gefallene Held ihn zerstören und somit verhindern kann, dass er jemals in die falschen Hände fällt."}},
