[3661] = {"Elunes Lieblinge?",nil, {"Sammelt 15 Wildekinfedern aus dem Hinterland für Erelas Himmelsbern in Rut'theran."}},
[3681] = {"Foliant der Offenbarung",nil, {"Sprecht mit Duthorian Rall in Sturmwind."}},
[3701] = {"Die glimmenden Ruinen von Thaurissan",nil, {"Begebt Euch zu den Ruinen von Thaurissan in der brennenden Steppe und sammelt Informationen über die Relikte von Thaurissan. Kehrt zur königlichen Historikerin Archesonus zurück, wenn Ihr die Informationen besorgt habt."}},
[3702] = {"Die glimmenden Ruinen von Thaurissan",nil, {"Hört die Erzählung der königlichen Historikerin Archesonus über die Geschichte von Thaurissan."}},
[3721] = {"Ein eigener OOX",nil, {"Ihr habt drei meiner OOX-Leitroboter gefunden! Ihr habt viel mehr als das getan, worum ich Euch gebeten hatte. Daher will ich Euch etwas Besonderes für Eure Hilfe geben. Dies ist eine Miniaturversion jenes Roboters, den Ihr jetzt kennt und liebt, den OOX!nnIch würde normalerweise niemandem solch ein potenziell tödliches Gerät anvertrauen, aber Ihr wart eine wirklich große Hilfe. Außerdem wurden die Kampfmechanismen und die Leitlogik dieses Exemplars deaktiviert.nnAber abgesehen davon ist das ein echter OOX!"}},
[3741] = {"Hilarys Halskette",nil, {"Sucht Hilarys Halskette und bringt sie Hilary in Seenhain zurück."}},
[3761] = {"Erde von Un'Goro",nil, {"Bringt 20 Erdproben von Un'Goro zu Ghede auf der Anhöhe der Ältesten in Donnerfels."}},
[3762] = {"Erzdruide Runentotem helfen",nil, {"Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem."}},
[3763] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
[3764] = {"Erde von Un'Goro",nil, {"Bringt 20 Erdproben von Un'Goro zu Jenal in der Enklave des Cenarius in Darnassus."}},
[3765] = {"Verderbnis in der Fremde",nil, {"Reist zu Gershala Nachtraunen in Auberdine."}},
[3781] = {"Morgenkornforschung",nil, {"Bringt den Samenkorngutschein des Erzdruiden zu Mathrengyl Bärenfährte in der Enklave des Cenarius des Darnassus."}},
[3782] = {"Morgenkornforschung",nil, {"Bringt den Samenkorngutschein zu Bashana Runentotem in Donnerfels."}},
[3783] = {"Sind wir endlich da, Yeti?",nil, {"Sammelt 10 dicke Yetifelle für Umi Rumpelkicher in der Ewigen Warte."}},
[3784] = {"Erzdruide Runentotem helfen",nil, {"Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem."}},
[3785] = {"Morgenkornforschung",nil, {"Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren.nnBringt 10 Einheiten Morgenkorn zu Mathrengyl Bärenfährte in Darnassus."}},
[3786] = {"Morgenkornforschung",nil, {"Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren.nnBringt 10 Einheiten Morgenkorn zu Bashana Runentotem in Donnerfels."}},
[3787] = {"Lebenshands Bitte",nil, {"Sprecht mit Quintis Lebenshand in der Mondfederfeste."}},
[3788] = {"Lebenshands Bitte",nil, {"Sprecht mit Quintis Lebenshand in der Mondfederfeste."}},
[3789] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
[3790] = {"Erzdruide Hirschhaupt helfen",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
[3791] = {"Das Geheimnis des Morgenkorns",nil, {"Bringt 10 Einheiten Morgenkorn zu Quintis Lebenshand in der Mondfederfeste."}},
[3792] = {"Morgenkorn für die Mondfederfeste",nil, {"Abermals hallo, <Name>. Meine Forschung macht gute Fortschritte, aber wenn Ihr fünf weitere Einheiten Morgenkorn habt, würde ich Euch die sehr gern abnehmen. Na ja... 'gern' ist nicht wirklich der richtige Ausdruck dafür; je länger ich mich in seiner Umgebung befinde, desto unwohler fühle ich mich. Aber für meine Forschungen ist es nun mal nötig, dass ich mich zusammenreiße und weiterarbeite."}},
[3801] = {"Dunkeleisenerbe",nil, {"Sprecht mit Franclorn Schmiedevater, wenn Ihr daran interessiert seid, einen Schlüssel für die Hauptstadt zu erhalten."}},
[3802] = {"Dunkeleisenerbe",nil, {"Erschlagt Fineous Dunkelader und bergt den großen Hammer Eisenhölle. Bringt Eisenhölle zum Schrein von Thaurissan und legt ihn auf die Statue von Franclorn Schmiedevater."}},
[3803] = {"Morgenkorn nach Darnassus",nil, {"Der Erzdruide sucht ständig nach mehr Morgenkorn für die weiteren Forschungsarbeiten des Zirkels des Cenarius. Wir nehmen jeweils zehn Einheiten ab und belohnen Euch mit umfangreichen Dingen, die Ihr nützlich finden werdet. Wenn Ihr zehn Einheiten habt, lasst es mich wissen, und ich werde Euch entsprechend belohnen."}},
[3804] = {"Morgenkorn nach Donnerfels",nil, {"Der Erzdruide sucht ständig nach mehr Morgenkorn für das Forschungsprojekt des Zirkels des Cenarius über den Un'Goro Krater. Wir nehmen jeweils zehn Einheiten ab und belohnen Euch mit umfangreichen Dingen, die Ihr nützlich finden werdet. Wenn Ihr zehn Einheiten habt, lasst es mich wissen, und ich werde Euch entsprechend belohnen."}},
[3821] = {"Schreckensfels",nil, {"Begebt Euch in die brennende Steppe und findet heraus, was aus Sha'ni Ehrenhauer geworden ist."}},
[3822] = {"Krom'Grul",nil, {"Holt Sha'nis Nasenring von Krom'Grul zurück und bringt ihn zu Thal'trak in Kargath. Durchsucht die verschiedenen Höhlen und Minen im Schreckensfels."}},
[3823] = {"Die Feuermagenoger auslöschen",nil, {"Tötet 15 Ogermagier der Feuermägen, 7 Oger der Feuermägen sowie 7 Schläger der Feuermägen und kehrt zu Oralius zurück, wenn Ihr fertig seid."}},
[3824] = {"Gor'tesh der Schläger-Lord",nil, {"Sucht Gor'tesh irgendwo im Westen des Schreckensfelsens. Tötet Gor'tesh und bringt dann seinen abgehackten Kopf zu Oralius in der brennenden Steppe zurück."}},
[3825] = {"Ogerkopf auf einem Pfahl = Party",nil, {"Nehmt Gor'teshs abgehackten Kopf und platziert ihn auf dem Gipfel des Schreckensfelsens. Sucht nach einem weichen Erdhügel, um den Pfahl aufzustellen."}},
[3841] = {"Waise sucht Zuhause",nil, {"Sprecht mit Quentin in Tausend Nadeln."}},
[3842] = {"Kurz vor dem Schlüpfen",nil, {"Bringt 2 Elixiere der Seelenstärke zu Quentin in Tausend Nadeln."}},
[3843] = {"Das neueste Mitglied der Familie",nil, {"Bringt Euer empfindliches Grimmlingflitzerei ins Hinterland und sprecht mit Agnar Tiermeister, aber beeilt Euch. Ihr habt nur eine Stunde Zeit, ihn zu finden. Dann stirbt das Jungtier in dem Ei."}},
[3844] = {"Es ist für jeden ein Geheimnis",nil, {"Sucht in dem Tümpel nach einem Hinweis darauf, wem dieses havarierte Floß gehören könnte."}},
[3845] = {"Es ist für jeden ein Geheimnis",nil, {"Untersucht die Gegenstände in dem Bündel und bringt sie zu ihrem Besitzer."}},
[3861] = {"GLUCK!",nil, {"Beschafft Euch etwas Spezialhühnerfutter und kehrt zu dem Huhn zurück, das sich mit Euch angefreundet hat. Wenn Ihr es gefunden habt, dann benutzt erst /cheer, ehe Ihr dem Huhn das Spezialhühnerfutter gebt."}},
[3881] = {"Rettungsexpedition",nil, {"Bringt eine Kiste mit Nahrungsmitteln und Forscherausrüstung zu Williden Marschall im Krater von Un'Goro."}},
[3882] = {"Knochenklappern",nil, {"Bringt 8 Dinosaurierknochen zu Funks Nilminer im Krater von Un'Goro."}},
[3883] = {"Eine fremdartige Ökologie",nil, {"Verwendet die Kratzphiole, um damit eine Schwarmbauwandprobe von einer der Schwarmbrutstätten der Gorishi im Krater von Un'Goro zu holen. Sucht nach den Kammern mit der hängenden Larvenbrut.nnBringt die Schwarmbauwandprobe zu Hol'anyee Marschall im Krater von Un'Goro."}},
[3884] = {"Willidens Tagebuch",nil, {"Bringt das Tagebuch zu Williden Marschall zurück. Er befindet sich irgendwo im Krater von Un'Goro."}},
[3901] = {"Klappernde Skelette",nil, {"Tötet 12 klappernde Skelette und kehrt zu Schattenpriester Sarvis in Todesend zurück, wenn Ihr fertig seid."}},
[3902] = {"Beutesuche in Todesend",nil, {"Durchsucht Todesend und Umgebung nach 6 Stücken aufgestöberter Ware und bringt sie zu Todeswache Saltain zurück."}},
[3903] = {"Milly Geltmark",nil, {"Sprecht mit Milly Geltmark."}},
[3904] = {"Millys Ernte",nil, {"Bringt 8 Kisten mit Millys Ernte zu Milly Geltmark in der Nähe der Abtei von Nordhain."}},
[3905] = {"Traubenliste",nil, {"Bringt die Traubenliste zu Bruder Neals in der Abtei von Nordhain."}},
[3906] = {"Disharmonie der Flamme",nil, {"Reist zum Steinbruch am Schwarzfels und tötet Übermeister Pyron. Kehrt zu Donnerherz zurück, sobald Ihr den Auftrag erledigt habt."}},
[3907] = {"Disharmonie des Feuers",nil, {"Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz."}},
[3908] = {"Es ist für jeden ein Geheimnis",nil, {"Bringt Linkens Schwert zu Donova Schneeweh in Winterquell."}},
[3909] = {"Das Videre-Elixier",nil, {"Sucht nach Gregan Hopfenspei im nördlichen Feralas. Lasst Euch von ihm erklären, wie Ihr Euch das Videre-Elixier beschaffen könnt, und kehrt dann zu Donova Schneeweh nach Winterquell zurück."}},
[3911] = {"Das letzte Element",nil, {"Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hin gemurmelt hat."}},
[3912] = {"Treffen am Grab",nil, {"Geht zum Friedhof außerhalb von Gadgetzan und benutzt dort das Videre-Elixier. Befolgt Donovas übrige Anweisungen, um Gaeriyan zu finden."}},
[3913] = {"Grabesstille",nil, {"Wenn Ihr auferstanden seid, sucht nach dem Grabstein, von dem Gaeriyan Euch erzählt hat."}},
[3914] = {"Linkens Schwert",nil, {"Liefert Linkens überragendes Schwert bei Linken im Krater von Un'Goro ab."}},
[3921] = {"Wenikee Kübelbolz",nil, {"Bringt das zerbrochene Samophlang zu Wenikee Kübelbolz."}},
[3922] = {"Schraubnieten",nil, {"Bringt 15 Schraubnieten zu Wenikee Kübelbolz im Brachland."}},
[3923] = {"Rilli Schmierklump",nil, {"Bringt das zerbrochene und ramponierte Samophlang nach Orgrimmar zu Rilli Schmierklump."}},
[3924] = {"Samophlanghandbuch",nil, {"Bringt das Samophlanghandbuch nach Orgrimmar zu Rilli Schmierklump."}},
[3941] = {"Die Hilfe des Gnoms",nil, {"Sucht J.D. Collie in der Höhle in Marschalls Zuflucht auf."}},
[3942] = {"Linkens Andenken",nil, {"Sucht Eridan Blauwind im südlichen Teil des Teufelswalds."}},
[3961] = {"Linkens Abenteuer",nil, {"Bringt das Silbertotem von Aquementas zu Linken in Marschalls Zuflucht."}},
[3962] = {"Allein ist es gefährlich",nil, {"Begebt Euch zum Feuersäulengrat südlich von Marschalls Zuflucht.nnSucht Sturmflamme und verwendet das Totem, um die schützende Aura, die ihn umgibt, zu entfernen. Sobald er besiegt ist, holt die Goldene Flamme aus der Höhle."}},
[3981] = {"Kommandant Gor'shak",nil, {"Sucht Kommandant Gor'shak in den Schwarzfelstiefen.nnIhr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen."}},
[3982] = {"Was ist los?",nil, {"Verteidigt Gor'shak."}},
[4001] = {"Was ist los?",nil, {"Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar.nnIhr erinnert Euch, dass Gor'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten."}},
[4002] = {"Das östliche Königreich",nil, {"Sprecht mit Thrall, wenn Ihr bereit seid, die von ihm geplante Mission zu übernehmen."}},
[4003] = {"Die königliche Rettung",nil, {"Tötet Imperator Dagran Thaurissan und befreit Prinzessin Moira Bronzebart von seinem bösen Zauber."}},
[4004] = {"Ist die Prinzessin gerettet?",nil, {"Kehrt zu Thrall zurück!"}},
[4005] = {"Aquementas",nil, {"Bringt das Buch von Aquor, die silbrigen Klauen und das Eisenstammherz nach Tanaris.nnBenutzt das Buch von Aquor, um Aquementas zu beschwören.nnWenn Ihr das vollständige Totem habt, bringt es zu J.D. Collie in Marschalls Zuflucht zurück."}},
[4021] = {"Gegenangriff!",nil, {"Bringt ein Teil von Krom'zars Flagge zu Regthar Totenfurt, westlich des Wegekreuzes."}},
[4022] = {"Eine Kostprobe der Flamme",nil, {"Zeigt Cyrus Therepentous die abgestreifte Haut des schwarzen Drachenschwarms, die Ihr von Kalaran Windklinge erhalten habt."}},
[4023] = {"Eine Kostprobe der Flamme",nil, {"Zeigt Cyrus Therepentous einen Beweis für Eure Nützlichkeit.nnIhr habt so ein Gefühl, dass Cyrus bereits weiß, dass Ihr unwürdig seid."}},
[4024] = {"Eine Kostprobe der Flamme",nil, {"Begebt Euch in die Schwarzfelstiefen und tötet Bael'Gar.nnIhr wisst lediglich, dass der Riese irgendwo in den Schwarzfelstiefen haust. Vergesst nicht, die veränderte Haut des schwarzen Drachenschwarms mit Bael'Gars Überresten zu benutzen und damit die feurige Essenz einzufangen.nnBringt die eingeschlossene feurige Essenz zu Cyrus Therepentous zurück."}},
[4041] = {"Das Videre-Elixier",nil, {"Klar, wenn Ihr mir eine Evowurzel bringt, dann kann ich Euch auch das Videre-Elixier machen.nnIch weiß alle möglichen Dinge über Kräuter und Alchimie... Ich könnte Euch Geschichten erzählen... zum Beispiel, wie ich mal Gold aus dem Käse gemacht habe, den ich eigentlich zum Mittagessen... he! Wo wollt Ihr hin?"}},
[4061] = {"Aufstieg der Maschinen",nil, {"Begebt Euch in die brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter.nnEuch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter."}},
[4062] = {"Aufstieg der Maschinen",nil, {"Bringt die Elementarsplitterprobe zu Lotwil Veriatus.nnIhr erinnert Euch, dass Theodora sagte, Lotwil sei in einem Lager im Osten stationiert."}},
[4063] = {"Aufstieg der Maschinen",nil, {"Sucht und tötet Golemlord Argelmach. Bringt Lotwil seinen Kopf. Außerdem müsst Ihr 10 intakte Elementarkerne von den Wuthäschergolems und Kriegshetzerkonstrukten, die Argelmach beschützen, beschaffen. Das wisst Ihr, weil Ihr übernatürliche Fähigkeiten habt."}},
[4081] = {"SOFORT TÖTEN: Dunkeleisenzwerge",nil, {"Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!nnKriegsherr Bluthauer möchte, dass Ihr 15 Gardisten der Zorneshämmer, 10 Aufseher der Zorneshämmer und 5 Fußsoldaten der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[4082] = {"SOFORT TÖTEN: Hochrangige Führungskräfte der Dunkeleisenzwerge",nil, {"Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!nnKriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[4083] = {"Der spektrale Kelch",nil, {"Der spektrale Kelch schwebt in der Luft, leicht ansteigend und dann absinkend, so, als bewege er sich im Takt eines sterbenden Herzens."}},
[4084] = {"Silberherz",nil, {"Beschafft 11 silbrige Klauen und 1 Eisenstammherz für Eridan Blauwind im Teufelswald."}},
[4101] = {"Den Teufelswald säubern",nil, {"Bringt 15 Einheiten Blutharz zu Arathandris Silberwolke im Teufelswald."}},
[4102] = {"Den Teufelswald säubern",nil, {"Bringt 15 Einheiten Blutharz zu Maybess Flussbrise im Teufelswald."}},
[4103] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln.nnIm Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
[4104] = {"Salbe durch Bergbau",nil, {"Wenn Ihr im Besitz eines cenarischen Leuchtfeuers seid, während Ihr im Teufelswald Bergbau betreibt, könnt Ihr das besudelte Vitriol sehen, von dem eine Ader Rohmetall verderbt wird. Ich weiß, wie man dieses Vitriol läutert und zu einem Reagenz verarbeitet, aus dem cenarische Pflanzensalbe hergestellt wird. Diese Salbe verwendet Ihr dann an Pflanzen, die Ihr draußen in der Wildnis findet und die gerettet werden müssen.nnWenn Ihr verderbtes Vitriol gesammelt habt, werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
[4105] = {"Salbe durch Sammlung",nil, {"Mit einem cenarischen Leuchtfeuer können erfahrene Kräuterkundige die heimtückische Teufelsranke erkennen, ein bösartiges Produkt der Verderbnis des Teufelswalds. Dank meiner Arbeit verfüge ich über das Wissen, die Teufelsranke zu einem Säuberungsreagenz verarbeiten zu können. Dieses verwenden wir für etwas, das wir jetzt cenarische Pflanzensalbe nennen.nnWenn Ihr im Teufelswald Kräuter sammelt, dann solltet Ihr, solange Ihr das Leuchtfeuer bei Euch habt, auf Teufelsranken stoßen. Im Austausch gegen das, was Ihr mir bringt, werde ich Euch fertige cenarische Pflanzensalbe geben."}},
[4106] = {"Salbe durch Kürschnerei",nil, {"Mit einem cenarischen Leuchtfeuer können gut ausgebildete Kürschner die Flecken besudelter Haut auf den im Teufelswald erbeuteten Fellen erkennen. Ich wandle diese scheinbar nutzlosen Flecken in eine dicke Paste um, mit der die cenarische Pflanzensalbe aktiviert wird. Wenn Ihr hier im Teufelswald Eure Beute häutet, dann solltet Ihr unbedingt immer ein Leuchtfeuer bei Euch haben.nnSobald Ihr besudelte Flecken gesammelt habt, bringt sie mir. Ich gebe Euch etwas cenarische Pflanzensalbe dafür, die ich bereits hergestellt habe."}},
[4107] = {"Salbe durch Entzauberung",nil, {"Die Macht eines entzauberten magischen Gegenstandes ist in der von ihm geschaffenen Essenz spürbar. Ich habe einen Weg gefunden, um die rohe Essenz, die von Verzauberern verwendet wird, in ein geeignetes Reagenz für die cenarische Pflanzensalbe umzuwandeln. Dazu braucht Ihr kein cenarisches Leuchtfeuer; jeder angemessen entzauberte Gegenstand, der geringe Netheressenzen abgibt, ist dafür geeignet.nnIch brauche nur eine einzige geringe Netheressenz; im Austausch dafür werde ich Euch cenarische Pflanzensalben geben. Die Essenz ist hochwirksam - ganz wenig davon genügt, um viel zu erreichen."}},
[4108] = {"Salbe durch Jagd",nil, {"Wer ein cenarisches Leuchtfeuer besitzt, kann mit diesem die verderbten Seelensplitter in den besudelten Bestien sehen, die zum Wohl der Allgemeinheit vernichtet werden. Ich schleife Splitter zu einem nutzbaren Reagenz, das für die Herstellung von cenarischer Pflanzensalbe verwendet wird. Wir werden diese Salbe benutzen, um verderbte Pflanzen wieder in gesunde zu verwandeln.nnIm Austausch für diese Splitter werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
[4109] = {"Salbe durch Bergbau",nil, {"Wenn Ihr im Besitz eines cenarischen Leuchtfeuers seid, während Ihr im Teufelswald Bergbau betreibt, könnt Ihr das besudelte Vitriol sehen, von dem eine Ader Rohmetall verderbt wird. Ich weiß, wie man dieses Vitriol läutert und zu einem Reagenz verarbeitet, aus dem cenarische Pflanzensalbe hergestellt wird. Diese Salbe verwendet Ihr dann an Pflanzen, die Ihr draußen in der Wildnis findet und die gerettet werden müssen.nnWenn Ihr verderbtes Vitriol gesammelt habt, werde ich Euch etwas cenarische Pflanzensalbe geben, die ich bereits vorbereitet habe."}},
[4110] = {"Salbe durch Sammlung",nil, {"Mit einem cenarischen Leuchtfeuer können erfahrene Kräuterkundige die heimtückische Teufelsranke erkennen, ein bösartiges Produkt der Verderbnis des Teufelswalds. Dank meiner Arbeit verfüge ich über das Wissen, die Teufelsranke zu einem Läuterungsreagenz verarbeiten zu können. Dieses verwenden wir für etwas, das wir jetzt cenarische Pflanzensalbe nennen.nnWenn Ihr im Teufelswald Kräuter sammelt, dann solltet Ihr, solange Ihr das Leuchtfeuer bei Euch habt, auf Teufelsranken stoßen. Im Austausch gegen das, was Ihr mir bringt, werde ich Euch fertige cenarische Pflanzensalbe geben."}},
[4111] = {"Salbe durch Kürschnerei",nil, {"Mit einem cenarischen Leuchtfeuer können gut ausgebildete Kürschner die Flecken besudelter Haut auf den im Teufelswald erbeuteten Fellen erkennen. Ich wandle diese scheinbar nutzlosen Flecken in eine dicke Paste um, mit der die cenarische Pflanzensalbe aktiviert wird. Wenn Ihr hier im Teufelswald Eure Beute häutet, dann solltet Ihr unbedingt immer ein Leuchtfeuer bei Euch haben.nnSobald Ihr besudelte Flecken gesammelt habt, bringt sie mir. Ich gebe Euch einige cenarische Pflanzensalben dafür, die ich bereits hergestellt habe."}},
[4112] = {"Salbe durch Entzauberung",nil, {"Die Macht eines entzauberten magischen Gegenstandes ist in der von ihm geschaffenen Essenz spürbar. Ich habe einen Weg gefunden, um die rohe Essenz, die von Verzauberern verwendet wird, in ein geeignetes Reagenz für die cenarische Pflanzensalbe umzuwandeln. Dazu braucht Ihr kein cenarisches Leuchtfeuer; jeder angemessen entzauberte Gegenstand, der geringe Netheressenzen abgibt, ist dafür geeignet.nnIch brauche nur eine einzige geringe Netheressenz; im Austausch dafür werde ich Euch cenarische Pflanzensalben geben. Die Essenz ist hochwirksam - ganz wenig davon genügt, um viel zu erreichen."}},
[4113] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4114] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4115] = {"Verderbte Windblüte",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Windblütenpflanze aussieht. Die Beeren, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4116] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4117] = {"Verderbte Peitscherwurzel",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Peitscherwurzelpflanze aussieht. Die sichtbaren Wurzeln der Pflanze sehen zäh und ledrig aus und ein schleimiger Film tropft von der Pflanze. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4118] = {"Verderbte Liedblume",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, das wie eine Liedblumenpflanze aussieht. Ein stechender, ungesunder Geruch geht von der Pflanze aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4119] = {"Verderbter Nachtdrache",nil, {"Ihr habt eine kränkliche, verderbte Ausgabe von etwas gefunden, was wie eine Nachtdrachenpflanze aussieht. Die Früchte, die von der Pflanze herabhängen, sehen verfault und giftig aus. Es muss sich dringend jemand um die Pflanze kümmern, wenn sie wieder zu ihrem Normalzustand zurückkehren soll."}},
[4120] = {"Die Kraft der Verderbnis",nil, {"Talo Dornhuf im Camp Mojache in Feralas möchte, dass Ihr 12 Wutklauengrizzlys und 12 Teufelspfotenverheerer im Teufelswald erledigt."}},
[4121] = {"Gefährliche Zwickmühle",nil, {"Eskortiert Euren Gefangenen, Grark Lorkrub, durch die brennende Steppe und durch den Schwarzfels in die sengende Schlucht.nnEuch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.nnIhr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen."}},
[4122] = {"Grark Lorkrub",nil, {"Begebt Euch in die brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.nnWenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen."}},
[4123] = {"Das Herz des Berges",nil, {"Bringt das 'Herz des Berges' zu Maxwort Funkelglanz in der brennenden Steppe."}},
[4124] = {"Der vermisste Kurier",nil, {"Sprecht mit Ginro Herdfeuer in der Mondfederfeste."}},
[4125] = {"Der vermisste Kurier",nil, {"Sucht das Boot des Kuriers irgendwo entlang der Küste von Feralas."}},
[4126] = {"Hurley Pestatem",nil, {"Bringt Ragnar Donnerbräu in Kharanos das verlorene Donnerbräurezept."}},
[4127] = {"Havarie",nil, {"Kehrt zur Mondfederfeste zurück und zeigt Ginro Herdfeuer das gefundene Messer."}},
[4128] = {"Ragnar Donnerbräu",nil, {"Sprecht mit Ragnar Donnerbräu."}},
[4129] = {"Das wieder aufgetauchte Messer",nil, {"Bringt Quintis Lebenshand in der Mondfederfeste das gefundene Messer."}},
[4130] = {"Psychometrie-Sitzung",nil, {"Sprecht mit Ginro Herdfeuer in der Mondfederfeste."}},
[4131] = {"Die Waldpfotengnolle",nil, {"Durchsucht die Lager der Waldpfotengnolle im Süden von Feralas nach Hinweisen auf den Verbleib des Kuriers oder seiner Habseligkeiten."}},
[4132] = {"Operation: Tod dem Zornesschmied",nil, {"Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt."}},
[4133] = {"Vivian Lagrave",nil, {"Sprecht mit Schattenmeisterin Vivian Lagrave in Kargath."}},
[4134] = {"Verlorenes Donnerbräurezept",nil, {"Bringt Vivian Lagrave in Kargath das verlorene Donnerbräurezept."}},
[4135] = {"Die verwundene Tiefe",nil, {"Da Ihr jetzt Raschals letzten bekannten Aufenthaltsort in Erfahrung gebracht habt, setzt Ihr die Suche nach ihm oder seinen sterblichen Überresten in der verwundenen Tiefe fort. Laut Notiz soll dieser Ort südlich der Lager der Waldpfotengnolle liegen."}},
[4136] = {"Ribbly Schraubstutz",nil, {"Bringt Yuka Schraubstutz in der brennenden Steppe Ribblys Kopf."}},
[4141] = {"Muigin und Larion",nil, {"Sammelt 15 Blutblüten und kehrt zu Muigin zurück."}},
[4142] = {"Ein Besuch bei Gregan",nil, {"Bringt Gregan Hopfenspei in Feralas eine Blutblüte."}},
[4143] = {"Der Dunst des Bösen",nil, {"Sammelt 5 Proben Dunst der Atal'ai und bringt sie Muigin im Krater von Un'Goro."}},
[4144] = {"Blutblütensprösslinge",nil, {"Das ist mein Plan, <Name>: Zieht los und sammelt ein paar Blutblütensprösslinge - Ihr wisst schon, das sind Pflanzen, die noch nicht ganz ausgetrieben haben, und bringt sie mir. Dann werde ich sehen, was man mit dem Dunst der Atal'ai anfangen kann.nnIhr haltet mich vielleicht für rachsüchtig ... Und damit liegt Ihr absolut richtig! nnDie Wahrheit ist, ich weiß, wie man hier wegkommt, aber solange Larion nicht zugibt, dass ich Recht habe, rühre ich mich nicht von der Stelle!nnUnd nun hinfort mit Euch. Sucht diese Blutblütensprösslinge, dann gönnen wir uns ein bisschen Spaß!"}},
[4145] = {"Larion und Muigin",nil, {"Jagt 5 Blutblütenpeitscher, 5 Blutblütendrescher, 5 Blutblütenschinder und 5 Blutblütenfallen für Larion in Marschalls Zuflucht."}},
[4146] = {"Schrumpftreibstoff",nil, {"Bringt Larion in Marschalls Zuflucht den ungeladenen Schrumpfer und 5 Proben Dunst der Atal'ai."}},
[4147] = {"Marvons Werkstatt",nil, {"Reist zu Marvon Nietensuchers Werkstatt in Ratschet."}},
[4148] = {"Blutblütenschrumpfer",nil, {"Damit der Schrumpfer funktioniert, brauchen wir ein Exemplar der Kreaturen, für die er eingestellt werden soll. Geht zum Krater und holt einen Blutblütensprössling. Dann kann ich ihn so einstellen, dass er bei Muigins kleinen Freunden funktioniert...nnWenn der Typ nicht so stur wäre, könnten wir alle schon längst auf dem Heimweg sein. Aber jetzt muss ich ihm erst einmal beweisen, dass mir seine Faxen nicht das Geringste ausmachen!"}},
[4161] = {"Rezept der Kaldorei",nil, {"Sammelt 7 kleine Spinnenbeine für Zarrin in Dolanaar."}},
[4181] = {"Gobliningenieurskunst",nil, {"Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan."}},
[4182] = {"Drachkin-Bedrohung",nil, {"Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 Schwarzwyrmkins und 1 Schwarzdrachen. Kehrt zu Helendis Flusshorn zurück, sobald die Aufgabe erledigt ist."}},
