[4521] = {"Wilde Wächter",nil, {"Trull Scheiterbann im Teufelswald möchte, dass Ihr 15 tobende Eulenbestien und 15 struppige Eulenbestien tötet."}},
[4542] = {"Nachricht zum Freiwindposten",nil, {"Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht."}},
[4561] = {"Test auf Unreinheiten - Krater von Un'Goro",nil, {"Ihr nehmt eine Brühschleimerprobe aus dem Un'Goro Krater und gebt etwas davon in die verschiedenen Röhren und Bechergläser auf dem Tisch. Als alles Mögliche zu blubbern und zu zischen anfängt, verändert der Brühschleimer seine Farbe. Dann kommt der Prozess zum Abschluss. Die Flüssigkeiten beruhigen sich wieder und Ihr seht eine kleine Petrischale, die Eure geläuterten Un'Goro-Brühschleimerproben sowie einige andere Rückstände enthält."}},
[4581] = {"Kayneth Stillwind",nil, {"Bringt Shindrells Notiz zu Kayneth Stillwind in Waldeslied."}},
[4601] = {"Der Funkelmat 5200!",nil, {"Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren."}},
[4602] = {"Der Funkelmat 5200!",nil, {"Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren."}},
[4603] = {"Funkelmatastisch",nil, {"Der Funkelmat 5200 ist leer und wartet auf ein schmutzverkrustetes Objekt zum Säubern sowie auf drei Silbermünzen, um loszulegen!"}},
[4604] = {"Funkelmatastisch",nil, {"Der Funkelmat 5200 ist leer und wartet auf ein schmutzverkrustetes Objekt zum Säubern sowie auf drei Silbermünzen, um loszulegen!"}},
[4605] = {"Der Funkelmat 5200!",nil, {"Der Funkelmat 5200 stoppt so schnell, wie er losgelegt hat. Ein einfaches \"Ding\" ist aus der Maschine zu hören und der Aufnahmebehälter der Maschine springt auf. Im Funkelmat 5200 liegt eine kleine Schachtel, die von einem Schleifchen umschlungen ist. Offensichtlich verpackt die Maschine ein Objekt, nachdem sie die Schmutzverkrustung entfernt hat. Selbst wenn die Maschine nicht funktioniert hat, ist der Gegenstand jetzt immerhin in einer hübschen Schachtel verpackt.nnTja, es gibt nur einen Weg herauszufinden, ob sie den Gegenstand gesäubert hat oder nicht..."}},
[4606] = {"Der Funkelmat 5200!",nil, {"Der Funkelmat 5200 stoppt so schnell, wie er losgelegt hat. Ein einfaches \"Ding\" ist aus der Maschine zu hören und der Aufnahmebehälter der Maschine springt auf. Im Funkelmat 5200 liegt eine kleine Schachtel, die von einem Schleifchen umschlungen ist. Offensichtlich verpackt die Maschine ein Objekt, nachdem sie die Schmutzverkrustung entfernt hat. Selbst wenn die Maschine nicht funktioniert hat, ist der Gegenstand jetzt immerhin in einer hübschen Schachtel verpackt.nnTja, es gibt nur einen Weg herauszufinden, ob sie den Gegenstand gesäubert hat oder nicht..."}},
[4621] = {"Holla, Admiral!",nil, {"Tötet Flottenmeister Seehorn und Baron Revilgaz aus Beutebucht und kehrt dann zu Flottenmeister Firallon zurück. Er befindet sich auf der Purpurschleier vor der Küste des Schlingendorntals."}},
[4641] = {"Euer Platz in der Welt",nil, {"Sprecht mit Gornek. Euch fällt ein, dass Kaltunk auf Eurer Karte markiert hat, wo er sich befindet. Außerdem erwähnte er, dass Gornek sich im Höhlenbau aufhielte, einem Gebäude im Westen."}},
[4642] = {"Vereinigung von Einflüssen",nil, {"Bringt eine verschmolzene Brühschlammerprobe zum Chemiker Brenner in Unterstadt."}},
[4661] = {"Test für Verderbnis - Teufelswald",nil, {"Ihr nehmt eine Brühschlammerprobe aus dem Teufelswald und gebt etwas davon in die verschiedenen Röhren und Bechergläser auf dem Tisch. Als alles Mögliche zu blubbern und zu zischen anfängt, verändert der Schleim seine Farbe. Dann kommt der Prozess zum Abschluss. Die Flüssigkeiten beruhigen sich wieder und Ihr seht eine kleine Petrischale, die Eure veränderten Teufelswaldschleimproben und einige andere Rückstände enthält."}},
[4681] = {"An Land gespült",nil, {"Bergt die Meeresschildkrötenüberreste der skelettierten Meeresschildkröte in den Gewässern westlich von Auberdine und sprecht anschließend in Auberdine mit Gwennyth Bly'Leggonde."}},
[4701] = {"Stellt sie ab",nil, {"Begebt Euch zur Schwarzfelsspitze und vernichtet die Quelle der Bedrohung durch die Worgs. Als Ihr Helendis verlasst, ruft er Euch noch einen Namen hinterher: Halycon. Darauf beziehen sich die Orcs im Zusammenhang mit den Worgs."}},
[4721] = {"Wilde Wächter",nil, {"Trull Scheiterbann im Teufelswald möchte, dass Ihr in Winterquell 10 Berserkereulenbestien jagt."}},
[4722] = {"Gestrandete Meeresschildkröte",nil, {"Bringt die Meeresschildkrötenüberreste nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4723] = {"Gestrandete Meereskreatur",nil, {"Bringt die Meereskreaturknochen nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4724] = {"Die Herrin der Meute",nil, {"Erschlagt Halycon, die Rudelführerin der Worgs der Blutäxte."}},
[4725] = {"Gestrandete Meeresschildkröte",nil, {"Bringt den seltsam markierten Kasten nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4726] = {"Brutlingessenz",nil, {"Bringt 8 Brutlingessenzen sowie den Draco-Incarcinatrix 900 zu Tinkee Kesseldampf am Flammenkamm in der brennenden Steppe."}},
[4727] = {"Gestrandete Meeresschildkröte",nil, {"Bringt die Meeresschildkrötenüberreste nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4728] = {"Gestrandete Meereskreatur",nil, {"Bringt die Meereskreaturknochen nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4729] = {"Kiblers Exotische Tiere",nil, {"Begebt Euch zur Schwarzfelsspitze und sucht Worgwelpen der Blutäxte. Benutzt den Käfig, um die wilden kleinen Bestien zu transportieren. Bringt einen eingesperrten Worgwelpen zu Kibler."}},
[4730] = {"Gestrandete Meereskreatur",nil, {"Bringt die Meereskreaturknochen nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4731] = {"Gestrandete Meeresschildkröte",nil, {"Bringt den seltsam markierten Kasten nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4732] = {"Gestrandete Meeresschildkröte",nil, {"Bringt die Meeresschildkrötenüberreste nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4733] = {"Gestrandete Meereskreatur",nil, {"Bringt die Meereskreaturknochen nach Auberdine zu Gwennyth Bly'Leggonde."}},
[4734] = {"Ei-Frosten",nil, {"Benutzt den Prototyp des Eiszilloskops an einem Ei im Horst."}},
[4735] = {"Eiersammlung",nil, {"Bringt 8 eingesammelte Dracheneier sowie das kollektronische Modul zu Tinkee Kesseldampf am Flammenkamm in der brennenden Steppe."}},
[4736] = {"Auf der Suche nach Menara Leerbringer",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4737] = {"Auf der Suche nach Menara Leerbringer",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4738] = {"Auf der Suche nach Menara Leerbringer",nil, {"Sprecht im Brachland mit Menara Leerbringer."}},
[4739] = {"Auf der Suche nach Menara Leerbringer",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4740] = {"GESUCHT: Gurgelbacke!",nil, {"Sucht und tötet den Murloc namens Gurgelbacke. Man nimmt an, dass die Kreatur die Hütten der Murlocs am Strand südlich von Auberdine beschützt.nnInformiert Schildwache Glynda Nal'Shea über Gurgelbackes Tod."}},
[4741] = {"Wilde Wächter",nil, {"Trull Scheiterbann im Teufelswald möchte, dass Ihr in Winterquell 13 mondbestrahlte Eulenbestien jagt."}},
[4742] = {"Siegel des Aufstiegs",nil, {"Sucht die drei Edelsteine der Befehlsgewalt: den Edelstein der Gluthauer, den Edelstein der Felsspitzoger und den Edelstein der Blutäxte. Bringt sie zusammen mit dem unverzierten Siegel des Aufstiegs zu Vaelan zurück.nnVaelan nannte Euch auch die Namen der drei Generäle: Kriegsmeister Voone von den Gluthauern, Hochlord Omokk von den Felsspitzogern und Oberanführer Wyrmthalak von den Blutäxten."}},
[4743] = {"Siegel des Aufstiegs",nil, {"Reist zum Drachensumpf in den Düstermarschen. Sucht den alten Drachen Aschenschwinge auf und bekämpft ihn ohne Gnade, bis sein Wille gebrochen ist.nnIn genau diesem Moment müsst Ihr das ungeschmiedete Siegel des Aufstiegs vor der großen Bestie hinlegen. Dann müsst Ihr schnell die Kugel der drachischen Energie auf seine geschwächte Gestalt anwenden und die Kontrolle über seine geistigen Kräfte übernehmen. Kontrolliert die Bestie und zwingt die Flammen des schwarzen Drachenschwarms auf das ungeschmiedete Siegel des Aufstiegs!"}},
[4761] = {"Thundris Windwirker",nil, {"Sprecht mit Thundris Windwirker in Auberdine."}},
[4762] = {"Der Klippenquell",nil, {"Geht von Auberdine aus nach Norden zum ersten Wasserfall des Klippenquell und entnehmt aus dem Teich dort eine Probe.nnKehrt mit der Probe des Klippenquells zu Thundris Windwirker nach Auberdine zurück."}},
[4763] = {"Die verderbten Schwarzfelle",nil, {"Füllt die leere Säuberungsschale am Mondbrunnen in Auberdine.nnHolt jeweils eine Probe von Früchten, Nüssen und Getreide aus dem Lager der Schwarzfellfurbolgs im Norden.nnMischt alles in der Schale und stellt diese bei dem Freudenfeuer neben dem Klippenquell auf Höhe des nördlichen Lagers ab und lockt dadurch den Satyrverderber an.nnNehmt den Talisman der Verderbnis und bringt ihn zu Thundris Windwirker in Auberdine."}},
[4764] = {"Doomriggers Schnalle",nil, {"Bringt Mayara Wolkenglanz in der brennenden Steppe Doomriggers Schnalle."}},
[4765] = {"Lieferung an Kronenbrunn",nil, {"Bringt Kronenbrunns Kiste zu Remington Kronenbrunn nach Sturmwind."}},
[4766] = {"Mayara Wolkenglanz",nil, {"Sprecht mit Mayara Wolkenglanz in der brennenden Steppe."}},
[4767] = {"Windreiter",nil, {"Bringt Elu am Freiwindposten 10 Eier von Steilhangflügeldrachen."}},
[4768] = {"Die Dunkelsteinschrifttafel",nil, {"Bringt der Schattenmagierin Vivian Lagrave in Kargath die Dunkelsteinschrifttafel."}},
[4769] = {"Vivian Lagrave und die Dunkelsteinschrifttafel",nil, {"Sprecht mit Schattenmagierin Vivian Lagrave."}},
[4770] = {"Auf der Heimreise",nil, {"Begleitet Pao'ka Bergrausch aus dem Steilhang heraus und sprecht dann am Weißgipfelposten mit Motega Feuermähne."}},
[4771] = {"Dämmerungstrickfalle",nil, {"Legt die Dämmerungstrickfalle in den Vorführraum von Scholomance. Besiegt Vectus und kehrt dann zu Betina Moppelzink zurück."}},
[4781] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Xizk Gutstich im Schlingendorntal einen Goldbarren."}},
[4782] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Kehrt zu Menara Leerbringer im Brachland zurück."}},
[4783] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Menara Leerbringer im Brachland 10 Phiolen Zornsäerblut und 1 geringen Höllenstein."}},
[4784] = {"Komponenten für die verzauberte Goldblutrobe",nil, {"Bringt Menara Leerbringer im Brachland feinen Goldfaden, 2 glimmende Kohlen und einen Seelensplitter."}},
[4785] = {"Feiner Goldfaden",nil, {"Bitte sehr, <Name>. Und bitte richtet doch Menara Grüße von uns aus.nnWir erwarten große Dinge von Euch. Wenn die Legion in die Knie gezwungen ist, wird man bestimmt Euren Namen rufen und Eure Kraft preisen. Ich kann es kaum erwarten!"}},
[4786] = {"Die fertiggestellte Robe",nil, {"Wartet, bis Menara Leerbringer Eure Robe fertiggestellt hat, und sprecht dann noch einmal mit ihr."}},
[4787] = {"Das uralte Ei",nil, {"Bringt das uralte Ei zu Yeh'kinya nach Tanaris."}},
[4788] = {"Die letzten Schrifttafeln",nil, {"Bringt Ausgrabungsleiter Eisenschuh in Tanaris die fünfte und sechste Schrifttafel von Mosh'aru."}},
[4801] = {"E'ko der Frostsäbler",nil, {"In Winterspring da werdeta gegen große Katzens kämpfen müssen, das sin' de Frostsäblers. Diese Biesties sin' unglaublich schnell un' gerissen. Wenn Ihr auch so schnell wie 'n Frostsäbler sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4802] = {"E'ko der Winterfelle",nil, {"In Winterspring da werdeta gegen fehlgeleitete Kreaturens kämpfen, das sin' de Winterfellfurbolgse. Weil ihr Anführer das so wollt', ham die unglaubliche Kraft gekriegt. Wenn Ihr auch so stark wie 'n Winterfell sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4803] = {"E'ko eines Splitterzahns",nil, {"In Winterspring, da sin' große Bärens mit extrem dicken Bälgens. Diese Splitterzahnbären kriegen davon so viel Schutz, dasse unheimlich viel Zeugs widerstehen könn'. Wenn Ihr gegen Feuer auch so widerstandsfähig sein wollt, dann bringt mir das E'ko, das Ihr von 'nem Splitterzahn kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4804] = {"E'ko der Eiswindschimären",nil, {"In Winterspring, da müssta gegen große fliegende Kreaturens kämpfen, das sin' de Eiswindschimären. Diese Biesties könn' Frostmagie wirken. Wenn Ihr gegen Frost auch so widerstandsfähig sein wollt, dann bringt mir das E'ko, das Ihr von 'ner Eiswindschimäre kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4805] = {"E'ko der Eisdistelyetis",nil, {"In Winterspring, da müssta gegen große Kreaturens kämpfen, das sin' de Eisdistelyetis. Diese Viecher bewegen sich nur langsam, aber se könn' Dank dem dicken Fell 'ne Menge Prügel einstecken. Wenn Ihr auch so gut wie die Angriffen ausweich'n wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4806] = {"E'ko der Frosthagelriesen",nil, {"In Winterspring habta gegen de großen Eisriesens gekämpft, de Frosthagel. Die verlassen sich auf rohe Gewalt und Kraft. Wenn Ihr auch so stark wie 'n Frosthagelriese sein wollt, dann bringt mir das E'ko, das Ihr von ihnen kriegt.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4807] = {"E'ko der Wildekin",nil, {"In Winterspring, da müssta gegen Kreaturens kämpfen, die ham anscheinend angeborene magische Fertigkeitens. Diese Wildekinses könn' echt starke Magie anwenden. Wenn Ihr Euren Intellekt erweitern wollt, dann bringt mir das E'ko von allen denen Eulenbiesters, die Ihr dort findet.nnNich' vergessen: Ihr müsst 'n Behälter von Mau'ari in Eurem Inventar haben, wenn Ihr Euch E'ko schnappen wollt."}},
[4808] = {"Felnok Stahlfeder",nil, {"Gebt Felnok Stahlfeder Tinkees Brief."}},
[4809] = {"Hörner der Eiswindschimären",nil, {"Bringt Felnok Stahlfeder 8 unbeschädigte Hörner einer Eiswindschimäre."}},
[4810] = {"Rückkehr zu Tinkee",nil, {"Bringt Tinkee Kesseldampf in der brennenden Steppe Felnoks Paket."}},
[4811] = {"Der rote Kristall",nil, {"Reist von Auberdine nach Osten und sucht nach einem großen, roten Kristall entlang der östlichen Bergkette von Dunkelküste. Informiert Schildwache Glynda Nal'Shea in Auberdine über Eure Erkenntnisse."}},
[4812] = {"Wenn das Wasser fließt",nil, {"Füllt die leere Wasserröhre am Mondbrunnen von Auberdine und untersucht dann den roten Kristall an der östlichen Bergkette von Dunkelküste."}},
[4813] = {"Die Fragmente im Inneren",nil, {"Informiert Schildwache Glynda Nal'Shea in Auberdine über Eure Erkenntnisse."}},
[4821] = {"Fremdartiges Ei",nil, {"Bringt Hagar Blitzhuf am Freiwindposten das fremdartige Ei."}},
[4822] = {"Lecker Eis",nil, {"Besorgt für Euren Schützling etwas Erdbeereis. Der kleine scheint die Marke 'Tigule und Forors Eiscreme' zu bevorzugen."}},
[4841] = {"Befriedung der Zentauren",nil, {"Tötet 12 Späher der Galak, 10 Zänker der Galak sowie 6 Windjäger der Galak und kehrt dann zu Klippenbehüter Langhorn am Freiwindposten zurück."}},
[4842] = {"Sonderbare Quellen",nil, {"Befolgt Donova Schneewehs Anweisungen und meldet Euch dann wieder bei ihr."}},
[4861] = {"Wütende Wildekin",nil, {"Findet Jaron Steinschleifers ruiniertes Lager samt dessen Vorräte im südlichen Winterquell."}},
[4862] = {"Be-Öh-Es-Eh",nil, {"Reist zur Schwarzfelsspitze und sammelt 15 Spitzenspinneneier für Kibler.nnLogischerweise müsstet Ihr diese Eier in der Nähe von Spinnen finden."}},
[4863] = {"Wütende Wildekin",nil, {"Sucht Jaron Steinschleifers zerstörten Wagen."}},
[4864] = {"Wütende Wildekin",nil, {"Klaubt Jarons Ladung aus dem Schnee und sucht dann die Wildekin, die das Amulett gestohlen haben, von dem Jaron gesprochen hatte.nnBringt Jaron Steinschleifer in der Lodge in Winterquell dessen Ladung und das blaugefiederte Amulett."}},
[4865] = {"Wilde Schlange",nil, {"Berichtet Motega Feuermähne von Euren Entdeckungen."}},
[4866] = {"Muttermilch",nil, {"Ihr findet Mutter Glimmernetz im Herzen der Schwarzfelsspitze. Kämpft mit ihr und bringt sie dazu, Euch zu vergiften. Es kann gut sein, dass Ihr sie sogar töten müsst. Kehrt zum struppigen John zurück, sobald Ihr vergiftet seid, damit er Euch 'melken' kann."}},
[4867] = {"Urok Schreckensbote",nil, {"Lest Waroshs Rolle. Bringt Waroshs Mojo zu Warosh."}},
[4881] = {"Auftragsmordplan",nil, {"Warnt Kanati Grauwolke am Weißgipfelposten, dass man vorhat, ihn zu töten."}},
[4882] = {"Geheimnisse bewachen",nil, {"Bringt Trull Scheiterbann beim Blutgiftposten das blaugefiederte Amulett."}},
[4883] = {"Geheimnisse bewahren",nil, {"Bringt Nara Wildmähne das blaugefiederte Amulett."}},
[4901] = {"Wächter des Altars",nil, {"Schützt Ranshalla, während sie versucht, den Altar von Elune wieder zu aktivieren. Berichtet Erelas Himmelsbern in Rut'theran von Eurem Fund."}},
[4902] = {"Wildekin von Elune",nil, {"Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus."}},
[4903] = {"Befehl des Kriegsherrn",nil, {"Tötet Hochlord Omokk, Kriegsmeister Voone und Oberanführer Wyrmthalak. Findet die wichtigen Schwarzfelsdokumente. Kehrt zum Kriegsherrn Bluthauer nach Kargath zurück, sobald Ihr diese Mission erledigt habt."}},
[4904] = {"Endlich frei",nil, {"Geleitet Lakota Windweise vom Düsterwolkengipfel hinab und sprecht dann im Freiwindposten mit Thalia Bernhaut."}},
[4906] = {"Weitere Verderbnis",nil, {"Eridan Blauwind im Teufelswald möchte, dass Ihr Xavaric sowie 8 Jadefeuerhöllenrufer, 8 Jadefeuerverräter und 8 Jadefeuerschwindler tötet. Kehrt zu Eridan zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[4907] = {"Tinkee Kesseldampf",nil, {"Sprecht mit Tinkee Kesseldampf."}},
[4921] = {"In der Schlacht vermisst",nil, {"Sucht Mankriks Frau und kehrt dann zu ihm in die Siedlung beim Wegekreuz zurück."}},
[4941] = {"Etriggs Weisheit",nil, {"Sprecht mit Etrigg in Orgrimmar. Wenn Ihr die Angelegenheit mit Etrigg besprochen habt, bittet Thrall um Rat.nnIhr erinnert Euch, dass Ihr Etrigg in Thralls Gemächern gesehen habt."}},
[4961] = {"Die Säuberung der Kugel von Orahil",nil, {"Tötet den Dämon der Kugel und sprecht dann mit Tabetha."}},
[4962] = {"Splitter eines Teufelshundes",nil, {"Nehmt den Teufelsrubin und benutzt ihn bei einem der Teufelshunde von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Teufelshundgeist und den Teufelsrubin."}},
[4963] = {"Splitter einer Höllenbestie",nil, {"Nehmt den Infussmaragd und benutzt ihn bei einer der Höllenbestien von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Leerbringer im Brachland den eingekerkerten Höllenbestiengeist und den Infussmaragd."}},
[4964] = {"Die fertiggestellte Kugel von Dar'Orahil",nil, {"Wartet, bis Menara Leerbringer die Kugel von Dar'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
[4965] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4966] = {"Kanati Grauwolke schützen",nil, {"Beschützt Kanati Grauwolke vor den Angriffen der Zentauren."}},
[4967] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4968] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4969] = {"Wissen der Kugel von Orahil",nil, {"Sprecht mit Menara Leerbringer im Brachland."}},
[4970] = {"Frostsäblerverpflegung",nil, {"Sammelt 5 Splitterzahnfleischstücke und 5 Fleischstücke einer Eiswindschimäre für Rivern Frostwind in Winterquell."}},
[4971] = {"Eine Frage der Zeit",nil, {"Benutzt den Temporalversetzer in der Nähe eines der Silos von Andorhal, um die temporalen Parasiten aufzuspüren.nnTötet 10 temporale Parasiten und bringt dann den Temporalversetzer zu Chromie im Gasthaus Andorhal in den westlichen Pestländern."}},
[4972] = {"Tempus fugit",nil, {"Sucht 5 Uhren von Andorhal, die in Schließkassetten überall im Schutt der Stadt zu finden sind. Kehrt mit ihnen zu Chromie ins Gasthaus Andorhal in den westlichen Pestländern zurück."}},
[4973] = {"Tempus fugit",nil, {"Sucht 5 Uhren von Andorhal in den Ruinen der Stadt und bringt sie Chromie im Gasthaus Andorhal in den westlichen Pestländern."}},
[4974] = {"Für die Horde!",nil, {"Begebt Euch zur Schwarzfelsspitze und tötet den Kriegshäuptling Rend Schwarzfaust. Nehmt seinen Kopf und kehrt nach Orgrimmar zurück."}},
[4975] = {"Die fertiggestellte Kugel von Noh'Orahil",nil, {"Wartet, bis Menara Leerbringer die Kugel von Noh'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
[4976] = {"Ablieferung der gesäuberten Kugel",nil, {"Bringt die gesäuberte Höllenkugel zu Menara Leerbringer ins Brachland."}},
[4981] = {"Agentin Bijou",nil, {"Begebt Euch zur Schwarzfelsspitze und findet heraus, was aus Bijou geworden ist."}},
[4982] = {"Bijous Habseligkeiten",nil, {"Sucht Bijous Habseligkeiten und bringt sie ihr. Ihr erinnert Euch daran, dass sie erwähnte, ihre Sachen auf der untersten Ebene der Stadt versteckt zu haben."}},
[4983] = {"Bijous Aufklärungsbericht",nil, {"Bringt Bijous Aufklärungsbericht zu Großmeister Lexlort nach Kargath."}},
[4984] = {"Die Tierwelt leidet auch",nil, {"Tötet 8 kranke Wölfe und kehrt dann zu Mulgris Tiefenfurt auf dem trostlosen Feld in den westlichen Pestländern zurück."}},
[4985] = {"Die Tierwelt leidet auch",nil, {"Tötet 8 kranke Grizzlys und kehrt dann zu Mulgris Tiefenfurt auf dem trostlosen Feld in den westlichen Pestländern zurück."}},
[4986] = {"Glyphenverzierter Eichenast",nil, {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Darnassus bringt. Bittet einen der Druiden dort um Hilfe."}},
[4987] = {"Glyphenverzierter Eichenast",nil, {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Donnerfels bringt. Bittet einen der Druiden dort um Hilfe."}},
[5001] = {"Bijous Habseligkeiten",nil, {"Sucht Bijous Habseligkeiten und bringt sie ihr. Viel Glück!"}},
[5002] = {"Nachricht an Maxwell",nil, {"Begebt Euch in die brennende Steppe und leitet Bijous Informationen an Marschall Maxwell weiter."}},
[5021] = {"Besser spät als nie",nil, {"Das wilde Gebrabbel der Geisterfrau deutete darauf hin, dass sie ein Paket ausliefern musste. Sie behauptete, dass es dort war, wo auch ihr Pferd war. Darüber, wo das Pferd jetzt ist oder wohin das Paket ausgeliefert werden muss, sagt der Geist nichts."}},
[5022] = {"Besser spät als nie",nil, {"Wendet Euch an die königlichen Disponenten von Sturmwind, um etwas über den Aufenthaltsort von Emma Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter im Rathaus."}},
[5023] = {"Besser spät als nie",nil, {"Wendet Euch an die königlichen Vorarbeiter von Unterstadt, um etwas über den Aufenthaltsort von Jeremiah Teufelsstein herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter in der Nähe der Gilden- und Wappenrock-Registrierung."}},
[5041] = {"Vorräte für das Wegekreuz",nil, {"Findet die Vorratskisten des Wegekreuzes und bringt sie zu Thork im Brachland zurück."}},
[5042] = {"Agammagans Kraft",nil, {"Ich benötige 4 Blutsplitter, wenn Ihr Agammagans Kraft wünscht, <Volk>.nnEure Feinde werden vor Eurer Kraft erzittern, wenn Ihr über diesen Segen verfügen würdet. nnDie größten Krieger der Grimmhauer sind mit solcher Stärke ausgestattet!"}},
[5043] = {"Agammagans Beweglichkeit",nil, {"Man sagt, dass die größten Späher meines Stammes in ihrer Genauigkeit unübertroffen seien und kein Schurke <schnaubt> ihrer Schnelligkeit im Kampf standhalten könne.nnBringt mir 4 Blutsplitter und ich werde Euch noch flinker machen als jeder lebende Stacheleber."}},
[5044] = {"Weisheit von Agammagan",nil, {"Die Schamanen und Geomanten der Stacheleberstämme <schnaubt> nutzen die Kraft von Agammagan im Kampf und bei ihren Ritualen, <Volk>.nnWenn auch Ihr die zusätzliche Macht von Agammagans Segen für Eure Zauber wünscht, dann bringt mir 4 Blutsplitter, und Quetschzahn wird Euch helfen."}},
[5045] = {"Steigerung des Geistes",nil, {"Krieger aller Art <schnaubt> benötigen einen starken Geist. Er treibt Euch zu größeren Leistungen, wenn alle Hoffnung verloren ist. Das ist den Grimmhauern nicht <schnaubt> unbekannt. Wir sind ein spiritueller Stamm... wir sind stolzer auf unsere Verbindung zu Agammagan als auf unsere Leistungsfähigkeit <schnaubt> im Kampf.nnBringt mir 4 Blutsplitter und ich werde Euch für den Kampf segnen. <schnaubt>"}},
[5046] = {"Klingenhaut",nil, {"Alle, die <schnaubt> in den Kampf eintreten, benötigen Schutz. Ihre Knochen müssen stark sein. Ihre Haut zäh. <schnaubt> Mein Stamm weiß das auch und um den natürlichen Schutz zu verstärken, den wir durch unsere Stacheln und unsere Haut erhalten, <schnaubt> rufen wir Agammagan an, uns vor Schaden zu schützen, bevor wir in den Krieg ziehen.nnBringt mir 4 Blutsplitter <schnaubt> und ich werde Euch den gleichen Schutz gewähren."}},
[5047] = {"Finkle Einhorn, zu Euren Diensten!",nil, {"Sprecht mit Malyfous Düsterhammer in der Ewigen Warte."}},
[5048] = {"Emma die Nette",nil, {"Sucht die alte Emma in Sturmwind und findet heraus, ob sie tatsächlich Emma Teufelsstein ist. Wenn sie es ist, dann möchte sie vielleicht das Paket haben, das Janice Teufelsstein für sie gepackt hat."}},
[5049] = {"Der Jammer des Jeremiah",nil, {"Sucht Jeremiah Lohnson in Unterstadt und findet heraus, ob er tatsächlich Jeremiah Teufelsstein ist. Wenn er es ist, dann möchte er vielleicht das Paket haben, das Janice Teufelsstein für ihn gepackt hat."}},
[5050] = {"Glücksbringer",nil, {"Bringt Janice Teufelsstein in den westlichen Pestländern die Hälfte ihres Glücksbringers zurück."}},
[5051] = {"Zwei Hälften vereint",nil, {"Sucht auf dem Teufelssteinfeld in den westlichen Pestländern nach der anderen Hälfte des Glücksbringers und setzt ihn wieder zusammen.nnGebt den Glücksbringer Janice Teufelsstein auf dem Teufelssteinfeld in den westlichen Pestländern."}},
[5052] = {"Blutsplitter von Agammagan",nil, {"Bringt Quetschzahn im Camp Taurajo im Brachland einen Blutsplitter.nnSammelt weitere Blutsplitter, wenn Ihr von ihm mit Agammagans Macht gesegnet werden möchtet."}},
[5054] = {"Ursius von den Splitterzahnbären",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Ursius tötet."}},
[5055] = {"Brumeran von den Eiswindschimären",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Brumeran tötet."}},
[5056] = {"Shy-Rotam",nil, {"Storm Schattenhuf in der Ewigen Warte in Winterquell möchte, dass Ihr Shy-Rotam besiegt."}},
[5057] = {"Frühere Abenteuer",nil, {"Bringt Storm Schattenhufs Gutschein zu Melor Steinhuf in Donnerfels."}},
[5058] = {"Frau Dalsons Tagebuch",nil, {"Ein Tagebuch mit vergilbten Seiten liegt aufgeschlagen da, sodass man den letzten Eintrag lesen kann:nn\"Ich weiß nicht, was passieren wird, jetzt, wo Harold sich verwandelt hat. Es hat mich meine ganze Kraft gekostet, ihn zu dem einzigen Ort zu schleppen, den ich finden konnte, in dem er sicher eingesperrt ist und nicht herauskommen kann. Den Schlüssel habe ich bei mir und ich kann nur beten, dass es funktionieren wird ...nnIch hoffe, ich schaffe es, hier herauszukommen, aber dazu brauche ich Geld. Unsere ganzen Sachen sind alle im Schrank, glaube ich ... und jetzt, wo ich darüber nachdenke - trägt Harold den Schlüssel dazu nicht bei sich?nnIch muss darüber schlafen. Im Augenblick bin ich todmüde.\""}},
[5059] = {"Weggeschlossen",nil, {"Der von Splittern gezeichnete Türrahmen des Plumpsklos sieht aus, als ob wiederholt und unablässig versucht worden wäre, die Tür gewaltsam zu öffnen. Sie ist derzeit von außen fest verschlossen.nnAls Ihr an der Tür rüttelt, hört Ihr aus dem Inneren ein kehliges Stöhnen! Was auch immer sich im Inneren befindet, es klingt nicht freundlich."}},
[5060] = {"Weggeschlossen",nil, {"Vor Euch steht ein sehr stabiler Schrank, sicher verschlossen mit einem eingebauten Schloss. Die Vorderseite des Schranks ist mit tiefen Kratzern und Riefen übersät. Offensichtlich sind das die Zeichen zahlreicher und gewaltsamer fehlgeschlagener Versuche, die Geheimnisse zu enthüllen, die der Schrank derzeit verbirgt.nnWenn Ihr diesen Schrank öffnen wollt, müsst Ihr den Schlüssel finden."}},
[5061] = {"Wassergestalt",nil, {"Kehrt nach Darnassus zurück und zeigt Mathrengyl Bärenfährte den Anhänger des Seelöwen."}},
[5062] = {"Hochheiliges Feuer",nil, {"Sammelt 10 Scheffel Brandpulveragaven und beratet Euch dann mit Magatha Grimmtotem auf der Anhöhe der Ältesten in Donnerfels."}},
[5063] = {"Kappe des scharlachroten Wissenden",nil, {"Um die Kappe des scharlachroten Wissenden herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 8 Arkankristalle; 5 verzauberte scharlachrote Fäden.nnIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
[5064] = {"Grimmtotemspionage",nil, {"Sucht die drei geheimen Notizen im Düsterwolkengipfel und bringt sie zurück."}},
[5065] = {"Die verlorenen Schrifttafeln von Mosh'aru",nil, {"Bringt Ausgrabungsleiter Eisenschuh in Tanaris die dritte und vierte Schrifttafel von Mosh'aru."}},
[5066] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
[5067] = {"Gamaschen von Arcana",nil, {"Um die Gamaschen von Arcana herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 5 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Einheiten Frostraunens Balsamierungsflüssigkeit.nnIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
[5068] = {"Brustplatte des Blutdurstes",nil, {"Um die Brustplatte des Blutdurstes herzustellen, müsst Ihr die folgenden Dinge zu Malyfous Düsterhammer bringen: 1 makelloser Balg der Bestie; 10 ausgefranste Monstrositätenstickereien; 5 Arkanitbarren; 5 Häute des Schattens.nnIhr müsst außerdem 50 Goldstücke für die Herstellung des Gegenstandes zahlen."}},
[5081] = {"Maxwells Mission",nil, {"Reist zur Schwarzfelsspitze und schaltet Kriegsmeister Voone, Hochlord Omokk und Oberanführer Wyrmthalak aus. Kehrt zu Marschall Maxwell zurück, wenn Eure Aufgabe erledigt ist."}},
[5082] = {"Bedrohung durch die Winterfelle",nil, {"Donova Schneeweh in Winterquell möchte, dass Ihr 8 Pfadfinder der Winterfelle, 8 Höhlenbehüter der Winterfelle und 8 Totemiker der Winterfelle erledigt."}},
[5083] = {"Feuerwasser der Winterfelle",nil, {"Bringt das leere Feuerwasserfläschchen zu Donova Schneeweh in Winterquell."}},
[5084] = {"Der Verderbnis erliegen",nil, {"Durchsucht das Furbolglager der Totenwaldfelle im nördlichen Teil des Teufelswalds nach einem Hinweis."}},
[5085] = {"Geheimnisvoller Glibber",nil, {"Berichtet Donova Schneeweh von Euren Entdeckungen."}},
[5086] = {"Toxinschrecken",nil, {"Sammelt 3 Toxinschreckentropfen für Donova Schneeweh in Winterquell."}},
[5087] = {"Läufer der Winterfelle",nil, {"Sucht die Läufer der Winterfelle und hindert sie daran, ihre Lieferung zum Lager der Winterfelle zu bringen."}},
[5088] = {"Arikara",nil, {"Erschlagt Arikara. Bringt ihre Überreste und das Brandpulver zu Motega Feuermähne im Weißgipfelposten als Beweis für Eure Tat."}},
[5089] = {"General Drakkisaths Befehl",nil, {"Bringt den Befehl von General Drakkisath zu Marschall Maxwell in der brennenden Steppe."}},
[5090] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
[5091] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich am Zugwindlager, nördlich des Alteracgebirges."}},
[5092] = {"Den Weg räumen",nil, {"Tötet 10 Skelettschinder und 10 sabbernde Ghuls in Trauerhügel.nnKehrt zu Kommandant Ashlam Ehrenschlag am Zugwindlager, westliche Pestländer, zurück."}},
[5093] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
[5094] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
[5095] = {"Der Ruf zu den Waffen: Die Pestländer!",nil, {"Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt."}},
[5096] = {"Scharlachrote Ablenkung",nil, {"Zieht zum Basislager des Scharlachroten Kreuzzugs zwischen dem Teufelssteinfeld und Dalsons Tränenfeld und zerstört sein Kommandozelt.nnLasst das Banner der Geißel im Lager wehen und kehrt dann zu Hochexekutor Derrington am Bollwerk in den westlichen Pestländern zurück."}},
[5097] = {"Turm um Turm",nil, {"Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren.nnWenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Kommandant Ashlam Ehrenschlag am Zugwindlager, westliche Pestländer, zurück."}},
[5098] = {"Turm um Turm",nil, {"Markiert mit der Signalfackel jeden Turm in Andorhal; Ihr müsst im Eingang jedes Turmes stehen, um ihn erfolgreich zu markieren.nnWenn alle vier Türme markiert sind, bringt Ihr die Signalfackel zu Hochexekutor Derrington am Bollwerk, westliche Pestländer, zurück."}},
[5101] = {"Lee's Ultimate Test Quest... of Doom!",nil, {"Tötet 6 Schafe für Benny... und dann nehmt die Füße in die Hand und rennt weg, was das Zeug hält!nnnnnWar nur 'n Scherz... was das Rennen angeht."}},
[5102] = {"General Drakkisaths Niedergang",nil, {"Begebt Euch zur Schwarzfelsspitze und schaltet General Drakkisath aus. Kehrt zu Marschall Maxwell zurück, wenn Eure Aufgabe erledigt ist."}},
[5103] = {"Heißer, feuriger Tod",nil, {"Jemand auf dieser Welt muss doch wissen, was mit diesen Stulpen zu tun ist. Viel Glück!"}},
[5121] = {"Oberhäuptling der Winterfelle",nil, {"Donova Schneeweh in Winterquell möchte, dass Ihr den Oberhäuptling der Winterfelle besiegt."}},
[5122] = {"Das Medaillon des Glaubens",nil, {"Die Macht dieser Kapelle gibt mir die Kraft, der Verderbnis in meinem Herzen zu widerstehen. Wenn ich sie verlasse, werde ich zu einem Todesritter. Aber es gibt ein Medaillon, das Medaillon des Glaubens, das mir so viel Kraft geben wird, dass ich trotzdem gehen kann.nnBitte, <Name>, findet das Medaillon für mich. Es wird von Malor dem Eifrigen bewacht, einem wahnsinnigen Mitglied der Scharlachroten Bruderschaft. Er lebt tief in der scharlachroten Bastion im westlichen Stratholme."}},
[5123] = {"Das letzte Stück",nil, {"Bringt das kunstlos geschriebene Logbuch zu Donova Schneeweh in Winterquell."}},
[5124] = {"Feurige Plattenstulpen",nil, {"Bringt Malyfous Düsterhammer 6 verzauberte Thoriumbarren, 2 Essenzen des Feuers und 4 Sternrubine. Ihr müsst außerdem Eure ungebrannten Plattenstulpen abgeben."}},
[5125] = {"Aurius' Abrechnung",nil, {"<Name>, ich sterbe... aber meine Seele ist gerettet. Dafür schulde ich Euch mehr, als man mit Worten ausdrücken kann. Bitte, nehmt dies... und kämpft weiter so mutig, <Klasse>!"}},
[5126] = {"Lorax' Geschichte",nil, {"Sprecht mit Lorax. Hört, was er zu sagen hat."}},
[5127] = {"Die Dämonenschmiede",nil, {"Begebt Euch zur Schwarzfelsspitze und sucht Goraluk Hammerbruch. Erschlagt ihn und wendet dann die blutbefleckte Pike auf seine Leiche an. Nachdem seine Seele abgesaugt wurde, wird die Pike seelenbefleckt sein.nnIhr müsst außerdem die ungeschmiedete runenbedeckte Brustplatte finden.nnBringt die seelenbefleckte Pike und die ungeschmiedete runenbedeckte Brustplate zu Lorax in Winterquell."}},
[5128] = {"Worte des Oberhäuptlings",nil, {"Bringt das kunstlos geschriebene Logbuch zu Kelek Himmelshüter im südlichen Teil des Teufelswalds."}},
[5141] = {"Drachenlederverarbeitung",nil, {"Bringt 2 feste Skorpidbrustplatten, 2 feste Skorpidhandschuhe und 10 Drachenschuppen zu Peter Galen in Azshara.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Drachenlederverarbeitung erhalten.nnIhr könnt dann allerdings weder Elementarlederverarbeitung noch Stammeslederverarbeitung erlernen; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5142] = {"Klein-Pamela",nil, {"Sucht Pamela Rotpfad."}},
[5143] = {"Stammeslederverarbeitung",nil, {"Bringt eine Wildlederweste und einen Wildlederhelm zu Caryssia Jägermond in Feralas.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5144] = {"Elementarlederverarbeitung",nil, {"Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Sarah Gerber in die sengende Schlucht.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5145] = {"Drachenlederverarbeitung",nil, {"Bringt 2 feste Skorpidbrustplatten, 2 feste Skorpidhandschuhe und 10 Drachenschuppen zu Thorkaf Drachenaug im Ödland.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Drachenlederverarbeitung erhalten.nnIhr könnt dann allerdings weder Elementarlederverarbeitung noch Stammeslederverarbeitung erlernen; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5146] = {"Elementarlederverarbeitung",nil, {"Bringt 2 Herzen des Feuers, 2 Kugeln des Wassers, 2 Erdenkerne und 2 Odem des Windes zu Brumn Winterhuf in den Arathibergen.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Elementarlederverarbeitung erhalten.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Stammeslederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5147] = {"GESUCHT: Arnak Grimmtotem",nil, {"Erschlagt Arnak Grimmtotem und bringt den Beweis Eurer Tat zu Klippenbehüter Langhorn im Freiwindposten."}},
[5148] = {"Stammeslederverarbeitung",nil, {"Bringt eine Wildlederweste und einen Wildlederhelm zu Se'Jib im Schlingendorntal.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr Zugang zur Kunst der Stammeslederverarbeitung erhalten.nnWenn Ihr diese Aufgabe abschließt, werdet Ihr allerdings weder Drachenlederverarbeitung noch Elementarlederverarbeitung erlernen können; seid also vorher sicher, dass dies der Weg ist, dem Ihr folgen wollt."}},
[5149] = {"Pamelas Puppe",nil, {"Sucht Pamelas Puppe."}},
[5150] = {"Dadanga ist hungrig!",nil, {"Dadanga sieht zu Euch auf, während Ihr auf sie zu geht. Sie scheint auf etwas zu hoffen...nnSie hat wahrscheinlich Hunger!nnIm Krater muss irgendetwas wachsen, was die perfekte Leckerei für einen riesigen Pflanzenfresser ist. Dadanga ist ein großes Mädchen; was immer Ihr ihr bringt... es muss viel davon sein!"}},
[5151] = {"Hyperkondensator-Dingsda",nil, {"Erschlagt die Bestie und bringt den Hyperkondensator sowie den Pantherkäfigschlüssel zu Wizlo Lagerputzer zurück."}},
[5152] = {"Tante Marlene",nil, {"Sprecht mit Marlene, südlich der Ruinen von Andorhal."}},
[5153] = {"Ein seltsamer Historiker",nil, {"Bringt Josephs Ehering zu Chromie."}},
[5154] = {"Die Annalen von Darroheim",nil, {"Bringt die 'Annalen von Darroheim' zu Chromie in Andorhal."}},
[5155] = {"Streitmacht von Jaedenar",nil, {"Betretet Jaedenar und erschlagt 4 Hunde von Jaedenar, 4 Gardisten von Jaedenar, 6 Adepten von Jaedenar und 6 Kultisten von Jaedenar, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt."}},
[5156] = {"Die Verderbnis überprüfen",nil, {"Sucht und zerstört 2 entropische Bestien und 2 entropische Schrecken, während Ihr den Narbengrund erforscht. Nachdem Ihr überprüft habt, ob die Krater immer noch mit verderbtem Wasser gefüllt sind, kehrt zu Taronn Rotfeder im Teufelswald zurück."}},
[5157] = {"Verderbtes Wasser sammeln",nil, {"Bringt die leere Feldflasche zum Mondbrunnen im Zentrum von Jaedenar und füllt sie, bevor Ihr zu Greta Mooshuf im Teufelswald zurückkehrt."}},
[5158] = {"Spirituelle Hilfe suchen",nil, {"Bringt das verderbte Mondbrunnenwasser in das Brachland zu Islen Wasserdeuter."}},
[5159] = {"Gesäubertes Wasser wird zum Teufelswald gebracht",nil, {"Bringt das geläuterte Mondbrunnenwasser zu Greta Mooshuf im Teufelswald."}},
[5160] = {"Die oberste Beschützerin",nil, {"Begebt Euch nach Winterquell und sucht Haleh. Gebt ihr Awbees Schuppe."}},
[5161] = {"Der Zorn des blauen Drachenschwarms",nil, {"Sprecht mit Haleh, wenn Ihr weitermachen wollt."}},
[5162] = {"Der Zorn des blauen Drachenschwarms",nil, {"Sprecht mit Jeziba in den Pestländern. Er wohnt in Andorhal."}},
[5163] = {"Sind wir endlich da, Yeti?",nil, {"Nehmt Umis mechanischen Yeti und erschreckt ihre Freunde damit:nnLegacki in der Ewigen Warte (Winterquell)nSprinkel in Gadgetzan (Tanaris)nQuixxil in Marschalls Zuflucht (Krater von Un'Goro)nnWenn Ihr damit fertig seid, bringt den mechanischen Yeti zu Umi zurück."}},
[5164] = {"Verzeichnis der Abtrünnigen",nil, {"Lest das 'Verzeichnis der Abtrünnigen'."}},
[5165] = {"Übergießen der Flammen des Schutzes",nil, {"Übergießt die Flammen der vier Kohlenpfannen des Schutzes in der Schattenfeste im Teufelswald mit dem geläuterten Mondbrunnenwasser und kehrt danach zu Greta Mooshuf im Smaragdrefugium zurück."}},
[5166] = {"Brustplatte des chromatischen Drachenschwarms",nil, {"Um die Brustplatte des chromatischen Drachenschwarms zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:nn1 chromatischen Knochenpanzer.nn10 glänzende chromatische Schuppen.nn10 Einheiten Blut von Helden.nn10 ausgefranste Monstrositätenstickereien."}},
[5167] = {"Beinplatten des chromatischen Widersetzers",nil, {"Um die Beinplatten des chromatischen Widersetzers zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:nn1 chromatischer Knochenpanzer.nn10 glänzende chromatische Schuppen.nn10 Einheiten Blut von Helden.nn5 Häute des Schattens."}},
[5168] = {"Helden von Darroheim",nil, {"Bringt Davils Buchband und Rotpfads Schild zu Carlin Rotpfad."}},
[5181] = {"Bösewichte von Darroheim",nil, {"Bringt den Schädel von Horgus und das zertrümmerte Schwert von Marduk zu Carlin Rotpfad in der Kapelle des hoffnungsvollen Lichts."}},
[5201] = {"Eindringlinge der Winterfelle",nil, {"Rivern Frostwind möchte, dass Ihr 5 Schamanen der Winterfelle und 5 Ursa der Winterfelle erschlagt."}},
[5202] = {"Ein merkwürdiger roter Schlüssel",nil, {"Durchsucht Jaedenar, um herauszufinden, wofür der blutrote Schlüssel verwendet wird."}},
[5203] = {"Rettung aus Jaedenar",nil, {"Bringt Arko'narin dorthin, wo Lichtschmied und ihre Ausrüstung aufbewahrt werden, und beschützt sie dann bei eurer Flucht aus der Schattenfeste. Sucht danach Jessir Mondsichel im Teufelswald auf und lasst sie wissen, was mit ihren Freunden geschehen ist."}},
[5204] = {"Vergeltung des Lichts",nil, {"Kehrt nach Jaedenar zurück und tötet den Sukkubus Rakaiah, bevor Ihr die Überreste von Trey Lichtschmied sucht."}},
[5206] = {"Marodeure von Darroheim",nil, {"Bringt 5 widerhallende Schädel und den Mystikerkristall zu Carlin Rotpfad in den östlichen Pestländern."}},
[5210] = {"Bruder Carlin",nil, {"Bringt die erweiterten 'Annalen von Darroheim' zu Carlin Rotpfad in den östlichen Pestländern."}},
[5211] = {"Verteidiger von Darroheim",nil, {"Befreit 15 Geister von Darroheim und kehrt dann zu Carlin Rotpfad zurück."}},
[5212] = {"Das Fleisch lügt nicht",nil, {"Sammelt 10 verseuchte Fleischproben in Stratholme und bringt sie zu Betina Moppelzink zurück. Ihr vermutet, dass Ihr besagte Fleischproben bei jeder Kreatur in Stratholme finden könnt."}},
[5213] = {"Der aktive Wirkstoff",nil, {"Reist nach Stratholme und durchsucht die Ziggurats. Sucht neue Geißeldaten und bringt sie zu Betina Moppelzink zurück."}},
[5214] = {"Der große Fras Siabi",nil, {"Sucht Fras Siabis Raucherladen in Stratholme und bergt einen Kasten von Siabis Tollem Tabak. Kehrt zu Smokey LaRue zurück, wenn Eure Aufgabe erledigt ist."}},
[5215] = {"Die Geißelkessel",nil, {"Sprecht mit der Hohepriesterin MacDonnell an der Zugwindspitze, westliche Pestländer."}},
[5216] = {"Ziel: Teufelssteinfeld",nil, {"Geht zum Teufelssteinfeld in den westlichen Pestländern. Sucht nach dem dortigen Kessellord und besiegt ihn. Er könnte einen Schlüssel haben, der den Zugang zu dem Kessel ermöglicht. Ihr müsst die leere Flasche: Teufelssteinfeld bei Euch haben, um eine Probe des Giftes zu entnehmen, das sich in dem Kessel findet."}},
[5217] = {"Rückkehr zum Zugwindlager",nil, {"Bringt die gefüllte Flasche: Teufelssteinfeld zur Hohepriesterin MacDonnell am Zugwindlager, westliche Pestländer."}},
[5218] = {"Der Kessel des Teufelssteinfelds",nil, {"Der Seuchenkessel der Geißel steht vor Euch und speit große Mengen Toxin in die Luft. Eine kleine, unauffällige Zugangsklappe befindet sich in der großen Plattform des Kessels.nnDa Ihr den Kessellord bereits besiegt und seinen Schlüssel an Euch genommen habt, könnt Ihr die geeigneten Reagenzien in den Kessel geben, um ein Gegenmittel gegen die Seuche zu schaffen."}},
