[5237] = {"Mission erfüllt!",nil, {"<Name>, Eure erfolgreiche Umarbeitung der Kessel ist ein überwältigender Sieg für uns!nnnnAlle, die sich hier in meinem Umfeld befinden, mögen Folgendes hören: Für Dienste am Königreich Stormwind und der Allianz, die über die gebotenen Pflichten hinausgingen - trotz wenig Erfolg versprechender Chancen und im Angesicht unglaublicher Gefahren, möchte ich <Name> dies geben und ihn unserer unvergänglichen Dankbarkeit als wahren Helden der Allianz versichern. Danke, sage ich!"}},
[5238] = {"Mission erfüllt!",nil, {"<Name>, Eure erfolgreiche Umarbeitung der Kessel ist ein überwältigender Sieg für uns!nnnnAlle, die sich hier in meinem Umfeld befinden, mögen Folgendes hören: Für Dienste an den Verlassenen und an der Horde, die weit über die erwarteten Pflichten hinausgingen - trotz wenig Erfolg versprechender Chancen und im Angesicht unglaublicher Gefahren, möchte ich <Name> dies geben und ihn unserer unvergänglichen Dankbarkeit als wahren Helden der Horde versichern. Hussah!"}},
[5241] = {"Onkel Carlin",nil, {"Sucht Carlin Rotpfad."}},
[5242] = {"Der endgültige Schlag",nil, {"Tötet Moora und Salia und bringt den Kopf von Schattenlord Fel'dan zu Greta Mooshuf im Teufelswald."}},
[5243] = {"Häuser der Heiligen",nil, {"Begebt Euch nach Stratholme im Norden. Durchsucht die Vorratskisten, die über die Stadt verstreut sind, und holt 5 Einheiten Weihwasser von Stratholme. Kehrt zu Leonidas Bartholomäus dem Geachteten zurück, wenn Ihr genug der gesegneten Flüssigkeit gesammelt habt."}},
[5244] = {"Die Ruinen von Kel'Theril",nil, {"Sprecht mit Jaron Steinschleifer in Sternfall."}},
[5245] = {"Die gequälten Seelen von Kel'Theril",nil, {"Benutzt Jarons Hacke, um die vier Fragmente des Hochgeborenenrelikts zu suchen. Bringt sie zu Aurora Himmelsrufer in den östlichen Pestländern."}},
[5246] = {"Fragmente der Vergangenheit",nil, {"Sucht die hochheiligen Schriften der Hochgeborenen im Tempel von Zin-Malor in Azshara und bringt sie zu Aurora Himmelsrufer beim Nordpassturm in den östlichen Pestländern."}},
[5247] = {"Fragmente der Vergangenheit",nil, {"Bringt einen verzauberten Thoriumbarren, 5 Kristallflicker und eine Phiole mit Düsterwasser zu Aurora Himmelsrufer beim Nordpassturm in den östlichen Pestländern."}},
[5248] = {"Gequält von der Vergangenheit",nil, {"Bringt den Kristall von Zin-Malor zum Kel'Therilsee in Winterquell und sucht einen Geist eines Hochgeborenen, der mit Euch sprechen will."}},
[5249] = {"Nach Winterquell!",nil, {"Sucht Sternfall in Winterquell und sprecht mit Wynd Nachtjagd."}},
[5250] = {"Sternfall",nil, {"Sucht die Siedlung Sternfall in Winterquell und sprecht mit Wynd Nachtjagd."}},
[5251] = {"Der Archivar",nil, {"Reist nach Stratholme und sucht Archivar Galford vom Scharlachroten Kreuzzug. Vernichtet ihn und verbrennt das Scharlachrote Archiv."}},
[5252] = {"Reuevolle Hochgeborene",nil, {"Sprecht mit Wynd Nachtjagd in Sternfall und fragt nach dem richtigen Ort für den Kristall von Zin-Malor."}},
[5253] = {"Der Kristall von Zin-Malor",nil, {"Überbringt dem Erzdruiden Fandral Hirschhaupt in Darnassus den Kristall von Zin-Malor."}},
[5261] = {"Eagan Pelzschneider",nil, {"Sprecht mit Eagan Pelzschneider."}},
[5262] = {"Die Wahrheit zeigt sich mit Macht",nil, {"Bringt den Kopf von Balnazzar zu Fürst Nicholas Zverenhoff in den östlichen Pestländern."}},
[5263] = {"Übertroffen",nil, {"Zieht nach Stratholme und vernichtet Baron Totenschwur. Nehmt seinen Kopf und kehrt zu Fürst Nicholas Zverenhoff zurück."}},
[5264] = {"Lord Maxwell Tyrosus",nil, {"Sprecht mit Lord Maxwell Tyrosus. Er befindet sich in der Kirche."}},
[5265] = {"Der Argentumtresor",nil, {"Öffnet den Argentumtresor und nehmt Euch Eure Belohnung."}},
[5281] = {"Die ruhelosen Seelen",nil, {"Sucht Egan. Ihr wisst nur, dass er zuletzt in der Nähe von Stratholme gesehen wurde."}},
[5282] = {"Die ruhelosen Seelen",nil, {"Wendet Egans Blaster auf die geisterhaften und spektralen Bürger von Stratholme an. Wenn die ruhelosen Geister ihre geisterhaften Hüllen sprengen, wendet den Blaster erneut an - dann sind sie endlich frei!nnBefreit 15 ruhelose Seelen und kehrt zu Egan zurück."}},
[5283] = {"Die Kunst des Rüstungsschmiedes",nil, {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Grumnus bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
[5284] = {"Der Weg des Waffenschmieds...",nil, {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Ironus bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
[5301] = {"Die Kunst des Rüstungsschmiedes",nil, {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Okothos bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
[5302] = {"Der Weg des Waffenschmieds...",nil, {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Borgosh bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
[5305] = {"Süße Beschaulichkeit",nil, {"Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück."}},
[5306] = {"Schlangenstein der Schattenjägerin",nil, {"Begebt Euch zur Schwarzfelsspitze und erschlagt Schattenjägerin Vosh'gajin. Holt Vosh'gajins Schlangenstein und kehrt zu Kilram zurück."}},
[5307] = {"Verderbnis",nil, {"Findet den Schwertschmied der schwarzen Wache in Stratholme und vernichtet ihn. Holt die Insignien der schwarzen Wache und kehrt zu Seril Geißelbann zurück."}},
[5321] = {"Der Schläfer ist erwacht",nil, {"Begleitet Kerlonian Schattenspiel zu Liladris Mondbach in Maestras Posten im Eschental."}},
[5341] = {"Das Familienvermögen der Barovs",nil, {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Darrowehr, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Süderstade. Kehrt zu Alexi Barov zurück, sobald die Aufgabe erledigt ist."}},
[5342] = {"Der letzte Barov",nil, {"Begebt Euch zum Zugwindlager - Territorium der Allianz - und ermordet Weldon Barov. Nehmt seinen Kopf und kehrt zu Alexi Barov zurück.nnSeid gewarnt, Weldon wird wahrscheinlich gut von Abschaum der Allianz geschützt."}},
[5343] = {"Das Familienvermögen der Barovs",nil, {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Darrowehr, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Süderstade. Kehrt zu Weldon Barov zurück, sobald die Aufgabe erledigt ist."}},
[5344] = {"Der letzte Barov",nil, {"Begebt Euch zum Bollwerk - Territorium der Horde - und ermordet Alexi Barov. Nehmt seinen Kopf und kehrt zu Weldon Barov zurück.nnSeid gewarnt, Alexi wird wahrscheinlich gut durch Abschaum der Horde geschützt."}},
[5361] = {"Stammbaum",nil, {"Bringt den Bericht von Klippenbehüter Langhorn zu Nataka Langhorn am Geistwandlerposten in Desolace."}},
[5381] = {"Hand von Iruxos",nil, {"Holt den Dämonenkasten aus der Festung Donneraxt und kehrt damit zu Taiga Klugmähne nach Schattenflucht zurück."}},
[5382] = {"Doktor Theolen Krastinov, der Schlächter",nil, {"Sucht Doktor Theolen Krastinov in der Scholomance. Vernichtet ihn, verbrennt dann die Überreste von Eva Sarkhoff und die Überreste von Lucien Sarkhoff. Kehrt zu Eva Sarkhoff zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[5383] = {"Krastinovs Tasche der Schrecken",nil, {"Bringt die Tasche der Schrecken zu Eva Sarkhoff in Darrowehr."}},
[5384] = {"Kirtonos der Herold",nil, {"Kehrt mit dem Blut Unschuldiger zur Scholomance zurück. Sucht die Veranda und legt das Blut der Unschuldigen in die Kohlenpfanne. Kirtonos wird kommen, um sich von Eurer Seele zu nähren.nnKämpft tapfer, gebt keinen Fußbreit nach! Vernichtet Kirtonos und kehrt zu Eva Sarkhoff zurück."}},
[5385] = {"Die Überreste von Trey Lichtschmied",nil, {"Bringt die Überreste von Trey Lichtschmied zu Jessir Mondsichel im Teufelswald."}},
[5386] = {"Der Fang des Tages",nil, {"Sprecht mit Jinar'Zillen in Schattenflucht und bringt Nataka Langhorn dann 2 Blutbauchfische."}},
[5401] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben.nnWährend Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung.nnErfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
[5402] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
[5403] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
[5404] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Sachen sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name> ... mit solch mächtigen Wesen ist nicht zu spaßen."}},
[5405] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben.nnWährend Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung.nnErfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
[5406] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Sachen sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name> ... mit solch mächtigen Wesen ist nicht zu spaßen."}},
[5407] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
[5408] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
[5421] = {"Fisch im Eimer",nil, {"Ich werde jeden belohnen, der mein Leben ein bisschen einfacher macht... Ich genieße es, so wenig wie möglich zu tun, Mann!nnUnter den Docks hier in Shadowprey habe ich viele Schalentierfallen. Wenn Ihr so freundlich wärt, meine Arbeit zu tun und meine Schalentiere einzusammeln, dann werde ich Euch mit etwas belohnen, das Ihr wollt... Ihr wisst schon, was ich meine, eh? Für jeweils fünf Schalentiere, die Ihr mir bringt, gebe ich Euch einen feinen Blutbauchfisch!"}},
[5441] = {"Faule Peons",nil, {"Benutzt den Großknechttotschläger an faulen Peons, wenn sie schlafen. Weckt 5 Peons auf und bringt dann den Großknechttotschläger zu Großknecht Thazz'ril ins Tal der Prüfungen zurück."}},
[5461] = {"Der Mensch Ras Frostraunen",nil, {"Reist in das Arathihochland zu den Ruinen von Stromgarde. Durchsucht Stromgarde nach einem Andenken der Erinnerung. Wenn Ihr einen solchen Gegenstand findet, kehrt mit ihm zu Magistrat Marduk zurück."}},
[5462] = {"Der Sterbende Ras Frostraunen",nil, {"Reist zur Hoffnung des Lichts in den östlichen Pestländern und sucht Leonidas Bartholomäus den Geachteten auf. Zeigt ihm das Andenken der Erinnerung und berichtet ihm alles, was Ihr entdeckt habt."}},
[5463] = {"Menethils Geschenk",nil, {"Begebt Euch nach Stratholme und sucht Menethils Geschenk. Platziert das Andenken der Erinnerung auf dem unheiligen Boden."}},
[5464] = {"Menethils Geschenk",nil, {"Bringt das seelengebundene Andenken zu Leonidas Bartholomäus in den östlichen Pestländern."}},
[5465] = {"Seelengebundenes Andenken",nil, {"Kehrt zu Magistrat Marduk in Darrowehr zurück. Zeigt ihm das seelengebundene Andenken."}},
[5466] = {"Der Lich Ras Frostraunen",nil, {"Sucht Ras Frostraunen in der Scholomance. Wenn Ihr ihn gefunden habt, wendet das seelengebundene Andenken auf sein untotes Antlitz an. Solltet Ihr ihn erfolgreich in einen Sterblichen zurückverwandeln können, dann schlagt ihn nieder und nehmt den menschlichen Kopf von Ras Frostraunen an Euch. Bringt den Kopf zu Magistrat Marduk."}},
[5481] = {"Gordos Aufgabe",nil, {"Sammelt 3 Düsterkräuter und bringt sie zu Jungapotheker Holland auf den Friedhof von Brill."}},
[5482] = {"Verdammniskraut",nil, {"Sammelt 10 Verdammniskräuter und bringt sie zu Jungapotheker Holland."}},
[5501] = {"Knochenjäger",nil, {"Bringt Bibbly F'utzbuckel bei Kormeks Hütte 10 Kodoknochen vom Kodofriedhof."}},
[5502] = {"Tageseltern der Horde",nil, {"Bringt Euren Schützling in das Waisenhaus von Orgrimmar zurück, indem Ihr die Pfeife des Orcwaisenkindes an Waisenmatrone Schlachterbe im Tal der Ehre übergebt."}},
[5503] = {"Anstecknadel der Argentumdämmerung",nil, {"Alle, die für die Argentumdämmerung das Böse bekämpfen, müssen unsere Anstecknadel tragen. Wenn Ihr eine solche tragt, erhaltet Ihr das Recht, Belohnungen in Anspruch zu nehmen, die unsere Rüstmeister anzubieten haben.nnWährend Ihr die Nadel tragt, könnt Ihr Euch Geißelsteine von erschlagenen Mitgliedern der Geißel holen; das sind Insignien, die sie manchmal besitzen. Beschafft Euch diese Insignien und bringt sie mir. Für alle, die Ihr abgebt, erhaltet Ihr unsere Marke des Heldentums - die Ehrenmarke der Argentumdämmerung.nnErfüllt Euer Schicksal, <Klasse> ... stürzt Euch noch heute in den Kampf!"}},
[5504] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Hasana ins Bollwerk in den westlichen Pestländern."}},
[5505] = {"Der Schlüssel zur Scholomance",nil, {"Tja, da ist er, <Name>, der fertige Skelettschlüssel. Ich bin eigentlich absolut sicher, dass dieser Schlüssel Euch Zutritt zur Scholomance verschaffen wird. Es gibt aber nur eine Möglichkeit, um absolut sicher zu sein, dass er wirklich funktioniert. Verschwendet keine Zeit darauf, es auszuprobieren, sage ich.nnViel Glück. Möget Ihr auch weiterhin Licht in die Dunkelheit bringen!"}},
[5507] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Funkellicht im Zugwindlager in den westlichen Pestländern."}},
[5508] = {"Geißelsteine des Verderbers",nil, {"Der Geißelstein des Verderbers ist die Insignie der Oberanführer der Geißel. Ein mächtiges Wesen, das eine Autoritätsposition über der Geißel innehat, wird immer einen besitzen. Eine solch bösartige Kreatur zu erschlagen, würde die Sache der Argentumdämmerung und alle guten Zwecke sicherlich voranbringen!nnIm Namen der Argentumdämmerung werde ich Euch eine Ehrenmarke im Tausch für jeweils eine dieser Insignien geben. Seid vorsichtig, wenn Ihr diese Insignien holt, <Name>... mit solch mächtigen Wesen ist nicht zu spaßen."}},
[5509] = {"Geißelsteine des Eindringlings",nil, {"Der Geißelstein des Eindringlings ist die Insignie der Elitetruppen der Geißel. Man findet sie manchmal bei den stärkeren Truppen, die in den Pestländern stationiert sind. Das Erschlagen einer solchen Kreatur ist wahrlich eine Herausforderung, aber eine, die jemand, der sich den Sieg über das Böse auf die Fahne geschrieben hat, mit Freude erfüllen sollte.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zehn dieser Insignien. Bringt sie mir und ich werde sicherstellen, dass Ihr Eure gerechte Belohnung erhaltet."}},
[5510] = {"Geißelsteine des Dieners",nil, {"Der Geißelstein des Dieners ist die Insignie des Fußvolkes der Geißel. Man findet sie manchmal bei den schwächeren Truppen, die in den Pestländern stationiert sind, obwohl der Begriff 'schwächere' natürlich relativ betrachtet werden muss. Die Geißel sollte, ganz gleich wo sie sich befindet, niemals unterschätzt werden.nnDie Argentumdämmerung bietet derzeit eine Ehrenmarke im Tausch für zwanzig dieser Insignien - ein Tausch, den ich freudig ausführe, vorausgesetzt, Ihr habt genug Geißelsteine!"}},
[5511] = {"Der Schlüssel zur Scholomance",nil, {"Tja, da ist er, <Name>, der fertige Skelettschlüssel. Ich bin eigentlich absolut sicher, dass dieser Schlüssel Euch Zutritt zur Scholomance verschaffen wird. Es gibt aber nur eine Möglichkeit, um absolut sicher zu sein, dass er wirklich funktioniert. Verschwendet keine Zeit darauf, es auszuprobieren, sage ich.nnViel Glück. Mögen Eure Siege Euch auch weiterhin Ruhm und Macht bringen, während Ihr die Scholomance in Schutt und Asche legt!"}},
[5513] = {"Mäntel der Dämmerung",nil, {"Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des hoffnungsvollen Lichts, östliche Pestländer."}},
[5514] = {"Sold reimt sich auf...",nil, {"Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan."}},
[5515] = {"Krastinovs Tasche der Schrecken",nil, {"Sucht nach Jandice Barov in der Scholomance und vernichtet sie. Entnehmt ihrer Leiche Krastinovs Tasche der Schrecken. Bringt die Tasche zu Eva Sarkhoff."}},
[5517] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des hoffnungsvollen Lichts, östliche Pestländer."}},
[5518] = {"Der Ogeranzug der Gordok",nil, {"Bringt 4 Runenstoffballen, 8 Stücke unverwüstliches Leder, 2 Runenfaden und etwas Ogergerbemittel zu Knot Zwingschraub. Momentan ist er im Gordokflügel von Düsterbruch angekettet."}},
[5519] = {"Der Ogeranzug der Gordok",nil, {"Ihr braucht einen weiteren Ogeranzug, richtig? Da ich wohl so schnell nirgendwo hingehe - bringt mir einfach die gleichen Sachen, um die ich Euch zuvor gebeten habe und ich fertige Euch einen weiteren Ogeranzug an."}},
[5521] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Funkellicht im Zugwindlager in den westlichen Pestländern."}},
[5522] = {"Leonidas Bartholomäus",nil, {"Bringt die gefrorenen Eier zu Leonidas Bartholomäus in den östlichen Pestländern."}},
[5524] = {"Der Chromatische Mantel der Dämmerung",nil, {"Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Hasana ins Bollwerk in den westlichen Pestländern."}},
[5525] = {"Befreit Knot!",nil, {"Sie nennen ihn den Gordokfesselschlüssel - Naja, zumindest tun es diese \"Tonnenweise Spaß auf zwei Beinen\"-Oger. Ich denke einer der Oger müsste ihn haben, leider weiß ich nicht genau welcher. Möglicherweise ist die Chance größer, dass einer der Wachen ihn hat...nnWas erzähle ich da? - sicher habt Ihr ihn schon, oder? Ist es der da??!"}},
[5526] = {"Die Splitter der Teufelsranke",nil, {"Sucht die Teufelsranke in Düsterbruch und nehmt einen Teufelsrankensplitter mit Euch. Aller Wahrscheinlichkeit nach, werdet Ihr Alzzin den Wildformer töten müssen, um an die Teufelsranke zu gelangen. Benutzt das Requiliar der Reinheit, um darin den Splitter sicher zu versiegeln, und bringt das versiegelte Reliquiar zu Rabine Saturna in Nachthafen, Mondlichtung."}},
[5527] = {"Ein Reliquiar der Reinheit",nil, {"Reist nach Silithus und sucht in den Ruinen des Südwindpostens nach einem Reliquiar der Reinheit. Sollte Eure Suche Erfolg haben, kehrt zu Rabine Saturna in Nachthafen auf der Mondlichtung zurück."}},
[5528] = {"Der Gordokgeschmackstest",nil, {"Ihr jetzt de' Big Boss hier. Also kriegen freie Kostproben meiner besseren Gebräue! Diese Tränke werden Euch verstärken UND Euch Schädel weghauen! Das ist da' guta Zeug, Chef... Ihr versuchen und dann Kreeg glauben!"}},
[5529] = {"Verseuchte Jungtiere",nil, {"Tötet 20 verseuchte Jungtiere und kehrt dann zu Betina Moppelzink bei der Kapelle des hoffnungsvollen Lichts zurück."}},
[5531] = {"Betina Moppelzink",nil, {"Bringt die gefrorenen Eier zu Betina Moppelzink."}},
[5533] = {"Scholomance",nil, {"Sprecht mit Alchimist Arbington an der Zugwindspitze, westliche Pestländer."}},
[5534] = {"Kim'jaels 'fehlende' Ausrüstung",nil, {"Sucht, in den Ruinen von Eldarath irgendeine Rune, die die Naga in ihrem Besitz haben, und bringt sie Kim'jael in Azshara."}},
[5535] = {"Spirituelle Unruhe",nil, {"Vernichtet 6 Erscheinungen einer Hochgeborenen und 6 Lichlinge eines Hochgeborenen, bevor Ihr zu Loh'atu in Azshara zurückkehrt."}},
[5536] = {"Ein Land voller Hass",nil, {"Tötet 6 Satyrn der Haldarr, 2 Schwindler der Haldarr sowie 2 Teufelsanbeter der Haldarr und kehrt dann zu Loh'atu an der Grenze zum Eschental zurück."}},
[5537] = {"Skelettfragmente",nil, {"Bringt 15 Skelettfragmente zu Alchimist Arbington an der Zugwindspitze, westliche Pestländer."}},
[5538] = {"Sold reimt sich auf...",nil, {"Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan."}},
[5541] = {"Munition für Schlingerschuss",nil, {"Bringt Schlingerschuss' Munition zu Hegnar Schlingerschuss in Dun Morogh."}},
[5542] = {"Dämonenhunde",nil, {"Erschlagt 20 kleine Seuchenhunde, 5 Seuchenhunde und 5 rasende Seuchenhunde. Kehrt zu Tirion Fordring zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[5543] = {"Blutgefärbter Himmel",nil, {"Erschlagt 30 Seuchenfledermäuse und kehrt zu Tirion Fordring zurück."}},
[5544] = {"Aassammlung",nil, {"Sucht nach den Aaslarven und Aasverschlingern dieses Gebiets. Erschlagt sie und sammelt ihr Fleisch. Kehrt zu Tirion Fordring zurück, wenn Ihr 15 Scheiben Aaswurmfleisch gesammelt habt."}},
[5545] = {"Ein Bündel Probleme",nil, {"Bringt 8 Holzbündel zu Raelen beim Holzfällerlager des Osttals."}},
[5561] = {"Kodozusammentrieb",nil, {"Bringt fünf gezähmte Kodos sowie den Kodokombobulator zu Smeed Schraubenbuddel bei Schraubenbuddels Lager."}},
[5581] = {"Portale der Legion",nil, {"Verbannt 6 Portale beim Zirkel der Mannoroc und kehrt dann zu Taiga Klugmähne nach Schattenflucht zurück."}},
[5582] = {"Gesunde Großdrachenschuppe",nil, {"Bringt die gesunde Großdrachenschuppe zu Betina Moppelzink bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
[5601] = {"Schwester Pamela",nil, {"Sucht Pamela Rotpfad in Darroheim."}},
[5621] = {"Gewänder des Mondes",nil, {"Sucht die Schildwache Shaya und heilt ihre Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt sie danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Laurna Morgenglanz in Dolanaar zurück."}},
[5622] = {"Elune geneigt",nil, {"Sprecht mit Laurna Morgenglanz in Teldrassil."}},
[5623] = {"Dem Licht geneigt",nil, {"Sprecht mit der Priesterin Josetta im Wald von Elwynn."}},
[5624] = {"Gewänder des Lichts",nil, {"Sucht die Wache Roberts und heilt seine Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Priesterin Josetta in Goldhain zurück."}},
[5625] = {"Gewänder des Lichts",nil, {"Sucht den Gebirgsjäger Dolf und heilt seine Wunden mit Hilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Maxan Anvol in Kharanos zurück."}},
[5626] = {"Dem Licht geneigt",nil, {"Sprecht mit Maxan Anvol in Dun Morogh."}},
