[5641] = {"Furchtlos",nil, {"Aus Euch könnte ein großartiger <Klasse> werden, <Name>. Leistet weiter so gute Arbeit!nnIhr habt bereits bewiesen, dass Ihr bereit für die Schlacht seid. Vielleicht ist es an der Zeit, Euch ein wenig mehr beizubringen."}},
[5642] = {"Schattenwache",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
[5643] = {"Schattenwache",nil, {"Sprecht mit Ur'kyo in Orgrimmar."}},
