[5675] = {"Elunes Anmut",nil, {"Sprecht mit der Priesterin Alathea in Darnassus."}},
[5676] = {"Arkane Rückkopplung",nil, {"Wir waren gezwungen, einige Magien zu lernen, um uns vor dem zu schützen, was üblicherweise unser stärkster Feind genannt wird: dem Arkanen.nnIch würde Euch einen Zauber beibringen, um Euch und Euren Kameraden bei der Verteidigung gegen diejenigen zu helfen, die diese Macht einsetzen."}},
[5677] = {"Arkane Rückkopplung",nil, {"Sprecht mit der Hohepriesterin Laurena in Sturmwind."}},
[5679] = {"Verschlingende Seuche",nil, {"Ich würde Euch 'Verschlingende Seuche' beibringen, wenn Ihr Euch bereit dazu fühlt, <Name>. Dieser Spruch ist nicht gerade von kleiner Macht und wird Euch in den kommenden Schlachten helfen. Übt damit - es wird später Prüfungen dazu geben."}},
[5680] = {"Schattenwache",nil, {"Eure Riten werden bald kommen. Fürs Erste lehre ich Euch einen Zauber, der Euch von großem Nutzen sein wird, wenn Ihr mit Euren Feinden kämpft."}},
[5713] = {"Ein Schuss, ein Treffer",nil, {"Schützt die Schildwache Aynasha und sprecht dann mit Onaeya bei Maestras Posten im Eschental."}},
[5721] = {"Die Schlacht um Darroheim",nil, {"Legt das Reliktbündel auf den Marktplatz von Darroheim.nnVerhindert Davil Lichterscheins Tod, bis Horgus stirbt.nnVerhindert Hauptmann Rotpfads Tod, bis Rotpfad der Verderbte erscheint.nnBesiegt Rotpfad den Verderbten.nnSprecht mit dem Geist von Joseph Rotpfad, sobald er auf dem Marktplatz erscheint, und sprecht dann mit Pamela in ihrem Zuhause."}},
[5722] = {"Die Suche nach dem verloren gegangenen Ranzen",nil, {"Sucht im Flammenschlund nach Maur Grimmtotems Leiche und durchsucht sie nach interessanten Gegenständen."}},
[5723] = {"Die Kraft des Feindes wird auf die Probe gestellt",nil, {"Sucht in Orgrimmar nach dem Flammenschlund, tötet dann 8 Flammenschlundtroggs und 8 Schamanen der Flammenschlundtroggs und kehrt anschließend zu Rahauro in Donnerfels zurück."}},
[5724] = {"Wiederbeschaffung des verloren gegangenen Ranzens",nil, {"Bringt den Grimmtotemranzen zu Rahauro nach Donnerfels."}},
[5725] = {"Die Macht der Zerstörung...",nil, {"Bringt die Bücher 'Schattenzauber' und 'Zauberformeln aus dem Nether' zu Varimathras nach Unterstadt."}},
[5726] = {"Verborgene Feinde",nil, {"Bringt das Insigne eines Offiziers zu Thrall nach Orgrimmar."}},
[5727] = {"Verborgene Feinde",nil, {"Bringt Neeru Feuerklinge das Insigne des Offiziers und sprecht mit ihm. Seht, ob er Euch glaubt, dass Ihr ein Mitglied der Brennenden Klinge seid, und kehrt dann zu Thrall nach Orgrimmar zurück."}},
[5728] = {"Verborgene Feinde",nil, {"Tötet Bazzalan und Jergosh den Herbeirufer, bevor Ihr zu Thrall nach Orgrimmar zurückkehrt."}},
[5729] = {"Verborgene Feinde",nil, {"Sprecht mit Neeru Feuerklinge in Orgrimmar."}},
[5730] = {"Verborgene Feinde",nil, {"Sprecht in Orgrimmar mit Thrall und berichtet ihm, was Ihr erfahren habt."}},
[5741] = {"Szepter des Lichts",nil, {"Besorgt das Szepter des Lichts und bringt es zurück zu Azore Aldamort am Turm in Ethel Rethor."}},
[5742] = {"Erlösung",nil, {"Hört Euch an, was Tirion Fordring zu sagen hat."}},
[5761] = {"Vernichtung der Bestie",nil, {"Begebt Euch in den Flammenschlund und erschlagt Taragaman den Hungerleider. Bringt anschließend dessen Herz zu Neeru Feuerklinge nach Orgrimmar."}},
[5762] = {"Hemet Nesingwary Jr.",nil, {"Bringt Kravels Kiste zu Hemet Nesingwary Jr. ins Schlingendorntal."}},
[5763] = {"Jagd im Schlingendorntal",nil, {"Bringt Roons Kodohorn zu Hemet Nesingwary Jr. ins Schlingendorntal."}},
[5781] = {"Von vergessenen Erinnerungen",nil, {"Begebt Euch zum Tiefgewölbe im südlichsten Abschnitt der östlichen Pestländer und sucht nach Taelans Hammer. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt."}},
[5801] = {"Feuersäulen geschmiedet",nil, {"Bringt die Skelettschlüsselform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Skelettschlüsselform am Lavasee, um den unvollendeten Skelettschlüssel zu schmieden.nnBringt den unvollendeten Skelettschlüssel dann zum Alchimisten Arbington an der Zugwindspitze in den westlichen Pestländern."}},
[5802] = {"Feuersäulen geschmiedet",nil, {"Bringt die Skelettschlüsselform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Skelettschlüsselform am Lavasee, um den unvollendeten Skelettschlüssel zu schmieden.nnBringt den unvollendeten Skelettschlüssel zu Apotheker Zitters im Bollwerk, westliche Pestländer."}},
[5803] = {"Arajs Skarabäus",nil, {"Zerstört Araj den Beschwörer und bringt Arajs Skarabäus zum Alchimisten Arbington an der Zugwindspitze in den westlichen Pestländern."}},
[5804] = {"Arajs Skarabäus",nil, {"Vernichtet Araj den Beschwörer und bringt Arajs Skarabäus zum Apotheker Zitters im Bollwerk, in den westlichen Pestländern."}},
[5805] = {"Willkommen!",nil, {"Bringt Merissa Stillbrunn in Goldhain den Goldhaingeschenkgutschein."}},
[5821] = {"Leibwache zu mieten",nil, {"Bringt Gizeltons Karawane am Dorf der Kolkarzentauren vorbei. Sprecht in Schraubenbuddels Lager mit Smeed, um Eure Belohnung zu erhalten."}},
[5841] = {"Willkommen!",nil, {"Bringt Yori Bruchhelm in Kharanos den Geschenkgutschein von Kharanos."}},
[5842] = {"Willkommen!",nil, {"Bringt Orenthil Wisperwind in Dolanaar den Geschenkgutschein von Dolanaar."}},
