[8268] = {"Aufopferungsabzeichen",nil, nil},
[8269] = {"Aufopferungsabzeichen",nil, nil},
[8271] = {"Held der Sturmlanzen",nil, {"Als ein Held der Stormpike, dürft Ihr Euch eines dieser erst kürzlich geplün… äh, wiederentdeckten Schätze, aussuchen."}},
[8272] = {"Held der Frostwölfe",nil, {"Was ist das für ein Gefühl, <Name>? Wie fühlt es sich an, die Linien des Feindes zu zerschmettern und jeglichen Widerstand auszulöschen? Es ist ein gutes Gefühl, nicht wahr?nnFür Eure Heldentat habt Ihr eine große Belohnung verdient."}},
[8273] = {"Orans Dankbarkeit",nil, {"Ach das ist ja interessant! Ihr sagt, Ihr habt diese Schrifttafel auf einer kleinen, unbewohnten Insel nahe der Aussichtsklippen im Hinterland gefunden, <Klasse>? Wirklich äußerst interessant. nnIch danke Euch für Eure Bemühungen in dieser Angelegenheit. Unsere dunkle Fürstin wird mit dieser Entdeckung sehr zufrieden sein. Erlaubt mir, Euch angemessen zu belohnen."}},
[8275] = {"Die Rückeroberung von Silithus",nil, {"Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus."}},
[8276] = {"Die Rückeroberung von Silithus",nil, {"Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus."}},
[8277] = {"Tödliches Wüstengift",nil, {"Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr 8 Stachel von Steinpanzerskorpiden und 8 Giftzähne von Sandhuschern für sie sammelt. Kehrt zu ihr zurück, sobald Ihr diese Aufgabe erfüllt habt."}},
[8278] = {"Noggles letzte Hoffnung",nil, {"Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr je 3 Stachel von Steinpanzerzangen und Steinpanzerschindern, sowie 3 Felspirschergiftzähne für sie sammelt."}},
[8279] = {"Das Lexikon des Schattenhammerklans",nil, {"Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Lexikons des Schattenhammerklans."}},
[8280] = {"Sicherung der Versorgungslinien",nil, {"Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 15 Baggerschläger tötet."}},
[8281] = {"Verstärkte Sicherheit",nil, {"Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 20 Baggerbrecher tötet."}},
[8282] = {"Noggles verlorener Ranzen",nil, {"Spürt Noggles Ranzen im südlichen Gebirge von Silithus auf und bringt ihn zu ihm, auf Burg Cenarius, zurück."}},
[8283] = {"GESUCHT: Totenstachel, Terror der Wüste",nil, {"Bringt Totenstachels Zange zu Vish Kozus, Hauptmann der Wache."}},
[8284] = {"Das Geheimnis des Schattenhammerklans",nil, {"Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr 8 Schrifttafelfragmente des Schattenhammerklans bringt."}},
[8285] = {"Der Deserteur",nil, {"Bringt die wiederhergestellte Schrifttafel zu Ortell dem Einsiedler in Silithus."}},
[8286] = {"Was uns morgen erwartet",nil, {"Reist zu den Höhlen der Zeit in Tanaris und findet Anachronos, Nachkomme Nozdormus."}},
[8287] = {"Ein teuflischer Plan",nil, {"Bringt Kommandant Mar'alith die entschlüsselte Abschrift der Schrifttafel."}},
[8288] = {"Nur einer kann sich erheben",nil, {"Bringt Brutwächter Dreschbringers Kopf zu Baristolth der Sandstürme in die Burg Cenarius in Silithus."}},
