[8298] = {"Noch mehr Vorratskisten",nil, nil},
[8299] = {"Zerschlagt die Versorgungslinien Arathors",nil, {"Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall."}},
[8300] = {"Noch mehr Vorratskisten",nil, nil},
[8301] = {"Der Pfad des Gerechten",nil, {"Kehrt zu Baristolth zurück, sobald Ihr 200 Silithidenknochenpanzerfragmente gesammelt habt."}},
[8302] = {"Die Hand der Gerechtigkeit",nil, {"Kehrt zu Baristolth zurück, sobald Ihr 200 Silithidenknochenpanzerfragmente gesammelt habt."}},
[8303] = {"Anachronos",nil, {"Sucht nach Anachronos bei den Höhlen der Zeit in Tanaris."}},
[8304] = {"Liebste Natalia",nil, {"Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr die Bewohner von Bronzebarts Lager befragt. Das Lager befindet sich südlich von Burg Cenarius.nnKehrt zu Kommandant Mar'alith zurück, wenn Ihr die benötigten Informationen gesammelt habt."}},
[8305] = {"Lang Vergessenes",nil, {"Sucht die Kristallträne in Silithus und blickt in ihr Innerstes."}},
[8306] = {"Der Schlund des Wahnsinns",nil, {"Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.nnSprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.nnUnd <Name>, denkt an die Worte des Kommandanten: \"Tut, was Ihr tun müsst...\""}},
[8307] = {"Wüstenrezept",nil, {"Haltet beim Lager des Schattenhammerklans westlich von Burg Cenarius nach Informationen zum Zubereiten von Sandwurmfleisch Ausschau."}},
[8308] = {"Der verlorene Brief von Brann Bronzebart",nil, {"Liefert Brann Bronzebarts verlorenen Brief in Bronzebarts Lager im südlichen Silithus ab."}},
[8309] = {"Glyphenjagd",nil, {"Rutgar Glyphenschnitzer in Bronzebarts Lager in Silithus möchte, dass Ihr in den Bau des Ashischwarms, Bau des Zoraschwarms und Bau des Regalschwarms eindringt und dort Abpausbilder von den glyphenverzierten Kristallen anfertigt.nnBenutzt das Abschriftenset für Geologen, um Abpausbilder der uralten Glyphen herzustellen. Kehrt nach Abschluss der Aufgabe zu Rutgar Glyphenschnitzer zurück."}},
[8310] = {"Knackt den Kode",nil, {"Frankal Steinsteg in Bronzebarts Lager in Silithus möchte, dass Ihr ihm jeweils ein Silithidengehirn des Zoraschwarms, Ashischwarms und des Regalschwarms bringt. Kehrt nach der Erfüllung Eurer Aufgabe zu Frankal zurück."}},
[8311] = {"Schlotternachtssüßigkeiten für Jesper!",nil, {"Sprecht mit den Gastwirten in Sturmwind, Eisenschmiede und Darnassus, sowie mit Talvash del Kissel in Eisenschmiede. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.nnKehrt zu Jesper ins Waisenhaus von Sturmwind mit einem Marzipan aus Darnassus, einer Kaugummikugel aus Gnomeregan, Nugat aus Sturmwind und einem Pfefferminzbonbon aus Eisenschmiede zurück."}},
[8312] = {"Schlotternachtssüßigkeiten für Spuks!",nil, {"Sprecht mit den Gastwirten in Orgrimmar, Unterstadt und Donnerfels, sowie mit Kali Remik in Sen'jin. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.nnKehrt zu Spuks ins Waisenhaus von Orgrimmar mit einem Marzipan aus Donnerfels, einer Kaugummikugel der Dunkelspeere, Nugat aus Orgrimmar und einem Pfefferminzbonbon aus Unterstadt zurück."}},
[8313] = {"Geteiltes Wissen",nil, {"Bringt die ausgerissene Rezeptseite zu Calandrath ins Gasthaus der Burg Cenarius."}},
[8314] = {"Des Rätsels Lösung",nil, {"Rutgar Glyphenschnitzer in Bronzebarts Lager möchte, dass Ihr Geologin Lerchenbann auf Burg Cenarius in Silithus das glyphenverzierte Kristallprisma bringt."}},
[8315] = {"Die Anrufung",nil, {"Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr den Kristallentriegelungsmechanismus vom Abgesandten der Qiraji beschafft.nnNehmt die Glyphen der Anrufung, begebt Euch zu den Knochen von Grakkarond südlich von Burg Cenarius und zeichnet sie dort in den Sand. Sollte der Abgesandte der Qiraji erscheinen, tötet ihn und nehmt den Kristallentriegelungsmechanismus an Euch. Kehrt anschließend zu Geologin Lerchenbann zurück.nnFür diese Aufgabe werdet Ihr eine kleine Armee benötigen, <Name>!"}},
[8316] = {"Kriegsausrüstung",nil, {"Ein Krieg wird kommen, <Name>, und mit ihm unbeschreibliches Leiden. Ihr habt alles getan, um was man Euch hätte bitten können, dafür sollt Ihr belohnt werden.nnSollte diese… diese Prophezeiung jemals wahr werden…nnnnDas könnte das Ende für unser aller Zukunft bedeuten.nnDiese Ausrüstung wird von unschätzbarem Wert für Euren Feldzug in der Wüste sein."}},
[8317] = {"Küchengehilfe",nil, {"Calandrath von der Burg Cenarius braucht 10 geräucherte Wüstenknödel."}},
[8318] = {"Geheime Nachrichten",nil, {"Bringt Bor Wildmähne auf Burg Cenarius 10 verschlüsselte Schattenhammertexte."}},
[8319] = {"Verschlüsselte Schattenhammertexte",nil, {"Bringt mir weitere Texte. Es dauert nicht mehr lange, und wir haben die Twilight-Hammer aus Silithus vertrieben."}},
[8320] = {"Geolords des Schattenhammers",nil, {"Tötet 10 Geolords des Schattenhammers und kehrt anschließend zu Huum Wildmähne auf Burg Cenarius zurück."}},
[8321] = {"Vyral der Üble",nil, {"Bringt Vyrals Siegelring zu Huum Wildmähne auf Burg Cenarius."}},
[8322] = {"Faule Eier",nil, {"Werft die faulen Eier in den großen Braukessel im Gasthaus von Süderstade."}},
[8323] = {"Wahrer Glauben",nil, {"Ortell der Einsiedler möchte, dass Ihr ihm 10 verschlüsselte Schattenhammertexte beschafft."}},
[8324] = {"Fester Glauben",nil, {"Konntet Ihr noch mehr verschlüsselte Texte finden? Die Entschlüsselung wird noch eininge Zeit brauchen, also bringt mir ruhig einen großen Stapel Texte."}},
[8325] = {"Rückeroberung der Insel der Sonnenwanderer",nil, {"Tötet 8 Manawyrm; sie halten sich rund um die brennenden Kristalle auf, die man nahe des Turms und des Vorbaus findet. Kehrt zu Magistrix Erona beim Turm auf der Insel der Sonnenwanderer zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
[8326] = {"Bedauerliche Maßnahmen",nil, {"Sammelt 8 Luchshalsbänder von erlegten Bachtatzenluchsen und Bachtatzenjungen ein. Kehrt zu Magistrix Erona auf der Insel der Sonnenwanderer zurück, sobald Ihr Eure Aufgabe erfüllt habt."}},
[8327] = {"Berichterstattung an Lanthan Perilon",nil, {"Sprecht mit Lanthan Perilon auf der Insel der Sonnenwanderer. Er steht in der Nähe des Pfads, der vom Sonnenturm Richtung Westen verläuft."}},
[8328] = {"Ausbildung zum Magier",nil, {"Sprecht mit Julia Sonnensturm - der Magielehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8329] = {"Ausbildung zum Krieger",nil, {"Sprecht mit Delios - dem Kriegerlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8330] = {"Solanians Habe",nil, {"Sammelt Brunnenbehüter Solanians Seherkugel, seine Rolle der Geißelmagie und sein Tagebuch ein. Ihr findet die Gegenstände auf der Insel der Sonnenwanderer bei dem Tümpel, dem Brunnen und bei einem der brennenden Kristalle. Bringt sie zu dem Brunnenbehüter oben im Sonnenturm auf der Insel der Sonnenwanderer, sobald Ihr alle gefunden habt."}},
[8331] = {"Aurel Goldblatt",nil, {"Sprecht mit Aurel Goldblatt auf Burg Cenarius."}},
[8332] = {"Fürsten des Rates",nil, {"Bringt Aurel Goldblatt auf Burg Cenarius 1 großen glänzenden Splitter und 3 Abyssische Wappen."}},
[8333] = {"Das Standesmedaillon",nil, {"Falls Ihr ein weiteres Standesmedaillon eines Twilightkultisten braucht, helfe ich Euch gerne weiter. In jedem Fall benötige ich jedoch einen weiteren glänzenden Splitter und einige abyssische Wappen…"}},
[8334] = {"Aggressivität",nil, {"Tötet 7 Borklinge und 7 wilde Borklinge. Kehrt dann zu Lanthan Perilon auf der Insel der Sonnenwanderer zurück."}},
[8335] = {"Felendren der Verbannte",nil, {"Tötet 8 Arkangespenster, 2 besudelte Arkangespenster und Felendren den Verbannten. Ihr findet sie in der Akademie von Falthrien. Bringt Felendrens Kopf zu Lanthan Perilon auf der Insel der Sonnenwanderer."}},
[8336] = {"Für eine Handvoll Fragmente",nil, {"Sammelt 6 Arkanspäne und bringt sie zu Arkanist Ithanas auf der Insel der Sonnenwanderer. Derartige Fragmente findet man bei allen Mana benutzenden Kreaturen auf der Insel."}},
[8338] = {"Besudelter Arkanspan",nil, {"Bringt den besudelten Arkanspan zu Arkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer."}},
[8339] = {"Royalty of the Council <NYI> <TXT> UNUSED",nil, {"Bringt die Gegenstände zu Aurel Goldblatt in der Burg Cenarius"}},
[8340] = {"Siegelring des Schattenhammers",nil, nil},
[8341] = {"Lords des Rates",nil, {"Bringt Aurel Goldblatt auf Burg Cenarius 5 große glänzende Splitter und 3 abyssische Siegel."}},
[8342] = {"Herrschaftsring des Schattenhammerklans",nil, nil},
[8343] = {"Goldblatts Entdeckung",nil, {"Sprecht mit Aurel Goldblatt auf Burg Cenarius."}},
[8344] = {"Schlüssel zur Macht",nil, {"Bring 4 Essenzen eines Gespenstes und 1 Essenz eines besudelten Gespenstes zu Beschwörer Teli'Larien beim Sonnenturm auf der Insel der Sonnenwanderer."}},
[8345] = {"Der Schrein von Dath'Remar",nil, {"Lest die Tafel am Schrein von Dath'Remar und kehrt anschließend zu Brunnenbehüter Solanian beim Sonnenturm auf der Insel der Sonnenwanderer zurück."}},
[8346] = {"Unstillbarer Durst",nil, {"Verwendet die Fähigkeit Manadurst sechsmal auf Kreaturen, die über Mana verfügen und kehrt anschließend zu Arkanist Helion zurück."}},
[8347] = {"Unterstützung für die Kundschafter",nil, {"Sprecht mit Kundschafterin Alarion nahe der Brücke, die in den Süden der Insel der Sonnenwanderer führt."}},
[8348] = {"Siegel der Fürsten",nil, {"Bringt Bor Wildmähne auf Burg Cenarius das abyssische Siegel."}},
[8349] = {"Bor Wildmähne",nil, {"Sprecht mit Bor Wildmähne auf Burg Cenarius."}},
[8350] = {"Erfolgreiche Lieferung",nil, {"Bringt Eronas Päckchen zu Gastwirtin Delaniel im Gasthaus beim Falkenplatz im Immersangwald."}},
[8351] = {"Bor möchte Euch sprechen",nil, {"Sprecht mit Bor Wildmähne auf Burg Cenarius."}},
[8352] = {"Szepter des Rates",nil, {"Bringt Bor Wildmähne auf Burg Cenarius ein abyssisches Szepter."}},
[8353] = {"Hühnchengegacker für ein Pfefferminzbonbon",nil, {"Spielt Gastwirt Feuerbräu ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Eisenschmiede geben!"}},
[8354] = {"Hühnchengegacker für ein Pfefferminzbonbon",nil, {"Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!"}},
[8355] = {"Kaugummikugelexpress",nil, {"Ahmt für Talvash del Kissel einen Zug nach und er wird Euch dafür eine Kaugummikugel aus Gnomeregan geben!"}},
[8356] = {"Muskelspiel für Nugat",nil, {"Ihr müsst vor Gastwirtin Allison angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Sturmwind gibt!"}},
[8357] = {"Tanzen für Marzipan",nil, {"Tanzt für Gastwirtin Saelienne und Ihr werdet im Gegenzug etwas von dem Marzipan aus Darnassus bekommen!"}},
[8358] = {"Kaugummikugelexpress",nil, {"Ahmt für Kali Remik einen Zug nach und sie wird Euch dafür eine Kaugummikugel der Dunkelspeere geben!"}},
[8359] = {"Muskelspiel für Nugat",nil, {"Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!"}},
[8360] = {"Tanzen für Marzipan",nil, {"Tanzt für Gastwirtin Pala und Ihr werdet im Gegenzug etwas von dem Marzipan aus Donnerfels bekommen!"}},
[8361] = {"Abyssische Kontakte",nil, {"Bor Wildmähne auf Burg Cenarius in Silithus möchte, dass Ihr einen abyssischen Templer vernichtet und ihm dessen abyssisches Wappen als Beweis für Eure Tat bringt."}},
[8362] = {"Abyssische Wappen",nil, {"Ihr habt Eure Fähigkeiten im Kampf mehrfach bewiesen, <Name>. Wir müssen den Druck auf die Twilight-Hammer weiter aufrecht erhalten. Zieht los und bekämpft weitere abyssische Templer. Eure Mühen sollen nicht umsonst sein, und ich werde Euch mit einigen der Sachen entlohnen, die Huum und ich in der Schlacht erbeutet haben."}},
[8363] = {"Abyssische Siegel",nil, {"Ihr habt bewiesen, dass Ihr mehr als fähig seid, es mit einem abyssischen Fürsten aufzunehmen, <Name>. Ich fördere gerne jemanden, der sich als so tapfer erweist. Bringt mir mehr Siegelringe und ich werde Euch reichlich belohnen."}},
[8364] = {"Abyssische Szepter",nil, {"Ihr habt all meine Erwartungen bei weitem übertroffen, <Name>. Ihr habt Euch Gegnern gestellt, bei denen Huum und ich lange und gründlichst über einen Angriff nachdenken würden. Das Beste, was ich in dieser Sache wohl tun kann, ist, Eure Bemühungen im Kampf gegen die Twilight-Hammer und ihre Oberanführer auch weiterhin zu belohnen. Bringt mir noch mehr abyssische Szepter und ich werde mein Bestes tun, um Euch gebührend zu entlohnen."}},
[8365] = {"Piratenhüte Ahoy!",nil, {"Sammelt 20 Südmeerpiratenhüte und bringt sie zurück zur eingebildeten Modeschöpferin beim Dampfdruckpier in Tanaris."}},
[8366] = {"Gaunerei und Erpressung im Südmeer",nil, {"Sicherheitschef Bilgenritzel beim Dampfdruckpier in Tanaris beauftragt Euch, 10 Piraten der Südmeerfreibeuter, 10 Seeräuber der Südmeerfreibeuter, 10 Dockarbeiter der Südmeerfreibeuter und 10 Schwadroneure der Südseefreibeuter zu töten."}},
[8367] = {"Große Ehre",nil, {"Bringt 1 Ehrenabzeichen des Alteractals, 1 Ehrenabzeichen des Arathibeckens, 1 Ehrenabzeichen der Kriegshymnenschlucht und 1 Ehrenabzeichen vom Auge des Sturms zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath."}},
[8368] = {"Schlacht der Kriegshymnenschlucht",nil, {"Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8369] = {"Die Invasoren des Alteractals",nil, {"Bringt 3 Ehrenabzeichen des Alteractals zu einem der Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8370] = {"Eroberung des Arathibeckens",nil, {"Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder."}},
[8371] = {"Gemeinsames Bestreben",nil, {"Bringt 1 Ehrenabzeichen des Alteractals, 1 Ehrenabzeichen des Arathibeckens, 1 Ehrenabzeichen der Kriegshymnenschlucht und 1 Ehrenabzeichen vom Auge des Sturms zu einem Brigadegeneral der Allianz in einer der Hauptstädte oder in Shattrath."}},
[8372] = {"Kampf um die Kriegshymnenschlucht",nil, {"Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Brigadegeneral der Allianz, außerhalb der Schlachtfelder."}},
[8373] = {"Die Kraft der Pinie",nil, {"Verwendet einen Stinkbombenreiniger, um alle Stinkbomben der Verlassenen zu vernichten, die hier in Süderstade losgelassen wurden. Kehrt zu Unteroffizier Hartman in Süderstade zurück, wenn Ihr fertig seid."}},
[8374] = {"Inbesitznahme des Arathibeckens",nil, {"Bringt 3 Ehrenabzeichen des Arathibeckens zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen."}},
[8375] = {"Vergesst das Alteractal nicht!",nil, {"Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen."}},
