[8383] = {"Vergesst das Alteractal nicht!",nil, {"Die Schlacht im Alteractal tobt noch immer, <Name>! Habt Ihr schon Euren Teil zum Sieg beigetragen, <Name>?"}},
[8384] = {"Inbesitznahme des Arathibeckens",nil, nil},
[8385] = {"Gemeinsames Bestreben",nil, {"Ah, <Name>. Ich hoffe Ihr gebt Eure Erfahrung auch noch auf weiteren Schlachtfeldern weiter. Viele unserer Jungens und Mädels sehen mit Ehrfurcht zu erfahrenen Veteranen wie Euch auf."}},
[8386] = {"Kampf um die Kriegshymnenschlucht",nil, nil},
[8387] = {"Die Invasoren des Alteractals",nil, {"Die Schlacht im Alteractal tobt noch immer, <Name>! Ihr müsst schnellstmöglich in das Alteractal zurückkehren und die Invasoren aus dem Gebiet der Frostwolf vertreiben."}},
[8388] = {"Große Ehre",nil, {"Viele mutige Kämpfer sehen zu Euch auf, <Name>. Ihr seid auch weiterhin ein Vorbild für alle, die sich in den Schlachten gegen die Allianz schlagen. Welche Neuigkeiten bringt Ihr mir von Euren Reisen?"}},
[8389] = {"Schlacht der Kriegshymnenschlucht",nil, {"Der Kampf um die Warsongschlucht geht weiter, <Name>. Tut alles, was in Eurer Macht steht, um die Schildwachen der Silverwing aus unserem Land zu vertreiben!"}},
[8390] = {"Eroberung des Arathibeckens",nil, nil},
[8391] = {"Inbesitznahme des Arathibeckens",nil, {"Der Krieg gegen die Horde verbraucht zusehends unsere Vorräte, <Name>. Es ist lebenswichtig, dass wir die Kontrolle über das Arathibecken halten können, um den Ressourcennachschub für unsere Kriegsproduktion aufrechtzuerhalten! Unsere Truppen im Arathibecken können jederzeit Eure Hilfe brauchen."}},
