[9119] = {"Zwischenfall beim Sanktum des Westens",nil, {"Sprecht mit Leybewahrerin Velania beim Sanktum des Westens im Immersangwald."}},
[9120] = {"Der Niedergang Kel'Thuzads",nil, {"Bringt Kel'Thuzads Phylakterium zu der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
[9121] = {"Die Zitadelle des Schreckens - Naxxramas",nil, {"Erzmagierin Angela Dosantos bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 5 Arkankristalle, 2 Nexuskristalle, 1 rechtschaffene Kugel und 60 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem wohlwollend sein."}},
[9122] = {"Die Zitadelle des Schreckens - Naxxramas",nil, {"Erzmagierin Angela Dosantos bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 2 Arkankristalle, 1 Nexuskristall und 30 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem respektvoll sein."}},
[9123] = {"Die Zitadelle des Schreckens - Naxxramas",nil, {"Erzmagierin Angela Dosantos bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern wird Euch die arkane Tarnung kostenfrei gewähren. Euer Ruf bei der Argentumdämmerung muss ehrfürchtig sein."}},
[9124] = {"Was man für Gruftpirscherrüstung braucht...",nil, {"Jäger Leopold bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 30 Teile eines Gruftscheusals."}},
[9125] = {"Teile eines Gruftscheusals",nil, {"Ihr habt mit dem ersten Schwung vorbildliche Arbeit geleistet, <Name>. Solltet Ihr noch mehr Teile haben, nehme ich sie Euch jetzt ab. Für jede abgelieferte Ladung werde ich Euch mit einer Insignie belohnen."}},
[9126] = {"Knochensensenjagd",nil, {"Rohan der Assassine bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 30 Knochenfragmente."}},
[9127] = {"Knochenfragmente",nil, {"Ein Mann meines Berufsstandes kann niemals zu viele Knochenfragmente besitzen, <Name>. Kommt zu mir, wann immer Ihr einen Überschuss an Fragmenten habt, ich tausche sie dann gegen eine weitere Insignie ein."}},
[9128] = {"Die Elementargleichung",nil, {"Erzmagierin Angela Dosantos bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 30 Kerne der Elemente."}},
[9129] = {"Kern der Elemente",nil, {"Glaubt mir, ich verwende diese Kerne zum Anfertigen von Frostfeuerrüstungen. Es ist die Rüstung, die von Magiern getragen wird, die in Naxxramas kämpfen. Ohne Euch und Euresgleichen, würden wir diesen Krieg höchstwahrscheinlich verlieren.nnBringt Ihr mir auch weiterhin mehr Kerne, so werde ich Euch dafür wieder mit Insignien entlohnen."}},
[9130] = {"Waren von Silbermond",nil, {"Bringt Rüstmeisterin Lymels Frachtbrief zu Himmelsmeister Sonnenschwinge in Tristessa."}},
[9131] = {"Bindung des Schreckenspanzers",nil, {"Korfax bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 30 Dunkeleisenfragmente."}},
[9132] = {"Dunkeleisenfragmente",nil, {"Ich gebe Euch für jede Ladung Dunkeleisenfragmente eine Insignie - je mehr, umso besser. Geht mir jetzt aus den Augen, bevor ich Euch noch zweiteile!"}},
[9133] = {"Fliegt nach Silbermond",nil, {"Erwerbt einen Flug nach Silbermond und bringt Rüstmeisterin Lymels Frachtbrief zu Sathren Morgenblau in Silbermond."}},
[9134] = {"Himmelsmeisterin Abendlicht",nil, {"Bringt Rüstmeisterin Lymels Waren zu Himmelsmeisterin Abendlicht am Hirtentor direkt außerhalb von Silbermond."}},
[9135] = {"Zurück zu Rüstmeisterin Lymel",nil, {"Erwerbt einen Flug zurück nach Tristessa in den Geisterlanden und bringt Rüstmeisterin Lymels Waren zu Rüstmeisterin Lymel in Tristessa in den Geisterlanden."}},
[9136] = {"Wildpflanzen",nil, {"Rayne bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte 30 Wildwedel."}},
[9137] = {"Wildwedel",nil, {"Für jeden Bündel Wildwedel, den Ihr bei mir abliefert, werde ich Euch mit einer von Euch gewählten Insignie, die der Dämmerung oder die des Kreuzzugs, bezahlen.nnDie Insignien können beim Rüstmeister für vielerlei Belohnungen eingetauscht werden."}},
[9138] = {"Sonnenkuppe",nil, {"Tötet 10 nerubische Wachen und kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück."}},
[9139] = {"Goldnebel",nil, {"Besiegt 6 Geister der Quel'dorei und 4 Gespenster der Quel'dorei in Goldnebel. Kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück."}},
[9140] = {"Windläuferdorf",nil, {"Sammelt 6 geisterhafte Substanzen und 4 Gargoylefragmente und bringt sie zu Arkanist Vandril in Tristessa in den Geisterlanden."}},
[9141] = {"Sie nennen mich \"den Schleifer\"",nil, {"Einsatzkommandant Metz in der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern möchte eine Ehrenmarke der Argentumdämmerung."}},
[9142] = {"Handwerksverfügung",nil, {"Ihr wisst wie's geht, Kind. Für eine Ehrenmarke bekommt Ihr eine Handwerksverfügung."}},
[9143] = {"Maßnahmen gegen Zeb'Sora",nil, {"Sammelt 6 Trollohren von Zeb'Sora und bringt sie zu Waldläuferin Valanna in den Geisterlanden."}},
[9144] = {"Verschollen in den Geisterlanden",nil, {"Folgt der Straße, die von Morgenluft aus nach Süden führt, und haltet Ausschau nach Kurier Morgenwandler, wenn Ihr die Geisterlande erreicht."}},
[9145] = {"Hilfe für Waldläuferin Valanna!",nil, {"Findet Waldläuferin Valanna und Leutnant Tomathren am Ufer des Elrendarsees nahe Sonnenkuppe in den Geisterlanden."}},
[9146] = {"Meldung bei Hauptmann Helios",nil, {"Sprecht mit Hauptmann Helios bei der Enklave der Weltenwanderer am südlichen Ufer des Elrendarseese in den Geisterlanden."}},
[9147] = {"Der sterbende Kurier",nil, {"Apothekerin Thedra im Immersangwald möchte, dass Ihr ihr 4 verseuchte Blutproben von Tieren aus den Geisterlanden bringt."}},
[9148] = {"Zustellung nach Tristessa",nil, {"Bringt den Brief aus Silbermond zu Arkanist Vandril in Tristessa in den Geisterlanden."}},
[9149] = {"Die verseuchte Küste",nil, {"Bringt 6 verseuchte Murlocrückgrate zu Apotheker Renzithen in Tristessa in den Geisterlanden."}},
[9150] = {"Reise zum vergessenen Sanktum",nil, {"Magister Darenis möchte, dass Ihr zum Sanktum des Mondes westlich von Tristessa reist und dort 8 kristallisierte Manaessenzen von den dort hausenden Arkanverschlingern und Manaschleichern erlangt."}},
[9151] = {"Das Sanktum der Sonne",nil, {"Sprecht mit Magister Idonis im Sanktum der Sonne in den Geisterlanden."}},
[9152] = {"Tombers Vorräte",nil, {"Erlangt Rathis Tombers Vorräte und kehrt zu ihm in Tristessa in den Geisterlanden zurück."}},
[9153] = {"Bedrohliche Schatten",nil, {"Seht auf Eure Karte, um von der Geißel angegriffene Gebiete ausfindig zu machen. Geht dorthin und beschädigt eine Nekropole, indem Ihr die sie umgebenden Truppen der Geißel besiegt. Bringt 10 ihrer nekrotischen Runen zu Kommandant Thomas Helleran bei der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
[9154] = {"Kapelle des hoffnungsvollen Lichts",nil, {"Bringt den Ruf zu den Waffen zum Bewahrer der Schriften in die Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
[9155] = {"Entlang der Todesschneise",nil, {"Tötet 10 hungrige Auferstandene und 10 gegeißelte Kannibalen in der Todesschneise. Kehrt anschließend zu Todespirscher Rathiel in Tristessa zurück, um Eure Belohnung zu erhalten."}},
[9156] = {"Gesucht: Faulbein und Luzran",nil, {"Sucht in den Geisterlanden nach Faulbein und Luzran. Bringt ihre Köpfe zu Todespirscher Rathiel in Tristessa, um eine Belohnung zu erhalten."}},
[9157] = {"Vergessene Rituale",nil, {"Bringt 8 Wellenfrontmedaillons zu Geranis Weißflor in den Geisterlanden."}},
[9158] = {"Überträger der Seuche",nil, {"Weltenwanderin Sedina in der Enklave der Weltenwanderer in den Geisterlanden möchte, dass Ihr 10 Geisterklauenluchse erschlagt."}},
[9159] = {"Gebietet der Seuche Einhalt",nil, {"Weltenwanderin Sedina in der Enklave der Weltenwanderer in den Geisterlanden möchte, dass Ihr 8 Spindelweberlauerer und 10 Vampirnebelfledermäuse erschlagt."}},
[9160] = {"Untersucht An'daroth",nil, {"Untersucht das Gebiet um An'daroth, tötet 12 Spione der Schildwache und kehrt anschließend zu Dame Auriferous in Tristessa in den Geisterlanden zurück."}},
[9161] = {"Schatten des Verräters",nil, {"Begebt Euch in die Geisterlande zum Morgensternturm und sucht nach möglichen Hinweisen auf Dar'Khans Vergangenheit."}},
[9162] = {"Die Vergangenheit erwacht",nil, {"Kehrt mit Dar'Khans Tagebuch zu Waldläuferin Vynna bei der Enklave der Weltenwanderer in den Geisterlanden zurück."}},
[9163] = {"In besetztem Gebiet",nil, {"Stehlt die Nachtelfenpläne: An'daroth, Nachtelfenpläne: An'owyn und Nachtelfenpläne: Auskundschaften der Sin'dorei und kehrt anschließend in die Geisterlande zu Dame Auriferous in Tristessa zurück."}},
[9164] = {"Gefangene der Todesfestung",nil, {"Nehmt Renzithens Regenerationsgebräu und begebt Euch zur Todesfestung. Befreit dort Lehrling Varnis, Apothekerin Enith und Waldläufer Vedoran. Kehrt danach zu Arkanistin Janeda beim Sanktum der Sonne zurück, um Eure Belohnung zu erhalten."}},
[9165] = {"Verfügung des freien Geleits",nil, {"Bringt die unterschriebene Verfügung des freien Geleits zu Einsatzkommandant Metz an der Kapelle des hoffnungsvollen Lichts in den östlichen Pestländern."}},
[9166] = {"Bringt die Pläne nach An'telas",nil, {"Bringt die Nachtelfenpläne zu Magister Sylastor bei An'telas in den Geisterlanden."}},
[9167] = {"Der Tod des Verräters",nil, {"Begebt Euch in die Todesfestung und tötet Dar'Khan Drathir. Bringt anschließend Dar'Khans Kopf zu Magister Kaendris beim Sanktum der Sonne in den Geisterlanden."}},
[9168] = {"Im Herzen der Todesfestung",nil, {"Zerstört 8 Augen von Dar'Khan und meldet Euch anschließend bei Magister Idonis am Sanktum der Sonne in den Geisterlanden."}},
[9169] = {"Die Abschaltung von An'owyn",nil, {"Beschafft die Kristallkontrollkugel und benutzt sie, um die nachtelfischen Mondkristalle bei An'owyn zu deaktivieren. Kehrt anschließend zu Magister Sylastor in An'telas in den Geisterlanden zurück."}},
[9170] = {"Dar'Khans Gefolgschaft",nil, {"Magister Idonis möchte, dass Ihr Euch zur Todesfestung begebt und Masophet den Schwarzen, Jurion den Betrüger, Borgoth den Blutvergießer und Mirdoran den Gefallenen tötet. Kehrt nach Abschluss Eurer Aufgabe zum Sanktum der Sonne in den Geisterlanden zurück und berichtet ihm."}},
[9171] = {"Knusperkost",nil, {"Sammelt 5 knusprige Spinnenbeine ein und kehrt zu Meisterkoch Mouldier in Tristessa in den Geisterlanden zurück."}},
[9172] = {"Bericht an Magister Kaendris",nil, {"Bringt Dar'Khans Tagebuch zu Magister Kaendris beim Sanktum der Sonne in den Geisterlanden."}},
