[9508] = {"Ruf des Wassers",nil, {"Bringt Tel'athions Kopf zu Aqueous im versteckten Riff bei der Blutmythosinsel zurück."}},
[9509] = {"Ruf des Wassers",nil, {"Nehmt das Fläschchen mit reinstem Wasser zu Weissager Nobundo innerhalb der Exodar auf der Azurmythosinsel."}},
[9510] = {"Bälge eines borstigen Grollhufs",nil, {"Versorger Braknar beim Mag'harposten auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 5 Bälge eines borstigen Grollhufs bringt."}},
