[9529] = {"Der Stein",nil, {"Beschafft Euch einen Leerstein von der Todesschneise."}},
[9530] = {"Einen Plan aus Hecken",nil, {"Admiral Odesyus bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr ihm einen ausgehöhlten Stamm und 5 Laubhaufen bringt."}},
[9531] = {"Laubangriff",nil, {"Admiral Odesyus bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr das Treffen beobachtet, das Geezle und der Verräter gemeinsam ausgemacht haben.nnKehrt zu ihm zurück, wenn Ihr ihm die Identität des Verräters mitteilen könnt."}},
[9532] = {"Findet Keltus Dunkellaub",nil, {"Macht Keltus Dunkellaub ausfindig."}},
