[9540] = {"Totem von Tikti",nil, {"Findet das Totem von Tikti, indem Ihr den Hinweisen folgt."}},
[9541] = {"Totem von Yor",nil, {"Findet das Totem von Yor, indem Ihr den Hinweisen folgt."}},
[9542] = {"Totem von Vark",nil, {"Findet das Totem von Vark, indem Ihr den Hinweisen folgt."}},
[9543] = {"Sühne",nil, {"Sprecht mit Amaan dem Weisen beim Tempel von Telhamat auf der Höllenfeuerhalbinsel."}},
